INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Engraulis encrasicolus',
  'Cape Anchovy',
  'Kaapse Ansjovis',
  'Anchovy Cape',
  ARRAY['Engraulis capensis','European Anchovy'],
  'Engraulidae',
  'Anchovies',
  NULL,
  NULL,
  NULL,
  NULL,
  NULL,
  'Decreasing, Least Concern',
  'Stock Status: Optimal;  Fishing Pressure: Optimal',
  '{"other_names": "Engraulis capensis, European Anchovy", "meristics": "Dorsal spines: 0; Dorsal soft rays: 16-18; Anal spines: 0; Anal soft rays: 13-15.", "appearance": "The Cape Anchovy has a deeply cleft mouth, the angle of the gape being behind the eyes. The pointed snout extends beyond the lower jaw. The fish resembles a sprat in having a forked tail and a single dorsal fin, but the body is round and slender, and it has a silver underbelly and blue, green or grey back and sides. It has a silver stripe along flank, disappearing with age.", "about": "In the east Atlantic, this species is present from Bergen, Norway to East London, South Africa perhaps reaching Durban. This coastal, pelagic species is found mainly in shallow water (to 50 m) and to depths of about 400 m, often forming large shoals and is migratory. It is euryhaline, tolerating salinities of five to 41% and in some areas entering lagoons, estuaries or lakes, especially in the warmer months. Pelagic fish occupy a key position in the marine food web where they are the link that transfers energy produced by plankton to large-bodied predator fish, seabirds, and marine mammals. Estimates of forage fish losses to predation are typically much higher than losses to fisheries. Anchovies are mostly reduced to fish meal and oil in industrial-scale factories and used as a protein supplement in agri- or aqua-feeds. The abrupt eastward shift in adult anchovy biomass is being monitored.", "biology": "Spawning occurs multiple times over an extended period with peaks usually in the warmest months. Eggs float in the upper 50 m, hatching in 24 to 65 hours. After hatching, larvae are colourless and transparent. Growth is rapid with fish reaching a length of 9-10 cm after one year. First spawning occurs at sizes greater than 12-13 cm. Maximum age of individuals from southwestern Africa was three years.", "citation": "The IUCN Red List of Threatened Species, FishBase, Status of the South African Marine Fishery Resources, DFFE 2023."}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0056, "exponent": 3.05, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Brama brama',
  'Angelfish',
  'Engelvis',
  'Angelfish',
  ARRAY['Atlantic Pomfret','Pomfret','Angel','Ray''s Bream'],
  'Bramidae',
  'Pomfrets',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Atlantic Pomfret, Pomfret, Angel, Ray''s Bream", "meristics": "Dorsal spines: 0; Dorsal soft rays: 35-38; Anal spines: 0; Anal soft rays: 29-32.", "appearance": "It has a compressed, deep body with a steeply curved head profile. Dorsal and anal fins scaled with rigid fin rays.", "about": "The Anglefish or Atlantic Pomfret is a highly migratory species and circumglobally distributed in tropical to warm temperate waters. It is found from shallow waters to 1,000 m depth. It is found in small schools on the continental slope. Anglefish embark on vertical migrations daily and exercises diel migration where it migrates to shallower depth at night. Seasonal migrations have been linked to water temperature. Its diet consists of myctophids (the most abundant and diverse mesopelagic (200 to 1000 m down) fishes in the Southern Ocean), other small bony fishes and invertebrates. It is an important prey item for large pelagic predators such as dolphin, sharks, and tunas. They feed on small fishes and macroinvertebrates including squid. They are year-round batch spawners that undergo significant morphological changes with growth. In South Africa, it is a common bycatch of the hake fishery where it is generally known and sold as \"angelfish\", although it is not a true marine angelfish.", "biology": "Brama brama is oviparous with planktonic eggs and larvae and spawns in the summer months. Its length is between 40 and 101 cm and can live to be at least 25 years.", "citation": "The IUCN Red List of Threatened Species, Wikipedea"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Squatina africana',
  'African Angelshark',
  'Afrikaanse Engelhaai',
  'Angelshark African',
  NULL,
  'Squatinidae',
  'Angelsharks',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Near Threatened',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: none; Dorsal soft rays: none; Anal spines: none; Anal soft rays: none.", "appearance": "This is a moderately large, flattened, raylike shark. The body is greatly depressed and broad. Enlarged thorns or denticles are present on head between eyes and spiracles and on nape, but none on midback. Head greatly depressed, nearly circular in dorsal view, with a distinct neck at pectoral fin origins; eyes moderately large, dorsal on head; 5 moderately long gill slits, ventrolaterally situated and concealed by the anterior pectoral fin lobes; spiracles very large; nostrils at tip of snout; nasal flaps not fringed, with a simple conical barbel; dermal folds on sides of head with a low triangular lobe opposite mouth corners; mouth terminal, short, extending below eyes; teeth in both jaws with a strong, short cusp and no cusplets; pectoral fins very large, angular, with a triangular anterior lobe; pelvic fins broad; first dorsal fin origin opposite or slightly posterior to free rear tips of pelvic fins; second dorsal fin about as large as first; anal fin absent; caudal fin short, nearly symmetrical, its lower lobe longer than the upper; caudal peduncle with a pair of short keels and a weak upper precaudal pit. Colour: Greyish or a reddish-brown above, white below, back with a series of bold white spots marbled with brown.", "about": "The African Angelshark inhabits shelf and upper slope waters to 500 m depth on sand and mud substrates in the Western Indian Ocean from South Africa to Somalia including Madagascar, and possibly the Gulf of Aden. This species is caught and retained as bycatch in several fisheries including industrial crustacean trawl and small-scale gillnet fisheries throughout most of its range. While the population trajectory is stable at the southern end of its range in KwaZulu-Natal, South Africa, it is suspected to be declining throughout the rest of its range as a result of largely unregulated trawl and gillnet fisheries. Individuals caught in the prawn trawl fishery over the Thukela Banks have been identified as juveniles and therefore there may be a nursery area nearby. The occasional catch (25 per annum) of this species in the KZN shark nets comprised mainly mature individuals of both sexes with a significant proportion released alive.", "biology": "The species is lecithotrophic (no nutrition other than the yolk originally contained within its egg) viviparous and gives birth to live young that are 28-30 cm total length with a gestation length of approximately one year and litter sizes of around six (range 1-12). Males mature at 77-95 cm TL and females at 82-107 cm TL. Maximum known size is 122 cm TL.", "citation": "The IUCN Red List of Threatened Species, FAO"}',
  '[{"measure": "TL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -11.35134983, "exponent": 2.933115959}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Umbrina robinsoni',
  'Baardman',
  'Belman',
  'Baardman',
  ARRAY['Slender Baardman','Slank Belman','Belman Kabeljou','Belmont','Belvis','Slender Tasselfish'],
  'Sciaenidae',
  'Croakers',
  40,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  NULL,
  '{"other_names": " Slender Baardman, Slank Belman, Belman Kabeljou, Belmont, Belvis, Slender Tasselfish. Note: The Canary Drum (Umbrina canariensis) is similar but smaller and found and caught and at depths of 400 m along the coasts from East London to Namibia by the trawl fishery. The Fusca Drum (Umbrina ronchus) does not occur in South African waters, and is a eastern Atlantic species occurring from Gibraltar to Angola. Umbrina canariensis and U. ronchus were once recognised as separate species but taxonomic and genetic studies in 2005 have shown that these are one species with the correct name being U. robinsoni.", "meristics": "Dorsal spines: 11; Dorsal soft rays: 23 - 26; Anal spines: 2; Anal soft rays: 7.", "appearance": "The body is rather elongate and moderately deep; the mouth small, inferior, the teeth are villiform, resembling bristles of a brush; the chin has a short rigid barbel, perforated by a pore at its tip. It has a curved dorsal fin with a caudal fin which is truncate to slightly emarginate. Scales are mostly ctenoid. The swim bladder is simple and carrot shaped, without appendages. Colour: dark brown, the upper sides and back with faint oblique lines along scale rows, tending to fade in larger individuals; pelvic fins and distal portion of anal fin jet black, other fins dusky. Depending on their surroundings they can change from a dark brown colour to silver in a few seconds. Fish found over sandy areas are usually silver in colour, while those in caves or in deeper water are a muddy brown to grey colour. As a juvenile it displays large white patches on its dorsal surface that darken with age.", "about": "Baardman are distributed in the western Indian Ocean from, Cape Point in SA to Oman. Baardman is a shallow-water resident species, occurring from the surf zone to 40 m water depth from False Bay along the entire eastern seaboard of SA. ORI and De Hoop tagging data show very limited coastwise movement, diver counts and depletion sampling indicates that Baardman are probably philopatric (tending to return to or remain near a particular site or area) at \"home\" caves. Adults inhabit sandy substrata, mixed rock and sand surf-zones and subtidal reefs; most commonly associated with high relief limestone or sandstone reef structures. Juveniles are found in mixed rock and sand surf-zones around shallow subtidal reefs. Adults feed on rocky reef and soft sediment invertebrates, including polychaetes (bristle worms), crustaceans and mollusks that are buried in the sand. Juveniles eat predominantly polychaetes (bristle worms) and small crustaceans. Baardman are an important target species in the spearfishery throughout its range where it ranks in the top 10 species. They are relatively easy to spear as they often aggregate in caves. They are targeted species in the shore angling fishery in the WC and EC, particularly on sand banks or in mixed rock and sand habitat. They are also occasionally taken as bycatches in beach treknetters along the northern shore of False Bay.", "biology": "Reproductive style: Gonochorist (separate sexes). Breeding/spawning season: KZN year-round, WC during the summer months (Nov-Feb). Breeding/spawning locality: Throughout range. Age at maturity: Males: 2-3 years; Females: 2.8-3.5 years. Length at maturity: Males: 37-43 cm TL; Females: 39-48 cm TL. Kosi Bay females attaine 50% sexual maturity at a significantly larger size (48 vs 39 cm) but at younger age (2.8 vs 3.4 years) than those at De Hoop. Maximum recorded age: 21 years, male, Stilbaai. Generally, fish in the WC have greater longevity than those in KZN. Maximum recorded weight: 12.6 kg; (SAUFF). Maximum recorded length: 87 cm TL, Kenton on sea.", "citation": "K Hutchings, CG Attwood In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180 and Marine Species Identification Portal, Two Oceans Aquarium and \u201cCOASTAL FISHERY RESOURCES - an easy guide\u201d by the South African Institute for Aquatic Biodiversity. The IUCN Red List of Threatened Species, Bruce Mann of ORI. Life-history strategies of Umbrina robinsoni (Sciaenidae) in warm-temperate and subtropical South African marine reserves by K Hutchings and MH Griffiths."}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 1.148e-05, "exponent": 3.02, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Sphyraena qenie',
  'Blackfin Barracuda',
  'Swartvin Barrakuda',
  'Barracuda Blackfin',
  ARRAY['Chevron Barracuda'],
  'Sphyraenidae',
  'Barracudas',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "Chevron Barracuda", "meristics": "Dorsal spines: 6; Dorsal soft rays: 9; Anal spines: 2; Anal soft rays: 7-9.", "appearance": "The Blackfin Barracuda is a large fish with many dark bars along the body, each bar oblique above the midline and almost vertical on the lower sides. These bands extend laterally down the torso and are v-shaped with the apex facing the front. There are about 18 to 22 bands. The number of bands sets them apart from similar species, the S. jello and S. putnamae. It has a largely blackish caudal fin. Adult Blackfin Barracuda have a pair of small lobes on the rear margin of the caudal fin near the middle of the fin.", "about": "Indo-Pacific: Persian Gulf; Red Sea and East Africa to the central Indian Ocean and French Polynesia. Eastern Pacific: Mexico and Panama. The exact range is uncertain because of confusion with Sphyraena jello (Pickhandle Barracuda) and Sphyraena putnamae (Sawtooth Barracuda). One of the largest barracudas and is found in inshore waters and on coral reefs. Depth range 1-100 m.", "biology": "In general, barracudas in large numbers aggegate during spawning season and migrate to specific spawning areas. The Blackfin Barracuda grows to 1.7 m and almost 30 kg in weight.", "citation": "FishBase"}',
  '[{"measure": "FL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -12.10168377, "exponent": 2.999994039}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Sphyraena barracuda',
  'Great Barracuda',
  'Groot Barrakuda',
  'Barracuda Great',
  ARRAY['Giant Barracuda','Seapike'],
  'Sphyraenidae',
  'Barracudas',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": " Giant Barracuda, Seapike", "meristics": "Dorsal spines: 6; Dorsal soft rays: 9; Anal spines: 2; Anal soft rays: 8.", "appearance": "The Great Barracuda is a long silvery fish with two widely separate dorsal fins. They have large scales and a pointed head with a large mouth and long knife-like teeth. Great Barracuda have a large gape. It has a lower jaw projecting which is helpful in biting. They are a grayish brown above and silvery below which is quite universal throughout their geographic range. They often have dark ink-like spots that are arranged in no pattern on their sides. The young have dark crossbars on their backs and blotches on their sides. The young also have a soft dorsal fin and the anal and caudal fins can be blackish. Males and females are indistinguishable to humans.", "about": "Great Barracuda are distributed in tropical and subtropical Indo-Pacific: Red Sea and east coast of Africa across to Hawaii and the Marquesan and Tuamoto islands, also in western and eastern Atlantic, but not in eastern Pacific. In southern Africa they occur mainly from KZN northwards, but occasional specimens may be found as far south as Port Alfred. Their movement patterns are unknown. There is evidence of nomadism in the Bahamas, but 21 recaptures reported in SA waters indicate residency in young fish, especially within the Kosi Bay estuarine lake system (ORI Tagging Project). Adults are found mainly over coral reefs and seagrass beds, down to at least 100 m. Juveniles inhabit tropical mangrove areas and estuaries. In the Kosi estuarine lake system, fry enter from the sea at a length of 20 mm and live in reed beds until reaching a length of 300 mm, when they move into open water within the estuary, all within a salinity range of 4-35%. Great Barracuda are classified as a marine species with juveniles occurring mainly in estuaries, but they are also found at sea. Adults are piscivorous feeding on species such as needlefish, halfbeaks, puffers, kingfish and mullet. Juveniles are also piscivorous feeding on small mullet, clupeids (the herrings, shads, sardines, etc), pursemouths (silverbiddies), gobies and sparids (sea breams and porgies). Juveniles are caught frequently in the fish traps and illegal gillnets in the Kosi estuarine lake system. Larger individuals are caught on live bait and lures from ski-boats, and sometimes by spearfishers. They are also occasionally taken by shore anglers. In KZN, Great Barracuda are mainly caught in the north of the province. The Kosi estuarine lake system is an important nursery area for this species. Catches by trap fishers may be imposing a high fishing mortality on juveniles.", "biology": "Reproductive style: Gonochoristic (separate sexes), broadcast spawner. Breeding/spawning season: Summer. Breeding/spawning locality: Spawning reported from tropical waters of East Africa and the Seychelles. Age at maturity: Males: 2 years; Females: 3 years. Length at maturity: Males: 55 cm FL; Females: 70 cm FL. Maximum recorded age: Unknown. Maximum recorded weight: 38.6 kg; Christmas Island, Kiribati (IGFA). Maximum recorded length: 180 cm FL.", "citation": "JQ Maggs, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, The Animal Diversity Web."}',
  '[{"measure": "FL", "reference": "", "coefficient": 0.00981, "exponent": 2.875, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Sphyraena jello',
  'Pickhandle Barracuda',
  'Piksteel Barrakuda',
  'Barracuda Pickhandle',
  ARRAY['Seapike'],
  'Sphyraenidae',
  'Barracudas',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Seapike", "meristics": "Dorsal spines: 6; Dorsal soft rays: 9; Anal spines: 2; Anal soft rays: 7-9.", "appearance": "The Pickhandle Barracuda is a large barracuda with dark chevron-like bars along the body that cross the lateral line, and a yellowish caudal fin. The bars are oblique above the lateral line, and almost vertical along the lower sides.", "about": "Pickhandle Barracuda are distributed in the Indo-West Pacific from the Red Sea south to the southeastern coast of SA and east to New Caledonia and Vanuatu and recently reported from Tonga.  Due to widespread confusion with Sphyraena putnamae and Sphyraena qenie, the exact range is uncertain. In SA they are common in KZN, reaching as far south as Knysna in the WC. Their movement patterns are unknown. Of the 48 recaptures (20.4%) reported to date the mean distance moved is only 1km, although some fish moved up to 44km (ORI Tagging Project). Reports of migrating shoals of Pickhandle Barracuda exist and another classifies this species as oceanodromous. Adults occur over coral and rocky reefs but also in bays, estuaries and turbid inner lagoons. Juveniles have been classified as a species where juveniles occur in estuaries but are more abundant at sea. Adults feed mainly on small fish and squid while the diet of juveniles is unknown but probably similar to adults. Spearfishers sometimes shoot a number of these fish when shoals are encountered and are also caught from recreational ski-boats at sea and in estuaries and harbours by shore and boat anglers. They are also taken in fish traps in the Kosi estuarine lake system.", "biology": "Reproductive style: Unknown. Breeding/spawning season: Peak spawning recorded during summer (Dec-Feb) on the KZN south coast. Breeding/spawning locality: Eggs collected mainly in deeper water off the KZN south coast suggesting that spawning occurs at depths >50 m. Age at maturity: Unknown. Length at maturity: Unknown. Maximum recorded age: Unknown. Maximum recorded weight: 24.2 kg; (SASAA). However, there may be some uncertainty with this record as the World angling record is only 15 kg (IGFA). Maximum recorded length: 150 cm FL.", "citation": "JQ Maggs, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, Fishes of Australia"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.0083, "exponent": 2.94, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Sphyraena putnamae',
  'Sawtooth Barracuda',
  'Saagtand Barrakuda',
  'Barracuda Sawtooth',
  NULL,
  'Sphyraenidae',
  'Barracudas',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 6; Dorsal soft rays: 9; Anal spines: 2; Anal soft rays: 7-9.", "appearance": "A silvery-grey barracuda fading to silvery-white below, with many chevron dark markings crossing lateral line on body; each bar oblique in upper half and almost vertical on the lower, and a mostly blackish caudal fin without white tips.", "about": "The Sawtooth Barracuda is nocturnally active but occurs in large schools during the day inhabiting bays and turbid inner lagoons and also occurs near prominent current-swept lagoon and seawardreefs. The diet consists mainly of fish.", "biology": "The maximum recorded total length is 90 cm. Common length is 60.0 cm TL.", "citation": "The IUCN Red List of Threatened Species, FishBase, Fishes of Australia"}',
  '[{"measure": "FL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -11.7953777, "exponent": 2.930708535}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Schedophilus velaini',
  'African Barrelfish',
  'Swerwer Dryfvis',
  'Barrelfish African',
  ARRAY['Oval Driftfish','Peregrine Driftfish','Black Butterfish','Swart Bottervis','Blue Eye Cod'],
  'Centrolophidae',
  'Medusafishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Oval Driftfish, Peregrine Driftfish, Black Butterfish, Swart Bottervis", "meristics": "Dorsal spines: 6-8; Dorsal soft rays: 26-29; Anal spines: 3; Anal soft rays: 18-20.", "appearance": "A large robust firm-bodied medusafish with a blunt snout, and a low spinous dorsal fin grading into the soft-rayed part of the fin. The African Barrelfish is dark silvery-green to brilliant bluish-violet above becoming silvery-white below, with broad pale bars along the upper side, dark metallic-grey fins fading to a dark brownish black along the edges. The deeper-bodied juveniles have much larger dorsal and anal fins, and much longer pelvic fins, and are mottled with irregular darker blotches and vermiculations.", "about": "The African Barrelfish is found in the eastern Atlantic, in the Gulf of Guinea, the Canary Islands, Cape Verde, St. Helena, the Vema Seamount, Tristan da Cunha, Namibia and South Africa. In the western Indian Ocean, it is found in the St. Paul and Amsterdam islands. In the southwest Pacific, it inhabits the waters of Australia, including Lord Howe Island, New Zealand and Rapa Island. In the southeast Pacific, it inhabits the waters off Easter Island, and Juan Fernandez Islands. It is found at depth of between 40-500 m. The diet most often consists of planktonic salps, cephalopods and small fishes.", "biology": "Little else is known about the ecology and life history of this species.", "citation": "The IUCN Red List of Threatened Species, Fishes of Australia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus lanceolatus',
  'Brindle Bass',
  'Briekwa Baars',
  'Bass Brindle',
  ARRAY['Giant Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  Prohibited,
  NULL,
  0,
  NULL,
  NULL,
  'Decreasing, Data Deficient',
  'Protected',
  '{"other_names": " Giant Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 14-16; Anal spines: 3; Anal soft rays: 8.", "appearance": "Small juveniles (12 cm SL) are yellow with wide, dark irregular bars and irregular dark spots on their fins, the first from spinous dorsal fin to belly and chest and extending onto head, the second from base of soft dorsal fin to anal fin and the last at base of caudal fin; small adults (20-50 cm SL) have irregular white or yellow spots on the black areas and fins with irregular black spots; adults (80-150 cm SL) dark brown with faint mottling, the fins with numerous small black spots: large adults (160-230 cm SL) dark brown, the fins darker.", "about": "Brindle Bass are the most widely distributed grouper in the world, throughout the Indo-Pacific from the East African mainland to Hawaii, north to Japan and south to Australia. Its absence in the Persian Gulf is puzzling. The southernmost limit in SA waters is Algoa Bay. Limited data from the ORI Tagging Project suggests that this species is likely to be fairly resident. Adult large fish can be found in estuaries and shelf waters to 100 m and also found on coral and rocky reefs, often associated with caves. Also occasionally caught in shallow (<50 m) prawn trawls on the Thukela Banks so must move onto sand-mud substrata at times. A favourite food on coral reefs and in rocky areas is spiny lobsters. A 177 cm standard length fish, caught from shore at Maui, Hawaiian Islands, contained 2 spiny lobsters and several large crabs. It is also known to eat a variety of fishes, including small sharks and batoids and juvenile sea turtles; in South African estuaries, the main prey item is the mud crab (Scylla serrata). Small juveniles are found in estuaries and sometimes in tidal rock pools. Adults feed on   lobsters, crabs, fish (including elasmobranchs). The juvenile\u2019s diet is unknown. Once a prime target for spearfishers due to its large size, trophy status and the fact that this species is inquisitive and easy to approach underwater, this species was over-exploited and has been fully protected in SA waters since 1992. Prior to its protection this species was also occasionally caught by recreational and commercial skiboat fishers, shore anglers and occasionally taken as a bycatch by shallow prawn trawlers. It is becoming increasingly popular in the live food and aquarium trade; this species is now being hatchery reared and grown in captivity. They are now a protected species and on the Prohibited Species List in South Africa.", "biology": "Brindle Bass are likely to be protogynous, (born female and changes sex to male) but not confirmed. Breeding/spawning season: Unknown. Breeding/spawning locality: Little known; may form spawning aggregations. Spawning aggregation observed in deepwater (>30 m) near Mafia Island, Tanzania. Age at maturity: Unknown. Length at 50% maturity: 129 cm TL; China. Maximum recorded age: Unknown. Maximum recorded weight: 400 kg; New Hebrides. Maximum recorded length: 270 cm TL.", "citation": "ST Fennessy, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180 and FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993."}',
  '[{"measure": "TL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -10.96440029, "exponent": 2.999917984}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus tukula',
  'Potato Bass',
  'Aartappel Baars',
  'Bass Potato',
  ARRAY['Aartappelbaars'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  Prohibited,
  NULL,
  0,
  NULL,
  NULL,
  'Unknown, Least Concern',
  'Protected',
  '{"other_names": " Potato Grouper, Aartappelbaars", "meristics": "Dorsal spines: 11; Dorsal soft rays: 14-15; Anal spines: 3; Anal soft rays: 8.", "appearance": "Body pale brownish grey with several dark brown to black widely spaced blotches, mostly larger than eye and varying in shape from round to oval or dumbbell-shaped; head with smaller dark brown spots and streaks (many radiating from eye, especially posteriorly); dark spots on fins, smaller distally. Large adults may be nearly black.", "about": "Potato Bass are a coral-reef species distributed widely (but sparsely) in the Indo-Pacific. It can be found from the Red Sea and Gulf of Aden to Kenya, Tanzania, MOZ, SA (KZN), Comoros Islands, Seychelles, Oman, Pakistan, India, Sri Lanka, Maldives and Laccadives, as well as from northern Australia, Taiwan, Japan and Papua New Guinea. In SA they are known to occur as far south as the Protea Banks off Port Shepstone in KZN. They are resident; of the 350 Potato Bass tagged, 19 (5.43%) have been recaptured with an average distance moved of only 2 km and a maximum distance of 14km. Adults are reef-associated species mainly found on deep reefs, seamounts, and other current prone areas in 10-400 m. Juveniles may be found in tidal pools and gullies, larger juveniles are found on shallow subtidal reefs in the surf-zone. The adult\u2019s diet includes a variety of reef fish, crustaceans and rays. Juveniles feed on small fish and crustaceans. Once abundant on inshore reefs in northern KZN, this species was over-exploited particularly by spearfishermen due to its large size, trophy status and the fact that this species is inquisitive and easy to approach underwater. The proclamations of the St Lucia and Maputaland MPAs in 1979 and 1986, respectively, have resulted in a strong recovery of this species. In addition, Potato Bass have been fully protected in SA waters since 1992. As a top keystone predator on coral reefs, Potato Bass is an important indicator species of reef health. They are now a protected species and on the Prohibited Species List in South Africa.", "biology": "Reproductive style: Assumed to be protogynous (born female and changes sex to male), but reproductive mode still uncertain. Breeding/spawning season: Spawning has been recorded during spring and summer. Breeding/spawning locality: Possibly northern KZN and southern MOZ, spawning recorded in East Africa; North Kenya Banks and Mafia Island. Spawning aggregations observed by Seychellois fishermen. Age at maturity: Unknown. Length at maturity: ~90 cm TL; SA and MOZ. Maximum recorded age: 26 years. Maximum recorded weight: 77.8 kg; Japan. 65.8 kg caught in KZN in 1956 (SADSAA). Maximum recorded length: Reported to reach over 200 cm TL.", "citation": "C Floros, ST Fennessy In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180 and FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993."}',
  '[{"measure": "TL", "reference": "Visboekie", "coefficient": 1e-05, "exponent": 3.07, "formula_type": "power", "result_unit": "kg"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Platax orbicularis',
  'Batfish',
  'Ronde Vermuisvis',
  'Batfish',
  ARRAY['Orbicular Batfish'],
  'Ephippidae',
  'Batfishes, Spadefishes and Scats',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Orbicular Batfish", "meristics": "Dorsal spines: 5; Dorsal soft rays: 34-39; Anal spines: 3; Anal soft rays: 25-29.", "appearance": "The body is orbicular and strongly compressed. Snout profile of large adults (above 40 cm total length) concave, with bony swelling between eyes. Opercle has no spines. There are five pores on each side of the lower jaw. In terms of colouration, adults (above 20 cm) exhibit a yellowish-silvery or dusky hue with a dark bar through the eye and another bar just behind the head. Occasionally, there are a few small, scattered black spots on the body. The median fins are yellowish with black margins posteriorly, while the pelvic fins are black. Small juveniles reddish brown, with irregular black spots and blotches and small, white (black-edged) ocelli on body. Small black spot at base of last 3 dorsal- and anal-fin rays. Caudal fin is transparent except for base, which is reddish brown.", "about": "This species is distributed in the Indo-Pacific from South Africa, Madagascar, the Mascarenes and Seychelles, north to the Red Sea, Arabian/Persian Gulf, and east to Micronesia and the Tuamotu Archipelago, north to southern Japan and south to northern Australia. It has recently been introduced into Atlantic waters off Florida in the USA. Its depth range is 5-60 m. This species inhabits shallow, protected, coastal areas, including coral reefs and mangroves, and deeper, somewhat silty areas. Juveniles occur solitarily or in small groups in mangroves and sheltered lagoons, where they mimic and associate with floating dead leaves during the day that serve as refuge and food. Juveniles do not demonstrate mimicry behaviour during the night, and adults do not show this behavior at all. Adults occur solitarily, in pairs or in small schools in open waters over sandy areas of deep lagoons, channels and seaward coral reefs. Feed on algae, invertebrates and small fishes. Juveniles are popular in the aquarium trade.", "biology": "Eggs are pelagic and females reach maturity at an estimated length of 32 cm. The maximum total length is about 50 cm.", "citation": "FishBase, The IUCN Red List of Threatened Species,"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Priacanthus hamrur',
  'Crescent-tail Bigeye',
  'Sekelstert Grootoog',
  'Bigeye Crescent-tail',
  ARRAY['Crescent Tail Bigeye'],
  'Priacanthidae',
  'Bigeyes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 10; Dorsal soft rays: 13-15; Anal spines: 3; Anal soft rays: 13-16.", "about": "It is a marine reef-associated fish; depth range 8-250 m. Found in the Indo-Pacific: Red Sea and southern Africa to French Polynesia, north to southern Japan, south to Australia. Uncommon species found in outer reef slopes and deep lagoon pinnacles from 8 to at least 80 m. In small aggregations, sometimes schools in oceanic locations. Also found under ledges or hovering next to coral heads during day. It feeds on small fish, crustaceans, and other small invertebrates. It is taken occasionally in moderate numbers in trawls and by hook and line.", "biology": "Max length: 45.0 cm TL", "citation": "FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Diplodus capensis',
  'Blacktail',
  'Dassie',
  'Blacktail',
  ARRAY['Kolstert','Swartstert','Das','Ntimla'],
  'Sparidae',
  'Seabreams, Porgies',
  20,
  NULL,
  5,
  NULL,
  NULL,
  'Increasing, Least Concern',
  'Over exploited, 25-40% of pristine',
  '{"other_names": " Kolstert, Swartstert, Das, Ntimla Note: The Blacktail was previously described under the name Diplodus sargus capensis, an endemic subspecies found from Angola to MOZ and southern Madagascar. Now it has been elevated to the rank of a separate species. There are two disjunct populations, one along the eastern seaboard of SA from Cape Point to southern and the other from NAM to southern Angola. Genetic comparison suggests that these two populations have been separated for the past two million years and are thus likely to be separate species.", "meristics": "Dorsal spines: 12; Dorsal soft rays: 14-15; Anal spines: 3; Anal soft rays: 13-14.", "appearance": "Blacktail are medium-size, oval, silver fish, sometimes with a few scattered yellow spots on their sides. They always have a large black spot on each side of their caudal peduncle. As juveniles, they have 8-10 pale vertical bars on their sides that fade with age.", "about": "Blacktail are residents. Tagging studies show a meant distance travelled at only 6km. Similarly, juveniles have been shown to display high levels of site fidelity in rocky intertidal nursery areas. Adults inhabit shallow rocky and sandy substrata down to 40 m but predominantly in the surf-zone, usually in close association with rocky areas. Juveniles can be found in intertidal rock pools, subtidal gullies, shallow subtidal reefs, sandy beach surf-zone and estuary mouths. Adults are generalists feeding on a wide range of organisms including red and green algae, echinoids (sea urchins), polychaetes (bristle worms), anthozoans (sea anemone or coral), gastropods (snails, whelks and slugs), bivalves and ascidians (sea squirts). Juveniles will eat small crustaceans such as grammarid (small, shrimp-like crustaceans) and caprellid amphipods (skeleton or ghost shrimps), harpacticoid copepods (small free swimming marine shrimps), polychaetes (bristle worms) and echinoids (sea urchins). Blacktail are caught primarily in the shore fishery along the entire south-eastern seaboard of SA where it ranks as the third most important shore angling species. The mean size of Blacktail in KZN has remained relatively constant at around 225 mm FL. Despite its relatively slow growth rate, Blacktail is still an extremely abundant species along the entire eastern seaboard of SA and appears to have been able to withstand increasing fishing pressure, probably mainly due to its small size/age at maturity, adaptable mode of reproduction, high fecundity and generalist lifestyle. Furthermore, indications are that fisheries management interventions (minimum size and daily bag limits) and establishment of no-take MPAs along the SA coast have helped to sustain Blacktail populations. Fish in warmer KZN waters reach maturity at a smaller size/age and do not reach the same maximum size as those in cooler Cape waters. As this species is extremely important to both recreational and subsistence shore fishers throughout its distribution, monitoring of catch and effort in the SA shore fishery should be improved.", "biology": "Reproductive style: Has been described in KZN as rudimentary hermaphrodite (possessing both male and female potential prior to sexual maturation where no sex change occurs in mature individuals), however evidence also exits that Blacktail is a digynous hermaphrodite with partial protandry (males turning into females) occurring in some of the male population. Breeding/spawning season: May-Dec peaking Jul-Sep in KZN; Aug-Mar peaking Sep-Dec in EC. Breeding/spawning locality: Inshore reefs, probably throughout its distribution. On the KZN south coast most eggs were collected inshore suggesting that spawning takes place inshore of the 30 m depth contour. Age at maturity: Combined sexes: 3 years; Tsitsikamma, EC and KZN. Length at maturity: Combined sexes: 150-160 mm FL; KZN; 211 mm FL; Tsitsikamma, EC. Maximum recorded age: 21 years; female; Tsitsikamma, EC, 14 years in KZN. Maximum recorded weight: 3.0 kg; (SASAA). Maximum recorded length: 450 mm FL.", "citation": "BQ Mann, SW Dunlop In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase. For more information: http://speciesstatus.sanbi.org/assessment/last-assessment/1014/"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 7.4e-06, "exponent": 3.242, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Polysteganus coeruleopunctatus',
  'Blueskin',
  'Blouvel',
  'Blueskin',
  ARRAY['Trawl Soldier','Blueskin Seabream'],
  'Sparidae',
  'Seabreams, Porgies',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Data Deficient',
  NULL,
  '{"other_names": "Trawl Soldier, Blueskin Seabream", "meristics": "Dorsal spines: 12; Dorsal soft rays: 10; Anal spines: 3; Anal soft rays: 8.", "appearance": "This species is distinguished from its congeners by the following set of characters: dorsal-fin rays are reddish pink as well as similar reddish pink on upper margin dorsal-fin ray (no clear yellow or faint yellowish); body with 7 or 8 faint irregular blue spotted lines, not visible from all angles; reddish membranes on caudal-fin lobes and caudal fin with clear white lowest margin of lower caudal fin lobe; without blue iridescence on each scale of dorsal and lateral sides of whitish pink body; canines are large, both upper and lower canines on both jaws usually visible even when mouth closed; body deep; anal- and pelvic- fin rays are whitish or reddish hyaline; weak bony bulge present before and above eye, forming a low median ridge in larger specimens of over 30 cm SL; caudal fin is moderately forked.", "about": "Blueskin are distributed in the Western Indian Ocean from the Red Sea south to at least Coffee Bay in the EC. In SA waters they are primarily caught off MOZ and KZN. Their movement patterns are unknown. Adults are found primarily on deeper reefs from 60-450 m. The habitat of juveniles is unknown; a few small (~5 cm) fish have been trawled on flat, hard substratum in northern KZN. Adults feed primarily on crustaceans. The juvenile\u2019s diet is unknown. In KZN they are mainly caught by commercial ski-boats fishing in deeper water but also caught in line, trawl and trap fisheries off MOZ. The decline in commercial CPUE and catch composition, despite the increasing trend of fishing in deeper water, indicates that this species may well be overfished.", "biology": "Reproductive style: Protogynous hermaphrodite (born female and changes sex to male). Breeding/spawning season: Unknown. Breeding/spawning locality: Unknown. Age at maturity: Females: 2.7 years; MOZ. Length at 50% maturity: Females: 190 mm FL; MOZ. Maximum recorded age: 18 years; male; MOZ. Maximum recorded weight: 4.5 kg, KZN; (SADSAA). Maximum recorded length: 650 mm TL.", "citation": "ST Fennessy, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase For more information: http://speciesstatus.sanbi.org/assessment/last-assessment/1038/"}',
  '[{"measure": "TL", "reference": "Visboekie", "coefficient": 8.794e-06, "exponent": 3.102, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Albula oligolepis',
  'Bonefish',
  'Beenvis',
  'Bonefish',
  ARRAY['Smallscale Bonefish'],
  'Albulidae',
  'Bonefishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  NULL,
  '{"other_names": "Smallscale Bonefish. Note: Previously described as A. vulpes and then A. glossodonta.Currently 9 different species have been described.", "meristics": "Not available.", "appearance": "Body long slender, somewhat cylindrical with the blunt conical snout overhanging the inferior or underslung mouth; eye covered by transparent adipose tissue in specimens longer than 20 cm SL; dorsal fin short-based, on middle of back, longest ray greater in length than pectoral-fin rays, last dorsal-fin ray slightly prolonged; pectoral fins low on body; pelvic fins below posterior dorsal-fin rays, tips not reaching or just reaching anus; caudal fin lobe strongly forked, upper ray slightly longer than lower. Bright silvery-white, upper surface with fine dark longitudinal lines running between their scales, nostrils and tip of snout black, pectoral and pelvic-fin bases slightly yellow, first pelvic-fin ray white; hind margin of caudal fin black. It differs from similar species in having fewer vertebrae and pored lateral-line scales.", "about": "This newly described species is found from the east coast of South Africa to Thailand, including Mozambique, Mauritius, Seychelles, Somalia, Yemen, Oman, India and the northeastern coast of Australia. An inshore, shallow-water species associated with sandy and muddy bottoms, also in mangrove swamps. They will eat crabs, cracker shrimps, worms and mollusks. Popular among light tackle shore anglers along the Zululand coast due to its fighting ability but is not considered good eating due to numerous tiny bones. Around Indian Ocean islands often targeted on fly tackle. They are occasionally netted in the beach seine fishery off Durban. Recent evidence of more Bonefish being caught in southern KZN suggesting a range extension possibly associated with increasing sea temperature. Knowledge of movement behavior is currently lacking for this species in SA waters.", "biology": "Larvae pass through an eel-like leptocephalus phase before maturing into the adult form at about 6 cm TL. It is probably gonochoristic (separate sexes), the male and female reproductive organs being in different individuals. Spawning is thought to occur year-round in slightly deeper water further offshore in tropical and subtropical regions. Age at 50% maturity: 1-2 years. Length at 50% maturity: 25-30 cm TL. Maximum recorded age: Unknown in SA waters, 19 years in the Florida Keys. Maximum recorded weight: 8.61 kg. Maximum recorded length: 100 cm FL, some of the largest specimens on record were caught in KZN waters.", "citation": "JQ Maggs, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, The IUCN Red List, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.0233, "exponent": 2.89, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Sarda sarda',
  'Atlantic Bonito',
  'Atlantiese Bonito',
  'Bonito Atlantic',
  ARRAY['Katonkel'],
  'Scombridae',
  'Mackerel, Tuna, Bonito',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least concern',
  '(ICCAT: Data deficient, improvements being made)',
  '{"other_names": "Katonkel", "meristics": "Dorsal spines: 20-23; Dorsal soft rays: 15-18; Anal spines: 0; Anal soft rays: 14-17.", "appearance": "Mouth is moderately large. Its body is completely scaled, with those scales in the pectoral fin area and the lateral line usually larger in size. They lack teeth on the roof of the mouth. Spleen is large. Liver has elongated left and right lobe and short middle lobe. Swim bladder is absent. Oblique dorsal stripes have a greater angle than in other species of Sarda.", "about": "Atlantic Bonito are distributed in the Eastern Atlantic: Oslo, Norway to Port Elizabeth, South Africa. They are known from the Mediterranean and Black Sea. Western Atlantic: Nova Scotia, Canada to Florida, USA and northern Gulf of Mexico; then from Colombia, Venezuela, and south of the Amazon River to northern Argentina; apparently absent from most of the Caribbean Sea.", "biology": "Found in the epipelagic (constituting the part of the oceanic zone into which enough light penetrates for photosynthesis), neritic (where sunlight reaches the ocean floor) and schooling species that may enter estuaries. Known to be cannibalistic, adults prey on small schooling fishes, invertebrates like squid and shrimps and can swallow relatively large prey. Eggs and larvae are pelagic. Utilized fresh, dried or salted, smoked, canned and frozen. It is able to adapt to different temperatures 12\u00b0 to 27\u00b0C. Maturity: 37.0 cm, Max length: 91.4 cm FL; Common length: 50.0 cm FL; Max. published weight: 11.0 kg; Max. reported age: 5 years.", "citation": "FishBase"}',
  '[{"measure": "FL", "reference": "FishBase", "variant": "Atlantic", "coefficient": 0.0236, "exponent": 2.87, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Sarda orientalis',
  'Striped Bonito',
  'Gestreepte Bonito',
  'Bonito Striped',
  ARRAY['Oriental Bonito','Mackerel Bonito'],
  'Scombridae',
  'Mackerel, Tuna, Bonito',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Oriental Bonito, Mackerel Bonito", "meristics": "Dorsal spines: 17 - 19; Anal spines: 0; Anal soft rays: 14-16.", "appearance": "The body is elongate and rounded; back and sides steel blue, with 8-11 dark, narrow slightly oblique (almost horizontal) stripes on back; lower stripes of broken lines; lower sides and belly silvery; 2nd dorsal fin entirely black.  Two dorsal fins close together.  There are 6-8 finlets after dorsal and anal fins: tail base with 2 small keels separated by third larger keel. Well developed corselet of large scales on front of body and completely covered with very small scales on body behind corselet. No fatty eyelids; top of tongue without cartilaginous crests; jaw teeth conical, large, prominent.  Swim bladder is absent. Spleen is large and prominent in ventral view. Liver has elongated left and right lobes and a short middle lobe.", "about": "This species is widespread in the Indian and Pacific oceans. This is a coastal pelagic species that can be found schooling with small tunas to depths of 30 m. It is also found around some islands. It feeds on clupeoids, other fishes, squids, and decapod crustaceans. It occurs at temperatures of 13.5-23\u00b0C. This species is mainly caught in artisanal fisheries, and catch landings are generally not reported for this species.", "biology": "They have been recorded at 102 cm, though they are commonly no longer than 55 cm. All Tackle Record: 10,65 kg.", "citation": "Smithsonian Tropical Research Institute, FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.0217, "exponent": 2.87, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Pachymetopon grande',
  'Bronze Bream',
  'Bruin Hottentot',
  'Bream Bronze',
  ARRAY['Bluefish','Pens-en-Derms','Copper Bream','Bronze Seabream','Vetvis','Damkoker','Fatfish'],
  'Sparidae',
  'Seabreams, Porgies',
  30,
  NULL,
  2,
  NULL,
  NULL,
  'Decreasing, Near Threatened',
  'Sustainable, > 40% of pristine (Fishing Pressure Sustainable)',
  '{"other_names": " Bluefish, Pens-en-Derms, Copper Bream, Bronze Seabream, Vetvis, Damkoker, Fatfish", "meristics": "Dorsal spines: 11; Dorsal soft rays: 11; Anal spines: 3; Anal soft rays: 10-11.", "appearance": "The Bronze Bream has a rather rotund, deep body.  The dorsal profile of the head is smoothly convex with a slight protuberance in front of the eyes. Individuals often develope distinct bulges or knobs on their heads with age. It a row of conspicuous incisors in each jaw but they have far fewer teeth than the similar looking German. The overall colour of the body is bronze, paler on the belly and breast, with an iridescent blue head and blue pectoral fins with a visible lateral line. Bronze Bream are often confused with Blue Hottentot . They can be distinguished by checking their teeth; Blue Hottentot have more teeth. About the Species Bronze Bream are endemic and are found from southern MOZ to Struisbaai. Reports of the Bronze Bream in Madagascar have not been substantiated. Adults are extremely resident with high site fidelity. Mean displacement of 40 recaptured fish in the Tsitsikamma National Park was 75 m. Adults inhabit shallow intertidal rocky shores and reefs down to 25 m. Juveniles are found in subtidal gullies and shallow (<10 m) subtidal reefs. Adults are omnivorous browsers feeding on red and green algae (62% of diet in EC) and small invertebrates. The juvenile\u2019s diet is similar to adults but with small crustaceans such as amphipods (beach fleas, sand hoppers, etc.) being more important in the diet. The Bronze Bream is a highly prized catch by shore-anglers along the southeast coast of South Africa. They are also targeted by the spearfishery and are occasionally caught in the ski-boat fishery. They are ranked third in terms of landed biomass by shore-anglers in the EC and the second most targeted species (12.5%) being 21% of the catch by spearfishers in the EC and WC.", "biology": "Reproductive style:  Late gonochorist (separate sexes), rudimentary hermaphrodite (possessing both male and female potential in the juvenile phase). Males and females mature after a non-functional intersexual stage. Breeding/spawning season: Spawning in groups with reproductive activity in the EC (Port Elizabeth) between Jan-Jun. However, on the KZN south coast the main spawning season is winter. Spawning may take place in the early morning and evening as some eggs at a late blastula stage (over 100 cells) have been recorded during midday. Breeding/spawning locality: EC, KZN and probably throughout distribution and inside the 30 m depth contour. Age at maturity: Combined sexes: 5.5 years; EC. Length at 50% maturity: Combined sexes: 300 mm FL; EC. Maximum recorded age: 40+ years; EC. Maximum recorded weight: 5.4 kg; (SAUFF). Maximum recorded length: 70 cm TL.", "citation": "M Mwale, PD Cowley In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180 and SAIAB Coastal Fisheries Guide 2016, FishBase, Wikipedia, ORI Fact Sheet For more information: http://speciesstatus.sanbi.org/assessment/last-assessment/1034/"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 2.2e-05, "exponent": 3.046, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Argyrops spinifer',
  'King Soldierbream',
  'Koning Soldaatbras',
  'Bream Kingsoldier',
  ARRAY['King Soldier Bream','Koningsoldaatbras'],
  'Sparidae',
  'Seabreams, Porgies',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "King Soldierbream, Koningsoldaatbras", "meristics": "Dorsal spines: 11-12; Dorsal soft rays: 10-11; Anal spines: 3; Anal soft rays: 8.", "appearance": "Contains elongated filamentous dorsal spines; angular steep forehead profile; head and body are silvery deep pink; dorsal and caudal fins are pink to orange with red margins. About the Species King Soldier Bream is found widespread in tropical Indo-West Pacific, Red Sea and East Africa stretching to the Indo-Malayan archipelago and northern Australia. In SA they extend down the east coast of Africa, occasionally reaching as far south as Knysna and are also abundant on the SE coast of Madagascar. There may be some confusion with the closely related Soldier Bream (A. filamentosus), which also occurs in our area. Their movement patterns are unknown. Some evidence exists of large aggregations during autumn in the Gulf of Aden. Adults are known to occur on a wide range of bottom substrata including soft bottoms and low relief reef in depths from 30-400 m. Juveniles are found in shallow waters of sheltered bays 5-30 m. Adults eat bottom living invertebrates, mostly hard-shelled molluscs, stomatopods (mantis shrimps), shrimps and crabs. The juvenile\u2019s diet is unknown. Small numbers of King Soldierbream are occasionally caught by recreational and commercial ski-boats off northern KZN and by lineboats in MOZ, but they make up a very small component of the catch in both areas.", "biology": "Reproductive style: Probably gonochoristic (separate sexes), however, circumstantial evidence from sex ratios implies that this species may be a protogynous hermaphrodite (born female and changes sex to male). Breeding/spawning season: Locally unknown but reported to be during summer and autumn in Gulf of Aden. Breeding/spawning locality: Locally, spawning has been reported from northern KZN. Age at maturity: Mean age at first maturity was 2.4 years for both females and males in the southern Arabian Gulf. Length at maturity: Mean size at first maturity was 26.9 cm FL for females and 26.7 cm FL for males in the southern Arabian Gulf. Maximum recorded age: 17 years; Gulf of Aden. Maximum recorded weight: 6.2 kg (SADSAA). Maximum recorded length: 700 mm TL.", "citation": "JQ Maggs, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, Gascoyne Wetline Fish Identification Field Guide, FishBase For more information: http://speciesstatus.sanbi.org/assessment/last-assessment/1051/"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.1047, "exponent": 2.6, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Acanthopagrus vagus',
  'River Bream',
  'Slimjannie',
  'Bream River',
  ARRAY['Riverbream','River Perch','Wandering Seabream'],
  'Sparidae',
  'Seabreams, Porgies',
  25,
  NULL,
  5,
  NULL,
  NULL,
  'Decreasing, Vulnerable',
  'Collapsed, <25% of pristine',
  '{"other_names": "Riverbream, River Perch, Wandering Seabream", "meristics": "Dorsal spines: 10-11; Dorsal soft rays: 10-12; Anal spines: 3; Anal soft rays: 8.", "appearance": "The River Bream is a silvery white to grey fish. The body has a compact bream shape, the front of the snout is slightly concave, and the anal fin is yellowish. Adults have yellow anal fins, and the edge of the operculum is blackish; juveniles have bluish fins. They are generally silver, but adults from brackish water are dark to almost black. The centers of the scales are typically dark. About the Species The River Bream is endemic occurring from central Mozambique and southwards to Swartvlei near Knysna. The distribution is not certain as this fish has been confused with the Picnic Seabream, Acanthopagrus berda. Because of the difficulty in differentiating the two fishes, anglers may not be able to identify which one they have caught. They are resident and an estuarine-dependent species with only a marine egg and larval phase. The River Bream is found mainly in estuaries and in brackish water, occasionally entering the lower stretches of freshwater rivers. Adults undertake an annual spawning migration down to the estuary mouth to spawn. Adults tolerate a wide range of salinities and turbidities and are rarely found far from estuaries in the marine environment. Juveniles less than 1 year old enter KZN estuaries at 10-50 mm SL, between Jul-Dec. Egg and larval development takes place at sea. The adult\u2019s diet is wide and includes amphipods (crustaceans with no carapace), polychaetes (bristle worms), bivalves, crustaceans, gastropods (snails, whelks and slugs), aquatic plants and small fish. The juvenile\u2019s diet from 20-60 mm consists mainly of amphipods (crustaceans with no carapace), planktonic copepods (small aquatic crustaceans), chironomid (midge) larvae, tanaids (bottom-dwelling marine and brackish-water crustaceans) and aquatic plants. In SA estuaries River Bream is harvested by recreational boat and shore anglers and by subsistence fishers using lines, fish traps and gillnets. It is a particularly important component of the recreational and subsistence catch in the three large northern KZN estuaries, Kosi Bay, St Lucia and Richards Bay and the Durban Harbour.", "biology": "Reproductive style: Assumed to be a protandrous hermaphrodite (changes sex from male to female). Breeding/spawning season: In KZN, occurring from Apr-Jul, with peak spawning possibly occurring in May-Jun. Also records exist of peak spawning in Durban Harbour in Jul-Aug, with spawning starting as early as March. Breeding/spawning locality: It occurs in nearshore marine environments, primarily in estuary mouths but seldom in the sea.  Spawning recorded in several large KZN estuaries including Kosi Bay, St Lucia, Richards Bay and Durban Bay. Age at maturity: Combined sexes: 3.6 years. Length at maturity: Combined sexes: 23 cm TL or 20 cm FL.", "citation": "NC James, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180 and Coastal Fishery Resource Guide, SAIAB, FOSAF. For more information: http://speciesstatus.sanbi.org/assessment/last-assessment/1013/"}',
  '[{"measure": "TL", "reference": "Visboekie", "coefficient": 1.28e-05, "exponent": 3.09, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Argyrops filamentosus',
  'Soldier Bream',
  'Soldaatbras',
  'Bream Soldier',
  ARRAY['Soldier'],
  'Sparidae',
  'Seabreams, Porgies',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Soldier", "meristics": "Dorsal spines: 12; Dorsal soft rays: 10; Anal spines: 3; Anal soft rays: 8.", "appearance": "Deep and compressed body; the head profile is steep, almost straight from upper lip to in front of eyes then gently curving backward to dorsal fin origin. The fish is characterized by 4 enlarged canines followed by 2 rows of molars at front of jaws and a large eye. Ground colour pinkish, belly and chin silvery-white; dorsal, pectoral and caudal fins pink, anal and pelvic fins very pale pink; margin of the opercula red. This species is easily and often confused with Argyrops spinifer, the King Soldierbream, which appears very similar.", "about": "The Soldier Bream occurs from South Africa to Madagascar, including Mauritius and R\u00e9union, to the Red Sea and Persian Gulf and has a depth range of 20-50 m. Soldierbream is found in small shoals on flatter, outer edges of reefs between 20 and 50 m and also occurs over sand bottoms adjacent to coral reefs. This species feeds on crustaceans and mollusks. Soldier Bream is a keystone species that has experienced significant decline in R\u00e9union and is extremely sensitive to human activities. The threats to this species in R\u00e9union include targeting by commercial fisheries, habitat loss and eutrophication/pollution. It is likely that similar threats may impact this species in other parts of its range. It comprises a small component of the commercial linefish catch in KwaZulu-Natal. Although mainly caught on lines, they are also caught in trawls, fish traps and demersal longliners. This species is mainly exploited by artisanal fishers.", "biology": "It has separate sexes. It can attain 70 cm in length but is more commonly found to 40 cm. The ages at first maturity for females and males are estimated to be 22.2 cm TL and 27.5 cm TL, respectively and attain maturity during the second year of life in the Arabian Sea. The maximum age for this species is estimated to be seven to eight years in the Arabian Sea and to ten years in the Gulf of Aden. Soldierbream is believed to spawn more than once a year, and the fecundity of females was found to range from 73,363 to 386,521 eggs. Length at maturity: 22.2 cm. Max length: 70.0 cm TL Common length: 40.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Neoscorpis lithophilus',
  'Stone Bream',
  'Stinkvis',
  'Bream Stone',
  ARRAY['Stonebream','Stone-bream','Stinker','Black Bream','Sea Moonie'],
  'Kyphosidae',
  'Chubs',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Not Evaluated',
  'Optimally exploited, 40-50% of pristine (Fishing Pressure Sustainable)',
  '{"other_names": " Stonebream, Stinker, Black Bream, Sea Moonie", "meristics": "Dorsal spines: 6-8; Dorsal soft rays: 20-25; Anal spines: 3; Anal soft rays: 23-26.", "appearance": "The Stone Bream is a deep-bodied fish with a smoothly rounded dorsal profile and nearly matching second dorsal and anal fins. The body is a silver-grey colour overall and may have a number of slightly darker, broad vertical bars along the flanks.", "about": "Stone Bream are an endemic subtropical species found between southern MOZ and False Bay and recently also found along the southeast coast of Madagascar. Their movement patterns are unknown but data from the ORI Tagging Project (<1% recaptured from 648 tagged) suggests they may be quite nomadic with a mean distance travelled of 30km. It is believed that its movement behaviour is more likely to be fairly resident, typical of other inshore reef fish species. Adults are inshore reef fish found in shallow, rocky areas with turbulent surf while juveniles occupy shallow subtidal gullies and rock pools. The adult\u2019s diet includes mainly red and green algae but also feeds on copepods (small crustaceans) and other small invertebrates associated with algae. Juvenile\u2019s feed similar to adults with no apparent change in diet with increase in size. Stone Bream are among the top six most important species caught in the shore fishery along the KZN and Transkei coasts, becoming progressively less abundant further south. They are commonly caught by subsistence fishers and larger specimens are occasionally taken by spearfishermen.", "biology": "Stone Bream are gonochoristic (separate sexes) with an extended breeding/spawning season from Jun-Jan in shallow coastal waters off southern MOZ and along the northern KZN coast, possibly with some spawning further south. They mature between 3-4 years old when males reach 262 mm FL and females, 292 mm FL. The maximum recorded age is 10 years however maximum age is likely to exceed this based on record size. The maximum recorded weight is 2.6 kg, and the maximum recorded length is 50 cm FL.", "citation": "BQ Mann, SW Dunlop In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, ORI Fish Fact Sheet"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 1.4e-05, "exponent": 3.08, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Acanthopagrus catenula',
  'Twobar Bream',
  'Tweebalk Seebras',
  'Bream Twobar',
  ARRAY['Twobar Seabream','Bridled Seabream'],
  'Sparidae',
  'Seabreams, Porgies',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  NULL,
  '{"other_names": "Twobar Seabream, Bridled Seabream. Note: Acanthopagrus catenula is a recently validated species that was previously thought to be a southern population of A. bifasciatus.", "meristics": "Dorsal spines: 11; Dorsal soft rays: 12-14; Anal spines: 3; Anal soft rays: 10.", "appearance": "Two conspicuous vertical black bars across the head; dorsal fin with wide black margin, especially distinct in distal spinous part of dorsal fin in fish < 30.36 cm SL, proximal part of dorsal fin yellow; hind margin of caudal fin with narrow black edge in fish to around 30 cm SL. Two rows of molars along each side of upper jaw; outer molar teeth row extends to rear end of both jaws.", "about": "This species is considered to be endemic to the Western Indian Ocean. The depth range is two to 50 m among coral reefs and enters bay and estuaries. Acanthopagrus catenula occurs in small schools and its diet primarily consists of molluscs and other benthic invertebrates.", "biology": "A. catenula is protogynous (individuals developing initially as immature females, then typically as mature females before later potentially changing sex to become a mature male) with 50% age at maturity about 4.6 years at 26.3 cm for females and 2.0 years at 21.9 cm for males. It can reach 50 cm in length but is more commonly found to be 35 cm. This species has been estimated to live to ten years. Additional research is needed to determine population trends and quantify exploitation amounts. There is also a lack of biology and life history information", "citation": "GBIF, FishBase, The IUCN Red List of Threatened Species"}',
  '[{"measure": "FL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -10.15195066, "exponent": 2.910000367}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Hyperoglyphe antarctica',
  'Antarctic Butterfish',
  'Antarktiese Bottervis',
  'Butterfish Antarctic',
  ARRAY['Blue Eye','Bluenose'],
  'Centrolophidae',
  'Medusafishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "Blue Eye, Bluenose", "meristics": "Dorsal spines: 7-8; Dorsal soft rays: 19-21; Anal spines: 3; Anal soft rays: 15-17.", "appearance": "Antarctic Butterfish have a dark blue body above and lighter blue below with large eyes. Larger individuals have a bronze sheen along the flanks.", "about": "Antarctic Butterfish are found in all the southern oceans, at depths of between 40 and 1,500 m but most commonly over or near rocky areas at 100-300 m. Generally, they remain close to the seabed during the day and move up in the water column at night, following concentrations of food. The fish are found over rough ground and at the edges of canyons and steep drop-offs. Antarctic Butterfish appear to prefer cold water as part of their general behavior. Antarctic Butterfish mainly feed on the tunicate Pyrosoma atlanticum which is found near the seabed during the day but dispersed throughout the water column at night. However, they will feed on a range of fish, molluscs, squid and crustaceans, and are also cannibalistic. Juveniles consume small planktonic and sedentary organisms.", "biology": "They can grow to 1.4 m in length and over 50 kg in weight. Studies have shown that fish between 62 and 72 cm are mature and range in age between 8-12 years respectively. Spawning appears to occur during March and April. Mature females can produce between 2 million and 11 million eggs prior to spawning. Its length is up to about 140 cm, with a maximum published weight of 63 kg.", "citation": "FishBase, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Stromateus fiatola',
  'Blue Butterfish',
  'Blou Bottervis',
  'Butterfish Blue',
  NULL,
  'Stromateidae',
  'Butterfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines:  0; Dorsal soft rays: 42-50; Anal spines: 0; Anal soft rays: 33-38.", "appearance": "Blue to brownish in colour and darker spots dorsally, silver to whitish ventrally; juveniles with vertical bars on body and small black pelvic fins.", "about": "Blue Butterfish are found over Mediteranian continental shelves and south along the coasts of Africa to the Cape of Good Hope. It is considered rare in the Adriatic Sea. It typically occurs at depths of 10-70 m but has been recorded in waters up to 160 m deep. Young are often found associated with pelagic jellyfish. They form large shoals. This species feeds on zooplankton, jellyfish and small fishes.", "biology": "Max length: 50.0 cm SL; Common length: 40.0 cm TL.", "citation": "FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Lepidopus caudatus',
  'Buttersnoek',
  'Bottersnoek',
  'Buttersnoek',
  ARRAY['Ribbonfish','Silver Scabbardfish'],
  'Trichiuridae',
  'Cutlassfishes, Ribbonfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  NULL,
  '{"other_names": "Ribbonfish, Silver Scabbardfish", "meristics": "Dorsal spines:  0; Dorsal soft rays: 98-110; Anal spines: 11; Anal soft rays: 59-66.", "appearance": "Its second anal-fin spine is plate-like. Its body is uniformly silvery with a very small pelvic fin. Dorsal fin with a black margin of membrain between first 3 soft dorsal rays and seventh to ninth soft rays in Southern Hemisphere.", "about": "It is found throughout the temperate seas of the world on continental shelf, along its edge and upper slope down to 400 m (600 m in Australia), usually over sandy and muddy bottoms from 100-250 m (over 300 m in Australia). It migrates into midwater at night and form schools; occasionally found inshore in upwelling of deep water when it appears at the surface. It feeds on crustaceans, small squid and fish. Lepidopus caudatus is a commercially targeted species and is a bycatch species in the South African demersal fishery.", "biology": "It attains a length of 125 cm at nine years of age. The age at first reproduction is 1.5-2.0 years. Max length: 210 cm TL; Common length: 117 cm SL; Max.published weight: 8.0 kg", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Argyrozona argyrozona',
  'Carpenter',
  'Kapenaar',
  'Carpenter',
  ARRAY['Silverfish','Silvervis','Silver','Doppies','Doppie'],
  'Sparidae',
  'Seabreams, Porgies',
  35,
  NULL,
  4,
  NULL,
  NULL,
  'Increasing, Near Threatened',
  'Stock status: Optimal;  Fishing Pressure: Optimal',
  '{"other_names": "Silverfish, Silvervis, Silver, Doppies, Doppie", "meristics": "Dorsal spines: 12; Dorsal soft rays: 10; Anal spines: 3; Anal soft rays: 8.", "appearance": "The Carpenter has a torpedo-shaped body with its depth being slightly less than the length of the head, the head having a smooth profile. The lower jaw is slightly protruding. It has a silvery body tinted with reddish hues, and paler on the lower body. There are a number of red horizontal lines along the flanks with silver between them appearing soon after death. The fins are pink and the eyes are orangey red.", "about": "Carpenters are endemic to SA and can be found in warm temperate waters and are most abundant between Cape Point and Kei Mouth. They are resident to rocks reefs on the South African coast from Cape Point to Margate. Adults Bentho-pelagic associated with high profile reef from 50-200 m are resident to a preferred area, but a small portion of Carpenters disperse widely. Juveniles inhabit shallow (10-40 m) inshore reefs. It is a predator, with pronounced canines that it uses to prey on sardines, anchovies and squid. As a juvenile it feeds primarily on crabs, crab larvae, amphipods (crustaceans with no carapace) and polychaete worms. Due to historic overfishing, the Carpenter saw a significant decline in its population until limitations were placed on its commercial fishery in 2003. Since then, wild populations have been slowly recovering. Carpenter continues to be the most important sparid in terms of catch volume for the commercial linefishery. The demersal trawl bycatch, especially of the inshore component of this fishery, continues to be significant with an estimated 80% of the linefish catch.", "biology": "Carpenters are gonochorist (seperste sexes). Breeding/spawning season: Summer and autumn. Breeding/spawning locality: Throughout its distribution range sometimes migrating to the Agulhas bank to spawn. Age at maturity: Combined sexes: 2 years (EC), 3 years (Agulhas Bank) and 5 years. Length at maturity: Males: 222 mm FL (EC), 261 mm FL (Agulhas Bank), 297 mm FL (Tsitsikamma); Females: 206 mm FL (EC), 267 mm FL (Agulhas Bank), 292 mm FL. Maximum recorded age: 30 years. Maximum recorded weight: 4.1 kg; (SADSAA). Maximum recorded length:  80 cm TL.", "citation": "SE Kerwath, H Winker In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, Two Oceans Aquarium, Wikipedia. For more information: http://speciesstatus.sanbi.org/assessment/last-assessment/1049/"}',
  '[{"measure": "FL", "reference": "Visboekie", "coefficient": 4e-05, "exponent": 2.8553, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Plotosus nkunga',
  'Eel Catfish',
  'Baberpaling',
  'Catfish Eel',
  ARRAY['Eeltail Barbel','Eel-catfish','Eel Tail Catfish','Barbel Eel','Eel Tail Barbel','Stinging Eel-catfish'],
  'Plotosidae',
  'Eeltail catfishes, Coral catfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "Eeltail Barbel, Eel-catfish, Eel Tail Catfish, Barbel Eel, Eel Tail Barbel, Stinging Eel-catfish", "meristics": "Dorsal spines: 1; Dorsal soft rays: 4; Anal spines: 0; Anal soft rays: 88-109.", "appearance": "Preserved specimens are brown or grey above, lighter brown or whitish below; 2 faint, longitudinal stripes sometimes present on body but not extending onto head. The scaleles skin is coated with mucus, and its mouth is surrounded by four pairs of sensory barbells. The spines of the serrated dorsal fin, which can lock into an erect position, are covered with toxic mucus that can be poisonous to humans.", "about": "It is found in estuaries and along open coasts in the western Indian Ocean from Kosi Bay to Knysna in South Africa, possibly extending to Zanzibar, Tanzania. It feeds by using its barbells to feel around in the mud for crabs and small fish.", "biology": "Not much is known about its reproduction or growth. Max length: 51.0 cm SL.", "citation": "FishBase, iNaturalist"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.00457, "exponent": 3.07, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Halaelurus lineatus',
  'Banded Catshark',
  'Band Kathaai',
  'Catshark Banded',
  ARRAY['Banded ShyShark','Band Skaamoog','Lined Catshark','Lined Cat Shark'],
  'Pentanchidae',
  'Deepwater Catsharks',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Banded ShyShark, Band Skaamoog, Lined Catshark, Lined Cat Shark", "meristics": "Dorsal spines: 0; Dorsal soft rays: 0; Anal spines: 0; Anal soft rays: 0.", "appearance": "The Banded Catshark has an upturned knob on the snout, a narrow head, and numerous small dark spots on the body. It is generally pale brown with numerous pairs of narrow, vertical, dark brown stripes outlining obscure dusky saddles, cream below.", "about": "It is found in the waters off the coasts of Beira, Mozambique, to East London, South Africa and inhabits rocky and soft substrates on the continental shelf and upper slope from the surfline to depths of at least 290 m. It feeds on small crustaceans, bony fish, and cephalopods. Although commonly caught by shore anglers, it is not generally considered a game fish.", "biology": "This species reaches a maximum size of 56 cm TL; females are mature at 46-52 cm TL, and males at 48-56 cm TL. Reproduction is possibly oviparous, with the females carrying up to eight capsules in each uterus simultaneously which are retained until embryos are close to hatching.", "citation": "FishBase, The IUCN Red List of Threatened Species"}',
  '[{"measure": "TL", "reference": "TORRES, F.S.B., Jr.", "formula_type": "log-linear", "coefficient": -12.89350986, "exponent": 3.048912048}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Haploblepharus fuscus',
  'Brown Catshark',
  'Bruin Kathaai',
  'Catshark Brown',
  ARRAY['Brown Shyshark','Bruin Skaamoog','Brown Shy Shark','Plain Happy'],
  'Scyliorhinidae',
  'Catsharks',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Vunerable',
  NULL,
  '{"other_names": "Brown Shyshark, Bruin Skaamoog, Brown Shy Shark, Plain Happy", "meristics": "Dorsal spines: 0; Dorsal soft rays: 0; Anal spines: 0; Anal soft rays: 0.", "appearance": "The Brown Catshark is small but stoutly built, with a broad, flattened head and rounded snout. Unlike other shysharks, the brown shyshark has a plain brown colour above and white below, though some individuals have faint \"saddle\" markings or light or dark spots. The largest shysharks are usually plain yellowish-brown above, becoming just yellowish below. The nostrils are very large, and are flanked by greatly expanded, triangular skin flaps that reach the mouth. There are furrows at the corners of the mouth on both jaws. The teeth have a central cusp and a pair of smaller cusplets on the sides. The five pairs of gill slits are positioned on the upper sides of the body.", "about": "The Brown Shyshark is endemic to South Africa to the shallow, coastal waters in the Southeast Atlantic and Western Indian Oceans from False Bay, Western Cape to Durban. This benthic (bottom-dwelling) species is usually found close to the bottom over sandy flats or rocky reefs, from the intertidal zone to a depth of 35 m. However, it has been reported from as deep as 133 m. The range of this species overlaps with the Puffadder Shyshark in the southeastern Cape region. There, the Brown Shyshark tends to favor shallow inshore habitats, while the Puffadder Shyshark inhabits deeper offshore waters. It feeds on lobsters, crabs and small bony fish. Although the Brown Shyshark is discarded, many fishers regard the species as a nuisance and persecute them as such likely causing high post-release mortality. It is suspected that the Brown Shyshark has undergone a population reduction of 30-49% over the past 60 years. Further research is needed on population size and trends, life history, and catch rates.", "biology": "Reproduction is oviparous, with females depositing egg capsules (known as \"mermaid''s purses\") two at a time. Embryos feed solely on yolk. Males reach sexual maturity at a length of 68-69 cm, and females at a length of 60-61 cm. Max length: 69,0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.00316, "exponent": 3.12, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Haploblepharus pictus',
  'Dark Catshark',
  'Donker Kathaaig',
  'Catshark Dark',
  ARRAY['Dark Shyshark','Dark Shy Shark','Donker Skaamoog','Pretty Happy'],
  'Scyliorhinidae',
  'Catsharks',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Dark Shyshark, Dark Shy Shark, Donker Skaamoog, Pretty Happy", "meristics": "Dorsal spines: 0; Dorsal soft rays: 0; Anal spines: 0; Anal soft rays: 0.", "appearance": "This small, stocky shark has a wide, flattened head with a rounded snout and a large flap of skin extending from before the nostrils to the mouth. Its dorsal colouration background colour ranges from light brown to reddish to grayish to almost black and may feature 6-8 orange, brown, or blackish variably shaped saddles along the back and tail, edged more or less obviously in black transitioning abruptly to white or cream below, sometimes with dark blotches beneath the paired fins. There may also be white spots within and/or between the saddles. The pectoral and pelvic fins are broad and rounded; the males have stout claspers. The pelvic and anal fins are about as large as the dorsal fins.", "about": "The Dark Shyshark is a small demersal catshark that inhabits kelp forests, sandy areas, and rocky reefs on the continental shelf in the Southeast Atlantic and Western Indian Ocean off Namibia and South Africa down to a depth of 35 m. The Dark Shyshark is a generalist predator whose main food sources are, in descending order of importance, small benthic crustaceans, bony fishes, and molluscs. Larger sharks consume proportionately more crustaceans. Polychaete worms and echinoderms are also taken on occasion. It is frequently caught by recreational fishers and persecuted as a pest and often kill it.", "biology": "Reproduction is oviparous and proceeds throughout the year. Females lay two egg cases at a time, which hatch after 6-10 months. Both sexes grow at approximately the same rate, reaching sexual maturity at around 15 years of age. Mature males and females range from 40-57 cm and 36-60 cm long respectively. The maximum lifespan is 25 years.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.00316, "exponent": 3.12, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Holohalaelurus regani',
  'Izak Catshark',
  'Izak Kathhaai',
  'Catshark Izak',
  ARRAY['Izak Shyshark','Izak Skaamoog','Izak Cat Shark','Mottled Dog-fish','Halaluja'],
  'Pentanchidae',
  'Deepwater Catsharks',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Increasing, Least Concern',
  'Stock status: Unknown - Fishing pressure: Unknown',
  '{"other_names": "Izak Shyshark, Izak Skaamoog, Izak Cat Shark, Mottled Dog-fish, Halaluja", "meristics": "Dorsal spines: 0; Dorsal soft rays: 0; Anal spines: 0; Anal soft rays: 0.", "appearance": "The Izak Catshark has a short, wide, flattened head and a robust body tapering to a long, slender tail. It can be identified by its ornate colour pattern of dark brown spots (in juveniles) or reticulations and blotches (in adults) on a light yellowish bac kground, as well as by the enlarged dermal denticles over its pectoral fins and along its dorsal midline from the snout to the second dorsal fin. Juvenile Izak catsharks are boldly patterned with many irregularly shaped dark brown spots on a light yellow to yellowish brown background. The spots enlarge and fuse with age to form an intricate pattern of reticulations and U-shaped markings in adults. The underside is plain white, with obvious black sensory pores beneath the head, body, and paired fins. Specimens less than 23 cm TL are blackish with white side spots.", "about": "The Izak Catshark is a small demersal catshark endemic to Namibia and South Africa in the Southeast Atlantic and Western Indian Oceans where it occurs on the continental shelf and slope at depths of 15-1,075 m. Females and juveniles tend to be found in shallower water than males. For the most part, the number of sharks in a given area remains largely constant throughout the year. However, sharks at the southernmost point of the Agulhas Bank may perform a small autumn migration towards the shore. It is a generalist that feeds on a wide variety of bony fishes, crustaceans, also fish offal, hagfish eggs, and cephalopods; larger sharks consume proportionately more crustaceans and fewer fishes. The Izak catshark is harmless and of no commercial value. It is regularly caught incidentally (and discarded) by a bottom trawl fishery targeting hake south of Cape Town. In contrast to most cartilaginous fishes, its numbers have been increasing in the presence of commercial fishing. The reasons for this may include its high reproductive rate, its breeding in less-fished shallower waters, its hardiness allowing for high post-capture survival, and its opportunistic diet.", "biology": "Oviparous, with one egg laid per oviduct at a time. Embryos feed solely on yolk. Max length: 69.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Poroderma pantherinum',
  'Leopard Catshark',
  'Luiperd Kathaai',
  'Catshark Leopard',
  ARRAY['Leopard Shyshark','Luiperd Skaamoog','Leopard Cat Shark','Blackspotted Catshark','Swartkol-kathaai'],
  'Scyliorhinidae',
  'Catsharks',
  Prohibited,
  NULL,
  1,
  NULL,
  NULL,
  'Increasing, Least Concern',
  'Protected',
  '{"other_names": "Leopard Shyshark, Luiperd Skaamoog, Leopard Cat Shark, Blackspotted Catshark, Swartkol-kathaai", "meristics": "Dorsal spines: 0; Dorsal soft rays: 0; Anal spines: 0; Anal soft rays: 0.", "appearance": "Striking leopard-like colour pattern, nasal barbels, first dorsal fin positioned posteriorly. Barbels of anterior nasal flaps longer are nearly or quite equal to nostril width and usually reaching the mouth. They have a striking, very handsome, leopard-like colour pattern of rosettes of dark spots and lines surrounding light centres, arranged in irregular longitudinal rows. They exhibit three different spot patterns.", "about": "Leopard Catsharks are endemic to SA and are resident displaying strong site fidelity and limited dispersal, based on the ORI Tagging Project and localized tag-recapture data. Adults are confined to the shelf in warm-temperate waters, from the intertidal zone to depths of 250 m. Usually found on rocky reefs and kelp forests and tend to be more active nocturnally. Very little is known about the juveniles, but they are likely to remain hidden amongst seaweed and kelp on shallow subtidal reefs. Adults feed on small fishes, octopus, crustaceans and polychaete worms. The juvenile\u2019s diet is unknown. Leopard Catsharks are commonly caught by shore anglers but often released. Sometimes regarded as a pest by recreational and commercial ski-boat fishers and killed. They are also occasionally taken as bycatch by inshore trawlers.", "biology": "Reproductive style: Oviparous - produces one egg per oviduct. Breeding/spawning season: No obvious breeding season but peak reproductive activity is believed to occur in autumn (Mar-May). Breeding/spawning locality: Assumed to be throughout distribution although limited observation of eggs cases in the EC. Age at maturity: Combined sexes: 10-17 years. Length at maturity: Males: 61-77 cm TL; Females: 51-67 cm TL; EC. Maximum recorded age: 19 years. Maximum recorded weight: 3.2 kg (SASAA. Maximum recorded length: 84 cm TL.", "citation": "J Escobar-Porras, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180 and Marine Species Identification Portal"}',
  '[{"measure": "TL", "reference": "Visboekie", "coefficient": 0.00802, "exponent": 2.92, "formula_type": "power", "result_unit": "kg"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Haploblepharus edwardsii',
  'Puffadder Catshark',
  'Pofadder Kathaai',
  'Catshark Puffadder',
  ARRAY['Puffadder Shyshark','Pofadder Skaamoog','Puff-adder Shy Shark','Happy Eddie'],
  'Scyliorhinidae',
  'Catsharks',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Endangered',
  'Stock status: Depleted, Fishing Pressure: Unknown',
  '{"other_names": "Puffadder Shyshark, Pofadder Skaamoog, Puff-adder Shy Shark, Happy Eddie", "meristics": "Dorsal spines: 0; Dorsal soft rays: 0; Anal spines: 0; Anal soft rays: 0.", "appearance": "The Puffadder Shyshark has a slender, flattened body and head and is more slender than other shysharks, with a short, broad, dorsally flattened head and a narrowly rounded snout. The dorsal colouration is strikingly patterned and consists of a light to dark brown bac kground with a series of 8-10 yellowish to reddish brown \"saddles\" with darker margins, all covered by a profusion of small white spots over its back. The underside is white. The dorsal, pelvic, and anal fins are all of similar sizes. The dorsal fins are located far back on the body, the first originating behind the pelvic fin origins and the second behind the anal fin origin. The pectoral fins are broad and moderate in size.", "about": "The Puffadder Catshark is a small catshark endemic to South Africa in the Southeast Atlantic and Western Indian Oceans, occurring from Langebaan Lagoon, Western Cape, to Algoa Bay, Eastern Cape. This species inhabits rocky reefs, kelp forests, sandy substrates, and the upper continental slope to a depth of 288 m. Recreational and rock lobster fishers consider the species a nuisance and persecute them as such, likely causing high mortality.  It is a generalist predator that feeds mainly on crustaceans, polychaete worms, and small bony fishes and octopus at times. Further research is needed on population size and trends, and life history, and catch rates should be monitored.", "biology": "Reproduction is oviparous with egg cases laid in pairs and reproduction occurs year-round.  The young shark hatches after three months. Size-at-birth is 9.3 cm TL. Age-at-maturity and maximum age are unknown. It reaches a maximum size of 64 cm TL; males and females mature at 37 cm TL. The age at maturation is estimated to be around 7 years, and the maximum lifespan is at least 22 years.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.00331, "exponent": 3.14, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Holohalaelurus punctatus',
  'Spotted Catshark',
  'Spikkel  Kathaai',
  'Catshark Spotted',
  ARRAY['Spotted Shyshark','Spikkel Skaamoog','Punctate Dogfish','Spotted Dogfish','African Spotted Catshark','White-spotted Izak'],
  'Pentanchidae',
  'Deepwater Catsharks',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Endangered',
  NULL,
  '{"other_names": "Spotted Shyshark, Spikkel Skaamoog, Punctate Dogfish, Spotted Dogfish, African Spotted Catshark, White-spotted Izak", "meristics": "Dorsal spines: 0; Dorsal soft rays: 0; Anal spines: 0; Anal soft rays: 0.", "appearance": "A broad-headed, dwarf catshark with small dark brown spots closely set on a yellow-brown or orange-brown background; small black pores on underside of head and body; no labial furrows.", "about": "The Spotted Catshark is found in the western Indian Ocean off the coasts of Natal, South Africa, southern Mozambique, Madagascar, Kenya, and Tanzania at depths of between 220 and 440 m. It feeds on small fishes, crustaceans, and cephalopods. Due to historically heavy levels of fishing pressure, ongoing fishing at lower levels of effort, a lack of recent records despite research, and some possible refuge at depth, this species is suspected to have undergone a population reduction of 50-79% over the past three generation lengths (30 years).", "biology": "Reproduction is oviparous, with one egg-case laid per oviduct with pairs of eggs laid at a time. Embryos feed solely on yolk.  It reaches a maximum size of 34 cm TL, males mature at 29 cm TL and females mature at 24 cm TL. Reproduction is oviparous, although the reproductive periodicity is unknown. The adult males are larger than females, a characteristic unusual among sharks.  Age-at-maturity is unknown, and maximum age is thought to be 10 years.  Max length Male: 35.0 cm TL; Female: 26.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Poroderma africanum',
  'Striped Catshark',
  'Streep  Kathaai',
  'Catshark Striped',
  ARRAY['Pyjama Shyshark','Pyjama Shark','Striped Cat Shark','Streep Skaamoog'],
  'Scyliorhinidae',
  'Catsharks',
  Prohibited,
  NULL,
  1,
  NULL,
  NULL,
  'Increasing, Least Concern',
  'Protected',
  '{"other_names": "Pyjama Shyshark, Pyjama Shark, Striped Cat Shark, Streep Skaamoog", "meristics": "Dorsal spines: 0; Dorsal soft rays: 0; Anal spines: 0; Anal soft rays: 0.", "appearance": "The striking dark longitudinal stripes, nasal barbels and posterior dorsal fins of this catshark make it unmistakable. Barbels of anterior nasal flaps short, less than half the nostril width, and usually not reaching the mouth.", "about": "The Striped Catshark is endemic to SA waters in EC and WC and is found from Saldanha Bay to just northeast of East London. It is a resident.  Based on ORI tagging data and high-resolution movement data, the species shows strong site fidelity and limited dispersal. Adults are a benthic species (bottom dwelling) on rocky reefs from shore to 100 m. Active nocturnally and known to rest in caves during the day. Juveniles inhabit similar areas. Both adults and juveniles eat cephalopods (octopus and squid), crustaceans, small bony fishes, bivalves and polychaete worms. They are cught as an unwanted bycatch in recreational and commercial linefisheries, demersal shark longline and inshore trawl fisheries. Although edible, flesh is seldom used or marketed.", "biology": "Reproductive style: Oviparous, lays one egg per oviduct. Hatched embryos are ~14 cm TL. Breeding/spawning season: It is believed that females lay eggs throughout the year. Breeding/spawning locality: Little known but likely to occur throughout distribution. However, few eggs cases observed in the northern parts of the EC. Age at 50% maturity: 10-13 years. Length at 50% maturity: Males: 84,5 cm TL, Females: 88,5 cm TL, Algoa Bay. Another study suggested that maturity is reached in males between 58-76 cm TL and in females between 65 -72 cm TL.", "citation": "J Escobar-Porras, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180 and Marine Species Identification Portal. Illustration credits: SAIAB, IZIKO Museum For more information or to get more involved to help monitor Southern African shark, skate and ray (Elasmobranchii) populations visit https://www.elmoafrica.org/"}',
  '[{"measure": "TL", "reference": "Visboekie", "coefficient": 0.00674, "exponent": 2.958, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Halaelurus natalensis',
  'Tiger Catshark',
  'Tier  Kathaai',
  'Catshark Tiger',
  ARRAY['Tiger Shyshark','Tiger Cat Shark','Tier Skaamoog'],
  'Pentanchidae',
  'Deepwater Catsharks',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Vunerable',
  NULL,
  '{"other_names": "Tiger Shyshark, Tiger Cat Shark, Tier Skaamoog", "meristics": "Dorsal spines: 0; Dorsal soft rays: 0; Anal spines: 0; Anal soft rays: 0.", "appearance": "The body of the Tiger Catshark is slim and firm. The head is broad and flattened, with a distinct and upturned snout tip. The horizontally oval eyes are situated high on the head. Beneath each eye is a broad ridge, and behind is a spiracle. The pectoral fins are fairly large and rounded. The anal fin is roughly equal in size to the pelvic fins, and smaller but longer based than the second dorsal fin. The short caudal fin has an indistinct lower lobe and a ventral notch near the tip of the upper lobe. The skin is thick; the dermal denticles have three-pointed crowns and are widely spaced compared to other species in the genus. Coloured yellowish brown above and cream below, this shark has a characteristic series of ten dorsal saddles from the head to the tail; each saddle is dark brown with a darker edge and a lighter middle. Unlike in the similar lined catshark, there are no spots or additional markings between the saddles.", "about": "The Tiger Catshark is a small southern African endemic that occurs over sandy areas and near reef peripheries in the Southeast Atlantic and Western Indian Oceans from South Africa to southern Mozambique from close inshore to depths of 172 m. The tiger catshark is a sluggish predator of a diverse array of benthic organisms.  Its diet is dominated by bony fishes and crustaceans, and also includes cephalopods, polychaete worms, smaller sharks, and scavenged fish offal. It has been observed at the spawning grounds of the chokka squid, feeding on squid that have descended to the bottom to mate and deposit eggs. Documented predators of this species include the Broadnose Sevengill Shark and the Ragged Tooth Shark.  The Tiger Catshark is caught incidentally by bottom trawlers, recreational anglers, and rarely squid fishers. Though edible, it is not a valued catch and is usually discarded.", "biology": "Reproduction is oviparous: females produce 6-11 (typically 6-9) eggs in each of their two oviducts at a time. The eggs are contained in tough capsules around 4 cm long and 1.5 cm across; the capsule has thick tendrils at the corners that allow it to be secured to the sea floor. The female retains the eggs internally until the embryos are substantially developed, measuring at least 4.3 cm long. Thus, the eggs hatch within only one or two months of being laid, reducing the amount of time that they are exposed to predators. Males and females begin to reach sexual maturity at lengths of 29-35 cm and 30-44 cm respectively. Max length: 45.0 cm TL male; 47.0 cm TL female.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -12.89350986, "exponent": 3.048912048}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Scyliorhinus capensis',
  'Yellowspotted Catshark',
  'Geelspikkel Kathaai',
  'Catshark Yellowspotted',
  NULL,
  'Scyliorhinidae',
  'Catsharks',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Near Threatened',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 0; Anal spines: 0.", "appearance": "The Yellowspotted Catshark is a long slender fish with rough skin and a compressed body. The upperparts are light to dark grey, copiously spotted with small golden-yellow spots. There are eight or nine dark patches on the back which bridge the spine. The underside is cream-coloured. Small flaps cover the nostrils but do not extend as far as the mouth and there are no nasoral grooves. There are two triangular dorsal fins, the 1st one considerably larger than the 2nd one.", "about": "The Yellowspotted Catshark is a small endemic common inshore to offshore species found in the seas around the coast of southern Africa at depths down to about 500 m. Its range extends from L\u00fcderitz in Namibia to central KZN. Although fairly common off the coast of South Africa it occurs less frequently and in deeper waters in the more tropical parts of its range in Namibia and KZN. Although seen over sandy bottoms, it is more frequent on rocky reefs. It feeds on small fish, crustaceans and squid. In the offshore waters there is a large hake fishery, and it is caught and discarded as bycatch during trawling.", "biology": "The Yellowspotted Catshark is an oviparous (egg-laying) species, laying a single egg at a time, and enclosing it in an egg case roughly 8 by 3 cm. The juvenile fish when they hatch may measure about 25 cm and the adults can grow to 1.22 m but most individuals are under a metre long. Males mature at 60 cm TL and females mature at 72 cm TL.  Maximum age is unknown but estimated to be 25 years. Max length: 122 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Dinoperca petersi',
  'Cavebass',
  'Spelonk Baars',
  'Cavebass',
  ARRAY['Lampfish','Lanternfish','Butterbream','Butterfish','Blackfish'],
  'Dinopercidae',
  'Cavebasses',
  Proposed: 33  TL,
  NULL,
  5,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "Lampfish, Lanternfish, Butterbream, Butterfish, Blackfish", "meristics": "Dorsal spines: 9 - 11; Dorsal soft rays: 18-20; Anal spines: 3; Anal soft rays: 12-14.", "appearance": "The Cavebass is blackish brown in colour; body with white specks which fade in adults; black band from front of snout to angle of preopercle.", "about": "Cavebass are distributed throughout the western Indian Ocean including Pakistan, southern Oman, Kenya, MOZ and the eastern seaboard of SA extending south to Mossel Bay. They are residents however, a small proportion of the population (<5%) appears to be more nomadic with movements of up to 90km. Adults inhabit high profile reefs to depths of 75 m and are found in caves and under ledges during the day and are active nocturnally, often solitary. Juveniles are found in intertidal rock pools and subtidal gullies. Adults feed on crustaceans (shrimps, mantis shrimps, crabs) and small fish. The juvenile\u2019s diet is unknown. They form a minor component of catches in all fishery sectors in which they occur, contributing between 0.07 (shore fishing) to 1.5% (recreational boat fishing) of catches by number , except for subsistence fishing for which the contribution is not known. They are frequently taken by spearfishers in KZN. Their mean size has remained stable at ~350 mm FL. This species is potentially vulnerable to overexploitation. Regular linefish surveys and on-going catch and effort monitoring is required to provide basic catch information.", "biology": "Their reproductive style is unknown. Their breeding/spawning season is reported to be in winter in KZN although highest frequency of eggs in southern KZN has been during Dec-Apr. They are known to spawn off KZN, suggested to be in shelf waters 20-30 m depth. The age at maturity is unknown and the length at maturity is estimated to be ~30 cm TL. The maximum recorded age is unknown. The maximum recorded weight is 5,8 kg and the maximum recorded length is 75 cm TL.", "citation": "ST Fennessy, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0249, "exponent": 2.96, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Polyamblyodon gibbosum',
  'Christie',
  'Kristie',
  'Christie',
  ARRAY['Cristie'],
  'Sparidae',
  'Seabreams, Porgies',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Cristie", "meristics": "Dorsal spines: 11; Dorsal soft rays: 13; Anal spines: 3; Anal soft rays: 10-11. The Christie is a robust, moderately deep body with a steep forehead and slightly protruding snout. It has a strongly defined lateral line and long pectoral fins. The colour is an overall blueish grey, with dusky to pale yellow fins.", "about": "It is known only from KwaZulu-Natal to central Mozambique and Madagascar and is common on Aliwal shoal and Sodwana reefs. It is found offshore coral and rocky reefs from shallow to 30 m, but not in the surf zone. The species is not targeted to any great extent, and it is found in MPAs. Polyamblyodon gibbosum is seldom caught by rod and line due to its preference for feeding on zooplankton, crustaceans and mollusks. It occasionally forms small shoals and will aggregate in mid-water under vessels where they can be targeted using small hooks.", "biology": "Information on the biology of this species is limited. The maximum recorded length for this species is 60 cm. Common length: 35.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Kyphosus cinerascens',
  'Blue Chub',
  'Blou Vetsak',
  'Chub Blue',
  ARRAY['Blue Sea Chub','Shortfin Rudderfish'],
  'Kyphosidae',
  'Chubs',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Blue Sea Chub, Shortfin Rudderfish", "meristics": "Dorsal spines: 11; Dorsal soft rays: 11-12; Anal spines: 3; Anal soft rays: 11-12.", "appearance": "Species of Kyphosidae are easily confused. The Blue Chub is a silvery-grey drummer with fine horizontal lines along the side, distinctly elevated fin rays on the rear of the dorsal and anal fins, and a more pointed snout than other drummer species.", "about": "This species was thought to be limited to the Indian Ocean, Red Sea, and Pacific Ocean, but is now known to be more widely distributed throughtout the warm temperate oceans including South Africa. It aggregates over hard, algal coated bottoms of exposed, surf-swept outer reef flats, lagoon and seaward rocky reefs, to a depth of at least 24 m. It schools in loose formations, often splitting and reforming. Juveniles are often associated with floating, pelagic seaweed material and can disperse across vast distances. It is a large browsing/grazing mainly herbivorous fish, feeding on seaweed and associated invertebrates that have important implications in trophic transfers and biomass turnovers from primary producers in marine habitats. It was given the nickname rudderfish because they are often seen following ships for the garbage that is dumped.", "biology": "It is known to start spawning as pair spawners until an aggregation forms and then spawns in a mass. It displays distinctive colour patterns during spawning. The maximum length is 50 cm but is commonly found to 45 cm. Females tend to be larger than males; length at first maturity is 18.1 cm FL for males and 25.3 cm for females.", "citation": "FishBase, The IUCN Red List of Threatened Species, Fishes of Australia"}',
  '[{"measure": "FL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -10.88860035, "exponent": 3.044718981}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Kyphosus vaigiensis',
  'Brassy Chub',
  'Brons Vetsak',
  'Chub Brassy',
  NULL,
  'Kyphosidae',
  'Chubs',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 10 - 11; Dorsal soft rays: 13-15; Anal spines: 3; Anal soft rays: 12-14.", "appearance": "The Brassy Chub has an elongate and oval-shaped body with a moderately emarginate caudal fin. The head is small with a short snout and a small, terminal mouth which has small incisor-shaped teeth; there are also teeth on the roof of the mouth and on the tongue. The dorsal and anal fins are very low. The dorsal fin can fold into a sheath. The presence of scales in the interorbital region is an identifying feature. The colour of body is silvery with a bluish shine, marked with 23-29 golden horizontal lines along the body with those above the lateral line curving parallel to it. There is a golden streak below the eye which runs from the snout to just beyion the front edge of the eye. The fins are grey or a slightly darker grey than the colour of the body.", "about": "It has a circumtropical distribution that encompasses most of the warmer seas and oceans of the world. In the Indian Ocean it occurs through to the Red Sea, eastern coast of Africa and off Madagascar.  Adults congregate over the hard, algal coated substrates of exposed surf-swept outer reef flats, lagoons, and seaward reefs. It occurs to a maximum depth of 40 m. It is found in exposed areas around rocky reefs, and adults usually stay close to the shoreline, while juveniles are recorded among flotsams and may be found in the open ocean near the surface. Juveniles feed on small crustaceans while the adults are carnivorous during the summer and autumn but feed on seaweed in the winter. They tend to be solitary at higher latitudes and more social in the tropics where they will form mixed schools. It is considered a good food fish.", "biology": "It is oviparous. The length at first maturity for females is approximately 36 cm. The maximum size is 90 cm TL; Common length: 50.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -10.81772995, "exponent": 3.036582947}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Kyphosus bigibbus',
  'Grey Chub',
  'Grys Vetsak',
  'Chub Grey',
  NULL,
  'Kyphosidae',
  'Chubs',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 11; Dorsal soft rays: 11-12; Anal spines: 3; Anal soft rays: 10-12.", "appearance": "The Grey Chub has an oval shaped body which is laterally compressed with a small head, a pointed snout and a slightly bulging forehead. The teeth are found on the centre of the roof of the mouth and on the tongue. The dorsal fin is a similar height along its length. The anterior part of the dorsal fin has 11 spines which fold down into a scaled furrow while the posterior part contains 10-12 rays. The anal fin has three spines and 10-12 moderately long soft rays and is reasonably long at its base. The symmetrical caudal fin is forked but not extremely so. The colour varies from uniform grey to brownish, silvery ventrally, bronze to greenish tint dorsally. There are very indistinct dark stripes on the body and there are usually whitish streaks on the cheek. The dorsal and anal fins are frequently dark with black margins on their soft-rayed parts. There is sometimes a dark patch visible on the lower posterior angle of the base of the pectoral fin. The caudal fin is usually dark. The cheek and area below eye usually have a white or silvery streak.", "about": "It is an herbivorous species which is found in subtropical and tropical seas worldwide. In the western Indian Ocean, it occurs as far north is the Red Sea and from the Gulf of Aden off Somalia south to Madagascar and R\u00e9union. Its range extends around the Cape of Good Hope. It is found in shallow coral and rocky reefs, including exposed isolated, offshore reefs and around islands. It forms large schools composed of hundreds of individuals over shallow rocky and coral reefs down to 25 m depth. They are frequently recorded in the surge zone. They are often recorded in mixed schools with other related species. Juveniles often hide among flotsams and can disperse for long distances among this floating material. It is an herbivore which feeds mainly on algae. It is considered a good eating fish.", "biology": "Length at Maturity: ~36.0 cm.  Max length: 75.0 cm TL; Max. Published weight: 1.9 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.01698, "exponent": 2.98, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Rachycentron canadum',
  'Cobia',
  'Kobia',
  'Cobia',
  ARRAY['Prodigal Son','Sergeant-fish'],
  'Rachycentridae',
  'Cobia',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": " Prodigal Son, Sergeant-fish", "meristics": "Dorsal spines: 7-9; Dorsal soft rays: 26-33; Anal spines: 2-3; Anal soft rays: 22-28.", "appearance": "Cobia are dark brown with a single dorsal fin usually with 8 short but strong isolated spines, not connected by a membrane; head broad and depressed; caudal fin lunate to emarginate in adults, upper lobe longer than lower, rounded in young with prolonged central rays. Colour of back and sides dark brown, with 2 sharply defined narrow silvery bands, belly yellowish. Young Cobia have distinct colouring, with alternating black and white horizontal stripes and splotches of bronze, orange, and green. Cobia are often mistaken for sharks or remoras. In fact, their closest living relative is the remora (shark sucker).", "about": "This species is distributed worldwide in tropical and subtropical waters but absent in the central and eastern Pacific. Within this geographic range, Cobia encounters both marine and estua-rine environments and is therefore able to tolerate a wide range of environmental conditions. In SA they are common in KZN but rare in waters further south of the former Transkei. They are a migratory species with peak abundance in SA waters during summer. Elsewhere they are considered migratory, either for spawning or food availability. Adults are pelagic, coastal species usually found in association with reefs or floating objects on the surface usually in shallow water (2-25 m) but have been taken at depths of 50 m often found swimming with whale sharks and manta rays. Early juveniles inhabit coastal areas, near beaches, river mouths, barrier islands, lower reaches of bays and inlets, or bays of relatively high salinities. They are strong, aggressive predators.  Adults feed almost exclusively on planktonic crustaceans such as portunid (swimming) crabs, but benthic invertebrates, squid and fish are also taken. The diet of the juvenile is unknown, presumably mainly planktonic organisms. Throughout SA, Cobia is an incidental catch in the various fisheries. They form a small component of the catch by the recreational boat sector in KZN (Mann et al. 1997a, Dunlop and Mann 2013). Also forms a small percentage of the catch in the KZN spearfishery. It has global potential for mariculture and is already being successfully farmed in many parts of the world. Due to its migratory habit, very fast growth rate and high fecundity, stocks are thought to be healthy and underexploited in SA waters.", "biology": "Reproductive style: Gonochoristic (separate sexes), batch spawners. No external sexual dimorphism has been reported. Females release between 375,000 and 2 million eggs each time they spawn. Breeding/spawning season: Little known locally. May-Sep in the north central Gulf of Mexico, Jun-Aug in the mid-western Atlantic Ocean and Oct-Dec in NE Australia. Breeding/spawning locality: Not known locally. Age at maturity: Males: 1-2 years, Females: 2-3 years. Length at maturity: Males, 518-640 mm FL; Females, 696-840 mm FL. Sexual maturity is attained by male Cobia at about 52 cm fork length in its second year and by female at about 70 cm in its third year. Fecundity for females: 100-125 cm fork length varies from 1.9 to 5.4 million eggs. Maximum recorded age: 15 years, Gulf of Mexico. Maximum recorded weight: 68 kg. 61.5 kg (IGFA).  Maximum recorded length: 200 cm TL (male).", "citation": "SW Dunlop, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, IGFA and EAFDEC Asian Aquaculture Vol. XXII No, by AP Surtida. 3 May-June 2000"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.0042, "exponent": 3.1, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Serranus cabrilla',
  'Comber',
  'Kammer',
  'Comber',
  ARRAY['African Seabass'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "African Seabass", "meristics": "Dorsal spines: 10; Dorsal soft rays: 13-15; Anal spines: 3; Anal soft rays: 7-8.", "appearance": "The Comber has a relatively stout body with a large head and a prominent jaw. It has two dorsal fins, the first has 11 thin spines and is joined to the second, which has 13-15 branched rays. The anal fin has 3 spines and 7-8 soft rays. The caudal fin is slightly truncate in shape. The colouration of the comber varies from light brown to dark brown to an intense reddish brown. It is marked with 7 to 9 darker transverse bands along its flanks; these are broken by a longitudinal white to yellowish stripe, running from the head to the tail. There are a few yellow or orange longitudinal lines on the sides of the head. The first dorsal fin is normally folded flat against the back when the fish is in the water.", "about": "The Comber is widely distributed in the eastern North and South Atlantic Oceans and into the southwestern Indian Ocean. It also occurs off the coasts of South Africa where it is found from the Cape of Good Hope to KwaZulu-Natal in depths of 5 - 500 m. The Comber is a solitary and territorial species which occurs over the continental shelf and upper continental slope among rocks, seagrass beds, and where there are substrates of sand and mud. It is a predatory species which feeds mainly on crustaceans. Off the Canary Islands the diet varied with size, the smaller fish consuming more crabs while the larger fish ate shrimp. Elsewhere their diet has been recorded as very varied and includes small and immature fish, worms and squid. They have been recorded scavenging on dead marine animals.", "biology": "They spawn between February to July with a peak in May, and are synchronous hermaphrodites, mature fish having both ovaries and testes. They are capable of being either male or female, and there are known to have been instances of self-fertilisation where fish have been unable to locate a partner for spawning. Sexual maturity is attained when the fish reaches a length of 15,2 cm. Max length: 40.0 cm SL; Common length: 25.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Drepane longimana',
  'Concertinafish',
  'Konsertinavis',
  'Concertinafish',
  ARRAY['Concertina Fish','Concertina-fish'],
  'Drepaneidae',
  'Sicklefishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "Concertina Fish, Concertina-fish", "meristics": "Dorsal spines: 8-9; Dorsal soft rays: 19-23; Anal spines: 3; Anal soft rays: 17-19.", "appearance": "Head and body deep, strongly compressed. Mouth protrusible; teeth pointed, slender, crowded together; a fringe of 4 to 6 short cirri ventrally on lower jaw (absent in specimens 25 cm or more in TL. May have 4-9 vertical grey bars on upper half of sides, but no spots.", "about": "The Concetinafish is native to the Indo-Pacific and northern Australia. It can migrate from fresh to salt water or from salt to fresh water at some stage of its life cycle. It is found inshore, on sand or mud bottoms, reefs, estuaries and harbors. Depth range for this species is 26-85 m. It feeds on benthic invertebrates. Flesh is edible but not esteemed.", "biology": "It breeds close inshore during spring. Max.Length: 50.0 cm TL; Common length: 23.0 cm TL.", "citation": "FishBase, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0495, "exponent": 2.895, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Trichiurus lepturus',
  'Cutlassfish',
  'Haarstert',
  'Cutlassfish',
  ARRAY['Walla Walla','Largehead Hairtail','Atlantic Cutlassfish'],
  'Trichiuridae',
  'Cutlassfishes, Ribbonfishes',
  NULL,
  NULL,
  NULL,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Walla Walla, Largehead Hairtail", "meristics": "Dorsal spines: 3; Dorsal soft rays: 130-135; Anal soft rays: 100-105.", "appearance": "The body of the Cutlassfish is extremely elongate, compressed and tapering to a point (they lack a fish tail in the usual form). The eyes are large, and the large mouth contains long pointed fang-like teeth. The dorsal fin is relatively high; anal fin reduced to minute spinules. The pelvic and caudal fins are absent. The fins are generally semi-transparent and may have a yellowish tinge. The lateral line begins at the upper margin of the gill cover, running oblique to behind the tip of the pectoral fins, then straight close to the ventral contour. They are silvery steel blue in colour, turning silvery gray after death.", "about": "Cutlassfish are found worldwide in tropical and temperate oceans. This benthopelagic species occurs on continental shelves to at least 100 m depth, usually in shallow coastal waters over muddy bottoms. Young and immature specimens feed on crustaceans and small fishes; adults are more piscivorous, occasionally on squids and crustaceans. Large adults usually feed near the surface during the daytime and migrate to the bottom at night but occasionally occur at the surface at night.", "biology": "This species matures at about 2 years. Eggs are pelagic. In Brazil, the total length at first gonadal maturity for males is 63.9 cm and 69.3 cm for females. Spawning occurs during late spring and summer and is probably year-round. Adult females remain to feed off southern Brazil during the cold season on the continental shelf while males move northward or offshore to warmer water. The current angling world record was caught in Rio de Janeiro''s Guanabara Bay and weighed 3.69 kg. Max length: 234 cm TL; Common length: 100.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0005, "exponent": 3.14, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Chrysoblephus cristiceps',
  'Dageraad',
  'Dageraad',
  'Dageraad',
  ARRAY['Dagga','Daggerhead','Daggerhead Seabream','Wawiel'],
  'Sparidae',
  'Seabreams, Porgies',
  40,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Critically Endangered',
  'Stock Status: Heavily Depleted; Fishing Pressure: Heavy',
  '{"other_names": " Dagga, Daggerhead, Daggerhead Seabream", "meristics": "Dorsal spines: 12; Dorsal soft rays: 10; Anal spines: 3; Anal soft rays: 8.", "appearance": "Dageraad has a deep body with a pointed snout and a steeply sloping forehead, which is slightly concave below the eyes. Overall colour is pinkish red with short blue lines above and below the eyes. A small black spot marks the end of the dorsal fin.", "about": "Dageraad is a slow-growing and long-lived species endemic to southern Africa and are found on the eastern seaboard from Durban to Cape Point. They are seldom caught in KZN waters but have been caught as far north as Richards Bay. They are more common in the EC and WC. Tagging results suggest that Dageraad are fairly resident although there may be some nomadic movement between reefs. Adults are found on offshore reefs between 20-100 m while juveniles occupy shallow subtidal reefs 10-30 m in the SE Cape. Adults are benthic carnivores feeding on a wide variety of reef-associated invertebrates; juveniles eat the same as adults but smaller size of food items. Dageraad are important to commercial and recreational ski-boat linefisheries in the EC and WC but a decline in abundance and strict species-specific regulations have reduced its importance in the commercial linefishery. They are occasionally taken by spearfishers capable of diving on deeper reefs (>20 m). There is strong evidence to suggest that the CPUE for Dageraad has declined substantially throughout their distributional range except within no-take MPAs such as the Tsitsikamma National Park. The increase in the minimum size limit to 40 cm TL and the drop in the commercial and recreational bag limit to 1pppd in April 2005 were partly responsible for the observed decrease in catch composition. This has skewed sex ratios with fewer males in areas with higher fishing effort. Fish are thus recruited into the fishery before maturity. There is strong evidence suggesting a decrease in mean size and age-at-sex change in exploited areas.", "biology": "Reproductive style: Dageraad are protogynous hermaphrodites (born female and changes sex to male) with sex change occurring at approximately 43.5 cm FL. Breeding/spawning season: Restricted spawning season between Oct-Jan. Breeding/spawning locality: Reef spawners with spawning recorded in the EC. Age at maturity: Females: 7.7 years; Males: 11 years (mean age at sex change). Length at maturity: Females: 27.5-36.5 cm FL; Males: 43.5 cm FL (mean length at sex change). Max age: 22", "citation": "CD Buxton, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, Two Oceans Aquarium, ORI Fact Sheet For more information: http://speciesstatus.sanbi.org/assessment/last-assessment/1015/"}',
  '[{"measure": "FL", "reference": "Visboekie", "coefficient": 1.3e-05, "exponent": 3.151, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Abudefduf sordidus',
  'Spot Damsel',
  'Kol Nooientjie',
  'Damsel Spot',
  ARRAY['Spot Damsel','Damselfish'],
  'Pomacentridae',
  'Damselfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Spot Damsel, Damselfish", "meristics": "Dorsal spines: 13; Dorsal soft rays: 14-16; Anal spines: 2; Anal soft rays: 14 - 15.", "appearance": "A greyish damselfish with six whitish bars on the head and body that are narrower than the adjacent dark bars, a prominent black saddle on the upper part of the caudal-fin base, a band of dark spots on the nape, and a black spot at the pectoral-fin base.", "about": "This species is widely distributed in the Indo-Pacific. This non-migratory species inhabits rocky lagoons, reef flat shorelines, and piers that are subject to mild to moderate surge, while juveniles are common in tide pools. Damsel Spot is associated with mixed coral and algal cover at an average depth of 2m. It occasionally forms schools. It feeds on algae, crustaceans and other invertebrates during the day.", "biology": "Damsel Spot is oviparous, with distinct pairing during breeding and are highly territorial. Eggs are demersal and adhere to the substrate. Males guard and aerate the eggs. Max length: 24.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.0358, "exponent": 3.18, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Porcostoma dentatum',
  'Dane',
  'Deen',
  'Dane',
  NULL,
  'Sparidae',
  'Seabreams, Porgies',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 13; Dorsal soft rays: 10-11; Anal spines: 3; Anal soft rays: 8-9.", "appearance": "Danes are stocky plump orangish-red fish with a purple and yellow band joining the eyes and a prominent blotch at the base of the perctorial fin, with their tail and anal fin fringed with white. This species\u2019 jaws have projecting canines. Juveniles are bright yellow with two distinctive black spots on the sides of the body which is lost as they mature.", "about": "Dane are endemic to SE Africa, mainly between Beira and Port St Johns. Strays sometimes are recorded down to Algoa Bay with some small juveniles are found as far south as Knysna. They are most common off the southern MOZ, KZN and Transkei coasts. Movement patterns are unknown as they are generally too small to tag and consequently little is known. They are, however, believed to be fairly resident. Dane occurs on offshore reefs often in the vicinity of pinnacles or steep drop-offs at depths from 20-120 m with conflicting reports of loose shoals versus solitary habits. Small juveniles have been found on shallow (15-30 m) offshore reefs in the Pondoland area, but occasional specimens have been observed as far south as Knysna in the WC. Danes are benthic carnivores feeding on a variety of organisms such as crinoids (sea lilies and feather stars), stomatopods (crustaceans having strong, clasping claws), polychaetes (bristle worms), crabs and hermit crabs. The juvenile\u2019s diet is unknown. Dane appears fairly regularly in catches of the commercial and recreational sectors of the KZN and Transkei ski-boat fisheries albeit as a bycatch as it is generally too small to be targeted. They are often retained by crew as \"fry\" in the commercial fishery, probably mostly released in the recreational fishery however not recorded in the MOZ semi-industrial or recreational fisheries but probably do occur. The decline in commercial catch rates and catch composition may be an aberration in the data, which is confounded by combining this species with other \"red fish\" in landed catches.", "biology": "Reproductive style: Unknown. Breeding/spawning season: Reportedly in spring. Breeding/spawning locality: Unknown. Age at maturity: Unknown. Length at maturity: 15 cm FL; estimated. Maximum recorded age: Unknown. Maximum recorded weight: 1 kg. Maximum recorded length: 42 cm TL.", "citation": "ST Fennessy, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, ORI Fish Fact Sheet For more information: http://speciesstatus.sanbi.org/assessment/last-assessment/1019/"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Squalus mitsukurii',
  'Longnose Spiny Dogfish',
  'Langneus Penhaai',
  'Dogfish Longnose Spiny',
  ARRAY['Shortspine Spurdog'],
  'Squalidae',
  'Dogfish sharks',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  NULL,
  '{"other_names": "Shortspine Spurdog", "meristics": "Dorsal spines: 2; Dorsal soft rays: 0; Anal spines: 0; Anal soft rays: 0.", "appearance": "A large dogfish with a long, angular snout and a small mouth slightly more than half the snout length; body without spots; 1st dorsal fin spine over pectoral fins. Pectoral fins rather broad, their inner corners narrowly rounded and their posterior margins nearly straight; caudal fin asymmetrical, without a subterminal notch but with a strong ventral lobe. Caudal peduncle has a low lateral keel or, each side and an upper precaudal pit. Colouration: Pearl-grey above, white below; fins with white edges, sides rarely with white spots.", "about": "The Longnose Spiny Dogfish is circumglobal in temperate and subtropical waters on the continental and insular shelves and upper slopes and is widely distributed in most oceans. It is benthopelagic; depth range 29 - 600 m, usually 48 - 533 m in water from 8\u00b0C - 17\u00b0C. It feeds on bony fishes including hake, snoek, conger eels and lantern fishes. Overall, it is suspected that the Longnose Spiny Dogfish has undergone a population reduction of 50-79% over the past 62 years.", "biology": "It is ovoviviparous and probably has 4 to 9 young per litter with most births in autumn off the east coast of South Africa. There might be a gestation period of up to two years, and sexual segregation of females in the southern part of its range. Size at birth is about 22 to 26 cm. Sexual maturity occurs ~77.8 cm. Max length: 89.8 cm TL (male); 94.3 cm TL (female); Common length male: 76.0 cm TL; Common length female: 82 cm TL. Female age-at-maturity is 15 years, and maximum age is 26 years.", "citation": "FishBase, The IUCN Red List of Threatened Species, FAO"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Squalus megalops',
  'Shortnose Spiny Dogfish',
  'Stompneus Penhaai',
  'Dogfish Shortnose Spiny',
  ARRAY['African Shortnose Spurdog','Bluntnose Spurdog','Bluntnose Dogshark','Shortnose Spiny Dogfish','Shortnose Spurdog','Piked Spurdog'],
  'Squalidae',
  'Dogfish sharks',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Near Threatened',
  'Stock status: Abundant - Fishing pressure: Heavy',
  '{"other_names": "African Shortnose Spurdog, Bluntnose Spurdog, Bluntnose Dogshark, Shortnose Spiny Dogfish, Shortnose Spurdog, Piked Spurdog", "meristics": "Dorsal spines: 2; Dorsal soft rays: 0; Anal spines: 0; Anal soft rays: 0.", "appearance": "A small dogfish with a short, angular snout and a small mouth almost as wide as the snout is long; body without spots; 1st dorsal fin spine over pectoral fins. Pectoral fins rather broad, their inner corners usually acutely pointed and their posterior margins deeply concave: caudal fin asymmetrical, without a subterminal notch but with a strong ventral lobe. Caudal peduncle has a low lateral keel on each side and an upper precaudal pit. Colouration is bronzey grey above, white below; dorsal fins with black tips and white edges but markings inconspicuous in adults.", "about": "The Shortnose Spiny Dogfish is a small shark located primarily off the coast of Southern Australia and South Africa. The Shortnose Spiny Dogfish\u2019s size is dependent on the sex of the animal. The female is consistently larger than the male and will typically measure out to about 56.41 cm while the male will typically only measure out to about 44.36 cm in length. The Shortnose Spiny Dogfish eats a variety of items but primarily favor cephalopods such as squid and octopuses for their meals. Other organisms that have been found in their stomachs include fish, hermit crabs, sponges, brittle stars, the remains of sea lion and even primary producers such as algae.", "biology": "The Shortnose Spiny Dogfish are ovoviviparous, with 2 to 4 young in a litter. They carry their pups in utero and typically for 2 years making it the longest gestation period of any vertebrate. Reproduction is perhaps the answer to why females are so much larger than males since larger female sharks are more likely to have a larger litter size. This is because larger females produce larger embryos that therefore have a higher survival rate. Only 50% of sexually mature females reach recruitment, meaning have offspring that will settle and grow into maturity and become part of the population. The average life span of a female Shortnose Spiny Dogfish is 0-29 years old while the male life span is 0-26 though it is estimated that a female can live up to 46 years while a male can only live up to 33. Females also take longer to reach sexual maturity than males and on average are not fertile until they are over 15 years of age. Males take 8 years to reach maturity.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia, FAO"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0072, "exponent": 2.93, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Squalus acanthias',
  'Spotted Spiny Dogfish',
  'Spikkel Penhaai',
  'Dogfish Spotted Spiny',
  ARRAY['Spiny Dogfish','Dogfish','Dog','Bone Dog','Spiny Dogshark','Spurdog','Piked Dogfish','Piked Spurdog','Cape Shark'],
  'Squalidae',
  'Dogfish sharks',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Vunerable',
  NULL,
  '{"other_names": " Spiny Dogfish, Dogfish, Dog, Bone Dog, Spiny Dogshark, Spurdog, Piked Dogfish, Cape Shark", "meristics": "Dorsal spines: 2; Dorsal soft rays: 0; Anal spines: 0; Anal soft rays: 0.", "appearance": "The spiny dogfish has dorsal fins, no anal fin, and white spots along its back. The caudal fin has asy mmetrical lobes, forming a heterocercal tail (the tip of the vertebral column turns upward, extending into the dorsal lobe of the tail fin). Pale caudal fin with poorly demarcated, whitish margin, blackish caudal blotch at the apex of upper lobe, anterior margins of both lobes whitish in juveniles; dark caudal bar absent; dorsal and lateral surfaces of body bluish grey with an irregular array of moderately large white spots; whitish ventrally; flank denticles tricuspid. The species name acanthias refers to the shark''s two spines at the leading edges of the two dorsal fins. These are used defensively. If captured, the shark can arch its back to pierce its captor with spines that secrete mild venom into its predator.", "about": "The Spotted Spiny Dogfish is a demersal, inshore and offshore dogfish with a global distribution, present in boreal and temperate waters in the Northeast and Northwest Atlantic, including the Mediterranean and Black Seas, the Southeast Pacific and Southwest Atlantic off South America, the Southeast Atlantic off South Africa, and the Southwest Pacific and Eastern Indian Oceans off Australia and New Zealand. Males and gravid females are usually found shallower than non-gravid females. The Spotted Spiny Dogfish tolerates brackish water and is found inshore in enclosed and open bays and estuaries, as well as offshore along the continental and insular slopes from the surface to 1,978 m, with most records occurring mainly between 10-200 m depth. It feeds on a diversity of prey, ranging from comb jellyfish, squid, mackarel and herring to a wide array of benthic fishes, shrimps, crabs and even sea cucumbers. In spite of its small size, the Spotted Spiny Dogfish is among the slowest-growing, latest maturing and longest lived of shark species. As a consequence, it is among the most vulnerable shark species to overexploitation by fisheries. It has one of the lowest intrinsic rates of population increase of any marine fish and this has contributed to the sharp collapse in its numbers. Once the most abundant shark species in the world, populations of Spotted Spiny Dogfish have declined dramatically. The species is used extensively for its flesh, fins, and liver oil, with most products destined for the EU market.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia, The Convention on Migratory Species ( CMS)", "biology": "Reproduction is aplacental viviparous, with distinct pairing with embrace. Mating takes place in the winter months with gestation lasting 22-24 months. Litters range between two and eleven, but on average, six or seven. Size at birth is 18-30 cm. In the northeast Atlantic, pups are born in winter, with size at birth about 26-28 cm. Sex ratio at birth is 1:1. Gravid females congregate in enclosed shallow bays to give birth. The mother shark experiences a series of rhythmic contractions, just like in mammals, and the young are delivered headfirst. Larger older females have bigger litters with larger pups; a female with 100 cm TL carries on average 4 times as many embryos compared to a 70 cm female and the former have higher survival rates than those born to small females. Males mature at around 11 years of age, growing to 80-100 cm in length; females mature in 18-21 years and are slightly larger than males, reaching 98.5-159 cm. Max published weight: 9.1 kg; Max reported age: 75 years."}',
  '[{"measure": "TL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -13.43025495, "exponent": 3.219990589}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Coryphaena equiselis',
  'Pompano Dolphinfish',
  'Pompano-dorade',
  'Dolfinfish Pompano',
  NULL,
  'Coryphaenidae',
  'Dolphinfish',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 0; Dorsal soft rays: 52-59; Anal spines: 0; Anal soft rays: 24-28.", "appearance": "Body is elongated and compressed. Tooth patch on tongue broad and square covering 50 to 60 percent of its surface. Dorsal fin originating on nape, almost reaching caudal fin, with 48-55 rays; anterior distal edge of anal fin convex; pectoral fins located near middle of head. Colour in life brilliant metallic blueish green dorsally, fading to grey with green tinge upon death; sides silvery with golden sheen and spots; dorsal fin dark; juveniles with entire caudal fin margin white. Head profile of large adults (30-200 cm) becomes vertical with the development of bony crest, but more in males but not to the same extent as male Dorado.", "about": "This species is found worldwide in tropical and subtropical seas to depths of 119 m. It is primarily an oceanic species but may enter coastal waters. Usually, schools form. Follows boats and may be found under floating objects. Pompano Dolphinfish are carnivorous, feeding primarily on small fish and squid.", "biology": "Eggs and larvae are pelagic. Max length: 146 cm TL; Common length: 50.0 cm TL; Max reported age: 4 years.", "citation": "FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Coryphaena hippurus',
  'Dorado',
  'Dorade',
  'Dorado',
  ARRAY['Dolphinfish','Dolfynvis','Dorrie','Mahi-mahi'],
  'Coryphaenidae',
  'Dolphinfish',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  '(ICCAT: Data deficient, improvements being made)',
  '{"other_names": " Dolphinfish, Dolfynvis, Dorrie, Mahi-mahi", "meristics": "Dorsal spines: 0; Dorsal soft rays: 58-66; Anal spines: 0; Anal soft rays: 25-31.", "appearance": "Mature males with prominent bony crest in front of the head; greatest body depth in adults less than 25% of standard length; tooth patch on tongue small and oval; single dorsal fin extending from above eye almost to caudal fin with 58-66 rays; a concave anal fin extending from anus almost to caudal fin; pectoral fin more than half of head length. Colour of body metallic blue-green on the back (fading to grey with green tinge when dead), sides silver with golden sheen, and 1 row of dark spots or golden blotches running below dorsal fin and 1, 2, or more rows on and below lateral line, some scattered irregularly; dorsal and anal fins black, the latter with a white edge; pectoral fins pale; caudal fin silvery with a golden sheen; in juveniles, only tips of caudal-fin lobes white, pelvic fins black. Adult males have a square head. Females have a rounded head. They are distinguished from the Pompano Dolphinfish by its 55 to 66 dorsal fin rays and a very wide, square tooth patch on the tongue.", "about": "Dorado are distributed worldwide in tropical and temperate waters. They occur in the Atlantic, Indian and Pacific Oceans and in the Mediterranean, most common in water temperatures between 21-30\u00b0C and generally between 30\u00b0 N and S latitudes. In southern Africa they are found throughout SA waters to 35\u00b0 south with occasionally large adults being caught off the Cape south coast but not in the Benguela system off the west coast and NAM but, however, are found in warmer oceanic waters further offshore. Dorado are largely nomadic although some pre-spawning migration has been proposed in the western and tropical areas of the Atlantic Ocean. Adults are epipelagic (the sunlit zone), wide-ranging in oceanic and shelf waters, sometimes aggregate under floating objects, often seen in pairs and may form small aggregations, normally close to the surface but can be found to depths of 85 m. Juveniles are pelagic (open ocean), and are often associated with flotsam but can sometimes drift into large embayments where their cryptic colouring provides good camouflage. Dorado are top predators that feed in surface water during the day. Adults feed on a wide variety of species, including small pelagic fishes, especially pelagic drifters such as juvenile trigger fish, juvenile tuna, billfish, jacks, pompano, and pelagic larvae of nearshore, bottom-living species crustaceans, nautilus, squid and a wide variety of planktonic organisms. They are also known to feed on flying fish, mackerel and small carangids. Juveniles eat planktonic organisms such as fish and crustacean larvae. Predators include large tuna, marine mammals, marlin, Sailfish, and Swordfish. Dorado are a highly prized commercial and  recreational species throughout its circumglobal range but seldom specifically targeted by commercial skiboat fishers but forms a major component of the recreational ski-boat sector in KZN, both by number (2.6%) and weight (9.8% and also comprises 1.02% of the catch by weight in the charter boat sector in KZN. It is often targeted around natural and artificial FADs and occasionally caught in the recreational ski-boat sector in the EC. It is caught as a bycatch in the pelagic longline fisheries in the SW Indian Ocean and forms a small percentage of the catch in the SA spearfishery. Due to low fishing pressure in SA, stocks are thought to be healthy and underexploited.", "biology": "Dorado are gonochoristic (separate sexes), multiple spawners, who exhibit sexual dimorphism; males growing larger and develop a steeper, squarer forehead than females. Breeding/spawning occurs mainly during summer, although spawning has been recorded throughout the year in tropical oceanic waters, often closer to shore. In the Atlantic they are known to spawn under patches of floating brown algae called Sargassum. They are believed to spawn every 2 to 3 days during the spawning season, releasing between 33,000 and 66,000 eggs each time. They are capable of reproducing at 3 to 5 months old off Florida and Barbados, but this varies considerably according to region. The length at maturity also varies considerably according to region but is normally around 65-70 cm FL for females and 75-80 cm FL for males. They can live up to 4 or 5 years but usually live less than 2 years. Atlantic Dorado can grow up to almost 2.134 m and 40 kg. The maximum recorded weight is 39,46 kg; the maximum recorded length is 200 cm FL but are more commonly found in the 100 cm FL range.", "citation": "SD Dunlop, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, NOAA"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.024, "exponent": 2.75, "formula_type": "power", "result_unit": "g"}]'
);

