INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Pseudorhombus arsius',
  'Largetooth Flounder',
  'Groottand Botvis',
  'Flounder Largetooth',
  NULL,
  'Paralichthyidae',
  'Lefteye flounders, Largetooth flounders',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 0; Dorsal soft rays: 71-84; Anal spines: 0; Anal soft rays: 53-62", "appearance": "It has an oval shaped body. The head has a slight notch in front of upper eye. The dorsal fin starts in front of its upper eye and of the nostrils on blind side. The mouth forms a deep cleft with the maxilla normally almost half the length of the head. The jaws and teeth are nearly equally developed on both jaws. It has simple fin-rays. The body is greenish to pale brownish in colour and is normally marked with variously sized rings, and there is frequently two dark blotches on the straight and the curved sections of the lateral line with a smaller blotch which is half-way to the caudal-fin peduncle. It can vary the colour of the body so that it closely matches the surface the fish rests on.", "about": "The Largetooth Flounder is an Indo-Pacific species occurring Persian Gulf and east coast of Africa (south to Algoa Bay and perhaps to Knysna, South Africa) to Fiji, north to southern Japan, south to the northern coast of Australia. It occurs in shallow waters and in estuaries where the substrate consists of mud and sand bottoms, to depths of 200 m. Juveniles are common in brackish water. When they are spawning, they are found in shallow water on sandbanks and close to shore. They move to deeper waters in the winter. They are predators which prey mainly on benthic animals.", "biology": "The spawning season runs for 3 to 4 months, from April to July and peaks in April and May, during which they move into the sandy shallows close to the shore. The spawn is laid in a single batch. They mature when they attain a total length of between 16 and 17 cm in total length. The sex ratio is always biased towards females, and the fecundity of the fish is dependent on its total length and body weight. They grow to 50 cm, but 30 cm in length is the more usual size.", "citation": "FishBase, Wikipedia"}',
  '[{"measure": "TL", "reference": "WILLING, R.S. et al.", "formula_type": "log-linear", "coefficient": -11.90448041, "exponent": 3.075998458}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Paralichthodes algoensis',
  'Measles Flounder',
  'Masels Botvis',
  'Flounder Measles',
  ARRAY['Peppered Flounder'],
  'Pleuronectidae',
  'Righteye Flounders, Dabs',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Peppered Flounder", "meristics": "Dorsal spines: 0; Dorsal soft rays: 67-74; Anal spines: 0; Anal soft rays: 47-54.", "appearance": "The peppered flounder is a right-eyed flounder with an elongated, oval body. Its wyed side is brownish grey with small dark spots, pectoral fin with 12-13 rays and its underside is white, pectoral fin with 11 rays. The lateral line is equally developed on both sides. It has a relatively large, symmetrical mouth.", "about": "Its native habitat is the southeastern Atlantic and the western Indian Ocean, specifically the African coastline from Mossel Bay, South Africa, to Delagoa Bay, Mozambique. It is a demersal fish that lives on sandy and muddy bottoms in subtropical waters, at depths of up to 100 m.", "biology": "It grows up to 50 cm TL.", "citation": "FishBase, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -11.63916969, "exponent": 3.077567101}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Boopsoidea inornata',
  'Fransmadam',
  'Karel Grootoog',
  'Fransmadam',
  ARRAY['Tzitsikama Phirana'],
  'Sparidae',
  'Seabreams, Porgies',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Tzitsikama Phirana", "meristics": "Dorsal spines: 11; Dorsal soft rays: 10-11; Anal spines: 3; Anal soft rays: 11.", "appearance": "The body is oval and silver with a small head and large eyes; a dark curved line behind head and onto rear edge of gill cover and a faint dark stripe along mid body extending forward from tail base; a brassy tint to tail base and rear of body with a dark lateral line. Juveniles are bright orange.", "about": "Fransmadam are endemic to southern Africa and are found from Cape Point to southern KZN. It is a shoaling species where adults live around scattered and flat reefs between 5 m and 30 m deep. Juveniles prefer shallow subtidal reefs and gullies, particularly those covered in coralline algae. Adults engage in benthic feeding habits with a diverse diet including crustaceans, ascidians (sea squirts), algae, polychaete worms, bryozoans and mollusks and red bait, small shells and snails, planktonic crustaceans, worms and seaweeds. The dominant crustaceans include amphipods (crustaceans with no carapace) , decapods, isopods and mysids. The presence of planktonic crustaceans suggests they also feed above the reef. The juvenile\u2019s diet is unknown. Although small, Fransmadam comprises an important component of recreational and commercial skiboat catches in the EC and WC. They are primarily used as bait but increasingly being used for food as other larger reef fish species become depleted. They are also frequently taken by shore anglers fishing in deep water in the EC and WC.", "biology": "Reproductive style: Gonochoristic (separate sexes). Breeding/spawning season: Spring and summer but fish with ripe gonads observed throughout the year. Breeding/spawning locality: Subtidal reefs in the EC but likely to occur throughout distribution. Age at maturity: Unknown. Length at 50% maturity: Unknown but all fish >140 mm SL are sexually mature. Maximum recorded age: Unknown. Maximum recorded weight: 0.4 kg; Tsitsikamma National Park. Maximum recorded length: 30 cm TL.", "citation": "AD Wood, PD Cowley In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase,Two Oceans Aquarium, Reef Life Survey For more information: http://speciesstatus.sanbi.org/assessment/last-assessment/1042/"}',
  '[{"measure": "FL", "reference": "Student Thesis", "coefficient": 5.73e-05, "exponent": 2.81, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Paracaesio sordida',
  'Dusky Fusilier',
  'Donker Piesang',
  'Fusilier Dusky',
  ARRAY['Dirty Ordure Snapper','Smallscale Snapper'],
  'Lutjanidae',
  'Snappers',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Dirty Ordure Snapper, Smallscale Snapper", "meristics": "Dorsal spines: 10; Dorsal soft rays: 9-10; Anal spines: 3; Anal soft rays: 8.", "appearance": "Dorsal and anal fins are without scales at base. The caudal fin is large and deeply forked. The maxilla is without scales. Preopercle with 1-3 rows of scales. 69-72 lateral-line scales. Overall, the colouration is dark purplish brown or greyish pink.", "about": "Dusky Fusiliers are distributed from the Red Sea and eastern Africa, including the Comoros, the Seychelles and South de Malha Bank and east to the eastern Indian region and also the Pacific Marquesas and Pitcairn as well as north to the Ryukyu Islands. Adults are found over rocky bottoms often in small schools at depths ranging from 30-200 m where they feed on planktonic organisms.", "biology": "No information on age or length at maturity is available; however, one Dusky Fusilier caught off Papua New Guinea with a length of 23.0 cm SL and weight of 0.34 kg was estimated at 4 years old. No information on spawning is available and estimated maximum age is unknown. Max length: 48.0 cm TL; Common length: 27.5 cm SL.", "citation": "FishBase, The IUCN Red List of Threatened Species, fishIDer"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Paracaesio xanthura',
  'Yellowtail Fusilier',
  'Geelstert Piesang',
  'Fusilier Yellowtail',
  ARRAY['Protea Bream','False Fusilier','Yellowtail Blue Snapper','Yellowtail False Fusilier'],
  'Lutjanidae',
  'Snappers',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Protea Bream, False Fusilier, Yellowtail Blue Snapper, Yellowtail False Fusilier", "meristics": "Dorsal spines: 10; Dorsal soft rays: 10-11; Anal spines: 3; Anal soft rays: 8-9.", "appearance": "This species is distinguished by the following characters: body relatively deep and laterally compressed, interorbital region convex; mouth terminal, jaws about equal; anterior end of upper lip without a thick fleshy protrusion; maxilla with or without scales and without longitudinal ridges; teeth in jaws caniniform to villiform; caudal fin forked. Colour bright blue; broad yellow band over most of the dorsal half of the body, including most of the caudal pedincle fin.", "about": "Yellowtail Fusiliers are distributed in the Indo-Pacific: East Africa across to Indonesia, north to southern Japan, south to south-eastern Australia. In SA their range extends to southern KZN where it is caught seasonally in fair numbers, particularly off the Shelly Beach area. Limited data indicates they are resident. Diving observations indicate that large reef-associated shoals are present year-round in the Park Rynie area on the KZN south coast. Strong seasonality in reported catches almost certainly reflects a switch in feeding behaviour, rather than a migration away from the area.  Adults form large shoals over offshore rocky and coral reefs in depths of 20-200 m. The juvenile\u2019s habitat is unknown. Adults feed predominantly on zooplankton. The juvenile\u2019s diet is unknown. They are primarily caught by commercial and recreational boat-based linefishers on offshore reefs along the KZN south coast. The recreational ski-boat sector shows an increasing contribution to reported catch in numbers but has never contributed more than 0.6%. Because of their diet (zooplanktivore) this increase may suggest an improvement in targeting or declines in catches of other species.  Greatest commercial and recreational landings of this species recorded at the Shelly Beach and Port Shepstone launch-sites on the KZN south coast during winter and early spring. Occasionally they are taken by spearfishers.", "biology": "Reproductive style: Unknown. Breeding/spawning season: Austral summer. Breeding/spawning locality: Eggs recorded on the 20-30 m depth contour off Park Rynie on the KZN south coast. Age at maturity: Unknown. Length at maturity: Unknown. Maximum recorded age: Unknown. Maximum recorded weight: 2.85 kg (SAUFF). Maximum recorded length: 45-50 cm TL.", "citation": "JQ Maggs, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.0227, "exponent": 2.9, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Dichistius capensis',
  'Galjoen',
  'Galjoen',
  'Galjoen',
  ARRAY['Damba','Blackfish','Black Bream','Gallie'],
  'Dichistiidae',
  'Galjoen',
  35,
  NULL,
  2,
  NULL,
  '15 October to end February',
  'Not Evaluated',
  'Collapsed, <25% of pristine. Near threatened',
  '{"other_names": "Damba, Blackfish, Black Bream, Gallie", "meristics": "Dorsal spines: 10; Dorsal soft rays: 18-19; Anal spines: 3; Anal soft rays: 13-14.", "appearance": "The body is robust and compressed, and the fins are well developed, with prominent spines. The pelvic fins have 1 spine and 5 rays, and the pectoral fins are typically shorter than the head. The body, fins, and head, with the exception of the front of the snout, are covered in scales. The lips are thick, with strong curved incisors at the front of the mouth, with smaller teeth behind the front incisors. Changes from dark grey to silver almost instantly to blend with environment; occasionally mottled or with 7-9 crossbars on flanks.", "about": "The Galjoen, SA\u2019s national fish, is endemic to southern Africa occurring from southern Angola to Durban and is more abundant in cooler waters <21\u02daC but rarely found on the KZN south coast. Most fish are resident (90%) however a small percentage is nomadic. Nomads can move throughout the range. It is uncertain whether individuals adopt one or the other pattern i.e. residency or nomadism, or if all fish can adopt both patterns, but in unequal proportions. Adults favour broken surf (white water) in areas of mixed rock and sand but are also found in the surf-zone along sandy beaches and in kelp beds and sometimes found on offshore pinnacles in large shoals. Juveniles inhabit the immediate sublittoral (always submerged at low tide) surf-zone. Adults are epilithic (off the rock) feeders, principally molluscivores (a carnivorous animal that specialises in feeding on molluscs such as snails, bivalves, brachiopods - marine animals that have a pair of shells, which they can clamp shut for protection, with brown mussels dominating the diet and cephalopods (octopus or squid). The diet of juveniles is unknown. Galjoen are among the most common targets of recreational shore anglers in the WC and are also frequently targeted by spearfishermen. They are also taken illegally in gillnets on the west coast. Galjoen catch per unit effort (CPUE) recovered dramatically in De Hoop MPA shortly after its closure. There is a marked difference in CPUE and size structure between exploited and unexploited areas. A small fraction of adults leave MPAs, as deduced from tagging, which suggests that MPAs sustain adjacent fisheries by way of adult spill over and perhaps also egg and larval export.", "biology": "Galjoen are gonochoristic (separate sexes), indeterminate batch spawners (eggs can develop at any time during the spawning season) where the breeding/spawning season lasts from September to February probably throughout their range. Males mature at 4.9 years at 31 cm TL while females mature at age 5.2 years at 43 cm TL. The maximum recorded age is 12 years for males and 21 years for females. The maximum recorded weight for males is 1,665 kg for males and 6.5 kg for females. The maximum recorded length for males is 47 cm TL and 74 cm TL for females.", "citation": "CG Attwood, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 9.03e-06, "exponent": 3.12, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Dichistius multifasciatus',
  'Banded Galjoen',
  'Band Galjoen',
  'Galjoen Banded',
  ARRAY['Striped Galjoen','Gestreepte Galjoen','Streep Galjoen','Sandvis','Bandit Galjoen'],
  'Dichistiidae',
  'Galjoen',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "Striped Galjoen, Gestreepte Galjoen, Streep Galjoen, Sandvis, Bandit Galjoen", "meristics": "Dorsal spines: 10; Dorsal soft rays: 21-23; Anal spines: 3; Anal soft rays: 13-14.", "appearance": "A small, robust, deep-bodied fish with a greyish brown colour and has about 14 alternating wide and narrow dark brown bars on the sides of the body.", "about": "The Banded Galjoen is a south-east African endemic from WC through to southern MOZ and off the SE coast of Madagascar. It occurs more abundantly from southern MOZ to Stilbaai in the WC and is common along the KZN and Transkei coast. It is a resident, however data from the ORI Tagging Project indicate that it may be regarded as a semi-resident species and it is likely that its movement behaviour is more similar to Galjoen with a high percentage of resident individuals. Adults are inshore reef fish found in the turbulent surf-zones along rocky shores while juveniles are found over shallow reefs, in tidal rock pools and sub-tidal gullies. Adults are benthic (bottom feeding) carnivores feeding on a variety of invertebrates including bryozoans (a family of small filter feeding invertebrates that live as colonies), ascidians (sea squirts), polychaetes (bristle worms), small crustaceans and mollusks. The juvenile\u2019s diet is unknown. The Banded Galjoen is of moderate importance to recreational and subsistence shore fishers along the KZN and Transkei coast. Larger specimens are occasionally speared.", "biology": "The Banded Galjoen\u2019s reproductive style is unknown. It has breeding/spawning season from March to October, peaking during winter and spring in KZN with recorded spawning off the KZN coast with eggs and larvae mainly in inshore waters. The age at maturity is unknown. The length at maturity for males is 190 mm FL and 215 mm FL for females. The maximum recorded age is unknown for both males and females. The maximum recorded weight for males is 1,7 kg. The maximum recorded length is 322 mm FL.", "citation": "BQ Mann, SD Dunlop In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 1.2e-05, "exponent": 3.152, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Atractoscion aequidens',
  'Geelbek',
  'Geelbek',
  'Geelbek',
  ARRAY['Cape Salmon','Hangpens','Geelbek Croaker','Silver Jewfish','Corvina','Teraglin','Meagre','Yellowmouth','African Weakfish'],
  'Sciaenidae',
  'Croakers',
  60,
  NULL,
  2,
  NULL,
  NULL,
  'Stable, Near Threatened',
  'Collapsed, <25% of pristine; Depleted; Fishing pressure: Heavy',
  '{"other_names": " Cape Salmon, Silver Jewfish, Geelbek Croaker, Corvina, Teraglin, Hangpens, Meagre, Yellowmouth, African Weakfish", "meristics": "Dorsal spines: 10 - 11; Dorsal soft rays: 25-32; Anal spines: 2; Anal soft rays: 9-10.", "appearance": "The colour of the body is iridescent silvery blue and purple coppery sheen dorsally and white below, with bright yellow on the edges of jaws and the inside of the gill cavity. There is a blotch on the axil of the pectoral-fin.", "about": "Geelbek are distributed from the Gulf of Guinea to Angola and from False Bay to southern MOZ to NW Africa and SE Australia. The stock from Cape Point to southern MOZ is separated from stock off Angola by cool waters off the west coast of SA and southern NAM. They are migratory with adults undertaking a seasonal migration from the WC to KZN where they spawn in spring. Adults >90 cm are found throughout distributional range, over sandy and rocky substrata to depths of 150 m. Juveniles occupy sandy and rocky substrata to depths of 100 m, early juveniles <50 cm in SE Cape, while sub-adults 50-85 cm are mainly found in the SW Cape. Adults feed on sardines (which are considered depleted), mackerel and massbanker while juveniles on shrimps and later anchovies. Geelbek are an important catch of the boat-based commercial and recreational linefishery. Catches per area are seasonal as adult fish undertake an annual spawning migration to KZN. They are also taken as bycatch by the inshore demersal trawl fishery. Despite the cut in commercial fishing effort and the decrease in recreational bag limits, the standardised CPUE does not indicate much of an improvement. Fishing concentrated on spawning adults needs to be regulated to improve this stock. The current high level of shark predation on adults hooked in these spawning aggregations along the KZN coast is further exacerbating the problem.", "biology": "Reproductive style: Gonochorist (separate sexes). Breeding/spawning season: Aug-Nov with peak in Sep-Oct. Breeding/spawning locality: In KZN waters mainly over offshore reefs in depths of 40-60 m. Age at maturity: Combined sexes: 5 years; SA. Length at 50% maturity: Combined sexes: 900 mm FL; SA. Maximum recorded age: 9 years; SA. Maximum recorded weight: 14.9 kg; Algoa Bay (SADSAA). Maximum recorded length: 130 cm TL.", "citation": "SE Kerwath, H Winker In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, SANBI IDentifyIt, Wikipedia"}',
  '[{"measure": "FL", "reference": "Visboekie", "coefficient": 8.42e-06, "exponent": 3.01, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
