  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Parupeneus macronemus',
  'Band-dot Goatfish',
  'Bandkol Bokvis',
  'Goatfish Band-dot',
  ARRAY['Long-barbel Goatfish'],
  'Mullidae',
  'Goatfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Long-barbel Goatfish", "meristics": "Dorsal spines: 8; Dorsal soft rays: 9; Anal spines: 1; Anal soft rays: 7.", "appearance": "Body gray to grayish red dorsally, whitish to pink ventrally, with a black stripe from eye along lateral line to anterior part of caudal peduncle; a roundish black spot larger than eye on side of peduncle about two-thirds above lateral line; a broad white zone extending anterior to peduncular spot to below posterior part of second dorsal fin; a vertical pale blue line or spot often present on scales below black stripe; a dusky band often present on snout from eye to side of upper lip; barbels white; first dorsal and caudal fins reddish gray; basal one-third to one-half of second dorsal fin black, the dark pigment continuing to end of last ray and adjacent membrane; outer part of fin with narrow pale blue and yellow stripes; anal fin dusky yellow with faint narrow pale blue stripes . Pale area at end of black stripe and dusky basal stripe in second dorsal fin distinguished it from the similar P. barberinus.", "about": "Found in the Indo-West Pacific: Red Sea and Persian Gulf south to KZN, South Africa extending east to Indonesia and the Philippines. It inhabits lagoon and seaward reefs to over 25 m and is found on sandy or weedy bottoms: rarely at depths of more than 40 m. It is usually seen solitary but often several individuals nearby. Using its long barbels, it probes into the sediment in search of crustaceans and worms.", "biology": "Length at maturity: 12.3 cm. Max length: 40.0 cm TL; Common length: 20.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species"}',
  NULL
);

