  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Parupeneus heptacanthus',
  'Redspot Goatfish',
  'Rooikol Bokvis',
  'Goatfish Redspot',
  ARRAY['Cinnabar Goatfish'],
  'Mullidae',
  'Goatfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Cinnabar Goatfish", "meristics": "Dorsal spines: 8; Dorsal soft rays: 9; Anal spines: 1; Anal soft rays: 7.", "appearance": "Body brownish yellow to light red (deeper-dwelling fish more red), the edges of the scales darker, shading to silvery white ventrally; adults with a small reddish brown spot on upper side of body just below seventh and eighth lateral-line scales; an indistinct narrow yellow stripe often visible above the lateral line (more evident in juveniles and subadults); dorsal body scales often with a pale blue or pearly spot; faint iridescent blue lines extending dorsoposteriorly and ventroanteriorly from eye, and often a parallel one on the cheek below eye; second dorsal and anal fins with faint pale blue or pink narrow bands alternating with pale yellow.", "about": "The Redpot Goatfish is native to the tropical western Pacific and Indian Oceans, from the Red Sea and east coast of Africa to the Caroline Islands and Fiji. Depth range: 12-350 m; usually at 60 m. It occurs singly or in small groups, over muddy, sandy, rubble, or seagrass bottoms of lagoon and seaward reefs, usually below 20 m. It is usually not observed on coral reefs. Flesh is fairly good for human consumption.", "biology": "The estimated mean length at first sexual maturity: 17 cm. Max length: 36.0 cm TL; Common length: 25.0 cm TL; Max. reported age: 6 years.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
