  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Parascorpis typus',
  'Jutjaw',
  'Kinnebak',
  'Jutjaw',
  NULL,
  'Parascorpididae',
  'Jutjaw',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 11 - 12; Dorsal soft rays: 15-17; Anal spines: 3; Anal soft rays: 13-15.", "appearance": "Jutjaw are grey-brown in colour.", "about": "Jutjaw are native to the Western Indian Ocean coast of Africa known only from Maputo, Mozambique to False Bay, South Africa. It is found at depths of 20-200 m. It feeds on zooplankton. Although considered to be good foodfish, but they are currently not sought out by commercial fisheries and are caught only rarely by recreational anglers.", "biology": "Max length: 60.0 cm TL.", "citation": "FishBase, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.01995, "exponent": 3.01, "formula_type": "power", "result_unit": "g"}]'
