) VALUES (
  'Oplegnathus peaolopesi',
  'Mozambique Knifejaw',
  'Mosambiekse Kraaibek',
  'Knifejaw Mozambique',
  NULL,
  'Oplegnathidae',
  'Knifejaws',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 11; Dorsal soft rays: 20-21; Anal spines: 3; Anal soft rays: 16.", "appearance": "Adults are dark bronzy brown in colour.", "about": "They occur in the Western Indian Ocean: Delagoa Bay in Mozambique northwards, but the northern limit of its range in not known. Depth range is 20-130 m. Inhabits reef areas and feeds on benthic invertebrates.", "biology": "Max length: 75.0 cm TL; Common length: 50.0 cm TL.", "citation": "FishBase"}',
