INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Oplegnathus robinsoni',
  'Natal Knifejaw',
  'Natalse Kraaibek',
  'Knifejaw Natal',
  ARRAY['Cuckoo Bass'],
  'Oplegnathidae',
  'Knifejaws',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "Cuckoo Bass", "meristics": "Dorsal spines: 11; Dorsal soft rays: 20-21; Anal spines: 3; Anal soft rays: 16.", "appearance": "The fish is greyish brown to dark bronzy brown colour and may be mottled. Juveniles, on the other hand, are yellow in colour with several vertical black stripes along the body from eye to tail. A parrot-like beak and teeth that are a part of the mouth allow it to tear organisms from surfaces. The Natal knifejaw has a compressed body, with a high second dorsal fin.", "about": "Natal Knifejaws are endemic to KZN and Moz waters and are common in the Pondoland MPA in the EC and juveniles have been seen as far south as Tsitsikamma. They are thought to be a resident species. Adults inhabit inshore coral and rocky reefs in depths from 10-50 m but apparently down to 100 m. Small yellow and black juveniles are pelagic in currents, commonly found under floating objects at sea, while slightly larger juveniles are found on shallow subtidal reefs. The adult\u2019s diet includes ascidians (sea squirts), soft corals and other reef-encrusting organisms. The juvenile\u2019s diet is unknown. This is an important target species for spearfishers in KZN. The Natal Knifejaw is occasionally, but rarely, taken on line.", "biology": "Reproductive style: Unknown. Breeding/spawning season: Evidence of gonadal activity throughout the year; ripe-running fish observed in Oct-Jan in KZN. Eggs of this species are found off the KZN south coast predominantly during summer. Breeding/spawning locality: Thought to occur throughout distribution range with eggs recorded off the KZN south coast slightly offshore suggesting spawning in depths of 30-40 m. Age at maturity: Unknown. Length at maturity: Little known, smallest mature fish sampled along the KZN coast was 355 mm FL. Maximum recorded age: 10 years. Maximum recorded weight: 3 kg (SAUFF). Maximum recorded length: 60 cm TL.", "citation": "BQ Mann, JQ Maggs In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180"}',
  '[{"measure": "FL", "reference": "Visboekie", "coefficient": 0.000594, "exponent": 2.479, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Argyrosomus japonicus',
  'Dusky Kob',
  'Kabeljou',
  'Kob Dusky',
  ARRAY['Kob','Giant Kob','Dusky Meagre','Daga Salmon'],
  'Sciaenidae',
  'Croakers',
  50  except KZN. 40  for KZN,
  NULL,
  NULL,
  NULL,
  NULL,
  'Decreasing, Endangered',
  'Stock Status: Heavily Depleted - 1.3% of pristine spawner biomass; Fishing Pressure: Heavy',
  '{"other_names": "Kob, Giant Kob, Dusky Meagre, Daga Salmon", "meristics": "Dorsal spines: 10 - 11; Dorsal soft rays: 25-30; Anal spines: 2; Anal soft rays: 7.", "appearance": "This is a large, slender fish. The caudal fin is angular in juveniles but becomes more rounded in larger fish. Colour: In life, silvery grey becoming darker above and lighter below; dorsal surface with a bluish bronze sheen that may become coppery on head. Larger fish (>80 cm) are golden brown; dorsal and caudal fins grey/brown, becoming darker with age, often with a reddish hue -due to vascular distension or haemorrhaging (possibly during capture). Pectoral and anal fins white in fish less than 10 cm, changing to yellow and finally to grey or grey/brown in fish greater than 35 cm, also becoming darker with size. Generally the goldish reen silver sheen quickly turns to a greyish brown colour when they die. Dusky Kob has a very peculiar strong metallic smell, and they excrete less slime compared to other kob after being caught. Mouth lining of juveniles is white, becoming yellow and finally yellowish grey in large adults. Axillary fold of pectoral fin is grey/black, generally with an irregular coppery-bronze patch. In general Silver Kob have longer pectoral fins than Dusky Kob. If the end of the pectoral fin exceeds past the end of the pelvic fin it is a Silver Kob. This is more distinct in older specimens. The other distinguishing characteristic is a thicker, shorter caudal peduncle (area between the body and tail) for the Dusky Kob. The base of Silver and Dusky Kob\u2019s pectoral fins are scaleless as well; except for the Sqaretail Kob; in this case their pectoral fin bases have scales.", "about": "Dusky Kob are distributed along the eastern seaboard of SA, southern and eastern seaboard of Australia; northern Indian Ocean off Pakistan and northwest coast of India; northern Pacific from Hong Kong northwards along Chinese coast to southern Korea and Japan. It is the largest of the kob species found in South African waters.  Dusky Kob are one of South Africa\u2019s most popular but severely threatened linefish species. In SA they are found along the southeast coast from Cape Point to southern MOZ, although it is most abundant from Cape Agulhas to northern KZN. Dusky Kob are considered migratory. Based on several tagging studies (i.e. ORI Tagging Project, unpubl. data and Childs in prep.), some fish are resident in specific estuaries and adjacent surf-zones and exhibit low levels of dispersal. However, a large proportion of the adult population from the EC and WC follows the \u2018sardine run\u2019 each year and migrate to KZN to spawn in winter/spring. Adults are found predominantly in the near-shore marine environment, from the surf-zone down to 50 m and possibly even 100 m but also frequent the lower-middle reaches of turbid estuaries (mostly deeper areas). Dusky Kob shows a strong affinity to estuarine habitats during early adulthood (up to the age/size at maturity of 6 years/~100 cm TL) and reduced estuarine use thereafter. They also school in the lower parts of estuaries and around river mouths during flood periods. Juveniles are estuarine-dependent and are euryhaline (able to tolerate a wide range of salinities). Early juveniles, 30-150 mm TL are found in estuaries only (mainly turbid and most often in the upper reaches). Juveniles 150-800 mm TL are predominantly found in estuaries (mainly turbid) but are also found in the inshore marine environment (mainly surf-zone). Larger juveniles (800-1 000 mm TL) are predominantly found in the inshore marine environment but are also found in estuaries (mainly turbid and most often in the lower/middle reaches). Adults are primarily piscivorous but also feed on cephalopods (octopus and squid) and crustaceans. Small juveniles (<50 mm TL) feed primarily on mysids (shrimps) and calanoid copepods (zooplankton, small crustaceans). Larger juveniles feed mainly on fish and mysids (shrimps). Diet also includes other crustaceans such as crabs, etc. Dusky Kob is one of the most important species in the recreational (estuarine and coastal shore angling as well as estuarine and inshore boat fishing), commercial and subsistence fisheries. They were found to be the primary target species in the EC recreational shore fishery and is prominent in the recreational shore fishery along the entire east coast of SA (Transkei and KZN), particularly in estuaries. Juveniles and sub-adults (<1100 mm TL) are commonly targeted by estuarine and surf-zone anglers, while adults are caught mainly by commercial and recreational ski-boat fishermen beyond the surf-zone. There is specific targeting of aggregations of these adult fish by ski-boats during late winter and spring in KZN, although surveys often don''t reflect this as catches are landed at night. Due to the similarity of the three kob species found in SA waters (i.e. Dusky, Silver and Squaretail Kob), regional differences in the regulations exist, particularly for boat-based fishers offshore. Due to the lack of enforcement and compliance, the bag and size limit restrictions are considered to be relatively ineffective. If precautionary measures advanced do not promote recovery, then a moratorium (a zero bag limit) and a shift to catch and release angling would be the only other option to ensure recovery of this collapsed stock.", "biology": "Reproductive style: Gonochorist (separate sexes). Breeding/spawning season: Oct-Jan in the EC and WC and Aug-Nov in KZN, however, the presence of Dusky Kob eggs has been documented in the coastal waters of KZN as early as July and as late as February. Breeding/spawning locality: Spawning aggregations occur on inshore reefs, pinnacles and wrecks (mainly at night) in KZN, Transkei and EC. When juveniles are about 3 cm they migrate into turbid (muddy) estuarine waters to find food and shelter. Age at maturity: Males: 5 years, Females: 6 years; SA. Length at maturity: Males: 92 cm TL, Females: 107 cm TL; SA. Maximum recorded age: 42 years; Female. Maximum recorded weight: 80 kg; Gamtoos Estuary mouth surf-zone, EC. Maximum recorded length: 205 cm TL; Gamtoos Estuary mouth surf-zone, EC.", "citation": "A-R Childs, ST Fennessy In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, Two Oceans Aquarium and SAIAB Coastal Fisheries Guide 2016, FishBase, Wikipedia, Griffiths, Marc H. and Phillip C. Heemstra. 1995. A contribution to the taxonomy of the marine fish genus Argyrosomus (Percifotmes: Sciaenidae), with descriptions of two new species from southern Africa. Ichthyological Bulletin of the J.L.B. SmithInstitute of Ichthyology, No. 65. DFFE (Department of Forestry, Fisheries and the Environment) 2023. Status of the South African marine fishery resources 2023. Cape Town: DFFE 2025"}',
  '[{"measure": "TL", "reference": "Visboekie", "coefficient": 8e-06, "exponent": 3.0397, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Argyrosomus inodorus',
  'Silver Kob',
  'Silver Kabeljou',
  'Kob Silver',
  ARRAY['Boer Kabeljou','Cape Kabeljou','Mild Meagre'],
  'Sciaenidae',
  'Croakers',
  50  except KZN. 40  for KZN,
  NULL,
  NULL,
  NULL,
  NULL,
  'Decreasing, Vulnerable',
  'Stock Status: Depleted; Fishing Pressure: Optimal',
  '{"other_names": "Boer Kabeljou, Cape Kabeljou, Mild Meagre", "meristics": "Dorsal spines: 11; Dorsal soft rays: 25-29; Anal spines: 2; Anal soft rays: 7-8.", "appearance": "The Silver Kob is very difficult to distinguish from the Dusky Kob but was named inodorus (\u2018without odour\u2019) as it lacks the typical brassy/metallic smell of fresh Dusky Kob. Silver Kob have a dark blotch at the base of the pectoral fins that lack scales. Anterior part of lateral line slightly curved. Caudal fin shape changes with growth; it may be pointed (<10 cm SL), elongate and S-shaped (10-22 cm SL), or strongly to slightly S-shaped (>30 cm). Caudal fin of larger specimens occasionally wedge-shaped to slightly rounded or truncate. Swim bladder is carrot-shaped with 31-42 arborescent appendages. Colour: In life, body silvery becoming green/brown above and white below. Dorsal surface with a copper to bronze sheen which may be very pronounced on head; dorsal and caudal fins pale yellow grey to pale grey/brown; anal, pelvic and pectoral fins from almost white to brown/grey. Mouth lining varies from pale yellow to yellowish grey; inner aspect of operculum grey to black, sometimes with a yellow tinge. In general Silver Kob have longer pectoral fins than Dusky Kob. If the end of the pectoral fin exceeds past the end of the pelvic fin it is a Silver Kob. This is more distinct in older specimens. The other distinguishing characteristic is a thicker caudal peduncle (area between the body and tail) for the Dusky Kob. The base of Silver and Dusky Kob\u2019s pectoral fins are scaleless as well; except for the Sqaretail Kob; in this case their pectoral fin bases have scales.", "about": "Silver Kob is endemic to southern Africa from northern NAM to southern Transkei. Three separate stocks have been identified in SA waters, one in the SEC, one in SC and one in SWC. Furthermore, a separate stock was identified in NAM. Silver Kob is the most abundant sciaenid species (a family of fish commonly called drums or croakers in reference to the repetitive throbbing or drumming sounds they make) in South Africa. These migratory fish can be found inshore (<60 m) in summer dispersing further offshore in winter in response to oceanographic patterns. Adults inhabit mostly moderate/low profile reef in 20-120 m depth in SEC and SC and 2-120 m in the SWC. Juveniles are found on sand/mud substrata, recruiting into shallow embayments (5-10 m depth) but moving offshore with growth. Adults feed on pelagic fish, shrimp and squid while the juvenile\u2019s diet is unknown. Silver Kob is an important component of the commercial ski-boat fishery in the SWC, SC and SEC and a sought-after species for many recreational boat-based anglers, particularly in the SEC. In South Africa it is predominantly caught between Cape Point and East London. Between Cape Agulhas and the Kei River, they are only caught by ski boat anglers and trawlers in deeper water and not in estuaries or in the surf zone. In the colder waters along the Cape west coast and Namibia they are caught in the surf zone. It is also an important component of the bycatch of inshore trawlers along the SEC coast. The inshore trawl catch is in the same order of magnitude as the linefish catch but is not restricted by size limits. It is important in both the commercial and recreational linefishery in NAM where it is restricted to a depth of 1-20 m. It is also caught in the beach seine-net fishery in SWC. Current management measures appear to have had a positive impact on stocks.", "biology": "Reproductive style: Gonochorist (separate sexes). Breeding/spawning season: Although breeding and spawning activity occurs continuously throughout the year, the main spawning season is from Aug-Dec with a peak between Sep-Nov. Breeding/spawning locality: Inshore throughout distribution. Age at maturity: Females: 1.3 years (SEC); 2.4 years (SC); 1.5 years (NAM). Length at maturity: Males: 29 cm TL (SEC); 32,5 cm TL (SC); 36 cm TL (NAM). Females: 31 cm TL (SEC); 37,5 cm TL (SC); 35 cm TL (NAM). Maximum recorded age: 25 years in SA waters; 28 years in NAM waters. Maximum recorded weight: 36.3 kg. Maximum recorded length: 145 cm TL.", "citation": "B Donovan, SE Kerwath In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180 and SAIAB, FishBase, Griffiths, Marc H. and Phillip C. Heemstra. 1995. A contribution to the taxonomy of the marine fish genus Argyrosomus (Percifotmes: Sciaenidae), with descriptions of two new species from southern Mrica. Ichthyological Bulletin of the J.L.B. Smith Institute of Ichthyology, No. 65. DFFE (Department of Forestry, Fisheries and the Environment) 2023. Status of the South African marine fishery resources 2023. Cape Town: DFFE 2025"}',
  '[{"measure": "TL", "reference": "Visboekie", "coefficient": 6e-06, "exponent": 3.07, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Johnius dorsalis',
  'Small Kob',
  'Klein Kabeljou',
  'Kob Small',
  ARRAY['Mini-kob','Kleinkob','Nondi','African Croaker','Bearded Croaker'],
  'Sciaenidae',
  'Croakers',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Mini-kob, Kleinkob, Nondi, African Croaker, Bearded Croaker", "meristics": "Dorsal spines: 11; Dorsal soft rays: 23-26; Anal spines: 2; Anal soft rays: 7.", "appearance": "The 2nd and 3rd dorsal spines are elongated. It has a small blunt barbell under the \u2018chin\u2019. The swim bladder is hammer-shaped with 14-15 appendages.", "about": "Small Kob are distributed in the western Indian Ocean from Kenya to SA; south to East London in shallow (<50 m) shelf waters and estuaries and also off the west coast of Madagascar. This species was previously referred to as Johnius dussumieri, which only occurs east of Pakistan. Movement patterns are unknown but juveniles in estuaries seem to be partially estuarine-dependent while the movement of adults are thought to move out to sea for spawning, although it is reported to conduct some spawning in Lake St Lucia. Adults inhabit offshore soft substrata, turbid water and are very common on Thukela Bank and in Lake St Lucia. Juveniles are found in estuaries and offshore soft substrata in turbid water and are common on Thukela Bank and in Lake St Lucia. Adults feed on mainly crustaceans and small fish but the juvenile\u2019s diet is unknown. Small Kob are very common in shallow water prawn trawl catches on the Thukela Bank although effort in this fishery is currently extremely low. Also commonly caught by estuarine recreational boat anglers in Lake St Lucia but effort in this fishery has also been very low since 2002 owing to drought and the closure of the estuary mouth. Featured in illegal estuarine gill net catches in Lake St Lucia; much less common in KZN beach seine catches over sandy substrata.", "biology": "Reproductive style: Gonochorist (separate sexes). Breeding/spawning season: Sep-Feb. Breeding/spawning locality: Thukela Banks; possibly in other areas with soft susbtrata; probability of some spawning in Lake St Lucia. Age at maturity: Unknown. Length at 50% maturity: Females: 125 mm TL. Maximum recorded age: Unknown. Maximum recorded weight: 139g; Female. Maximum recorded length: 230 mm TL.", "citation": "ST Fennessy, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase"}',
  '[{"measure": "TL", "reference": "Visboekie", "coefficient": 3.08e-06, "exponent": 3.28, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Otolithes ruber',
  'Snapper Kob',
  'Snapper Kabeljou',
  'Kob Snapper',
  ARRAY['Longtooth Kob','Tigertooth Croaker','Snapperkob'],
  'Sciaenidae',
  'Croakers',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  'Over exploited, 25-40% of pristine',
  '{"other_names": " Longtooth Kob, Tigertooth Croaker, Snapperkob", "meristics": "Dorsal spines: 10-11; Dorsal soft rays: 27-30; Anal spines: 2; Anal soft rays: 7-8.", "appearance": "This is a slender species with elongated body. Snout longer than eye diameter; mouth large, terminal, slightly upturned; teeth in 2 series in upper jaw, with 1 or 2 pairs of strong canines at front; a pair of canine teeth at tip of lower jaw. Colour: brownish above, silvery with a golden sheen on flanks and belly, often with oblique dark streaks dorsally. About the Species Snapper Kob are distributed in the Indo-West Pacific: SA to Oman (absent in Red Sea), India, Indonesia, southern China and Queensland coast of Australia. In SA they are found from MOZ south to Algoa Bay. Movement patterns are unknown. Adults inhabit offshore soft substrata, turbid water, very common on Thukela Bank and occasionally in estuaries. Juveniles are found on offshore soft substrata in turbid water and are common on Thukela Bank. Adults eat mainly crustaceans and small fish; the juvenile\u2019s diet is unknown. Snapper Kob are very common in shallow water prawn trawl catches on the Thukela Bank although effort in this fishery is currently extremely low; also, fairly commonly caught by recreational paddleski fishery off Durban but abundance appears to have declined since the damming of the Umgeni River. Occasionally taken in KZN beach seine catches over sandy substrata.", "biology": "Reproductive style: Gonochorist (separate sexes). Breeding/spawning season: Aug-Jan. Breeding/spawning locality: Thukela Banks, possibly other areas with soft substrata such as the Sofala Banks in MOZ. Age at maturity: Females: 1.7 years. Length at maturity: Females: 237 mm TL. Maximum recorded age: 8 years (Female). Maximum published weight: 7.0 kg. The largest fish sampled on the Thukela Banks was a female of 1.26 kg. Maximum recorded length: 90 cm TL. Common length: 40 cm.", "citation": "ST Fennessy, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, Conxemar"}',
  '[{"measure": "TL", "reference": "Visboekie", "coefficient": 4.94e-06, "exponent": 3.13, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Argyrosomus thorpei',
  'Squaretail Kob',
  'Stompstert Kabeljou',
  'Kob Squaretail',
  ARRAY['Half Kob','Stompstert-kob'],
  'Sciaenidae',
  'Croakers',
  50  except KZN. 40  for KZN,
  NULL,
  NULL,
  NULL,
  NULL,
  'Decreasing, Endangered',
  'Collapsed, ~17% of pristine',
  '{"other_names": "Stompstert-kob", "meristics": "Dorsal spines: 11; Dorsal soft rays: 26-28; Anal spines: 2; Anal soft rays: 6-7.", "appearance": "Squaretail Kob have an elongated body and with the anterior half of lateralline strongly curved. Caudal fin elongate S-shaped in juveniles, becoming slightly S-shaped and occasionally truncate in adults. It has a silvery grey colouration becoming darker and blueish (with a coppery sheen) on the upper flanks and dorsal surface and lighter below. The fins are orange-brown or yellowish. The bases of the Sqaretail Kob\u2019s pectoral fins are scaleless.  The bases of Silver and Dusky Kob\u2019s pectoral fins have scales. Mouth lining is pale yellow; inside of operculum grey/black with yellow tinge; peritoneum pigmented with grey/black dots.", "about": "Squaretail Kob are endemic to southern Africa and are distributed along the south-east coast of Africa from Xai-Xai in MOZ to Port Elizabeth in EC in waters less than 100 m and rarely found south of Port St Johns. They are apparently fairly resident.  Adults inhabit low relief, offshore reefs <100 m and congregate in large shoals around deep reefs and pinnacles while juveniles occupy relatively shallow <50 m offshore waters over soft substrata moving to reefs on reaching maturity. Adults eat mainly fish, some cephalopods (octopus and squid) and crustacean while juveniles eat mainly crustaceans. Squaretail Kob comprised a substantial component of commercial ski-boat catches in northern KZN during the 1980s, but catches declined sharply during the 1990s. They are also caught by recreational skiboat anglers, mainly between Thukela and Richards Bay on the KZN north coast. Juveniles used to be a fairly common bycatch in Thukela Bank prawn trawl catches especially between Jan-Feb. Given the extreme declines in CPUE and despite uncertainties in the per-recruit assessment, the stock probably collapsed in late 1980s as a result of commercial overfishing of a relatively small stock, exacerbated by prawn trawling bycatch. Concerningly, eggs (presumed to be) of this species have not been observed off Park Rynie since 2002, despite regular collecting.", "biology": "Reproductive style: Gonochorist (separate sexes). Breeding/spawning season: Jun-Sep. Breeding/spawning locality: Thukela Banks in central KZN; likely to spawn in the Xai-Xai area in MOZ as well. Age at maturity: Females: 2.1 years; KZN. Difficulty experienced in obtaining adequate samples to positively establish age at maturity. Length at 50% maturity: Females: 329 mm TL; KZN. Difficulty experienced in obtaining adequate samples to positively establish size at maturity. Maximum recorded age: 13 years; Female. Maximum recorded weight: 17 kg, (SADSAA). Maximum recorded length: 1100 mm TL.", "citation": "ST Fennessy, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, SAIAB, Zululand Fishing Route, Griffiths, Marc H. and Phillip C. Heemstra. 1995. A contribution to the taxonomy of the marine fish genus Argyrosomus (Percifotmes: Sciaenidae), with descriptions of two new species from southern Mrica. Ichthyological Bulletin of the J.L.B. Smith Institute of Ichthyology, No. 65"}',
  '[{"measure": "TL", "reference": "Visboekie", "coefficient": 2e-05, "exponent": 2.89, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Argyrosomus coronus',
  'West Coast Kob',
  'Weskus Kabeljou',
  'Kob West Coast',
  ARRAY['West Coast Dusky Kob'],
  'Sciaenidae',
  'Croakers',
  50  except KZN. 40  for KZN,
  NULL,
  NULL,
  NULL,
  NULL,
  'Decreasing, Data Deficient',
  NULL,
  '{"other_names": "West Coast Dusky Kob", "meristics": "Dorsal spines: 11; Dorsal soft rays: 26-30; Anal spines: 2; Anal soft rays: 7.", "appearance": "Swim bladder carrot-shaped; pectoral fin axillary fold without scales; peritoneum unpigmented; anterior part of lateral line slightly curved.", "about": "Argyrosomus coronus previously had a restricted range from the southern coast of Angola to the northern coast of Namibia but has recently undergone a temperature-mediated expansion into central Namibia and the south-western Cape in South Africa as far south as St. Helena Bay, with anecdotal reports even as far south as Table Bay in Cape Town, leading to hybridization with the extremely similar A. inodorus. West Coast Kob occurs in estuaries, the surf zone and further offshore in the Southeast Atlantic from South Africa to Angola. In southern Angola, it is caught mainly in depths of 20-40 m. They become more abundant in Argyrosomus catches going northwards, indicating its preference for warmer waters. Fish up to 13 kg were found to be immature. Adults are rarely found in Namibia but are said to be abundant in southern Angola, where the lack of permanent estuaries there suggests that this habitat type does not play an important role in the life history of this species.", "biology": "Max length: 200 cm TL; Max published weight: 77.0 kg", "citation": "FishBase, The IUCN Red List of Threatened Species"}',
  NULL
);

