  'Koester',
  ARRAY['Soup Bully'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Soup Bully", "meristics": "Dorsal spines: 11-13; Dorsal soft rays: 15-17; Anal spines: 3; Anal soft rays: 7-8.", "appearance": "Head and body have small orange spots and few scattered dark brown blotches, caudal and anal fins dark grey, pelvic fins dusky yellow.", "about": "Koester are distributed in the Southeast Atlantic: Namibia to East London, South Africa at a depth range 1-30 m amoung rocks and up to Mozambique. This species is caught by anglers and is noted to have excellent flesh.", "biology": "Information on life history and ecology of this species is limited. Max length: 35.0 cm TL. Common length: 17.3 cm.", "citation": "FishBase, The IUCN Red List of Threatened Species"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.00955, "exponent": 2.97, "formula_type": "power", "result_unit": "g"}]'
