INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Elops lacerta',
  'Atlantic Ladyfish',
  'Atlantiese Springer',
  'Ladyfish Atlantic',
  ARRAY['West African Ladyfish'],
  'Elopidae',
  'Ladyfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "West African Ladyfish", "meristics": "Dorsal spines: 0; Anal spines: 0.", "appearance": "Diagnosis: Elops lacerta is characterized by 17-19 gill-rakers on lower limb of first gill arch and small scales, those in lateral line numbering 72-83 and ornamented with a small, unbranched, tubule.", "about": "The species is widespread in coastal waters from Mauritania to at least Angola. This species uses estuarine areas and hypersaline lagoons; changes in the quality of these habitats may affect this species'' population dynamics. Although this species may not be closely associated with any single habitat, it may be adversely affected by development and urbanization. It is a pelagic species that lives in shallow coastal waters with sandy-muddy substrates. It has been caught in brackish water at the river mouths and as far upstream 100 km from the coast. It feeds primarily on small fishes, mainly clupeids, crustaceans and molluscs.", "biology": "Max length: 100.0 cm TL; Common length: 60.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Lichia amia',
  'Leerfish',
  'Leervis',
  'Leerfish',
  ARRAY['Garrick','Leerie'],
  'Carangidae',
  'Kingfishes',
  70,
  NULL,
  2,
  NULL,
  NULL,
  'Unknown, Least Concern',
  'Vulnerable, <14% of pristine',
  '{"other_names": "Garrick, Leerie", "meristics": "1st Dorsal spine: 7-8 (with only their bases connected by a membrane); Dorsal soft rays: 19-21; 2nd Dorsal spine with 1 spine and 19-21 soft rays; anal fin with 2 detached spines followed by 1 spine and 17-21 soft rays.", "appearance": "Diagnosis: body elongate, moderately deep and compressed; dorsal profile of head almost straight, ending in a pointed snout; eyes relatively small; upper jaw slender and rounded posteriorly, ending below or beyond posterior eye margin. Pectoral fins short; scales small, those on breast oval to strongly lanceolate, and partially embedded in the skin; lateral line very irregular and wavy, describing a convex curve over, and a concave curve behind pectoral fins; no lateral-line scutes. Colouration: Brown above, silvery white below lateral line; lobes of dorsal and anal fins black distally; juveniles (to at least 12 cm fork length) with brownish black bars on sides.", "about": "The global distribution of Leerfish is confined to parts of the Mediterranean and Black Sea, along the west coast of Africa including Mauritania to northern Namibia and along the south-eastern seaboard of SA extending all the way through to Cape Vidal and possibly on to Maputo.  They are scarce between Cunene Mouth and Table Bay. Studies have shown that the Namibian/Angolan stock is separate from the SA stock. Adults migrate annually to KZN during winter to spawn. During summer adults return to Cape waters. Adults are found in nearshore surface waters from the surf zone to a depth of 50 m and seldom found more than 500 m from shore. Juveniles are common in EC and WC estuaries which provide important nursery areas, and coastal shallows. Adults and juveniles are primarily piscivorous while smaller juveniles also feed on crustaceans. Although highly sought after as a recreational trophy fish by shore anglers, ski-boat anglers and spearfishers, especially during the winter/spring season in KZN, they are also targeted in estuaries in the EC. They make up a relatively small percentage of the overall catch of these sectors.", "biology": "Leervis are gonochoristic (separate sexes). Adult leervis undertake an annual migration to spawn in KZN coastal waters during austral winter from Sep-Nov. After spawning, eggs and larvae are transported southwards inshore of the Agulhas Current, where juveniles recruit into estuaries in the Eastern and Western Cape during summer. They remain in these nursery habitats for 1 to 3 years, before moving to the coastal marine environment to join the adult population. Males and females mature at age 4 when about 80 cm FL. The maximum recorded age is 10 years, the maximum recorded weight is 32.2 kg and the maximum recorded length is 180 cm TL.", "citation": "BQ Mann, WM Potts In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180 and Murray TS, Duncan MI, Winkler AC, Childs A-R, Mann BQ, Potts WM (eds). 2020. Linefish resilience in the Anthropocene: The Proceedings of the 5th Southern African Marine Linefish Symposium. Rhodes University, Makhanda, 113pp. FishBase"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 1.124e-05, "exponent": 3.015, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Plectorhinchus flavomaculatus',
  'Lemonfish',
  'Lemmetjievis',
  'Lemonfish',
  ARRAY['Yellow-spotted Sweetlips'],
  'Haemulidae',
  'Grunts',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 12 - 13; Dorsal soft rays: 19-22; Anal spines: 3; Anal soft rays: 7.", "appearance": "This species is distinguished by the following characters: chin with 6 pores, no median pit; 4th or 5th spines longest; lips fleshy, moderately swollen with age; scales ctenoid (rough to touch); lateral line tubed scales about 53-60; caudal fin slightly rounded in juveniles becoming emarginate in adults. Colour of body bluish grey, juveniles with golden or rusty orange stripes along the body that break into a reticulate pattern and later form small spots that may be lost with age or remain as lines, or lines of spots on head", "about": "Lemonfish are distributed in the Indo-West Pacific: Red Sea south to SA, east to Papua New Guinea, north to southern Japan, and south to Western Australia and New South Wales. In southern Africa they occur as far south as Port St Johns. In Kenya they are reported to be mostly resident with some incidence of longer-range dispersal. In SA limited tagging data supports the predominance of residency. Adults are found on coral and rocky reefs from the surf-zone down to 80 m while juveniles, in tidal rock pools and subtidal gullies, often in seaweed. Juveniles are known to enter tropical estuaries and harbours. The adult diet consists of small crabs, marine worms, shrimps and sometimes small fish while the juvenile\u2019s diet is unknown. Because their flesh makes for good eating, they are popular amongst anglers and as they are often reluctant to take baits, they are mostly taken by spearfishers. Voluntarily submitted competition data from the KZN recreational shore-fishery indicates an increasing trend in CPUE, catch composition and mean size.", "biology": "Their reproductive style is largely unknown but spawning aggregations are reported from Kenya but the season and locality is unknown. The age at maturity is also unknown but the length at maturity is estimated at 34 cm TL in the Asia-Pacific region. The maximum recorded age is unknown. The maximum recorded weight is 4,4 kg and the maximum recorded length is 60 cm TL.", "citation": "JQ Maggs, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 2e-05, "exponent": 2.9515, "formula_type": "power", "result_unit": "kg"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Saurida undosquamis',
  'Largescale Lizardfish',
  'Grootskub Akkedisvis',
  'Lizardfish Largescale',
  ARRAY['Brushtooth Lizardfish'],
  'Synodontidae',
  'Lizardfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Brushtooth Lizardfish", "meristics": "Dorsal spines: 0; Dorsal soft rays: 11-12; Anal spines: 0; Anal soft rays: 11-12.", "appearance": "Cigar-shaped, rounded or slightly compressed; the head pointed and depressed; the snout rounded. Dorsal origin is closer to the adipose origin than the snout tip. Anal fin origin is closer to the tail base than the operculum. Colour is brownish gray above and creamy below, with 8-10 indistinct darker spots along the middle of the sides.", "about": "This is a demersal species that occurs in the Eastern Indian Ocean, Malay Peninsula, northern Java, Arafura Sea, Louisiade Archipelago, southern Philippines and northern Australia, which occurs over sand or mud bottoms in tropical waters at depths of up to 200 m. It feeds on fish, crustaceans, and other invertebrates.", "biology": "Length at Maturity: 19.8 cm. Max length: 50.0 cm SL; Common length: 30.0 cm TL; Max reported age: 7 years.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Synodus dermatogenys',
  'Sand Lizardfish',
  'Sand Akkedisvis',
  'Lizardfish Sand',
  NULL,
  'Synodontidae',
  'Lizardfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 0; Dorsal soft rays: 11-13; Anal spines: 0; Anal soft rays: 8-10.", "appearance": "Cheeks usually not scaled to preopercular margin; membranous flap on anterior nostrils long and slender; pectoral fins not reaching a line connecting origins of dorsal and pelvic fins. Body is brownish above. It can be identified by the five or six irregular red-brown or blackish vertical bars that intersect a red horizontal broken band on the flank, immediately below this band is a line of whitish dots although the colours can vary depending on the surroundings.", "about": "This species is native to the areas of Indo-Pacific, Red Sea, Hawaiian, Line, Marquesan, Tuamoto islands, north to Ryukyu Islands, and south to Lord Howe, Micronesia, Southeast Atlantic, Algoa Bay, and South Africa. It is common to find this species in sand-rubble areas of lagoon and seaward reefs to over 90 m of benthic depth. This species buries itself in the sand while exposing its eyes and nostrils. It is known to find this species solitary or in small groups. Sand Lizardfish is a predator of small fish and crustaceans; its mouth is full of sharp needle-like teeth. It exhibits biofluorescence, that is, when illuminated by blue or ultraviolet light, it re-emits it as green and appears differently than under white light illumination. Biofluorescence may assist in intraspecific communication and camouflage. It is sometimes found in pairs or small groups. It is of no importance to fisheries.", "biology": "Generally solitary, but up to four males may have been observed courting a single larger female. A courting male may station itself atop a female and puff out its opercula and spread its fins in a display of aggression towards other males. Max length: 24.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Synodus variegatus',
  'Variegated Lizardfish',
  'Bont Akkedisvis',
  'Lizardfish Variegated',
  ARRAY['Redband Lizardfish','Rooistreep-akkedisvis'],
  'Synodontidae',
  'Lizardfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Redband Lizardfish, Rooistreep-akkedisvis", "meristics": "Dorsal spines: 0; Dorsal soft rays: 10-14; Anal spines: 0; Anal soft rays: 8 - 10.", "appearance": "Adults are variable from grey to red. On some individuals, the hourglass- or saddle-shaped markings along its side may be obliterated by an almost solid dark red band. Large individuals are often thick-bodied, giving a hump-backed appearance.", "about": "Indo-Pacific: Red Sea and East Africa to the Hawaiian, Line, Marquesan, and Ducie islands, north to the Ryukyu Islands, south to the Lord Howe, Kermadec and Rapa islands. Also has been reported from New Zealand and Persian Gulf. Depth range of 3-121 m; usually 5-60 m. Inhabits deep lagoon and seaward reefs to depths over 40 m; sometimes found on sandy bottoms concealing itself in the sand. Prefers to rest on hard surfaces and frequently occurs in pairs. It feeds on small fish by seizing these from passing schools, also shrimp.", "biology": "Length at maturity: 14.0 cm. Max length: 40.0 cm TL; Common length: 30.0 cm TL.", "citation": "FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Selene dorsalis',
  'Lookdownfish',
  'Afkykvis',
  'Lookdownfish',
  ARRAY['African Moonfish'],
  'Carangidae',
  'Kingfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "African Moonfish", "meristics": "Dorsal spines: 9; Dorsal soft rays: 23-24; Anal spines: 1-3; Anal soft rays: 18 - 20.", "appearance": "Diagnosis: body short, deep and strongly compressed; head with a typical hump above eyes and a steep frontal profile that is slightly concave in front of eyes; lower jaw prominent; 2 dorsal fins, 1st with 8 spines that are very short in adults, 2nd with 1 spine and 23 or 24 soft rays; anal fin with 2 anterior detached spines (resorbed in adults), followed by 1 spine and 18-20 soft rays; anterior lobes of soft dorsal and anal fins small or indistinct; pelvic fins very short, nearly rudimentary; body (except breast) entirely covered with minute cycloid scales embedded in the skin; lateral line distinctly arched over pectoral fins, with some weak scutes on the straight posterior part; 2 lateral keels at base of caudal fin. Colouration: entirely silvery with bluish reflections that are more pronounced on dorsal half; a blackish spot at upper angle of gill cover; in juveniles, another blackish spot on sides of body, at the inflexion point of the lateral line; fins glassy.", "about": "It is widely distributed in the Eastern Atlantic from Portugal to South Africa and is known from the Canary and Cape Verde islands and along the African coast from Senegal to Walvis Bay, Namibia. is a schooling species, which is usually found near the bottom from inshore waters to at least 60 m depth. The young of less than 3 cm FL occur near the surface, and juveniles may be found in bays and river mouths. This species feeds on small fishes and crustaceans. It occurs in shelf waters throughout its range.", "biology": "Eggs are pelagic. Max length: 40.0 cm TL; Common length: 24.0 cm FL. Max published weight: 1.5 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Luvarus imperialis',
  'Louvar',
  'Louvar',
  'Louvar',
  ARRAY['Luvar'],
  'Luvaridae',
  'Louvars',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Luvar", "meristics": "Dorsal soft rays: 20; Anal spines: 0; Anal soft rays: 18.", "appearance": "The absence of dorsal and anal spines and of pelvic fins contributes to streamlining of the body, as does the flat and relatively consolidated opercular spines. The juvenile form has a pair of spines near the base of the tail though they are lost in the adult. It is pink in colour and possesses a characteristic bulging forehead.", "about": "This species is distributed circumglobally, but it is primarily an Indo-Pacific species with scattered records in the Atlantic but known to be fond west of Cape Point, South Africa. In the Indo-Pacific it is known along East Africa from Somalia to Mozambique. Western Pacific: Japan, Australia, and New Zealand. Eastern Pacific: Oregon, USA to Chile. It is apparently solitary. It can be found at depths from near the surface to 200 m. It feeds on jellyfish, ctenophores, and other soft-bodied planktonic animals. It is rarely recorded in commercial catches although prized as an eating fish.", "biology": "Reproduction occurs at the end of spring and during summer. Juveniles go through a dramatic metamorphosis in the shape and sizes of the head and fins. Max length: 200 cm TL; Common length: 152 cm SL; Max published weight: 150.0 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Trachurus capensis',
  'Cape Horse Mackerel',
  'Maasbanker',
  'Mackerel Cape Horse',
  ARRAY['Horse Mackerel','Carapau'],
  'Carangidae',
  'Kingfishes',
  NULL,
  NULL,
  NULL,
  NULL,
  NULL,
  'Unknown, Least Concern',
  'Stock status: Optimal;  Fishing pressure: Optimal',
  '{"other_names": "Horse Mackerel, Carapau", "meristics": "Dorsal fin spines: 8; Dorsal soft rays: 31-36. Anal fin spines: 2; Anal soft rays: 24-32.", "appearance": "Pelvic fin moderate in size, originating below end of pectoral fin base.  Scales in curved lateral line: 31-36, expanded dorsolaterally and scute-like; in straight lateral line 33-46 scutes. Total scales and scutes: 65-79. The Cape Horse Mackerel has an elongated, slightly compressed, body with a large head. It is characterised by a distinct dark spot on the gill cover and a row of enlarged scutes (spiny scales) along the S-shaped lateral line. The dorsal part of body and head are dark and can be dusky, almost black or grey, to bluish green in colour while the flanks, belly and head are usually paler, varying from whitish to silvery.", "about": "Cape Horse Mackerel are fond in the Eastern Atlantic: Gulf of Guinea to South Africa. Its population is mainly confined to the Benguela current system from southern Angola to the Wild Coast. Off South Africa, adult horse mackerel are currently more abundant off the South Coast than the West Coast. Adults are found mainly over the continental shelf, often over sand bottoms in a depth range 0-500 m, usually 100-300 m. Juveniles occur in patchy aggregations, and in South Africa, adults occur mostly on the Agulhas Bank while large seasonal aggregations of juveniles occur in the St. Helena Bay area. Shoals rise to feed in surface waters at night and are found close to the bottom during the day. Juveniles feed mainly on copepods while adults prey on fish and a wide range of invertebrates. This species supports a large, multi-national commercial purse seine fishery. Both juveniles and adults are caught with pelagic purse seines as well as mid-water and bottom trawls, but since the mid 1960''s, the catch was reduced to mainly juveniles. Cape Horse Mackerel yield a low-value product and are a source of cheap protein. The majority of the South African catch is exported to West Africa.", "biology": "As individuals mature, they move further offshore. Spawning occurs throughout the year and peaks in the austral summer and autumn along the 200 m isobaths. Max length: 60.0 cm FL; Common length: 30.0 cm.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia, DEFF (Department of Environment, Forestery and Fisheries) 2020. Status of the South African marine fishery resources 2020. Cape Town: DEFF."}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.0078, "exponent": 3.011, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Scomber japonicus',
  'Chub Mackerel',
  'Makriel',
  'Mackerel Chub',
  ARRAY['Japanese Mackerel','Common Mackerel','Slimy Mackerel','Pacific Chub Mackerel'],
  'Scombridae',
  'Mackerel, Tuna, Bonito',
  NULL,
  NULL,
  NULL,
  NULL,
  NULL,
  'Stable, Least Concern',
  'Over exploited, 25-40% of pristine',
  '{"other_names": "Mackerel, Common Mackerel, Slimy Mackerel, Pacific Chub Mackerel", "meristics": "Dorsal spines: 9-11; Dorsal soft rays: 112; Anal spines: 1; Anal soft rays: 12-14.", "appearance": "Chub Mackerel sport narrow, spindle-shaped body which tapers at both ends with light-blue backs with dark blue to black rippled patterns and white bellies. They have a pointy head and a large mouth. The head is dark blue, the back is dark blue with about 30 dark wavy lines, and the undersides are silverish green. Chub Mackerel can be distinguished from other mackerel by counting the finlets on their back; Chub Mackerel typically have four to six finlets.", "about": "Chub Mackerel exhibit an anti-tropical distribution (species found north and south of the tropical zone but not in the tropics): NW, NE and SE Pacific; absent from Indian Ocean except for SA waters. Elsewhere they are replaced in the Atlantic Ocean by Altantic Chub Mackerel (Scomber colias) and by Blue Mackerel (Scomber australasicus) in the Indian Ocean and Red Sea. In southern African waters Chub Mackerel is found from Angola to southern MOZ. It was thought that a single population represents the stock in the SE Atlantic but the connectivity between the Indian and Atlantic populations is uncertain. They are migrants.  Seasonal migrations are thought to be related to their breeding cycle - congregating in northern Benguela region in summer, migrating southwards during winter, and possibly migrating northward again towards the end of the year. Migrations could also be related to prey availability. Adults are a coastal pelagic species and to a lesser extent epipelagic to mesopelagic over the continental shelf and shelf slope to depths of 300 m. A shoaling species often found in mixed shoals of other species such as Pilchards, Maasbanker or other scombrids (mackerels, tunas, and bonitos). Juveniles can be found inshore, gradually moving into deeper waters upon reaching maturity. Adults feed on zooplankton such as euphausiids (shrimp-like planktonic marine crustaceans), mysids (various small, shrimplike, chiefly marine crustaceans) and copepods (crabs, shrimps, and prawns) and small fish and squid. Juveniles eat mainly zooplankton. Historically Chub Mackerel are taken mainly on handline. Since 1954 was an important pelagic species caught by commercial purse-seine and beach-seine net fisheries, also taken by mid-water and bottom trawlers, especially in NAM waters. However, very little caught by commercial purse-seiners since the mid-1970s and direct targeting by 174 of these vessels is not allowed. On average less than 350 mt per annum caught as a bycatch since 2000, contributing less than 1% to the entire purse-seine catch. They are frequently landed by recreational ski-boat anglers who often use them as bait. Healthy mackerel stocks will ultimately be dependent on the maintenance of suitable levels of abundance of forage species, especially Anchovey.", "biology": "Reproductive style: Gonochoristic (separate sexes). Breeding/spawning season: Jun-Sep on the EC and WC coasts and Jul-Dec off the KZN coast. Breeding/spawning locality: Primary spawning area is between Lambert\u2019s Bay and Dassen Island on the West Coast. However, spawning occurs along the entire SA coast. Off the KZN south coast spawning occurs across the shelf but mainly in the 40-50 m depth range. Age at maturity: Combined sexes: 3 years. Length at maturity: Combined sexes: 39 cm FL. Maximum recorded age: 8 years. The maximum age reported in the literature is 18 years. Maximum recorded weight: 5 kg but generally much smaller. 2.85 kg (SADSAA). Maximum recorded length: 70 cm TL but seldom greater than 50 cm TL.", "citation": "BQ Mann, J Coetzee In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, IGFA"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0044, "exponent": 3.26, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Rastrelliger kanagurta',
  'Indian Mackerel',
  'Indiese Makriel',
  'Mackerel Indian',
  ARRAY['Longjaw Mackerel','Langkaak Makriel'],
  'Scombridae',
  'Mackerel, Tuna, Bonito',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  NULL,
  '{"other_names": "Longjaw Mackerel, Langkaak Makriel", "meristics": "Dorsal spines: 8 - 11; Dorsal soft rays: 12; Anal spines: 0; Anal soft rays: 12.", "appearance": "The body of the Indian mackerel is moderately deep, and the head is longer than the body depth. These fish have thin dark longitudinal bands on the upper part of the body, which may be golden on fresh specimens. There is also a black spot on the body near the lower margin of the pectoral fin. Dorsal fins are yellowish with black tips, while the caudal and pectoral fins are yellowish. The remaining fins are dusky.", "about": "This Indo-West Pacific species is found from the Red Sea and East Africa to Indonesia, north to the Ryukyu Islands and China, south to Australia, Melanesia and Samoa. It entered the eastern Mediterranean Sea through the Suez Canal. This species occurs in coastal bays, harbors and deep lagoons, usually in some turbid plankton-rich waters. It is a common, coastal pelagic species, often found in large schools. Adult individuals feed on macroplankton such as larval shrimps and fish. This species is highly commercial and is caught with a number of different gears including purse-seines, fish corrals, gillnets, cast and drift nets, and by dynamiting and is important in commercial and artisanal fisheries throughout its range.", "biology": "Size at first maturity is approximately 17-20 cm, and longevity is estimated to be approximately four years. Max length: 42.1 cm TL; Common length: 25.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0058, "exponent": 3.21, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Scomberomorus commerson',
  'King Mackerel',
  'Koning Makriel',
  'Mackerel King',
  ARRAY['Katonkel','Couta','Couda','Barred Mackerel','Narrow-barred Spanish Mackerel','Narrowbarred Mackerel','Spanish Mackerel'],
  'Scombridae',
  'Mackerel, Tuna, Bonito',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Decreasing, Near Threatened',
  'Optimally exploited, 40-50% of pristine (IOTC: overfished, subject to overfishing)',
  '{"other_names": "Katonkel, Couta, Couda, Barred Mackerel, Narrow-barred Spanish Mackerel, Narrowbarred Mackerel, Spanish Mackerel", "meristics": "Dorsal spines: 15-18; Dorsal soft rays: 15-20; Anal spines: 0; Anal soft rays: 16-21.", "appearance": "The body is elongate, moderately strongly compressed; upper jaw reaching to posterior margin of eye or slightly beyond; teeth in jaws strong and compressed. Lateral line is abruptly bent downward below end of second dorsal fin. They are vivid blue to dark grey in colour along their backs and flanks and fade to a silvery blueish grey on the belly. Spanish mackerel have scores of narrow, vertical lines down their sides but in juveniles they are frequently spotted.", "about": "King Mackerel are found widespread in warm waters of the Indo-Pacific, distributed from SA north along the east coast of Africa to the Red Sea, east along the south coast of Asia as far north as China and Japan and south to Australia. It is an immigrant to the eastern Mediterranean by way of the Suez Canal. In SA they are common in MOZ and KZN waters, rarely extending as far south as Mossel Bay. They are migrants. Adults migrate southwards during summer (Nov-Mar) into KZN waters from MOZ and return during winter. This probably represents a feeding migration as little spawning has been recorded in KZN water. Adults are pelagic, extending from the edge of the continental slope inshore to shallow coastal waters. Younger fish tend to shoal in single cohorts. Juveniles have been recorded from large marine embayments in MOZ. Adults mainly feed on open water pelagic fish e.g. Clupeidae (herrings, shads, sardines), Carangidae (jacks and pompanos) and Scombridae (mackerels, tunas, bonitos) and will opportunistically feed on species such as lizardfish. Juveniles feed on small pelagic bait fish and crustaceans. King Mackerel are very important in the recreational and commercial ski-boat linefishery and spearfishery in KZN and MOZ. Historically, they were a prime target of the KZN commercial linefishery, however in recent years, primarily for economic reasons, it is has become more of an opportunistic catch in this fishery. They are rarely taken by recreational shore-based anglers. Juveniles are caught in the artisanal beach-seine fishery and as a bycatch of the prawn-trawl fishery in northern MOZ.", "biology": "Reproductive style: Gonochoristic (separate sexes). Breeding/spawning season: Spring-Summer. Breeding/spawning locality: MOZ waters, but spawning does appear to occur further south into KZN waters during years with optimum environmental conditions. Age at maturity: Males: 0.6 years; Females: 1.5 years. Length at maturity: Males: 651-705 mm FL; Females: 823-1096 mm FL; Combined sexes: 677 mm FL. Maximum recorded age: 14 years; Female; KZN but up to 16 years elsewhere. Maximum recorded weight: 46.4 kg (SADSAA); 42.18 kg (IGFA). Maximum recorded length: 220 cm FL; KZN.", "citation": "B Lee, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.0091, "exponent": 2.96, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Scomberomorus plurilineatus',
  'Queen Mackerel',
  'Natalse Snoek',
  'Mackerel Queen',
  ARRAY['Natal Snoek','Spotted Mackerel','Gespikkelde Makriel','Gespikkelde Katonkel','Kanadi Kingfish'],
  'Scombridae',
  'Mackerel, Tuna, Bonito',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  NULL,
  '{"other_names": "Natal Snoek, Spotted Mackerel, Gespikkelde-makriel, Gespikkelde Katonkel, Kanadi Kingfish", "meristics": "Dorsal spines: 15-17; Dorsal soft rays: 19-21; Anal soft rays: 19-22.", "appearance": "Body covered with small scales. Lateral line has no auxiliary branches anteriorly, gradually curving down toward caudal peduncle. Juveniles have spots but develop adult patterns of interrupted lines by the time they reach 40 cm.", "about": "This species is found in the western Indian Ocean from Seychelles, Kenya and Zanzibar to South Africa and along the west coast of Madagascar. In South Africa there has been a suggested 50% decline of unfished biomass. This is a pelagic, oceanodromous, and neritic species. It forms large schools in the Zanzibar Channel from March to September. It feeds mainly on anchovies, sardineans and other small fishes, squids, and mantis shrimps. It is very important in the recreational line fishery in South Africa. Spearfishermen also take Queen Mackerel in KZN. There are clashing interests between recreational and commercial fishermen over the right to exploit the species.", "biology": "In South Africa, 50% maturity for this species is attained at age of two years and a length of 72-74 cm FL for males, 76-78 cm for females. Longevity is estimated to be six years. Max length: 120 cm FL; Max published weight: 12.5 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species"}',
  '[{"measure": "FL", "reference": "Visboekie", "coefficient": 1.26e-05, "exponent": 2.94, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Istiompax indica',
  'Black Marlin',
  'Swart Marlyn',
  'Marlin Black',
  NULL,
  'Istiophoridae',
  'Billfishes',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  'Unknown (IOTC: Uncertain, cause for concern)',
  '{"other_names": "", "meristics": "Dorsal spines: 0; Dorsal soft rays: 39-50; Anal spines: 0; Anal soft rays: 16-21.", "appearance": "Black Marlin can be quickly and positively identified since it is the only marlin that has rigid pectoral fins, with 19 to 20 rays that cannot be folded flat up against the body without breaking the joints. It is also set apart by the falcate (airfoil shape) pectoral fins and by its very short ventral fins, which almost never exceed 30 cm in length, regardless of the size of the fish. The first dorsal fin is proportionately the lowest of any billfish, usually less than 50 percent of the body depth; membrane of first dorsal fin blue black, without spots; flanks without spots; other fins dark brown with tinges of dark blue in some specimens. The body is elongate and laterally compressed, rather than rounded, much more so than in similar sized Blue Marlin. The body is dark slate blue above, changing abruptly to silvery white below the lateral line and is densely covered with small, embedded scales with 1 or 2 sharp points. When feeding or leaping, the Black Marlin may display light blue vertical stripes on the sides. The upper jaw produced into a robust but not a very long beak.  Slight variations in colour cause some specimens to have a silvery haze over the body.", "about": "Black Marlin are distributed throughout tropical and sub-tropical waters of the Indo-Pacific in temperatures ranging from 15-30\u00b0C. They are rarely reported from the Atlantic. During summer their extent reaches temperate waters. They are both coastal and oceanic. In southern Africa only one stock is thought to exist which is found throughout the Indian Ocean extending down the eastern seaboard of SA with occasional strays entering the Atlantic Ocean by way of the Cape. They are migratory; a highly mobile species which appears to move into higher latitudes during summer and returns to warmer, tropical waters during winter, but movement patterns appear not to be entirely predictable. In the tropical WIO, Black Marlin are more abundant during the north-east monsoon period (mid Oct - end Dec). Black Marlin are the most common marlin species in SA waters and is most abundant off the KZN coast during the summer months and is known to range as far south as Cape Point. Adult Black Marlin are oceanic and epipelagic, usually above the thermocline, often found close to land masses, islands and coral reefs at depths ranging from 0-200 m and are most common off KZN during summer especially around deep, submarine canyons that reach close to the shore. Juveniles are widely distributed by surface currents and are found predominantly in nearshore coastal waters. No juvenile Black Marlin have yet been positively identified in SA waters. Adults are powerful predators of pelagic shoaling fish, including tuna and squid. They take food at the surface but also at depths and occasionally near reefs. The diet of juveniles is unknown. Black Marlin are taken by pelagic longlines (44%) and drift gillnets (49%) throughout the Indian Ocean region, especially by China, Taiwan, Japan, Korea, Sri-Lanka and Indonesia: total catch reached 4, 569 mt per annum but is much less than other billfish species. They are an important recreational target species, caught by ski-boat anglers primarily off east coast of Africa including KZN but most are released. Little commercial harvesting of Black Marlin occurs from mainland Africa although foreign fleets do take this species as bycatch under license in the MOZ Channel and elsewhere in the SWIO. A highly rated game fish, the Black Marlin has the power, size, and persistence of which anglers dream. Fishing methods include trolling with large, whole baits (mackerel, bonito, flying fish, squid and others) or with artificial lures. Live bait is also effective. Catch and release should continue to be encouraged in the recreational linefishery and greater use of circle hooks should be promoted to enhance post release survival.", "biology": "Black Marlin are heterosexual where females grow bigger than males. They prefer temperatures 27-28\u00b0C for spawning and are highly fecund with a mature female producing in the order of 40 million eggs.  The breeding/spawning season and locality is unknown in southern Africa as no reproductively active Black Marlin have been recorded in SA waters. The age and length at maturity is unknown however weight at maturity is ~50-80 kg for males and >200 kg for females. The maximum recorded age is 13 years for males and 20 years for females. The maximum recorded weight is 707,6 kg (Peru 1953, IGFA) while in SA it is 354.2 kg (KZN 1987, SADSAA). The maximum recorded length is 448 cm TL (Indian Ocean).", "citation": "RP van der Elst, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, IGFA"}',
  '[{"measure": "LBFL", "reference": "FishBase", "coefficient": 8e-05, "exponent": 2.668, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Makaira nigricans',
  'Blue Marlin',
  'Blou Marlyn',
  'Marlin Blue',
  NULL,
  'Istiophoridae',
  'Billfishes',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Decreasing, Vulnerable',
  'Unknown (IOTC: Not overfished, subject to overfishing) (ICCAT: Overfished and overfishing)',
  '{"other_names": "", "meristics": "Dorsal spines: 0; Dorsal soft rays: 45-50; Anal spines: 0; Anal soft rays: 19-23.", "appearance": "The pectoral fins of Blue Marlin are never completely rigid, even after death, and can be folded completely flat against the sides except in the largest specimens. The dorsal fin is high and pointed anteriorly (rather than rounded) and its greatest height is less than the greatest body depth. The anal fin is relatively large and it too is pointed. Juveniles may not share all the characteristics listed above, but the peculiar lateral line system is usually visible in small specimens. In adults it is rarely visible unless the scales or skin are removed. The vent is just in front of the anal fin, as it is in all billfish except the spearfish. The back is cobalt blue and the flanks and belly are silvery white. There may be light blue or lavender vertical stripes on the sides, but these usually fade away soon after death, and they are never as obvious as those of the Striped Marlin. There are no spots on the fins.", "about": "Some taxonomists believed that the Atlantic and Indo-Pacific Blue Marlins were closely related but separate species. They applied the scientific name Makaira nigricansto the Atlantic species only and the name Makaira mazara to the Pacific and Indian Ocean species. Others treated the two populations as subspecies, Makaira nigricans nigricans and Makaira nigricans mazara but now they are considered conspecifics: both known under the name of Makaira nigricans. The adults of this epipelagic and migratory species occur in tropical and temperate clear, warm surface oceanic waters >24\u00b0C above the thermocline, not usually found close to land masses or islands unless there is a steep drop-off of the shelf. In the Atlantic Ocean it is found from 45\u00b0N to 35\u00b0S, and in the Pacific Ocean from 48\u00b0N to 48\u00b0S. It is less abundant in the eastern portions of both oceans. In the Indian Ocean it occurs around Ceylon, Mauritius, and off the east coast of Africa and sometimes reaching as far south as Cape Town. Fish migrate into higher latitudes during the summer months, and seasonal concentrations occur in the Indian Ocean (0\u00b0-13\u00b0S) from April to October. Some evidence exists of migratory activity being greater in males.  Juveniles are also pelagic and oceanic, often found in association with warm currents. No larvae or juveniles have been positively identified off SA. Adults are top predators feeding on a diversity of epipelagic fishes, especially smaller tunas, frigate mackerel and squids. The juvenile\u2019s diet is unknown. Blue Marlin are an important bycatch component of commercial fisheries in the Indian Ocean being taken mainly by pelagic longlines (60%) and drift gillnets (30%) with remaining catches recorded by line (trolling). More recently, larger catches have been reported from the western and central tropical Indian Ocean and, to a lesser extent, the MOZ Channel and SA. The catches of Blue Marlin are typically higher than those of Black Marlin and Striped Marlin combined. The mean annual catch of Blue Marlin in the Indian Ocean over past decade was about 9 600 mt. Blue Marlin are occasionally captured by recreational sport fishermen off the KZN coast usually during summer (Nov-Mar) with most being hooked further offshore than other billfish species. A powerful, aggressive fighter, they run hard and long, sound deep, and leap high into the air in a seemingly inexhaustible display of strength. Fishing methods include trolling large whole baits such as bonito, dolphin, mullet, mackerel, bonefish, ballyhoo, flying fish and squid as well as various types of artificial lures and sometimes strip baits. Catch and release should continue to be encouraged in the recreational line fishery and greater use of circle hooks should be promoted to enhance post release survival.", "biology": "Blue Marlin are a heterosexual, dimorphic species with females growing considerably larger than males. The breeding/spawning season occurs in summer-autumn in southern latitudes < 30\u00b0S in the Indian Ocean but may spawn year-round in equatorial waters < 10\u00b0S. Distinct breeding populations and spawning occurs between 30\u00b0N and 30\u00b0S but specific spawning grounds have yet to be identified in the Indian Ocean. The maximum recorded age is 18 years for males and 27 years for females. The maximum recorded weight is 636 kg (Brazil, IGFA). SA angling record is 521.4 kg; (Sodwana, SADSAA). A further report states that males reach a maximum of 150 kg, while females may reach over 906 kg. Male Blue Marlin rarely exceed 136 kg. The maximum recorded length is 450 cm TL; 350 cm EFL.", "citation": "RP van der Elst, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, IGFA"}',
  '[{"measure": "LBFL", "reference": "FishBase", "variant": "Atlantic", "coefficient": 0.0054, "exponent": 3.07, "formula_type": "power", "result_unit": "g"}, {"measure": "LBFL", "reference": "FishBase", "variant": "Pacific", "coefficient": 0.0054, "exponent": 3.07, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Kajikia audax',
  'Striped Marlin',
  'Gestreepte Marlyn',
  'Marlin Striped',
  ARRAY['Markaire'],
  'Istiophoridae',
  'Billfishes',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Decreasing, Near Threatened',
  'Unknown',
  '{"other_names": "Markaire", "meristics": "Dorsal spines: 0; Dorsal soft rays: 42-48; Anal spines: 0; Anal soft rays: 18-24.", "appearance": "Striped Marlin are large, oceanic fish with long, round bills, small teeth, and a tall dorsal fin. The most distinguishing characteristic is its high, pointed first dorsal fin, which normally equals or exceeds the greatest body depth. Even in the largest specimens this fin is at least equal to 90 percent of the body depth. Like the dorsal fin, the anal and pectoral fins are pointed. They are also flat and movable and can easily be folded flush against the sides, even after death. The sides are very compressed. The lateral line is straight, single and clearly visible. The back is steely dark blue-black fading to bluish silver on the upper flanks and white below the lateral line. There are a number of iridescent blue spots on the fins and pale blue or lavender vertical stripes on the sides. These may or may not be prominent, but they are normally more prominent than those of other marlins, and they persist after death, which is not always true on other marlins. Striped Marlin are smaller than other marlin species.", "about": "Striped Marlin is widely distributed in tropical and temperate waters of the Indo-Pacific and strays are occasionally found in the south-eastern Atlantic. It is the most wide-ranging of the Indo-Pacific marlins found between 45\u00b0N and 35-40\u00b0S latitude.  In the WIO they are densely distributed in the Arabian Sea, around Sri Lanka and in the MOZ Channel, extending southwards to the WC during summer. The Striped Marlin is seasonally migratory, moving toward the equator during the cold season and away again during the warm season. Migratory rates and patterns are, however, highly variable and preliminary genetic study suggests spatial partitioning of genetic variation and possible spawning site fidelity. Adults are pelagic and oceanodromous species usually found above the thermocline although it has been found to depths of 289 m. It generally inhabits cooler water than either Black or Blue Marlin. Abundance increases further offshore; usually seen close to shore only where deep drop-offs occur. It is mostly solitary but forms small schools by size during the spawning season. Juveniles are epipelagic; distributions appear to coincide with larval distributions in equatorial waters of both the Indian and Pacific Oceans. In the Pacific juveniles migrate eastward to feed areas on the west coast of America. Adults are diurnal feeders of small shoaling fish (pilchards, anchovies, mackerel, and flying fish), crustaceans and squids. The juvenile\u2019s diet is unknown. Striped Marlin are caught mainly with pelagic longlines (98%) with smaller numbers taken by drift gillnets and trolling lines. They are not considered a target species but comprise an important bycatch for industrial fisheries in the Indian Ocean. Modest but unknown quantities taken by sport fishing, but most are released. Only of moderate importance in sport catches off SA, with most fish caught between Nov-Dec. Second most important billfish species (after Sailfish) caught by the sports fishery in Kenyan waters. It is well known for its fighting ability and has the reputation of spending more time in the air than in the water after it is hooked. In addition to long runs and tail walks, it will \u201cgreyhound\u201d across the surface, making up to a dozen or more long, graceful leaps. It can be caught fairly close to shore and lacking the size and weight of the Blue Marlin or the Black Marlin, it is more acrobatically inclined. Fishing methods include trolling whole fish, strip baits, or lures, also live bait fishing. Catch and release should continue to be encouraged in the recreational line fishery and greater use of circle hooks should be promoted to enhance post release survival.", "biology": "Their reproductive style is heterosexual with no sexual dimorphism, but females tend to grow larger than males. Their breeding/spawning season occurs from early to mid-summer in the Indian Ocean. In the Pacific, major spawning areas are located in the NW and SW Pacific. Sexual maturity takes place at ~2-3 years at 140-170 cm EFL (Eye Fork Length). The maximum recorded age is 9 years, however, with limited data available due to difficulties associated with ageing this species. The maximum recorded weight is 224.1 kg (New Zeeland, IGFA). SA angling record is 116.35 kg; (WC, SADSAA). The maximum recorded length is 320-350 cm TL. It has also been reported to weigh 440 kg at 420 cm TL.", "citation": "RP van der Elst, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, IGFA"}',
  '[{"measure": "LBFL", "reference": "FishBase", "coefficient": 0.01517, "exponent": 2.88, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Kajikia albida',
  'White Marlin',
  'Wit Marlyn',
  'Marlin White',
  NULL,
  'Istiophoridae',
  'Billfishes',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Decreasing, Vunerable',
  '(ICCAT: Overfished but no overfishing)',
  '{"other_names": "", "meristics": "Dorsal spines: 0; Dorsal soft rays: 43-52; Anal spines: 0; Anal soft rays: 17-23.", "appearance": "White Marlin are mid-sized billfish with a body structure designed for fast swimming with a long, streamlined appearance with a bill that is round in cross section, and distinctly forked blackish brown caudal fin. They are blue-black above, silvery white and splattered with brown on the sides, silvery white below; sometimes with more than 15 rows of obscure whitish stripes.  One of the most noticeable features of white marlin is the dorsal fin, which extends along the majority of the dorsal portion of its body. 1st dorsal fin is dark blue with black dots; 2nd dorsal fin is also dark blue. White Marlin have a set of similar pectoral fins which are blackish brown, with tinges of silvery white in some specimens and pelvic fins that are blue-black with a black fin membrane. These two sets of fins are rounded and wide at the tip. They also have two anal fins which can be used for identification between species. The lateral line on this species is prominent, with a hump above the pectoral fin and extends the length of the fish toward the caudal fin. White Marlin have small but sharp teeth that are used when eating fish and cephalopods.", "about": "This is a pelagic, oceanodromous species that usually occurs above the thermocline. Its distribution varies seasonally, reaching higher latitudes in both the north and south hemispheres only during the respective warm seasons. It is usually found in pelagic habitats in waters over 50 m depth with surface temperatures over 22\u00b0C.", "biology": "Each female can lay 190,000 to 586,000 eggs. Growth of white marlin is very rapid, with males reaching sexual maturity at only 153.2 cm LJFL and females becoming sexually mature at 189.9 cm LJFL. The lifespan of white marlin has been identified as 15 or more years. It migrates into subtropical waters to spawn, with peak spawning occurring in early summer. The spawning areas are found in deep and blue oceanic waters, generally at high temperatures ranging from 20-29\u00b0C except in the south Atlantic gyrals, and high surface salinities. Max length: 300 cm TL; Common length: 210 cm TL; Max published weight: 82.5 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Chanos chanos',
  'Milkfish',
  'Melkvis',
  'Milkfish',
  NULL,
  'Chanidae',
  'Milkfish',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 2; Dorsal soft rays: 13-17; Anal spines: 2; Anal soft rays: 8-10.", "appearance": "This species is characterized by the following: body elongate and somewhat compressed; mouth small and toothless; single dorsal fin about mid-level of the body; pectoral fins falcate; caudal fin large and deeply forked; no scutes on belly; branchiostegal rays 4. Colour of the body olive green dorsally; flanks silvery; unpaired fins with dark margins.", "about": "Indo-Pacific: along continental shelves and around islands, where temperatures are greater than 20\u00b0C from the Red Sea and South Africa to Hawaii and the Marquesas, north to Japan, south to Victoria, Australia. Eastern Pacific: San Pedro, California to the Galapagos. Adults are found in offshore marine waters and shallow coastal embayments, but also frequently enter estuaries and occasionally penetrate freshwater streams. They occur in small to large schools near the coasts or around islands where reefs are well developed. Larvae eat zooplankton; juveniles and adults eat cyanobacteria, soft algae, small benthic invertebrates, and even pelagic fish eggs and larvae. Milkfish aquaculture first occurred around 1800 years ago in the Philippines and spread to Indonesia, Taiwan, and into the Pacific. Larvae are collected from rivers and are grown in culture ponds into juveniles which are marketed fresh, smoked, canned or frozen. Brood stocks can be raised and spawned in captivity to produce larvae in the hatchery. This species can thrive and grow in water as hot as 32\u00b0C.", "biology": "They tend to school around coasts and islands with coral reefs. The young fry live at sea for two to three weeks and then migrate during the juvenile stage to mangrove swamps, estuaries, and sometimes freshwater lakes, and return to sea to mature sexually and reproduce. Females spawn at night up to 5 million eggs in saline shallow waters.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.012, "exponent": 3.01, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Scyris indica',
  'Indian Mirrorfish',
  'Indiese Spielvis',
  'Mirrorfish Indian',
  ARRAY['Indian Threadfish'],
  'Carangidae',
  'Kingfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Indian Threadfish", "meristics": "Dorsal spines: 7; Dorsal soft rays: 18-20; Anal spines: 3; Anal soft rays: 15-20.", "appearance": "The body of adults is a silvery blueish green colour above, being darkest on the head and silver below. The upper operculum has a small diffuse dark spot. The long filamentous soft dorsal and anal fins as well as the pelvic fin are dark blue to black colour, while the others are pale green to glassy in appearance. Juveniles have 5 to 7 broad dark vertical cross bands through their body.", "about": "Indo-Pacific: Red Sea and East Africa to French Polynesia, north to southern Japan, south to the Arafura Sea and Australia. The depth range for this species is 20-100 m. This species occurs in large shoals (30 to 50 individuals) and is wide ranging in coastal waters. Juveniles of this species are solitary and reported to enter estuaries and may mimic jellyfish. This predatory species feeds on a variety of fish species, small squids, and crustaceans. A popular game fish, adults are mainly taken by hook and line, while juveniles are often taken with beach seines.", "biology": "Max length: 165 cm TL; Common length: 100.0 cm TL; Max published weight: 25.0 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.081, "exponent": 2.57, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Alectis ciliaris',
  'Threadfin Mirrorfish',
  'Draadvin Spielvis',
  'Mirrorfish Threadfin',
  ARRAY['African Pompano'],
  'Carangidae',
  'Kingfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "African Pompano", "meristics": "Dorsal spines: 7-8; Dorsal soft rays: 18-22; Anal spines: 3; Anal soft rays: 15-20.", "appearance": "It is a deep and laterally compressed fish, with the deepest point of the body located between the origin of the dorsal and anal fins and having the head and tail tapering either side. A major distinguishing feature of the adult being its more curved head compared to Indian Mirrorfish''s more angular head profile. The skin of the fish appears scaleless, but has minute, embedded scales scattered on the body. The lateral line has a strong and moderately long arch dorsally, with its posterior section having 12-30 scutes. The body is a silvery-metallic blue to blueish green colour above, being darkest on the head and upper shoulders while the underside is silverier. The juveniles have five chevron-shaped dark bars on their bodies, with a black blotch at the base of the third to sixth soft dorsal fin rays. The base of the filaments are dark blue to black, with all other fins pale to glassy in appearance.", "about": "The Threadfin Mirrorfish is distributed worldwide in tropical seas including Algoa Bay, South Africa in a temperature range of 18-27\u00b0C, although is more often found in coastal waters.  It is a schooling predatory fish which takes predominantly a variety of slow-swimming or sedentary crustaceans, and occasionally small crabs and fishes. Adults are usually solitary and frequent shallow coastal waters; juveniles are pelagic and drift. The fighting prowess and good table fare attract anglers to the species in many parts of the world,", "biology": "Little is known of their reproductive habits and maturation lengths. Max length: 150 cm TL male/unsexed; Common length: 100.0 cm FL; Max published weight: 22.9 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "FL", "reference": "MAGN\u00faSSON, J. et al.", "formula_type": "log-linear", "coefficient": -10.09704883, "exponent": 2.849995135}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Monodactylus falciformis',
  'Cape Moony',
  'Kaapse Maanskynvis',
  'Moony Cape',
  ARRAY['Moony','Kaapse Maanvis'],
  'Monodactylidae',
  'Moonyfishes, Fingerfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Moony, Kaapse Maanvis", "meristics": "Dorsal spines: 8; Dorsal soft rays: 25-30; Anal spines: 3; Anal soft rays: 25-29.", "appearance": "Adults are silvery with dusky dorsal and anal lobes, juveniles are dusky with dark vertical bars.", "about": "Cape Moony occur in the Western Indian Ocean: Red Sea to False Bay, South Africa, Madagascar (where it frequently enters tidally influenced rivers and streams), Seychelles, R\u00e9union and Mauritius and Pakistan. They inhabit coastal waters including estuaries and lagoons but seldom enter freshwater.", "biology": "Length at maturity: 14.0 cm. Max length: 31.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0309, "exponent": 2.99, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Monodactylus argenteus',
  'Natal Moony',
  'Natalse Maanskynvis',
  'Moony Natal',
  ARRAY['Round Moony','Silver Moony'],
  'Monodactylidae',
  'Moonyfishes, Fingerfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Round Moony, Silver Moony", "meristics": "Dorsal spines: 7-8; Dorsal soft rays: 27-31; Anal spines: 3; Anal soft rays: 27-32.", "appearance": "Adults are bright silver with yellow and dusky dorsal fin tips. Small juveniles are more colourful with yellow over most of the dorsal fin and two vertical black bands over the head. Fins yellow except pectoral translucent. Eyes are large, and the mouth is small.", "about": "It is found in the waters of the Red Sea and the Persian Gulf and the Indo-Pacific, from East Africa, from the Red Sea to South Africa including the Mozambique Channel, Madagascar and Mascarenes east to Micronesia, Mariana Islands and Samoa and Australia. It is almost exclusively found in shallow bay areas and estuaries and lower reaches of freshwater streams and almost certainly spawn there. Its ability to survive in a wide range of salinities makes it a model organism in the study of salinity tolerance. It can remain solitary or form schools. It is a detritivore and planktivore. It is a commercially sought after fish, for sale and consumption as well as for the ornamental aquarium trade.", "biology": "Length at maturity: 13.0 cm. Max length: 17.0 cm TL. Common length: 12.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.03034, "exponent": 2.964, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Crenimugil buchanani',
  'Bluetail Mullet',
  'Bloustert Harder',
  'Mullet Bluetail',
  NULL,
  'Mugilidae',
  'Mullets',
  NULL,
  NULL,
  50,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 4-5; Dorsal soft rays: 8; Anal spines: 3; Anal soft rays: 9.", "appearance": "This species is greenish dorsally; flanks and abdomen are silvery; small gold patch on upper operculum and iris; caudal fin bright blue and pectoral fins yellow with dark blue spot dorsally at origin.", "about": "Indo-Pacific: South Africa through Philippines, parts of Indonesia, Micronesia and Melanesia; north to the Mariana Islands and southern Japan. Depth range 0-10 m. They inhabit coastal waters, including estuaries and rivers. Young fish frequent estuaries and also ascend rivers and coastal creeks. Feed on algae, diatoms, detritus, and crustaceans. Oviparous, eggs are pelagic and non-adhesive.", "biology": "Length at maturity: 36 cm. Max length: 100.0 cm TL; Common length: 35.0 cm SL.", "citation": "FishBase"}',
  '[{"measure": "FL", "reference": "KULBICKI, M. et al.", "formula_type": "log-linear", "coefficient": -11.50212002, "exponent": 3.104231119}]'
);

