);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Mugil cephalus',
  'Flathead Mullet',
  'Platkop Harder',
  'Mullet Flathead',
  ARRAY['Black Mullet','Grey Mullet','Flathead Grey Mullet','Springer Mullet'],
  'Mugilidae',
  'Mullets',
  NULL,
  NULL,
  50,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Black Mullet, Grey Mullet, Flathead Grey Mullet", "meristics": "Dorsal spines: 5; Dorsal soft rays: 7-9; Anal spines: 3; Anal soft rays: 8-9.", "appearance": "The back of the fish is olive-green; sides are silvery and shade to white towards the belly.The fish may have six to seven distinctive lateral horizontal stripes. Lips are thin. It has no lateral line. Pelvic fins, anal fin, and lower lobe of caudal fin is yellowish in specimens from the tropical Atlantic coast of Africa.", "about": "Flathead Mullet are cosmopolitan and found globally in coastal waters between 42\u00b0N and 42\u00b0S latitudes and are the most widespread species among the family Mugilidae, which comprises a total of 20 genera and 70 valid species.  In SA they occur around the entire coastline. They are migratory known to migrate in large shoals down to the mouths of estuaries for spawning. Adults are also known to undertake extensive movements at sea in other parts of the world. Adults inhabit estuaries and coastal marine environment while juveniles are attracted to and are largely dependent on estuaries as nursery areas. Due to a reduction in freshwater flow and a 60% loss of floods, climate change, increased hydropower demands and freshwater abstraction in West Coast estuaries, juveniles of obligate estuary-dependent fish such as Flathead Mullet have declined in abundance to <10% of pristine level and are likely to decline to <5% under future flow projections. Adult\u2019s diet consists of benthic microalgae, detritus and small invertebrates while larvae feed on zooplankton with the switch to a juvenile/adult diet occurring at about 20 mm SL. In SA this species is mainly caught in illegal estuarine gillnet fisheries such as in the northern parts of Lake St Lucia. They are also caught in the artisanal trap fishery in Kosi Bay and the beach seine-net fishery off Durban. Seldom caught on line but is often caught using cast nets and used for bait.", "biology": "Although this species is gonochoristic (separate sexes) it is also capable of exhibiting non-functional hermaphroditic characteristics. Breeding/spawning season: Limited data, but appears to have an extended spawning season between autumn and spring (May-Nov) in SA waters. Breeding/spawning locality:  There is evidence that spawning along the east coast of SA takes place in the marine environment, often in the vicinity of estuary mouths. Age at maturity: Combined sexes: 3 years. Length at maturity: Males: 44 cm TL; Females: 48 cm TL. Maximum recorded age: 6 years, Kowie Estuary, however, known to reach ages of up to 16 years elsewhere. Maximum recorded weight: 8 kg. Maximum recorded length: 72 cm TL.", "citation": "AK Whitfield, SJ Lamberth In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, Wikipedia, DEFF 2020 - Status of the South African marine fishery resources, FishBase"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0138, "exponent": 2.98, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
