  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Pseudomyxus capensis',
  'Freshwater Mullet',
  'Varswater Harder',
  'Mullet Freshwater',
  ARRAY['River Mullet','Freshwater Springer'],
  'Mugilidae',
  'Mullets',
  NULL,
  NULL,
  50,
  NULL,
  NULL,
  'Not Evaluated',
  'Over exploited,Vulnerable',
  '{"other_names": "River Mullet, Freshwater Springer", "meristics": "Dorsal spinesotal: 5; Dorsal soft rays: 8; Anal spines: 3; Anal soft rays: 9.", "about": "It is only known from southern Africa in southeastern and east coastal estuaries and rivers from the Bre\u00eb River to Kosi Bay. Freshwater Mullet, originally limited to the Southeast and East Coast, have expanded past Cape Agulhas, are abundant in Table Bay estuaries and now also occur in the Orange River estuary on the West Coast. Adults occur in freshwater to estuarine conditions. Females remain in fresh water for 7 years, males for 4 years. They return to estuaries before maturing sexually prior to spawning. They feed on benthic algae and small invertebrates over muddy bottoms. A decline in population has been observed from obstruction to free passage into rivers by dams and weirs.", "biology": "Adults breed at sea; juveniles move into estuaries and enter rivers in late winter or early spring. They are oviparous, eggs are pelagic and non-adhesive. Length at maturity: 23 cm. Max length: 45.0 cm FL.", "citation": "FishBase, DEFF 2020 - Status of the South African marine fishery resources"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Chelon dumerili',
  'Groovy Mullet',
  'Keep Harder',
  'Mullet Groovy',
  ARRAY['Grooved Mullet'],
  'Mugilidae',
  'Mullets',
  NULL,
  NULL,
  50,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  NULL,
  '{"other_names": "Grooved Mullet", "meristics": "Dorsal spines: 5; Dorsal soft rays: 8-9; Anal spines: 3; Anal soft rays: 8-9.", "appearance": "Anal fin and lower lobe of caudal fin are whitish or grayish. Scales are numerous and not large.", "about": "Groovy Mullet is the only species of mullet whose distribution extends from the tropical Atlantic Ocean, around the Cape, to the Indian Ocean coast of Africa. In the eastern Atlantic that inhabits coastal estuarine, brackish, or marine waters. It has occasionally been reported from freshwater and supersaline environments. Adults filter out organic detritus, blue-green algae, diatoms, gastropods, and foraminifera from coarse sand. They are typically hardy and capable of rapid growth. Tropical/warm-temperate Groovy Mullet from the East Coast have increased from less than 10% to more than 30% of total mullet abundance in Cape South Coast estuaries over the past 25 years.", "biology": "Length at maturity: 17 cm. Max length: 40.0 cm SL; Common length: 18.0 cm SL.", "citation": "FishBase, Wikipedia, DEFF 2020 - Status of the South African marine fishery resources"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
