) VALUES (
  'Planiliza macrolepis',
  'Largescale Mullet',
  'Grootskub Harder',
  'Mullet Largescale',
  NULL,
  'Mugilidae',
  'Mullets',
  NULL,
  NULL,
  50,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines): 4 - 5; Dorsal soft rays: 8-9; Anal spines: 3; Anal soft rays: 8 - 10.", "appearance": "The Largescale Mullet is greenish grey above and silvery grey beneath. The fins are bluish grey with darker edges with dusky margins and pectoral fins may appear yellowish at base and perhaps a dark spot. The top of the back is nearly straight when viewed from the side while the underside is rounded.", "about": "This widespread species is found throughout the Indo-Pacific, from east Africa and the Red Sea, east to French Polynesia, north to Japan and the Northern Marianas Islands. This species occurs in shallow coastal waters and from brackish water to freshwater regions of rivers. The species forms schools in larger aggregations during spawning. It feeds on small algae, diatoms, benthic polychaetes, crustaceans, molluscs, organic matter and detritus. They feed on small algae, diatoms, forams, benthic polychaetes, crustaceans, mollusks, organic matter and detritus, fry feed on copepods and floating algae.", "biology": "They form larger aggregations during spawning, which takes place at sea. They are oviparous, eggs are pelagic and non-adhesive. Length at maturity: 23 cm. Max length: 60.0 cm SL; Common length: 26.0 cm SL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
