);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Tylosurus crocodilus',
  'Crocodile Needlefish',
  'Krokodil Naaldvis',
  'Needlefish Crocodile',
  ARRAY['Crocodile Garfish','Hound Needlefish'],
  'Belonidae',
  'Needlefishes',
  NULL,
  NULL,
  NULL,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Crocodile Garfish, Hound Needlefish", "meristics": "Dorsal spines: 0; Dorsal soft rays: 21-24; Anal spines: 0; Anal soft rays: 19-22.", "appearance": "The jaws are extremely long, forming a stout beak armed with very sharp teeth; no gill rakers absent; caudal fin deeply emarginate, the lower lobe much longer than the upper one and the caudal peduncle with a distinct, black lateral keel; body colour dark bluish green above, silvery below; juveniles (to 20 cm body length) with elevated black lobe in posterior part of dorsal fin which is lost with growth; scales and bones green.", "about": "This epipelagic species is common in coastal waters and occurs offshore occasionally solitary or in small groups. Indo-West Pacific: Red Sea and South Africa and Persian Gulf to French Polynesia north to Japan, south to New South Wales, Australia. Western Atlantic: New Jersey, USA to Brazil. Eastern Atlantic: Fernando Poo, Cameroon, Liberia, and Ascension Island; from Senegal and Guinea; and Cape Verde. It has been recorded from the Mediterranean Sea. It is often observed at the surface near jetties, piers, and reefs. It feeds mainly on small fish. The green-coloured flesh is of good quality. They can puncture wounds with their sharp snouts when jumping out of the water.", "biology": "Length at maturity: 51.7 cm, range 50-55 cm. Max length: 150 cmTL; Common length: 90.0 cm SL.  Max published weight: 6.4 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0008, "exponent": 3.2, "formula_type": "power", "result_unit": "kg"}]'
);

