INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Pomacanthus rhomboides',
  'Old Woman',
  'Ou Vrou',
  'Old Woman',
  ARRAY['Old Woman Angelfish'],
  'Pomacanthidae',
  'Damselfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Old Woman Angelfish", "meristics": "Dorsal spines: 11 - 13; Dorsal soft rays: 22-25; Anal spines: 3; Anal soft rays: 21-23.", "appearance": "The Old Woman angelfish, as an adult it is possibly the least colourful of the damselfishes. The adults are bronzeish brown on the anterior two thirds of their bodies with the posterior third being pale bluish grey. The juveniles resemble the juveniles of other species in the genus and are, therefore, more colourful than the adults. They are mainly black with thin, want blue and white lines.", "about": "This species occurs in the western Indian Ocean where it has been recorded along the East African coast from Delagoa Bay (Mozambique) to Knysna (South Africa). Animals have been reported at depths between 5-30 m. They are associated with rocky reefs and shoreline areas. Juveniles may be encountered in shallow tidal pools. Groups of adults (20 to 30 animals can occasionally be seen swimming above reefs in mid-water, sometimes coming close to the water\u2019s surface when feeding on plankton.", "biology": "Max length: 46.0 cm TL; Max published weight: 3.0 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -10.85877991, "exponent": 2.970304012}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Pterogymnus laniarius',
  'Panga',
  'Panga',
  'Panga',
  ARRAY['Panga Seabream','Pangha','Pangas','Dikbekkie'],
  'Sparidae',
  'Seabreams, Porgies',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  'Under exploited, >50% of pristine',
  '{"other_names": " Panga Seabream, Pangha, Pangas, Dikbekkie, Panga Seabream, Pangha, Pangas", "meristics": "Dorsal spines: 12; Dorsal soft rays: 10; Anal spines: 3; Anal soft rays: 8.", "appearance": "There is a lack of scales on the soft rayed parts of the dorsal and anal fin. The dorsal profile of the head is smoothly convex, and the eye is large with the rear edge of the preorbital bone having a wave-like shape and is not covered by the scales of the cheek. The overall colour of the body is red or pink, lighter in colour on the lower body, with 5 or 6 indistinct bluish, horizontal lines made up of small blue spots on its sides underneath the lateral line. It has a prominent mouth with fleshy lips and large canines. These canines help it to prey on small crustaceans, squid and other small fish. Small hairs on its lips allow it to feed over soft sediments without swallowing any.", "about": "Panga are endemic occurring from Yzerfontein (WC) to Transkei in the EC. It is a relatively small sparid species and is considered the most abundant sparid species on the Agulhas Bank. Juveniles and subadults are found in a nursery area on the Central Agulhas Bank.  It is over these deep, low-profile reefs where they are considered the most abundant sparid species. After maturation adults disperse throughout their range. Adults are found predominantly on deep, low and high-profile reefs and to a lesser extent, mud and sand down to 120 m and on flat reefs (>50 m depth). Panga comprises over 77% of the catch in traps. The juvenile\u2019s habitat is similar to adults. Adults feed predominantly on crabs with polychaetes (bristle worms), ophiuroids (brittlestars) and fishes are also present in the diet but in smaller quantities.  Juveniles feed predominantly on mysids (small, shrimp-like crustaceans). Prior to 1992 Panga was targeted by foreign trawlers operating in SA waters. During the period 1992-95 it comprised 63% of the SA demersal inshore trawl fishery and 11% of the demersal deepsea trawl fishery in the SE Cape and S Cape. Also important to the offshore commercial linefishery in the SE Cape where it made up an average of 16.5% of total landings between 1985 and 2007. Panga are of little importance to the recreational ski-boat fishery and are sometimes used as bait in SE Cape. There has been a steady decline in the contribution by Panga to the commercial linefishery catch in the SE Cape from around 26% to around 16% a decade later and to around 5% in 2007.", "biology": "Reproductive style: Late gonochorist (separate sexes), (rudimentary hermaphrodite), (the female reproductive organs come to maturity before the male). Breeding/spawning season: Throughout the year with a peak from Sep-May. Breeding/spawning locality: Primarily on the Agulhas Bank. Age at maturity: Combined sexes: 5.2 years; SE Cape. Combined sexes: 4.3 years; SE Cape. Length at maturity: Combined sexes: 286 mm TL; SE Cape. Combined sexes: 204 mm FL; SE Cape. Maximum recorded age: 16 years; SE Cape. Maximum recorded weight: 1.5 kg; SE Cape. Maximum recorded length: 405 mm FL; SE Cape.", "citation": "AJ Booth, T Hecht In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, Two Oceans Aquarium, FishBase, Management of the panga Pterogymnus laniarius (Pisces: Sparidae) on the Agulhas Bank, South Africa using per-recruit models; AJ Booth, CD Buxton, Fisheries Research 32, 1997, Wikipedia. For more information: http://speciesstatus.sanbi.org/assessment/last-assessment/1037/"}',
  '[{"measure": "FL", "reference": "Visboekie", "coefficient": 2e-05, "exponent": 3.031, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Chlorurus cyanescens',
  'Blue Humphead Parrotfish',
  'Blou Ramkop Papegaaivis',
  'Parrotfish Blue Humphead',
  NULL,
  'Scaridae',
  'Parrotfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "None available", "appearance": "No discription available", "about": "This species is relatively rare and is found in East Africa and the southwestern tropical Indian Ocean known only from KZN (South Africa), Zanzibar (Tanzania), Madagascar, and Mauritius, but probably more widespread. This is a large species with relatively restricted distribution. It is found solitary or in small groups around deep rocky reefs up to a depth of at least 30 m. There are no reliable records of fishery or information on its biology. It is captured by subsistence fisheries but is unlikely to be heavily fished.", "biology": "Max length: 50.0 cm TL", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Scarus ghobban',
  'Bluebarred Parrotfish',
  'Bloubalk Papegaaivis',
  'Parrotfish Bluebarred',
  NULL,
  'Scaridae',
  'Parrotfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 9; Dorsal soft rays: 10; Anal spines: 3; Anal soft rays: 9.", "appearance": "Caudal fin rounded in small female, with prolonged lobes in large adult. Colour of male dark reddish brown anteriorly with a bright green dot at top end of line from mouth through eye to top of opercular opening; female red on head, belly and fins, side with wavy black and white stripes, and dark green bands around the mouth and eye. This species is blueish green to green in colour but its appearance is variable. It may have a central stripe on the dorsal and anal fins that is pink in colour. The underside of the body may be pinkish or yellowish. There may be blue markings around the area of the pectoral fin.", "about": "Indo-Pacific: Persian Gulf; Red Sea and Algoa Bay, South Africa to Rapa and Ducie islands, north to southern Japan, south to Perth, New South Wales. Likely found at Seychelles. Eastern Pacific: Gulf of California to Ecuador and eastern Mediterranean. It occurs in places with sandy bottoms and in areas with seagrass. Males are common in atolls where they live mainly around the inner and outer edges of barrier reefs at depths of about 14 m; females prefer deeper habitat. Small juveniles in groups are found inshore on algae reef habitat and sometimes enter silty, murky environments.  It tends to congregate in small schools. They can rotate their mandible bone at a high velocity scraping algae from rocks and corals; this allows them to function as a scraper and facilitate hydrological transport of fine sediments. As other parrotfish, it is dependent on coral reefs to some degree and is likely to suffer from the continued degradation of same.", "biology": "They are protogynous hermaphrodites and oviparous displaying distinct pairing during breeding. Sex change occurs at 29.0 cm TL and 7 years of age. Max length: 75.0 cm TL; Common length: 30.0 cm TL. Max reported age: 13 years.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.0165, "exponent": 3.041, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Chlorurus sordidus',
  'Bullethead Parrotfish',
  'Koelkop Papegaaivis',
  'Parrotfish Bullethead',
  ARRAY['Split Paint Parrotfish','Daisy Parrotfish'],
  'Scaridae',
  'Parrotfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Split Paint Parrotfish, Daisy Parrotfish", "meristics": "Dorsal spines: 9; Dorsal soft rays: 10; Anal spines: 3; Anal soft rays: 9.", "appearance": "Initial phase is very variable in colouration. Small individuals may be uniformly dark brown to light gray with or without the dark-centered light area on the caudal peduncle; large individuals may have a series of irregular rows of small light spots posteriorly or have the dark-centered light area on the caudal peduncle. The terminal phase is also variable with or without a large tan area on the side or on the caudal peduncle. It has a rounded snout.", "about": "It is one of the most widespread parrotfishes, but highly variable and some geographical forms that are probably subspecific). Inhabit both coral rich and open pavement areas of shallow reef flats and lagoon and seaward reefs, as well as drop-offs, behaving differently in various areas. It is benthopelagic. Juveniles found in coral rubble areas of reef flats and lagoons. Juveniles and individuals in the initial phase form large groups that migrate great distances between feeding and sleeping grounds. They feed on benthic algae. Before going to sleep, it secretes mucus which surrounds the fish in a complete cocoon presumably to protect itself from parasites.", "biology": "It is a protogynous hermaphrodite and oviparous with distinct pairing during breeding. Length at maturity: 15 cm. Size at sex change: 35.1 - 47.2 cm TL. Max length: 40.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Calotomus carolinus',
