);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Arothron stellatus',
  'Star Puffer',
  'Ster Blaasop',
  'Puffer Star',
  ARRAY['Stellar Puffer'],
  'Tetraodontidae',
  'Puffers, Blaasops, Blowfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Stellar Puffer", "meristics": "Dorsal spines: 0; Dorsal soft rays: 10-12; Anal spines: 0; Anal soft rays: 10-11.", "appearance": "Its body is oval shaped, spherical and relatively elongated. The skin is not covered with scales but is prickly. The fish has no pelvic fin and no lateral line. The dorsal fin and the anal fin are small, symmetric, and located at the rear end of the body. The head is large with a short snout that has two pairs of nostrils, and the mouth is terminal with four strong teeth. The background colouration goes from white to grey, and the body is harmoniously dotted with black spots. The ventral area is usually clearer. The size of the spots is inversely proportional to the size of the fish; thus, a young individual will have large spots and adults of maximal size will have small spots. Juveniles have a yellowish body bac kground colouration with dark stripes on belly, becoming spots with growth, adults with or without spots on fin. The young adults still have stripes on the ventral area that will turn to spots later, and also some recollection of yellow on the body.", "about": "This species is found in tropical and subtropical waters from the Indian Ocean and Red Sea as far as Polynesia, southern Japan, the western, northern and eastern coasts of Australia and Lord Howe Island. It is a relatively uncommon species and lives close to external reef slopes and sheltered lagoons with clear water, but mainly in close proximity to sandy areas, at depths from the surface down to about 58 m. It feeds on benthic invertebrates, sponges, algae, the polyps of corals such as Acropora, crustaceans and mollusks. This pufferfish is diurnal. It is mainly solitary and defends territory. It feeds on sea urchins, sponges, crabs and coral. Arothron stellatus contains a highly toxic poison, Tetrodotoxin, in its ovaries and to a lesser extent its skin and liver, which protects it from voracious predators. It becomes toxic as it eats bacteria that contains toxins. To ward off potential enemies, they can inflate their bodies by swallowing air or water. A. stellatus may be experiencing population decline due to habitat loss in parts of its range.", "biology": "It is oviparous (producing young by means of eggs which are hatched after they have been laid by the parent). It is considered the giant among puffers reaching a total length well in excess of a meter. Max length: 120 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -9.29916161, "exponent": 2.67238793}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Triodon macropterus',
  'Threetooth Puffer',
  'Drietand Blaasop',
  'Puffer Threetooth',
  ARRAY['Threetooth Blaasop'],
  'Triodontidae',
  'Three-toothed puffer',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "Threetooth Blaasop", "meristics": "Dorsal spines: 0; Dorsal soft rays: 10-12; Anal spines: 0; Anal soft rays: 9-10.", "appearance": "It is recognizable for its large belly flap which has the ability to blend into the body when fully retracted. Its body is yellowish-brown with a white belly flap except for a yellow area around a black blotch as large as or larger than its body which it inflates with seawater when threatened.  The flap is inflated by rotating the shaft-like pelvis downwards, exposing a black eyespot contoured with yellow. This makes the animal appear much larger to predators, and less likely to be eaten. When danger is not present, the flap is retracted seamlessly into the body, and the eyespot is not visible. The head of an adult Threetooth Puffer makes up approximately 30.6% of the length of its body, and the eyes make up about 7.5% of its body length. The upper jaw is composed of two dental plates while the teeth on the lower jaw protrude from a single dental plate, resulting in a beak. The Threetooth Puffer has ribs, a beak, and no pelvic fins which are all characteristics of tetraodoes.", "about": "The Threetooth Puffer is the only living species in the genus Triodon and family Triodontidae and is native to the Indo-West Pacific: East Africa to the Philippines, north to Japan, and south to Australia and New Caledonia at depths from 30-300 m. It has also recently been reported from Tonga.  Its habitat is pelagic, consisting of continental shelves, slopes, seamounts, and knolls. The Threetooth Puffer is harmless to humans unless eaten, at which point the species is considered poisonous.", "biology": "The Threetooth Puffer reaches a maximum length of 54 cm TL.", "citation": "FishBase, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Arothron hispidus',
  'Whitespotted Puffer',
  'Witspikkel Blaasop',
  'Puffer Whitespotted',
  NULL,
  'Tetraodontidae',
  'Puffers, Blaasops, Blowfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Whitespotted Puffer", "meristics": "Dorsal spines: 0; Dorsal soft rays: 10-11; Anal spines: 0; Anal soft rays: 10-11.", "appearance": "The body is generally light grey, or greyish or yellowish to greenish brown in colour, the back, sides and caudal fin profusely speckled with white spots that become concentric contrasting white and dark grey lines that radiate around the eyes and pectoral fins. The belly is marked with white bars with a single bent lateral line. The \"shoulder\" (around the pectoral fins) is dark. It also has concentric contrasting white and dark grey lines that radiate around the eyes and pectoral fins.", "about": "The Whitespotted Puffer is widely distributed in the Indian and Pacific Oceans. It is associated with a variety of habitats including coral reefs, rocky reefs, coastal bays and estuaries, at depths ranging from 3 to at least 50 m. It is usually solitary and territorial. It feeds on a variety of species including fleshy, calcareous, or coralline algae, zoanthid anemones, crabs, tube worms and echinodermsalgae, detritus, molluscs, tunicates, sponges, corals, although it is primarily carnivorous. It is known to prey on the Crown-of-Thorns Starfish and may provide ecosystem services by regulating the population of this destructive starfish. The skin, testes, liver, and ovaries are toxic.", "biology": "It is oviparous (producing young by means of eggs which are hatched after they have been laid by the parent). Max length: 50.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "LETOURNEUR, Y. et al.", "formula_type": "log-linear", "coefficient": -9.772453721, "exponent": 2.800998837}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Gerres methueni',
  'Evenfin Pursemouth',
  'Gelykvin Plooibek',
  'Pursemouth Evenfin',
  ARRAY['Striped Silver Biddy'],
  'Gerreidae',
  'Pursemouths',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "Striped Silver Biddy", "meristics": "Dorsal spines: 9; Dorsal soft rays: 10-11; Anal spines: 3; Anal soft rays: 7.", "appearance": "The body is silvery in colour but olive brown above; brown stripes following scale rows above lateral line and the 4 rows below it; spinous dorsal fin with black margins; inner edge of tail fin dusky.", "about": "Distributed in the western Indian Ocean: southern Mozambique to Algoa Bay, South Africa; also reported from Madagascar. Adults inhabit coastal waters including estuaries. They form schools. They feed on invertebrates. Fry about 10 cm in length enter the estuaries and stay until they reach maturity.", "biology": "Length at maturity: 14.0 cm. Max length: 30.0 cm TL; Common length: 20.0 cm TL.", "citation": "FishBase"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.01778, "exponent": 3.04, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Gerres longirostris',
  'Smallscale Pursemouth',
  'Kleinskub Plooibek',
  'Pursemouth Smallscale',
  ARRAY['Strongspine Silver Biddy'],
  'Gerreidae',
  'Pursemouths',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Strongspine Silver Biddy", "meristics": "Dorsal spines: 9; Dorsal soft rays: 10; Anal spines: 3; Anal soft rays: 7.", "appearance": "Live specimens'' head and trunk sides olive-green above, becoming silvery below; snout region blackish dorsally; dorsal fin membrane with faint black blotches or small spots at each spine and ray, just above edge of scaly basal sheath. Caudal fin is dusky with broad darker margin posteriorly; ventral edge of lower caudal fin lobe white or bluish white. Generally, body with prominent dark stripes along scale rows above lateral line; 4-9 somewhat oblique dusky bars or columns of ovoid dark spots immediately below lateral line in specimens over 10 cm SL (dusky bars on less than 10 cm). Pectoral fins reach to or beyond level of anal-fin origin; caudal fin forked deeply.", "about": "Found in the Indo-Pacific: Red Sea and South Africa to the Marquesas Islands, north to the Ryukyu Islands, south to Australia and also reported from Persian Gulf. Adults often in clear coastal waters up to about 50 m depth; juveniles often in estuaries or lagoons influenced by fresh water; they prefer shallow waters over sandy bottoms, from coral reefs to brackish waters. Fry about 10 cm in length enter the estuaries and stay until they reach maturity. It occurs singly or in groups and feeds on small bottom invertebrates. In some areas, the species forms spawning aggregations for a few days around the new moon.", "biology": "Length at m Maturity: 20.6 cm. Max length: 44.5 cm TL; Common length: 20.0 cm TL.", "citation": "FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Scomberoides lysan',
  'Doublespotted Queenfish',
  'Dubbelkol Koninginvis',
  'Queenfish Doublespotted',
  NULL,
  'Carangidae',
  'Kingfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 7-8; Dorsal soft rays: 19-21; Anal spines: 3; Anal soft rays: 17-19.", "appearance": "They are primarily silver in colour, with dark colouration on the dorsal and caudal fins and a row of dark spots on either side of the lateral line. Scales needle-like and embedded in tough skin; breast scales sharply lanceolate (narrow and tapering to each end) and embedded in the middle of body below lateral line but lack the scutes of some other jacks.", "about": "The Doublespotted Queenfish occurs in Indo-Pacific from South Africa east to the Hawaiian Islands and Tonga, north to Japan and south to Australia. This species is a coastal water species that inhabits a variety of areas including shallow lagoons, seaward reefs and neritic waters over sandstone with coral, mud, and sand and over offshore areas to depths of 100 m. This species occurs singly or in small schools. The adult diet consists of fishes and small crustaceans and juveniles feed on the scales and epidermal tissues of other fishes using specialised rasping teeth. In estuaries of KZN, this species was only found in clear waters and appears to be more tolerant of turbid waters in the juvenile and fry phases. Adults are associated with reefs. They are primarily solitary. Juveniles feed on the scales of schooling fish. Like most jacks, adults prey on fish and crustaceans. This species is venomous with the venom found on the spines of the dorsal and anal fins. Doublespotted queenfish are pursued as game fish and are sometimes used as bait as well.", "biology": "Max length: 110 cm TL; Common length: 60.0 cm TL; Max published weight: 11.0 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.0204, "exponent": 2.79, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Scomberoides tol',
  'Needlescaled Queenfish',
  'Naaldskub Koninginvis',
  'Queenfish Needlescaled',
  NULL,
  'Carangidae',
  'Kingfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 7-8; Dorsal soft rays: 19-21; Anal spines: 3; Anal soft rays: 18-20.", "appearance": "The Needlescaled Queenfish is bluish green to bluish grey on the dorsal part of its body becoming silvery white on the ventral part. It has 5-8 vertically oval black spots along its flanks. The anterior 4-5 spots overlap the lateral line. The outer half of the lobe of the dorsal fin is black while the anterior lobe of the anal fin is white. The flank spots are faint or absent in juvenile fishes. The body of this fish is strongly compressed, oblong and elliptical in shape with the dorsal and ventral profiles are similarly convex. In the adults the upper mandible reaches the rear margin of the pupil. The soft rays of posterior dorsal and anal fins are made up of semi-attached finlets.", "about": "This species is a coastal species that is common throughout the Indo-West Pacific, from South Africa north to Japan and south to Australia and east to Tonga and Fiji. This species is a coastal species that is generally found in small schools. The adult diet consists mainly of fishes, while juveniles feed on the scales and epidermal tissues of other fishes using specialised rasping teeth. It is a target species for both commercial and recreational fisheries. Dorsal and anal fins may carry toxins.", "biology": "Length at maturity: 26.0 cm. Max length: 60.0 cm TL; Common length: 40.0 cm TL; Max published weight: 0.51 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0105, "exponent": 2.86, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Scomberoides commersonnianus',
  'Talang Queenfish',
  'Talang Koninginvis',
  'Queenfish Talang',
  ARRAY['Largemouth Queenfish'],
  'Carangidae',
  'Kingfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Largemouth Queenfish", "meristics": "Dorsal spines: 7 - 8; Dorsal soft rays: 19-21; Anal spines: 3; Anal soft rays: 16-19.", "appearance": "The Talang Queenfish has a single row of 5-6 large dark silvery spots or blotches running along the flanks over the lateral line. It does not have a dark tip on the dorsal fin lobe. The snout is rather blunt, and the large mouth has several rows of very sharp teeth. The anal fin and the dorsal fin are truncated with the posterior part of each fin reduced to spines. The caudal fin is strongly forked. The head and back is bluish grey while the ventral side of the body is silvery.", "about": "Talang Queenfish are widely distributed throughout the Indian and western Pacific Ocean and in SA are found northwards from Algoa Bay but nowhere are they very abundant. Little is known regarding their movement patterns, but the ORI Tagging Project suggests that they may display a degree of resident behaviour, possibly related to food availability. Adults are found in pelagic, tropical coastal regions and periodically enter estuaries. They are generally intolerant of low salinities but will enter turbid water and will swim in small groups and often frequent reefs and offshore islands. Juveniles are classified as a species which is only partially dependent on estuaries. Juveniles are however abundant in estuaries in north-east Australia. Small juveniles have not been recorded in SA estuaries, but sub-adults are fairly common in St Lucia, Durban Bay and Richards Bay. The adult diet includes small pelagic and bottom-dwelling fish, as well as mantis shrimps, swimming crabs and squid while the juveniles have a specialised dentition which enables them to feed on scales of other fish. Larger juveniles also eat mysids, juvenile penaeid prawns and small fish.", "biology": "Their breeding/spawning season is unknown locally but has a protracted spawning season between Sep-Mar (summer) in northern Australia. They are most abundant in KZN during the summer. Maturity for females occurs about 63.5 cm FL when they are 4-5 years old. The maximum recorded age is 15-16 years, the maximum recorded weight is 16 kg and the maximum recorded length is 120 cm TL.", "citation": "SW Dunlop, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.0248, "exponent": 2.792, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Siganus sutor',
  'Whitespotted Rabbitfish',
  'Witspikkel Konynvis',
  'Rabbitfish Whitespotted',
  ARRAY['Shoemaker Spinefoot'],
  'Siganidae',
  'Rabbitfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Shoemaker Spinefoot", "meristics": "Dorsal spines: 13-14; Dorsal soft rays: 10; Anal spines: 7; Anal soft rays: 9-10.", "appearance": "Colouration is highly variable and influenced by substrate''s colour and mood of the fish. During the daytime, the body of the Whitespotted Rabbitfish is usually a silver colour with white spots. Sometimes there would be blue spots within the white spots. During nighttime, the fish has a mottled green or grey colour. It has the ability to immediately change between these two-colour patterns. The colour patterns of the fish extend to the fins. The spines are slender, pungent and venomous. The cheeks have few scales, and the midline of thorax is without scales between pelvic ridges. The long flap of anterior nostril shortens with increasing age. Venom glands in the spines can cause great pain but are unlikely to be fatal to a healthy adult. If a person is envenomated by the spines, the treatment is to use hot water (as hot as one can take) for 60 to 90 minutes.", "about": "Whitespotted Rabbitfish are found in the Indiant Ocean: from East Africa, Seychelles, Comoro Islands, Madagascar, Reunion, Mauritius and Rodriquez Island; East Andaman Sea off Thailand and Myanmar; Bali, Indonesia. This is a broadly distributed species that is highly abundant in parts of its range. It inhabits seagrass beds and rocky/coral reefs typically at depths of 1-12 m. It tends to occur in schools. It is a short-lived (2.5 yrs), fast-growing, and herbivorous reef species.", "biology": "It is a gonochorist (separate sexes) and returns to the same offshore coral patch reef aggregation site to spawn on subsequent full moons, migrating at most 3.3 km from fishing grounds closer to shore. Max length: 45.0 cm SL; Common length: 30.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "FL", "reference": "SAMBOO, C.R. et al.", "formula_type": "log-linear", "coefficient": -9.730881691, "exponent": 2.756633997}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Elagatis bipinnulata',
  'Rainbow Runner',
  'Renboog Pylvis',
  'Rainbow Runner',
  ARRAY['Rainbow Yellowtail'],
  'Carangidae',
  'Kingfishes',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Rainbow Yellowtail", "meristics": "Dorsal spines: 7; Dorsal soft rays: 25-30; Anal spines: 2; Anal soft rays: 18-22.", "appearance": "The colour of the fish is possibly the easiest way to identify the rainbow runner. The upper body is a dark olive blue to green and fades to white underneath. Two narrow, light blue to bluish white stripes run longitudinally along the sides, with a broader olive to yellow stripe between them.", "about": "Rainbow Runner is circumtropically distributed. In the Indo-Pacific it is known along East Africa, the Red Sea, India, Thailand, Indonesia, the Philippines, Japan, Australia, New Zealand, and the Hawaiian Islands. In the eastern Pacific it is known from the southern Gulf of California, Mexico to Ecuador including the offshore islands. It is a pelagic and also highly migratory species, found mainly near the surface, over reefs and sometimes offshore. It may form large schools and prey items include fish and pelagic or planktonic crustaceans, including shrimp and crabs. The rainbow runner is rarely targeted as a commercial fish, but many of the species often turn up as bycatch from tuna and shark fisheries. In the recreational fishery they are taken while trolling for other species such as tuna and mackerel but are often targeted inshore by anglers using surface popper-style lures. The fish are caught on a wide variety of lures and baits, with deep-diving lures, surface lures, and even saltwater flies. Their flesh is said to be of fair to excellent standard, depending on personal preferences.", "biology": "The fish is oviparous, producing pelagic eggs and larvae. Length at maturity: 64.6 cm. Max length: 180 cm TL; Common length: 90.0 cm TL; Max published weight: 46.2 kg; Max reported age: 6 years.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.0135, "exponent": 2.92, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Torpedo fuscomaculata',
  'Blackspotted Electric Ray',
  'Swartspikkel Drilvis',
  'Ray Blackspotted Electric',
  ARRAY['Blackspotted Torpedo Ray'],
  'Torpedinidae',
  'Electric Rays',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Data Deficient',
  NULL,
  '{"other_names": "Blackspotted Torpedo Ray", "appearance": "It is a large ray with a small caudal fin and small papillae around the spiracles The colouration is generally dull grey to yellowish or reddish-brown above, with numerous closely packed dark brown spots, white below. However, there is enormous variation in the number, size, and disposition of the darker spots, and some specimens have a dark grayish- or blackish-brown dorsal colouration with no spots at all. Other specimens have gray flecks along with the darker spots, or an altogether gray colouration.", "about": "The Blackspotted Torpedo is a small poorly known demersal electric ray that inhabits estuaries, the intertidal zone, and sandy areas near deep rocky reefs in the Southeast Atlantic and Western Indian Ocean from South Africa to Tanzania, and off Madagascar, Mauritius, and the Seychelles down to 440 m. It is found in sandy areas near deep rocky reefs and feeds on fish and cuttlefish. There are anecdotal reports of this species penetrating inland up rivers and estuary systems that have recently increased in salinity in the Eastern Cape, South Africa. For example, this species has been positively identified in the Kariega estuary, about 2 km from the coast. The Blackspotted Torpedo is caught frequently as bycatch by inshore trawlers and inshore anglers. Although torpedo rays are typically discarded, post-release mortality is unknown and potentially high. Monitoring, abundance estimates, and basic biological data are needed for the black-spotted torpedo in order to assess its conservation status.", "biology": "It is ovoviviparous (aplacental viviparity), embryos feeding initially on yolk, then receiving additional nourishment from the mother by indirect absorption of uterine fluid enriched with mucus, fat or protein through specialised structures. The young are born in the summer. Max length: 64.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "DW", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -9.804895401, "exponent": 2.807568073}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Taeniura lymma',
  'Bluespotted Ribbontail Ray',
  'Bloukol Lintstertrog',
  'Ray Bluespotted Ribbontail',
  ARRAY['Ribbontail Stingray'],
  'Dasyatidae',
  'Whiptail stingrays',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Unknowwn, Near Threatened',
  NULL,
  '{"other_names": "Ribbontail Stingray", "appearance": "The pectoral fin disc of the Bluespotted Ribbontail Ray is oval in shape with a rounded to broadly angular snout. The large, protruding eyes are immediately followed by the broad spiracles. There are 15-24 tooth rows in either jaw, arranged into pavement-like plates, and two large papillae on the floor of the mouth. The thick, depressed tail measures about 1.5 times the disc length and bears one or two (usually two) serrated spines well behind the tail base; there is a deep fin fold on the ventral surface, reaching the tip of the tail, and a low midline ridge on the upper surface. The skin is generally smooth, save for perhaps a scattering of small thorns on the middle of the back. The dorsal colouration is striking, consisting of numerous circular, neon blue spots on a greyish brown to yellowish brown, olive-green or reddish brown or green background; the spots vary in size, becoming smaller and denser towards the disc margin. The tail has two stripes of the same blue running along each side as far as the spines. The eyes are bright yellow and the belly is white. Individuals found off southern Africa may lack the blue tail stripes.", "about": "The Bluespotted Lagoon Ray is a small (to 35 cm disc width) stingray with a wide distribution in the Indian and Western Pacific Oceans from South Africa to Papua New Guinea. It is demersal inshore on coral reefs from the surface to a depth of 50 m. It lives primarily around coral reefs, sheltering under ledges and in caves during the day. At night, small groups assemble and swim onto shallow sandy flats with the rising tide to feed on mollusks, worms, shrimps, and crabs. Unlike many other stingrays, this species seldom buries itself in sand. Its meat is widely utilized. While timid and innocuous towards humans, the bluespotted ribbontail ray can inflict an excruciating wound with its venomous tail spines.", "biology": "Like other stingrays, this species is aplacental viviparous: the embryos are initially sustained by yolk, which later in development is supplemented by histotroph (\"uterine milk\", containing mucus, fat, and proteins) produced by the mother.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "DW", "reference": "FishBase", "coefficient": 0.023, "exponent": 3.16, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Mobula mobula',
  'Devil Ray',
  'Duiwelrog',
  'Ray Devil',
  ARRAY['Giant Devil Ray','Reuse Duiwelrog','Spinetail Devil Ray','Devil Fish'],
  'Mobulidae',
  'Devil, Eagle and Manta rays',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Endangered',
  NULL,
  '{"other_names": "Giant Devil Ray, Reuse Duiwelrog, Spinetail Devil Ray, Devil Fish", "appearance": "It is considered to be the only Devilray with a tail spine and prickles.", "about": "Devil Rays inhabit offshore areas to the neritic zone, their range as deep as several thousand meters. They are typically observed in small clusters and may occasionally form larger groups. The Devil Ray is a large (to 350 cm DW) pelagic species which is circumglobal in tropical and subtropical waters from the surface to 1,112 m deep. It spends most of its time in less than 50 m of water. Devil Rays feed on planktonic crustaceans and small schooling fish, which are funneled into the mouth with cephalic flaps. When these flaps are rolled up, they look like horns, giving the animal a \"devil-like\" silhouette. The species exhibits large-scale movements, up to 1,800 km at minimum speeds of 63km per day. It is targeted and caught incidentally in industrial and artisanal fisheries. These rays are captured in a wide range of gear types including harpoons, drift nets, purse seine nets, gillnets, traps, trawls, and longlines. Their tendency to aggregate and their coastal and offshore distribution makes mobulids particularly susceptible to bycatch in purse seine and longline fisheries, targeted capture in artisanal fisheries, and incidental entanglement. It is utilized for its meat as protein source (except the head) and gill plates as ingredient in Chinese medicine.", "biology": "It has a very low reproductive capacity. This means that the species gives birth to a single offspring at unknown intervals. The species is ovoviviparous: the young hatch from their eggs inside the mother''s body and emerge later when they are more fully grown. Males mature at 200-220 cm disc width (DW) and females mature at 215-240 cm DW). Reproduction is aplacental matrotrophic with a gestation period of 12 months, and generally a single large pup (though two per litter occur occasionally) of 90-160 cm DW. The reproductive cycle is 1-3 years, with resting periods between pregnancies in some years. Max length: 520 cm DW; Max published weight: 1,500 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Gymnura natalensis',
  'Diamond Ray',
  'Diamantrog',
  'Ray Diamond',
  ARRAY['Butterfly Ray','Backwater Butterfly Ray','Rem-vlinderrog'],
  'Gymnuridae',
  'Butterfly rays',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Butterfly Ray, Backwater Butterflyray, Rem-vlinderrog", "appearance": "The broad diamond-shaped disc is almost twice as wide as it is long; tail shorter than body with black and white bands and a small sting; tentacle at rear edge of each spiracle). Grey, green or brown above, often with darker mottling; white below; can change colour of upper disc rapidly to match substrate.", "about": "The Diamond or Butterfly Ray is endemic and occurs from southern NAM to southern MOZ, possibly extending to Kenya and Tanzania and is found around the entire SA coast. Data from the ORI Tagging Project suggests they are not resident, but no clear migratory pattern emerges.  Adults can be found on shallow, sandy beaches, estuaries and offshore banks to a depth of 75 m. Juveniles are mainly found in sheltered sandy bays along the coast and in estuaries but also on offshore banks. Adults eat mainly fish, but crustaceans, benthic copepods, (crabs, shrimps, and prawns), molluscs (bivalves, cephalopods (octopus and squid), gastropods (snails, whelks and slugs)) and polychaetes (bristle worms) supplement the diet. The juvenile diet is unknown but is probably similar to adults. These rays are commonly caught as a bycatch by recreational shore anglers and are specifically targeted during shore angling competitions, but most are released. Many are also landed in estuaries. Although mostly released alive, a practice which should continue to be encouraged, release mortality is not known and could be substantial due to angling practices such as gaffing and poor handling. They also comprise a large percentage (12.9%) of the bycatch taken in prawn trawls off the Thukela Banks during summer, with an associated 46.4% release mortality. Occasionally they are caught by competitive light tackle boat anglers but are seldom kept. They are often caught in the shark nets off certain KZN beaches, but most are released alive. Adults and juveniles are caught as a bycatch in the beach seine fishery off Durban and in the WC.", "biology": "Diamond Rays are ovoviparous (internal fertilization and the young ones are born alive, no placental connection, the unborn young ones are nourished by egg yolk; the mother''s body does provide gas exchange). After a 12 month gestation period 2-9 pups per litter are born at a length of ~35 cm DW. Pregnant females have been found in Durban Bay from Jan-Aug. The breeding/spawning locality is unknown but probably takes place throughout its distribution off shallow sandy beaches and bays. A large number of individuals are caught every year between Richards Bay and the Thukela River in KZN during summer following strong north-easterly winds and it is thought that this may be a breeding aggregation. Males mature at ~2 years at 100 cm DW and females at ~6 years at 150 cm DW. Note that the smallest mature male dissected at the KZNSB was 964 mm DW and the smallest female was 1 669 mm DW. The maximum recorded age for males is ~24 years. The maximum recorded weight is 120 kg and the maximum recorded disk width is 2500 mm.", "citation": "SW Dunlop, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase"}',
  '[{"measure": "DW", "reference": "FishBase", "coefficient": 7.5e-06, "exponent": 3.04, "formula_type": "power", "result_unit": "kg"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Aetomylaeus bovinus',
  'Duckbill Ray',
  'Bulrog',
  'Ray Duckbill',
  ARRAY['Bull Ray','Bullray','Duckbill Eagle Ray'],
  'Myliobatidae',
  'Devil, Eagle and Manta rays',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Critically Endangered',
  NULL,
  '{"other_names": "Bull Ray, Bullray, Duckbill Eagle Ray", "appearance": "It is called the Duckbill Ray in South Africa for its long, flat, round snout. They can be considered as giants of shallow waters. They are long, flat; the head thick and pectoral disc with sharply curved, angular corners; upper or lower jaw; usually with 7 rows of flat teeth. Light brown with several pale blue-grey stripes (may be absent) dorsally, white ventrally.", "about": "This species is often confused with Eagle Ray. The Duckbill Ray is found in the eastern Atlantic: Portugal and Morocco to Angola, including the Mediterranean, Madeira and the Canary Islands; then from Saldanha Bay to KZN (South Africa) and southern Mozambique. It is demersal and semi-pelagic in estuaries, lagoons, and on the continental shelf to a depth of 150 m.  It is found between the surf zone and depths of 65 m or more and also enters estuaries and lagoons. Duckbill Rays are \"bentho-pelagic\" feeders, which means that they feed on the sea floor and the water column. They feed on various invertebrates including crabs, hermit crabs, squids, prawns, gastropod molluscs and bivalve molluscs. They frequent both the bottom and the surface and sometimes leap from the water. Their maximum length and very narrow depth range of 0 to 30 m exposes them to various threats. Indeed, large-body, shallow-water species are at greatest risk of threats. The species is a bycatch of industrial and artisanal trawl and net fisheries, as well as recreational fisheries in South Africa, and is retained for human consumption or discarded. It is suspected to have limited biological productivity similar to other myliobatid rays (including a low fecundity of 3-6 pups per litter), limiting its capacity to sustain fishing pressure. Its flesh is highly esteemed. Females are larger and heavier than males.", "biology": "It exhibits ovoviparity (aplacental viviparity), with embryos feeding initially on yolk, then receiving additional nourishment from the mother by indirect absorption of uterine fluid enriched with mucus, fat or protein through specialised structures). Gestation is about 6 months; 3-7 young produced. Size at birth:  45 cm WD. Length at maturity: 90.0 cm. Max length: 222.0 cm WD (female); Common length: 150 cm WD. Max published weight: 116.0 kg. The generation length is inferred to be 17 years.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "DW", "reference": "FishBase", "coefficient": 0.0194, "exponent": 2.903, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Myliobatis aquila',
  'Eagle Ray',
  'Arendrog',
  'Ray Eagle',
  ARRAY['Eagleray','Common Eagle Ray'],
  'Myliobatidae',
  'Devil, Eagle and Manta rays',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Critically Endangered',
  NULL,
  '{"other_names": "Eagleray, Common Eagle Ray", "appearance": "The Eagle Ray has a rhomboidal disc with a pair of large, triangular pectoral fins projecting on both sides, and a single dorsal fin. The snout is rounded and the tail slender, with a large spine at its base but no caudal fin. The dorsal surface is brown or blackish while the ventral surface is white. The upper or lower jaw usually has 7 rows of plate-like teeth.", "about": "Eagle Rays are distributed from the North Sea to SA in the eastern Atlantic, including the Mediterranean, and also recorded off Kenya in the western Indian Ocean. Populations in Europe and off Kenya may be regionally different compared to elsewhere. In southern Africa they are more common along the cooler EC and WC coast than in KZN. Although this species has been reported off Kenya, very few individuals have been recorded in MOZ. There is insufficient data to establish movement patterns. The ORI Tagging Project data reveals only 3 recaptures from 480 animals tagged (0.63%) with a mean distance travelled of 18km. Adults of this semipelagic ray are found inshore and offshore, although it appears to prefer inshore, coastal areas (<50 m), readily entering shallow lagoons and estuaries. It has been reported from depths of up to 537 m in some areas.  Large aggregations of adults have also been noted. Juveniles can be found inshore and offshore similar to adults. Adults are carnivorous feeding on bivalves, snails, slugs, whelks, crabs, worms and small fish. The juvenile\u2019s diet is unknown, probably similar to adults. They are commonly caught as a bycatch by recreational shore anglers and are specifically targeted during shore angling competitions, but most are released. Adults and juveniles are caught as a bycatch in inshore trawls and in the beach seine fishery in the WC. Although often captured in the KZNSB nets, 65-70% are released alive.", "biology": "Reproductive style: Ovoviviparous with aplacental viviparity, (embryos develop inside eggs that are retained within the mother''s body until they are ready to hatch), 3-7 young born after 6-8 month gestation. Breeding/spawning season: Unknown in SA waters. Breeding/spawning locality: Unknown in SA waters but probably throughout its distribution. Age at maturity: Unknown. Length at maturity: Males: 40-50 cm DW; Females: 60-70 cm DW. Other research has recorded 31.8 and 42.5 cm DW as the size at maturity for males and females respectively in SA waters.", "citation": "SW Dunlop, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, Wikipedia"}',
  '[{"measure": "DW", "reference": "Visboekie", "coefficient": 1.41e-05, "exponent": 3.06, "formula_type": "power", "result_unit": "kg"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Rhinoptera javanica',
  'Flapnose Ray',
  'Flapneus Rog',
  'Ray Flapnose',
  ARRAY['Javanese Cow-nose Ray'],
  'Myliobatidae',
  'Devil, Eagle and Manta rays',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Unknown, Vunerable',
  NULL,
  '{"other_names": "Javanese Cow-nose Ray", "appearance": "Double-lobed snout and indented forehead; jaws usually with 7 rows of plate-like teeth; no caudal fin. Coulouration: Brown above, white below.", "about": "The Flapnose Ray is an inshore batoid with a widespread distribution in the Indo-West Pacific from South Africa to Southeast Asia and north to Japan. They are found in open seas and inshore over continental and insular shelves near coral reefs and have been recorded in muddy areas and shallow estuaries and in coastal bays, lagoons and mangroves. They are found usually solitary or in small aggregations but can form large schools of as many as 500 individuals. Food includes clams, oysters and crustaceans.", "biology": "It exhibits ovoviparity (aplacental viviparity), with embryos feeding initially on yolk, then receiving additional nourishment from the mother by indirect absorption of uterine fluid enriched with mucus, fat or protein through specialised structures. This species most likely has 1 to 2 young per litter due to the large size at birth (63 cm DW). Catches taken in the protective shark nets off the beaches of KwaZulu-Natal, South Africa, peak from March to June (65% of the total annual catch), although the species is caught throughout the year. Max length: 165 cm WD; Max published weight: 4.5 kg.", "citation": "FishBase, Wikipedia"}',
  '[{"measure": "DW", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -11.59589005, "exponent": 3.138524055}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Pateobatis jenkinsii',
  'Jenkins Ray',
  'Jenkinsrog',
  'Ray Jenkins',
  ARRAY['Jenkins Whipray','Brown Stingray','Dragon Stingray','Golden Whipray','Sharpnose Stingray','Draak Pylstert'],
  'Dasyatidae',
  'Whiptail stingrays',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Vulnerable',
  NULL,
  '{"other_names": "Jenkins Whip Ray, Dragon Stingray, Golden Whip Ray. Note: This species was previously known as Himantura jenkinsii.", "appearance": "The pectoral fin disc of the Devil Stingray is diamond-shaped and rather thick in the center, measuring 1.1-1.2 times wider than long; the outer corners of the disc are broadly rounded. The anterior margins of the disc are nearly straight and converge at a very obtuse angle on the snout, which has a barely protruding tip. The eyes are medium-sized and closely followed by larger spiracles. A short, broad curtain of skin with a finely fringed posterior margin is present between the long, thin nostrils. The mouth is wide and gently arched and contains four papillae (nipple-shaped structures) on the floor, the inner pair of which is shorter than the outer. The pelvic fins are small and narrow. The cylindrical, tapering tail lacks fin folds and measures slightly longer than the disc width. One to three serrated, stinging spines are located atop the tail, approximately one-quarter of the total tail length back from the base. The upper surface of the disc has a granular texture and bears a broad central band of closely spaced, flattened heart-shaped dermal denticles, beginning between the eyes, becoming widest at the \"shoulders\", and extending to entirely cover the tail. One or more rows of large, spear-like thorns also run along the dorsal midline from the center of the disc to the base of the sting. Barring the possible spotted variant, this species is a uniform yellowish brown above, with the disc margin and underside white, and the tail gray past the sting.", "about": "The Devil Stingray is patchily distributed in the Indo-Pacific region from South Africa to the Malay Archipelago to northern Australia. An inshore ray, usually found at less than 50 m depth, but also reportedly to 90 m. It feeds mainly on small bony fishes and crustaceans. It may be encountered alone or in groups; there is some evidence of segregation by sex. Because of its large thorns, the Jenkins'' whipray is highly prized for its skin; the meat and cartilage may also be utilized.", "biology": "Reproduction is viviparous with histotrophy; with embryos feeding initially on yolk and then receiving additional nourishment from the mother by indirect absorption of uterine fluid enriched with mucus, fat or protein through specialised structures. Males mature at about 75-85 cm DW and size at birth is around 23 cm DW. Max length: 130 cm WD (male); 110.8 cm WD (female).", "citation": "FishBase, The IUCN Red List of Threatened Species, Bray, D.J. 2018, Pateobatis jenkinsii in Fishes of Australia, accessed 07 Sep 2021, https://fishesofaustralia.net.au/home/species/2027"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Torpedo sinuspersici',
  'Marbled Electric Ray',
  'Marmer Drilvis',
  'Ray Marbled Electric',
  ARRAY['Marbled Torpedo Ray','Gulf Torpedo Ray','Variable Torpedo Ray'],
  'Torpedinidae',
  'Electric Rays',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  NULL,
  '{"other_names": "Marbled Torpedo Ray, Gulf Torpedo Ray, Variable Torpedo Ray", "appearance": "The Gulf torpedo has a rounded pectoral fin disc, 84% as wide as long. The tail is short and stocky; bearing two small, subtriangular dorsal fins placed very close together, with the second three-quarters the size of the first. The pelvic fins are fleshy and partly fused to the disc, while the caudal fin is small and broadly rounded. The eyes are small; the spiracles are larger than the eyes in adults and bear 9-10 papillae on the rim. The skin is smooth. The mouth is wide and contains small, sharply cusped teeth. This species has distinctive dorsal colouration, composed of a base dark red or brown to blackish colour with strong, thick cream, white or golden bright colour patterns of circles and irregular marks over the disc, pelvic fins, and tail, with many cream-coloured irregular spots, no larger than eye diameter, on the anterior and lateral disc regions. However, the colouration varies somewhat within the species. Some specimens have cream-coloured brain-shaped figures or rosettes over the disc and tail, becoming smaller towards the disc margins, along with small spots laterally on the disc and the front of the snout.", "about": "The Marbled Electric Ray is a medium-sized poorly known electric ray with a wide distribution in the Western Indian Ocean ranging from India to South Africa. It is a benthic electric ray that inhabits the continental shelf on coral reefs and sandy and rocky areas to a depth of 150 m. At night it actively hunts for food, sculling slowly through the water about a meter above the bottom; during the day it usually rests on the bottom and opportunistically ambushes unwary bony fishes.", "biology": "Males mature at a disc width of 39 cm and females at 45 cm. A litter of 9-22 pups measure about 10 cm DW. Max length: 130 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0162, "exponent": 3.04, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Aetomylaeus vespertilio',
  'Ornate Eagle Ray',
  'Versierde Arendrog',
  'Ray Ornate Eagle',
  ARRAY['Reticulate Eagle Ray','Netarig Arendrog'],
  'Myliobatidae',
  'Devil, Eagle and Manta rays',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Endangered',
  NULL,
  '{"other_names": "Reticulate Eagle Ray, Netarig Arendrog", "appearance": "It has a clearly distinct pattern of reticulating dark lines and rings on its back. If the extremely long tail is unbroken, it can considerably add to the maximum body length of 4 m. Lacking a spine on the tail, it is deemed harmless to humans.", "about": "The Ornate Eagle Ray has a sporadic distribution in the Indo-West Pacific from Mozambique, the Red Sea, India, the Maldives, Southeast Asia and the Philippines, China and Taiwan, and across northern Australia. It is a large, uncommon eagle ray. It is found in muddy bays and banks and on coral reefs from close inshore to 110 m depth. Caught in bottom trawls, gillnets, seines, and fish traps. It is utilized for its meat and cartilage (possibly used as \"filler\" in shark fin soups as with other large eagle rays. It is suspected to have undergone a population decline exceeding 50% over the past three generations (45 years).", "biology": "Ovoviviparous (aplacental viviparity), with embryos feeding initially on yolk, then receiving additional nourishment from the mother by indirect absorption of uterine fluid enriched with mucus, fat or protein through specialised structures. It is suspected to have limiting life history parameters similar to other myliobatid rays, including low fecundity. Maximum size of around 240 cm DW.", "citation": "FishBase, The IUCN Red List of Threatened Species, iNaturalist"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Taeniurops meyeni',
  'Round Ribbontail Ray',
  'Ronde Lintstertrog',
  'Ray Round Ribbontail',
  ARRAY['Blotched Fantail Ray','Black-blotched Stingray','Giant Reef Ray','Fantail Stingray','Speckled Stingray'],
  'Dasyatidae',
  'Whiptail stingrays',
  NULL,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Vulnerable',
  NULL,
  '{"other_names": "Blotched Fantail Ray, Black-blotched Stingray, Giant Reef Ray, Fantail Stingray, Speckled Stingray", "appearance": "The round ribbontail ray has a thick pectoral fin disc wider than it is long, with a smoothly rounded outer margin. The eyes are of medium size and are followed by larger spiracles. The mouth is wide and curved, with faint furrows at the corners. The pelvic fins are small and narrow. The tail is relatively short, not exceeding the width of the disc, and bears one (rarely two) long, serrated stinging spine on the upper surface. The base of the tail is broad; past the spine, the tail rapidly thins and bears a deep ventral fin fold that runs to the tail tip. The upper surface of the disc and tail are roughened by a uniform covering of small, widely spaced granules. The dorsal colouration is light to dark gray, brown-gray, or purplish, becoming most intense towards the fin margins, with a highly variable pattern of irregular darker mottling and white speckles or streaks. The tail past the spine, including the fin fold, is uniformly black, while the underside is creamy-white with darker fin margins and additional dots. Young rays are plainer in colouration than adults. It is one of the largest stingray species.", "about": "It is widely distributed in the Indo-West and is associated with coral reefs and sandy habitats. Bottom-dwelling in nature, the Round Ribbontail Ray is found close inshore around coral reef habitats and on sand substrates, from the surf zone offshore to 439 m depth. It is found singly or in aggregates and usually with kingfish and cobia swimming near them and feeds on bottom fish, bivalves, crabs and shrimp. It is not normally aggressive, but it has been responsible for at least one human fatality. Sought by surf and ski boat anglers in southern Africa but usually released unharmed.", "biology": "It is a viviparous species, with reported litter size of up to seven young. Age data is estimated; females mature at 15 years and reach 28 years, giving an estimated generation length of 21.5 years. Males mature at 100-110 cm DW. Size at birth is 30-35 cm DW. It reaches a maximum size of 180 cm. Max length: 330 cm TL; 180 cm DW. Max published weight: 150.0 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "DW", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -9.153886795, "exponent": 2.712804079}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Aetobatus narinari',
  'Spotted Eagle Ray',
  'Spikkel Arendrog',
  'Ray Spotted Eagle',
  ARRAY['Whitespotted Eagle Ray','Spotted Bonnet Ray'],
  'Myliobatidae',
  'Devil, Eagle and Manta rays',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Decreasing, Near Threatened',
  NULL,
  '{"other_names": "Whitespotted Eagle Ray, Spotted Bonnet Ray", "appearance": "Spotted Eagle Rays have flat disk-shaped bodies, deep blue or black with white spots on top with a white underbelly, and distinctive flat snouts similar to a duck''s bill.  Their tails are longer than those of other rays and may have 2-6 venomous serrated spines, just behind the pelvic fins. The front half of the long and wing-like pectoral disk has five small gills in its underside.", "about": "Although traditionally considered to have a circumglobal distribution in tropical oceans throughout the world, recent authorities have restricted the true Aetobatus narinari to the Atlantic Ocean based on genetic and morphologic evidence. It is commonly found in coastal habitats at at least 60 m depth and swims close to the surface, occasionally leaping out of the water, or close to the bottom. It frequently forms large schools during the non-breeding season. Spotted Eagle Ray preys mainly upon bivalves, crabs, whelks, benthic infauna they also feed on mollusks, crustaceans, particularly crayfish] and also upon hermit crabs, shrimp, octopuses, and some small fish. The Spotted Eagle Ray''s specialized chevron-shaped tooth structure helps it to crush the mollusks'' hard shells. The jaws of these rays have developed calcified struts to help them break through the shells of mollusks, by supporting the jaws and preventing dents from hard prey. These rays have the unique behavior of digging with their snouts in the sand of the ocean. While doing this, a cloud of sand surrounds the ray and sand spews from its gills. The species often enters estuarine waters where fishing pressure is high.", "biology": "Reproduction is ovoviviparous and litters of 1-5 pups, with an average of 3, are produced annually after a 12-month gestation and size-at-birth is estimated at 18-36 cm DW. Mature Spotted Eagle Rays can be up to 5 m in length; the largest have a wingspan of up to 3 m.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "DW", "reference": "TORRES, F.S.B., Jr.", "coefficient": 0.00586, "exponent": 3.13, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Hilsa kelee',
  'Razorbelly',
  'Skeermespens',
  'Razorbelly',
  ARRAY['Kelee Shad','Kelee-haring','Five Spot Herring'],
  'Dorosomatidae',
  'Gizzard shads and sardinellas',
  NULL,
  NULL,
  NULL,
  NULL,
  NULL,
  'Stable, Least Concern',
  'Depleted',
  '{"other_names": "Kelee Shad, Kelee-haring, Five Spot Herring", "meristics": "Dorsal spines: 0; Dorsal soft rays: 16-19; Anal spines: 0; Anal soft rays: 17-23.", "appearance": "Diagnosis: Body fairly deep and compressed, belly with distinct keel of scutes; top of head with numerous fronto-parietal striae; upper jaw with median notch; a series of small triangular scales above axil of pectoral fin; hind part of body scales perforated. A black spot appears behind gill cover, usually followed by up to 10 spots along flank.", "about": "Razorbelly is a coastal marine and estuarine species that is widely distributed throughout the Indo-West Pacific including as far south as Durban and Madagascar generally in tropical waters. It can tolerate low salinities. It feeds mostly on phytoplankton (mainly diatoms and dinoflagellates), but also on copepods, molluscan and crustacean larvae, prawns, amphipods, and polychaetes.", "biology": "Length at maturity: 15.0 cm. Max length: 24.5 cm SL, 35.0 cm TL; Common length: 16.5 cm SL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Remora remora',
  'Remora',
  'Remora',
  'Remora',
  ARRAY['Brown Remora','Shark Sucker'],
  'Echeneidae',
  'Remoras',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Brown Remora, Shark Sucker", "meristics": "Dorsal spines: 0; Dorsal soft rays: 22-26; Anal spines: 0; Anal soft rays: 22-24.", "appearance": "The Remora is dark brownish-grey or black in colour. During the course of development, the dorsal fin\u2019s soft rays are transformed into a suction disc allowing it to attach to larger marine animals.", "about": "Remora occurs worldwide in tropical and sub-tropical waters in a depth range 0-200 m. In the eastern Atlantic it is known from Iceland, the North Sea, throughout the Mediterranean Sea, and along West Africa to South Africa. This pelagic, offshore species is usually associated with sharks, but also attaches itself to other large fishes, such as whales, dolphins, sharks, sea turtles and even ships. Sometimes it is free-swimming. Remora and its host seem to partake in a symbiotic relationship; the Remora does not seem to have a negative overall effect on its host. The host provides the Remora with fast-moving water to bathe its gills, a steady flow of oxygen, food, transportation, and protection. The remora benefits the host by feeding in part on some of its parasites but increases its hydrodynamical drag. The attachment to one host can last for up to three months. It feeds on food scraps from its host, as well as plankton and parasitic copepods.", "biology": "It is not clear when during the year the Common remora spawns, and little is known about the fish''s reproductive behavior. Max length: 86.4 cm TL; Common length: 40.0 cm TL. Max published weight: 1.1 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -12.38527434, "exponent": 3.000015677}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus areolatus',
  'Areolate Rockcod',
  'Areolate Klipkabeljou',
  'Rockcod Areolate',
  ARRAY['Squaretail Rockcod','Stompstert Klipkabeljou'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Aerolate Grouper, Squaretail Rockcod, Stompstert Klipkabeljou", "meristics": "Dorsal spines: 11; Dorsal soft rays: 15-17; Anal spines: 3; Anal soft rays: 8.", "appearance": "Anal fin of adults is rounded to slightly angular; caudal fin slightly convex in juveniles, truncate or slightly emarginate in adults. Head, body, and fins pale; covered with numerous close-set brown, brownish yellow, or greenish yellow spots, the largest about size of pupil, those on front of head smaller than those on operculum. Pectoral fins pale, with small dark spots on the rays. The posterior edge of caudal fin has a distinct white margin.", "about": "Aerolate Rockcod occurs in the Red Sea, Persian Gulf, and western Indian Ocean south to KZN, South Africa; its range extends eastward to India, Sri Lanka, Andaman Islands, Thailand, Malaysia, Viet Nam, Indonesia, Philippines, Hong Kong, China, Taiwan, Japan, northern Australia (and nearby islands), Papua New Guinea, New Caledonia, and Fiji. It appears to be absent from Micronesia, Polynesia, and most of the islands of the western Indian Ocean (e.g., Madagascar, Mascarenes, Comoros, Chagos Archipelago and the Maldives). It has been taken at the Seychelles (market specimen) and the Laccadives. This species is usually found in seagrass beds or on fine sediment bottoms near rocky reefs, dead coral, or alcyonarians, in shallow continental shelf waters. Juveniles are common at water depths to 80 m. It has been taken in depths of 6-200 m. Probably spawn during restricted periods and form aggregations when doing so. Feed on fish and benthic invertebrates, primarily prawns and crabs. Aerolate Rockcod is common in the markets of Hong Kong, Singapore, and probably throughout its range. It is caught with hook-and-line, traps, and in trawls.", "biology": "Length at maturity 20.0 cm, range 19 - ? cm. Eggs and early larvae are probably pelagic. Max length: 47.0 cm TL; Common length: 35.0 cm; Max published weight: 1.4 kg; Max reported age: 15 years.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus macrospilos',
  'Bigspot Rockcod',
  'Grootkol Klipkabeljou',
  'Rockcod Bigspot',
  ARRAY['Snubnose Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Stable,  Least Concern',
  NULL,
  '{"other_names": "Snubnose Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 15-17; Anal spines: 3; Anal soft rays: 8.", "appearance": "Head profile angular; flat or slightly concave interorbital area; strongly projecting lower jaw, 2-4 rows of teeth at midlateral part; rounded caudal fin.  Indian Ocean specimens: Head and body with round to polygonal brown to dark brown spots, variable in size (some spots on juveniles may be as large as eye) and close-set, the narrow interspaces forming a pale network pattern; median fins yellowish, with blackish brown spots like those on body; soft dorsal, caudal, and anal fins with pale margin; underside of lower jaw usually with dark spots. Small juveniles (5 to 7 cm standard length) with dark spots on head and body fewer and much larger than in adults, those on caudal fin represented by a broad black area covering two-thirds of the fin; Pacific Ocean juveniles with prominent white blotches. Specimens from the western Indian Ocean differ from those in the eastern Indian and Pacific oceans in usually having dark spots on the underside of the jaw, and the dark spots on the head and body are larger and close-set, forming a pale mesh dorsally on fresh specimens.", "about": "Indo-Pacific region from east coast of Africa (Kenya to Natal, South Africa) to the central Pacific, including Madagascar, Comoros, Mascarenes, Seychelles, Chagos, Nicobars, Cocos-Keeling, Western Australia (Scott Reef), Indonesia, Okinawa, Great Barrier Reef, Marquesas Islands, and most western Pacific islands (both on and off the Pacific Plate). Bigspot Rockcod is not known from the Red Sea, Persian Gulf, or Hawaii. Found in lagoon and seaward to a depth of at least 44 m: including outer reef slopes. It is more abundant on oceanic reefs and rare on coastal reefs and feeds on crustaceans (mainly crabs), fishes, octopi, and squid. They are solitary. Bigspot Rockcods are common in some areas and are undoubtedly important in artisanal fisheries. It is caught with hook-and-line, spear, and traps.", "biology": "Max length: 51.0 cm TL; Max published weight: 2.0 kg.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0135, "exponent": 3.03, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Plectropomus laevis',
  'Blacksaddled Coralgrouper',
  'Swartsaal KoraalKabeljou',
  'Rockcod Blacksaddled Coralgrouper',
  ARRAY['Saddleback Rockcod','Black-saddled Leopardgrouper','Swartsaal Luiperdkabeljou'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Saddleback Rockcod, Black-saddled Leopardgrouper, Swartsaal Luiperdkabeljou", "meristics": "Dorsal spines: 7 - 8; Dorsal soft rays: 10-12; Anal spines: 3; Anal soft rays: 8.", "appearance": "Two colour forms: the pale black-saddled form is whitish or pale yellowish with 5 dark brown to black, irregular, saddle-like or wedge-shaped bars or blotches on dorsal part of head and body, the first just behind eyes, the second on nape, the third to fifth under dorsal fin; small dark-edged blue spots may be visible on body (mainly posteriorly); fins, caudal peduncle, snout and jaws yellow; black blotch centrally at base of paired fins; small juveniles (7-15 cm SL) with membranes between first and fifth dorsal-fin spines mostly blackish. The dark form is brown, olivaceous, red, or nearly black, the dark bars (as on pale form) are usually faint or absent; numerous, round, dark-edged blue spots (about half pupil diameter) on head, body (except ventrally); soft dorsal, caudal and anal fins, and base of pectoral fins: pectoral fins dark brown distally, the rays darker than membranes, the rear edge white.", "about": "Distributed in the Indo-Pacific, from southern Mozambique to the Tuamotus, Austral Islands and Rapa, but not in the Red Sea or Persian Gulf. Except for the east coasts of Africa and Australia, records of Blacksaddled Coralgrouper Rockcod are from insular localities; it ranges from Kenya to Delagoa Bay and is known from most of the islands of the Indian Ocean. In the western Pacific, it occurs from Japan (Ryukyu Islands) to the southern Great Barrier Reef and is known from most of the islands in the central and western Pacific. Blacksaddled Coralgrouper Rockcod is a conspicuous and fairly common species on coral reefs of the Indo-Pacific region at depths of 4 to at least 90 m. Blacksaddled Coralgrouper Rockcod feeds exclusively on fishes. The prey comprises a variety of large reef fish, including groupers. This diet of large fish is responsible for the high concentrations of ciguatera toxins found in this species.", "biology": "Maturity seems to occur at 50-52 cm SL (2.8 to 3.2 kg). Max length: 125 cm TL; Common length: 84.0 cm SL; Max published weight: 24.2 kg.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus fuscoguttatus',
  'Blotchy Rockcod',
  'Gevlekte Klipkabeljou',
  'Rockcod Blotchy',
  ARRAY['Brownmarbled Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Decreasing, Vulnerable',
  NULL,
  '{"other_names": "Brownmarbled Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 14-15; Anal spines: 3; Anal soft rays: 8.", "appearance": "Body moderately deep; interorbital area flat or slightly concave; dorsal head profile of adults indented at eyes and distinctly convex from there to dorsal-fin origin; rounded preopercle, finely serrate; upper edge of operculum distinctly convex, descending almost vertically to rear end of operculum; lower jaw with 3 or 4 rows of teeth; posterior nostrils triangular, 4-7 times larger than anteriors in adults; caudal fin rounded; pectoral-fin rays 18-20; lateral-line scales 52-58. Colour: Pale yellowish brown, with 5 vertical series of dark brown blotches that are very irregular in outline; head, body, and fins covered with close-set small brown spots, those on the dark blotches much darker than spots in-between blotches; small black saddle spot on rear half of peduncle; 2 or 3 faint, dark bars at side of jaws.", "about": "Widely distributed in the Indo-Pacific region, including the Red Sea, but not known from the Persian Gulf, Hawaii, or French Polynesia. E. fuscoguttatus occurs at probably all of the tropical islands of the Indian and west-central Pacific oceans (east to Samoa and the Phoenix Islands) along the east coast of Africa to Mozambique, and it has also been reported from Madagascar, India, Thailand, Indonesia, tropical coast of Australia, Japan, Philippines, New Guinea, and New Caledonia. It occurs in lagoon pinnacles, channels, and outer reef slopes, in coral-rich areas and with clear water and rocky bottoms to depths of 60 m: Juveniles in seagrass beds.  It feeds on fish, crabs, and cephalopods. Adults are not common. It is caught with hook-and-line, traps, and spear.", "biology": "Max length: 120 cm TL; Common length: 50.0 cm TL; Max published weight: 11.0 kg; Max reported age: 40 years.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Cephalopholis formosa',
  'Bluelined Rockcod',
  'Bloustreep Klipkabeljou',
  'Rockcod Bluelined',
  ARRAY['Bluelined Hind'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Bluelined Hind", "meristics": "Dorsal spines: 9; Dorsal soft rays: 15-17; Anal spines: 3; Anal soft rays: 7-8.", "appearance": "Body with ctenoid scales, including abdomen; rounded caudal fin; pelvic fins; rounded preopercle, finely serrate, lower edge fleshy; smooth subopercle and interopercle; maxilla naked, reaching to or just past vertical at rear edge of eye. Colour: Dark brown to yellowish brown, with slightly irregular dark blue lines on head, body, and fins (those on body and fins sometimes absent); snout, lips, and ventral part of head and chest with small dark blue spots.", "about": "The distribution of Bluelined Rockcod is primarily continental, and it seems to avoid atolls or oceanic islands. It is known from Sri Lanka, and the Lakshadweep Islands; but there are no other reports from islands in the western Indian Ocean. It occurs from western India to Thailand, Indonesia, Philippines, China, Taiwan, Japan (Honshu), and northern Australia. Like C. boenak, it prefers shallow dead or silty reefs, and this may account for the primarily continental distributions of these two species. Bluelined Rockcod is too small to be of commercial importance as a food fish. It is caught with hook-and-line, traps, and in trawls.", "biology": "Max length: 34.0 cm TL.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Cephalopholis boenak',
  'Brownbarred Rockcod',
  'Briunbalk Klipkabeljou',
  'Rockcod Brownbarred',
  ARRAY['Chocolate Hind'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Chocolate Hind", "meristics": "Dorsal spines: 9; Dorsal soft rays: 15-17; Anal spines: 3; Anal soft rays: 8.", "appearance": "Preopercle rounded, very finely serrate; no enlarged spines at angle, lower edge fleshy; characterized further by having ctenoid scales on body including abdomen; greatest depth of body 2.6-3.1 in SL; rounded caudal fin; pelvic fins not usually reaching anus. Colour: Dark brown (occasionally dark reddish brown), usually with 7 or 8 dark bars on body; some fish with dark brown bands radiating from eye; black spot between upper and middle opercular spines; soft dorsal, anal, and caudal fins darker distally, with a pale bluish line on the edge (except central part of caudal fin).", "about": "Brownbarred Rockcods are primarily continental in its distribution, occurring from Kenya to southern Mozambique and eastward to the western Pacific (Ryukyu Islands Taiwan, China, Philippines, Indonesia, Papua New Guinea, New Caledonia, and eastern Australia). It has not been reported from oceanic islands in the Indian Ocean, except for Aldabra, Madagascar, and the Comoro, Andaman, and Lakshadweep islands. It is not known from the Red Sea, Persian Gulf, or the islands of Micronesia or Polynesia, except for Palau. Brownbarred Rockcod are mainly a coastal species usually found in 4-30 m on silty dead reefs in protected waters: also taken in trawls to depths of 64 m. It feeds primarily on fish and crustaceans. Brownbarred Rockcod are too small to be of commercial importance but are caught with hook-and-line, traps, and in trawls.", "biology": "Length at maturity: 12.2, range 8 - ? cm. Max length: 30.0 cm TL; Max reported age: 11 years.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus chlorostigma',
  'Brownspotted Rockcod',
  'Bruinspikkel Klipkabeljou',
  'Rockcod Brownspotted',
  ARRAY['Brownspotted Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Brownspotted Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 16-18; Anal spines: 3; Anal soft rays: 8.", "appearance": "Live colour whitish, with small dark brown spots on head, body, and fins except ventrally on head, thorax, and lower abdomen. It can assume a transient colour phase of 3-4 rows of very large round dark spots on top of the small-spot pattern. 96-125 scales in longitudinal series; characterized further by having ctenoid body scales, except cycloid scales on nape, thorax, parts of ventral abdomen; body with auxiliary scales; truncate to slightly emarginate caudal fin; interorbital slightly convex; preopercle angle rounded, 4-7 enlarged serrae at angle below a slight notch; upper edge of operculum straight; posterior nostril not noticeably larger than anterior nostril; maxilla reaches about to vertical at rear edge of eye; scaly maxilla, low step on posterior part of ventral edge; 2-4 rows of teeth in midlateral part of lower jaw, inner ones about twice the size of outer teeth. Colour: Head, body, and fins with small, irregular, close-set dark brown spots, the ground colour forming a pale network; caudal fin usually with a white line along rear margin; spots on pectoral fins mainly confined to rays.", "about": "Brownspotted Rockcod occurs from the Red Sea and east coast of Africa (south to KZN, South Africa) to the western Pacific (from southern Japan to New Caledonia); confirmed records also include the western Gulf of Aden (Djibouti and Aden), Kenya, Tanzania, Seychelles, Chagos Islands, Maldives, Lakshadweep Islands, Andaman Islands, Nicobar Islands, Viet Nam, China, Hong Kong, Korea, Papua New Guinea, New Ireland, Caroline Islands, American Samoa, and Fiji. The wide distribution of Brownspotted Rockcod has some puzzling gaps. There are no verifiable records from the Comoros, the continental shelf between Oman and Cambodia, Indonesia, Philippines, Taiwan, and Australia. This solitary species is found over a wide range of habitats like seagrass beds and outer reef slopes and on mud bottoms. The depth range is 4-280 m. Brownspotted Rockcod feed on fish and crustaceans (mainly stomatopods and crabs). It is an excellent food fish, common in the markets of Singapore and Hong Kong.", "biology": "Reproduction of Brownspotted Rockcod at the Seychelles was studied: females are mature at 23 to 29 cm total length, and sexual transition occurs between 35 and 45 cm, but all females do not change sex. Sex change occurs at length of 46.4 cm TL and 3 years of age. Mature individuals form spawning aggregations. Length at maturity: 28.0 cm range 23-31 cm. Max length: 80.0 cm TL; Common length: 50.0 cm TL; Max published weight: 7.0 kg; Max reported age: 29 years.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus polyphekadion',
  'Camouflage Rockcod',
  'Maskering Klipkabeljou',
  'Rockcod Camouflage',
  ARRAY['Camouflage Grouper','Marbled Rockcod','Marble Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Vunerable, Decreasing',
  NULL,
  '{"other_names": "Camouflage Grouper, Marbled Rockcod, Marble Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 14-15; Anal spines: 3; Anal soft rays: 8.", "appearance": "This species is distinguished by the following characters: preopercle rounded, finely serrate; upper edge of operculum distinctly convex; rear nostril diameter about twice that of anterior nostril; dorsal-fin spines third or fourth longest, and distinctly shorter than longest dorsal-fin ray; 2-3 rows of teeth on midlateral part of lower jaw; front of jaws with inconspicuous fixed canines; body scales ctenoid in broad zone on side from beneath pectoral fin to caudal peduncle, the rest cycloid; caudal fin rounded; pectoral-fin rays 16-18; short pelvic fins; lateral-line scales 47-52, Colour of head, body, and fins pale brown, covered with small dark brown spots; upper half whitish, head and body with irregular dark blotches (more distinct on live specimens) superimposed over the dark spots; prominent black saddle blotch on caudal peduncle; dark spots extend all over head, including lower jaw, lips, branchiostegal membranes, gular area, and inside of mouth; numerous small white spots on fins (more distinct on live fish). a few on head and body; juveniles with a pair of blackish spots on each side of snout and a black spot at margin of second and third interspinous dorsal fin membranes.", "about": "Camouflage Rockcod is widely distributed in the tropical and subtropical Indo-West Pacific region from the Red Sea and east coast of Africa to French Polynesia. Camouflage Rockcod is almost always found in clear water on coral reefs, either in lagoons or on the outer reef; it is most abundant at islands, particularly atolls. It feeds mainly on crustaceans (primarily hermit crabs, but also some scyllarid and panularid lobsters) and fish; gastropods and cephalopods are less important food items. Camouflage Rockcod is probably the grouper species that is the least wary of divers; hence it is uncommon at localities with heavy spearfishing. Camouflage Rockcod was formerly common in the markets of Zanzibar and probably throughout its range. It is of considerable importance in the artisanal fisheries of the tropical Indo-Pacific region but occasionally implicated in cases of ciguatera fish poisoning. Recently, the aquaculture industry of Singapore has become interested in the culture of this species; known locally as the \u201cMarble Grouper\u201d. It is caught with hook-and-line, spears, and traps.", "biology": "Females are mature at 38 cm SL, males at about 42 cm SL, and spawning occurs in January and February. Max length: 90.0 cm SL; Common length: 90.0 cm TL.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus andersoni',
  'Catface Rockcod',
  'Katgesig Klipkabeljou',
  'Rockcod Catface',
  ARRAY['Catface Grouper','Spotted Rockcod'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  50,
  NULL,
  5,
  NULL,
  NULL,
  'Decreasing, Near Threatened',
  'Vulnerable (2007). Optimally exploited,40-50% of pristine',
  '{"other_names": "Catface Grouper, Spotted Rockcod", "meristics": "Dorsal spines: 11; Dorsal soft rays: 13-15; Anal spines: 3; Anal soft rays: 8. Body is elongate; angular preopercle, with several enlarged serrae at the angle; upper edge of operculum slightly convex; posterior and anterior nostrils equal; scaly maxilla, reaching to or past vertical at rear edge of eye; 2-3 rows of teeth on midside of lower jaw; rounded caudal fin; ctenoid midlateral-body scales (mostly embedded), with auxiliary scales. Colour: Head, body, and fins brown; body, caudal, and dorsal fins with numerous, small, close-set, irregular, dark brown spots; 2 dark brown stripes posteriorly from eye across operculum and another continuing from maxillary groove to lower edge of preopercle. Juveniles with longitudinal dark stripes that break up into spots posteriorly on body; black blotch at base of last few dorsal-fin spines; 2 small black spots at base of dorsal-fin rays, and a third on top of caudal peduncle; these black blotches separated by 4 or 5 white spots.", "about": "Catface Rockcods are endemic from MOZ (Quissico) to Knysna in the WC. Reports of this species occurring in Madagascar are dubious. Recently they have been recorded as far south as De Hoop MPA in the WC but are uncommon south of Knysna. Based on tag recapture studies, this species appears to be largely resident but with some individuals undertaking extensive movements >400 km. There are anecdotal reports of migrating shoals and the possibility that this is a \"pioneer\" species capable of locating and occupying vacated niche space. There is likely to be some northward movement of pre-adults from southern to northern KZN, as no spawning occurs south of Durban, although considerable numbers of reproductively inactive fish are caught in southern KZN. Adults inhabit rocky reefs from surf zones to 70 m but rarely recorded on coral reefs. Juveniles are found in tidal pools and inshore shallow reefs. Catface Rockcod are sedentary ambush predators that feed on fishes, crabs, and spiny lobsters and make a moderate contribution to ski-boat catches in KZN - ~3% by weight to both recreationals and commercials respectively; around 1% to spearfishing catches and 0.3% to recreational shorefishing; slightly lower contribution (1-2% by weight) to semi-industrial lineboat catches in southern MOZ. There are indications that this species forms spawning aggregations, and if these aggregations occur within the St Lucia and Maputaland MPAs, they would be effectively protected as they probably provide the only protection to the spawning component of the SA population. This species is primarily of interest to sportfishermen.", "biology": "Reproductive style: Protogynous (born female and changes sex to male) - diandric - some males derived from mature females, others develop directly from the juvenile state. Breeding/spawning season: Sep-Feb in KZN. Breeding/spawning locality: Reefs off northern KZN and MOZ. Another reports spawning sometimes occurring inshore in depths shallower than 30 m. Age at maturity: Females: 3.9 years; Males: 2.7 years (minimum age with some males maturing directly from juvenile state while others are derived from mature females by means of sex change); northern KZN. Length at maturity: Females: 49.2 cm TL; Males: 43.0 cm TL (could not be reliably estimated for males as too few immature individuals sampled); Max length: 87.0 cm TL; Max published weight: 8.7 kg. Max recorded age: 11 years, female. Max recorded weight: 9.4 kg, off Durban.", "citation": "ST Fennessy, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180 and FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  '[{"measure": "TL", "reference": "Visboekie", "coefficient": 2.26e-05, "exponent": 2.905, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Cephalopholis miniata',
  'Coral Rockcod',
  'Koraal Klipkabeljou',
  'Rockcod Coral',
  ARRAY['Bluespot Rockcod','Bluespotted Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Bluespot Rockcod, Bluespotted Grouper, Coral Hind", "meristics": "Dorsal spines: 9; Dorsal soft rays: 14-15; Anal spines: 3; Anal soft rays: 8-9.", "appearance": "Orangey-red to reddish brown, covered with small (usually dark-edged) pale blue-grey spots; pectoral fins orangey-yellow distally; soft dorsal, caudal, and anal fins often with a narrow blue margin and blackish submarginal line; pelvic fins orangey-red, the distal edge dark bluish grey. Juveniles are more yellowish, with faint pale blue spots, fewer than in adults.", "about": "Tropical Indo-Pacific and Red Sea (but not the Persian Gulf or French Polynesia), from the African coast (south to Durban, South Africa) to the Line Islands in the central Pacific, including most islands in the Indian and west-central Pacific oceans, southern Japan, Taiwan, Philippines, Indonesia, northern Australia, Papua New Guinea, New Caledonia, and the islands of Micronesia. Coral Rockcod are usually found on well-developed exposed coral reefs in clear water at depths of 2-150 m. It feeds mainly during the early morning (07.00-09.00 am) and mid afternoon (14.00-16.00 pm). Most of the prey (86%) were fishes that school just above the reef (predominantly Pseudanthias squamipinnis), and most of these were caught by a quick rush (\u201cambush attack\u201d) from the bottom. The remaining 14% of the food comprised crustaceans. Fish were the only food item in the 6 specimens with stomach contents that was examined. Although rather small, Coral Rockcods are a common species that is of considerable economic importance to local fisheries. It is caught with hook-and-line, spear, and in traps.", "biology": "Coral Rockcod forms haremic groups comprising a dominant male and 2-12 females. These groups occupy territories of up to 475 m\u00b2, subdivided into secondary territories, which are defended by a single female. Reported sexual maturity (females?) is 25 cm SL. Maximum TL about 50 cm.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0112, "exponent": 3.1, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus poecilonotus',
  'Dot-dash Rockcod',
  'Kolstreep Klipkabeljou',
  'Rockcod Dot-dash',
  NULL,
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 11; Dorsal soft rays: 14-15; Anal spines: 3; Anal soft rays: 8.", "appearance": "Small juveniles (5 to 12 cm standard length) pale yellowish grey, with oval black blotch on body between bases of third to ninth dorsal-fin spines and extending to edge of fin between first and seventh spines: a semicircular pale band passes ventrally around the oval black blotch and isolates it from a dark brown band that begins broadly on nape and bifurcates just behind operculum, the upper branch curving dorsally and expanding broadly over basal half of dorsal fin between the ninth spine and fourth soft ray, the lower branch also curving dorsally and expanding at base of the last 4 dorsal-fin rays. A second curved brown band, parallel to the one above, runs from interorbital area and rear edge of eye to a black saddle spot on caudal peduncle; a third (and narrowest) dark brown band runs from lower edge of eye to subopercle and continues as a series of dark dots along ventral part of body to base of caudal fin. Fins pale yellow, except where dark markings occur on dorsal fin. On larger juveniles (15-25 cm SL) the oval black blotch at base of dorsal-fin spines and the dark brown bands on body break into series of black spots, a faint dark band runs from the dark maxillary streak to corner of preopercle. On small adults (40 to 50 cm standard length) most of the dark spots on body are faint or have disappeared completely and the dark bands on head are fading too; at this stage the fins are yellowish brown, the triangular interspinous dorsal-fin margins abruptly orange-yellow or brownish yellow; soft dorsal, anal, and caudal fins shading to blackish distally with a bluish white edge.", "about": "Dot-dash Rockcod is an Indo-West Pacific species ranging from the east coast of Africa to Japan, Korea, South China Sea, Viet Nam and Fiji; it is not known from the Red Sea or the Persian Gulf. We examined specimens from South Africa (KZN to Port Alfred), Mozambique, Kenya, Somalia, Comoros, Mauritius, Maldives, Sri Lanka, India, and Japan. This deep-water species has been taken in depths of 45-375m. It has been reported that 19 specimens were taken by hook-and-line at the bottom in 95-130 m on the North Kenya Banks; these specimens were 35-51 cm standard length. Dot-dash Rockcod seems to be rare. It is caught with hook-and-line, vertical longlining, and occasionally in trawls.", "biology": "It is estimated that maturity occurs at 41.5 cm TL (35 cm SL). Max length: 65.0 cm TL; Max published weight: 4.0 kg", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Cephalopholis urodeta',
  'Duskyfin Rockcod',
  'Donkervin Klipkabeljou',
  'Rockcod Duskyfin',
  ARRAY['Blackfin Grouper','Darkfin Hind','Duskyfin Hind'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Blackfin Grouper, Darkfin Hind, Duskyfin Hind", "meristics": "Dorsal spines: 11; Dorsal soft rays: 14-15; Anal spines: 3; Anal soft rays: 8.", "appearance": "Reddish brown to brownish red, darker posteriorly; body sometimes with small pale spots and/or 4 faint, irregular, dark bars that bifurcate ventrally and another two dark bars on caudal peduncle; head with numerous, small, close-set orange-red spots (more evident on paler parts of head) and sometimes with irregular reddish brown blotches; dark spot between upper and middle opercular spines (more evident on juveniles); a pair of small dark spots on edge of lower lip in line with the pair of anterior canine teeth; soft dorsal and anal fins with small orange-red spots; outer triangular part of interspinous dorsal-fin membranes orange; pelvic fins orange-red, usually with a dark blue edge. Caudal fin of Indian Ocean fish dark reddish brown to almost black and covered with small pale spots. Pectoral fins of Indian Ocean fish coloured like body basally, becoming dark brown to almost black distally. Some fish from dark habitats in the Comoro Islands are uniformly black.", "about": "Indian Ocean from the coast of Africa (Kenya to northern South Africa) to French Polynesia and the Pitcairn Islands; not known from the Red Sea, Gulf of Aden, Gulf of Oman, Persian Gulf or the coast of India. Duskyfin Rockcod is a widespread and common species that probably occurs at all of the tropical islands and shallow banks of the Indian and west-central Pacific Oceans. It is also known from the northern coast of Australia. Although we have seen one specimen (USNM, 11.8 cm SL) from Sri Lanka, and the species occurs at the nearby Maldive Islands, it appears to be absent from the entire Arabian/Asian coast of the Indian Ocean. This is a common coral reef species that is usually seen in outer reef areas, but also found in lagoons, back-reef areas, and on the reef-top. It has been collected in depths of 1-60 m. It feeds mainly on fish (68%) and crustaceans. Because of its small size, Duskyfin Rockcod is not of much interest as a food fish (except perhaps to subsistence fisheries), but it does well in an aquarium and may find a market as an aquarium fish. It is caught with hook-and-line and in traps.", "biology": "Max length: 65.0 cm TL; Max published weight: 4.0 kg.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus spilotoceps',
  'Foursaddle Rockcod',
  'Viersaal Klipkabeljou',
  'Rockcod Foursaddle',
  ARRAY['Foursaddle Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Foursaddle Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 14-16; Anal spines: 3; Anal soft rays: 8.", "appearance": "Preopercle rounded, with a shallow notch; the serrae are enlarged but covered with skin; upper edge of the operculum almost straight. The head and body mostly covered with close-set dark brown, olive, or reddish-brown spots. Those on the dorsolateral parts of the head, body, and on median fins are polygonal and close-set, with the interspaces forming a white reticulum. Those on the ventral portions are more rounded, more separated and often more reddish. Three dark blotches are often visible at the base of the dorsal fin and one forming a saddle blotch on the caudal peduncle. Small conspicuous dark spots appear on snouts and sometimes between or below the eyes.", "about": "This species is distributed in the tropical Indo-West Pacific primarily in insular localities. It occurs from Mozambique and Madagascar, north to Kenya, all the tropical Western Indian Ocean Islands (Mauritius, Comoros, Seychelles, Chagos, Maldives, Laccadives, Andaman and Nicobar Islands), east to Micronesia, the Line Islands and Cook Islands, and the Northwest Shelf and Great Barrier Reef of Australia to the Philippines. This is a widely distributed relatively small grouper pecies inhabits shallow coral reefs, primarily in lagoonal patch reefs, the upper slopes of channels and outer reef margins. It primarily occurs in insular, rather than continental areas at depth range of 0-30 m and can be common in parts of its range. It is solitary and carnivorous.", "biology": "Nothing has been published on its biology. Max length: 35.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Cephalopholis aurantia',
  'Golden Rockcod',
  'Goue Klipkabeljou',
  'Rockcod Golden',
  ARRAY['Golden Hind'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 9; Dorsal soft rays: 14-16; Anal spines: 3; Anal soft rays: 8-9.", "appearance": "The Golden Rockcod is pale orangey-red to orange-yellow or golden with red or yellow dots on head, anterodorsally on body, and on base of dorsal fin. The posterior margin of caudal fin has a pale blue edge and black submarginal line, posterior parts of dorsal and anal fins often with a narrow pale blue edge.", "about": "It is a rare, solitary species found off steep seaward reefs off South Africa and islands of the western Indian Ocean to the central Pacific and Japan. Golden Rockcod is a deep-water grouper usually caught in depths over 100 m, usually at depths of 100-250 m, but can occur in shallower water. Except for a single specimen caught off the coast of Natal, South Africa, we know of no confirmed records from continental localities. Specimens are rare in museums. Because of its small size and occurrence in moderately deep water, Golden Rockcod is of little commercial importance as a food fish.", "biology": "Max length: 60.0 cm TL; Common length: 30.0 cm TL.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus tauvina',
  'Greasy Rockcod',
  'Slym Klipkabeljou',
  'Rockcod Greasy',
  ARRAY['Greasy Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  NULL,
  '{"other_names": "Greasy Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 13-16; Anal spines: 3; Anal soft rays: 8.", "appearance": "Head and body pale greenish grey or brown, covered with roundish dark spots that vary from dull orange-red to dark brown, the centres darker than edges; spots on head progressively smaller anteriorly; a large black blotch (or group of black spots) often visible on body at base of last 4 dorsal-fin spines and extending onto lower part of fin: 5 faint subvertical dark bars may be present on body, 4 below dorsal fin and fifth on peduncle (these bars may be represented by dusky blotches at base of dorsal fin and a dark saddle blotch on peduncle); fins also covered with dark spots, those on pectoral fins becoming smaller and less distinct on distal part of fin; posterior margin of caudal, anal and pectoral fins often with white edge; dark spots on soft dorsal, caudal and anal fins of juveniles are so close set that the pale interspaces form a white reticulum.", "about": "Greasy Rockcod occurs from the Red Sea to South Africa and eastward to Ducie in the Pitcairn Group, the easternmost atoll of Oceania; in the western Pacific it ranges from Japan to New South Wales and Lord Howe Island. Greasy Rockcod is more common at islands than along continental shores, but it is also known from continental areas with well-developed coral reefs (e.g., the Gulf of Aqaba). Greasy Rockcod prefers clear water areas on coral reefs; juveniles have been taken on reef flats and in tidepools, but adults are found in deeper water (to 50 m). Randall found that adults from Oceania were almost exclusively piscivorous; only 1 of 19 fish that contained food had eaten crab. Fish eaten comprised of holocentrids, a mullid and a pomacentrid. A newly transformed juvenile of 26 mm SL which was collected in April 1972 on the outer reef flat at Enewetak Atoll in the Marshall Islands. This specimen has the second dorsal-fin spine elongated and a large, serrated spine (half eye diameter in length) at the corner of the preopercle. Where it is abundant Greasy Rockcod are certainly of importance to artisanal fisheries, but separate statistics for this species are not available. Caught with hook-and-line, spear, and in traps. It is sold fresh in local markets.", "biology": "Length at maturity: 61.1 cm, range 61 - ? cm. Max length: 100.0 cm TL; Common length: 90.0 cm TL.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus rivulatus',
  'Halfmoon Rockcod',
  'Halfmaan Klipkabeljou',
  'Rockcod Halfmoon',
  ARRAY['Halfmoon Grouper','Butcher''s Prick','Witch''s Prick'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  'Under exploited, >50% of pristine',
  '{"other_names": "Halfmoon Grouper, Witch''s Prick, Butcher''s Prick, Charlie Court Cod", "meristics": "Dorsal spines: 11; Dorsal soft rays: 16-18; Anal spines: 3; Anal soft rays: 8.", "appearance": "Body generally reddish to greenish brown, with a small white or pale blue spot (about size of nostrils) on each scale; 4 irregular dark bars usually visible on body and extending onto base of dorsal fin, and a fifth dark bar on peduncle, the third and fourth bars run from soft dorsal fin to anal fin and are usually joined midlaterally; pectoral fins dusky, with a dark red or reddish brown semicircular blotch on base of rays; 2 dark red or reddish brown bands on thorax and a dark brown spot on front of isthmus. Head mostly dark brown or pale reddish, with irregular pale blotches and pale blue or violet vermiculations; 4 white or pale spots along lower part of head (2 on side of lower jaw and upper lip, 1 just behind end of maxilla and last on interopercle); fins greenish yellow or greyish brown, the dorsal usually with a dark brown to black streak along the base (may be restricted to base of spinous part); margin of interspinous dorsal-fin membranes yellow to reddish next to spine, becoming transparent posteriorly, with a pale stripe below triangular part of fin. Some fish with white dots on operculum like those on body.", "about": "Halfmoon Rockcods are distributed in the Indo-West Pacific from SA to Oman; also, Australia northwards to Japan; apparently absent from the northern Indian Ocean. The southern limit in SA is Knysna. It is a resident species. Based on tagging data this species undertakes limited movement with no evidence for spawning migrations. Adults inhabit rocky reefs, algal flats, seagrass beds, mangroves, out to 150 m. Rarely found in the surf-zone. Juveniles inhabit reefs in shallow inshore waters. Adults feed on crustaceans and small fish but the juvenile\u2019s diet is unknown. Halfmoon Rockcods make a small contribution to ski-boat catches in KZN - 0.25% by weight to commercials and 0.6% to recreationals. Also, low contribution (1% by weight) to semi-industrial lineboat catches in southern MOZ. Seldom caught in the surf-zone and generally too small for recreational spearfishers to spear. Commercial skiboat CPUE and catch composition data from catch returns for individual serranid species are impossible to interpret because of lumping of species. It is probably of some importance in areas where it is common. Caught with handlines, longlines, gillnets, traps, and spear.", "biology": "Reproductive style: Protogynous (born female and changes sex to male); socially mediated sex change. Breeding/spawning season: Jul-Nov in KZN. Breeding/spawning locality: Reefs off KZN and MOZ perhaps around the 40 m depth contour. Age at maturity: Females: 2.1 years; Males: 4 years (minimum age as males are derived from mature females). In Western Australia estimates the age at maturity of females and males as 1 and 2 years respectively. Length at maturity: Females: 219 mm TL (could be over-estimated owing to a lack of small fish sampled); Males: 261 mm TL (minimum length as males derived from mature females); KZN. In Western Australia the estimated length at maturity of females and males is 194-221 mm TL respectively. Maximum recorded age: 17 years; KZN and 24 years in Western Australia. Maximum recorded weight: 1.6 kg (SADSAA). Maximum recorded length: 47 cm TL.", "citation": "ST Fennessy, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180 and FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  '[{"measure": "TL", "reference": "Visboekie", "coefficient": 1.96e-05, "exponent": 2.968, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus merra',
  'Honeycomb Rockcod',
  'Heuningkoek Klipkabeljou',
  'Rockcod Honeycomb',
  ARRAY['Honeycomb Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Honeycomb Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 15-17; Anal spines: 3; Anal soft rays: 8.", "appearance": "Preopercle rounded or subangular, the serrae at angle enlarged; upper edge of operculum almost straight; midlateral part of lower jaw with 2-4 rows of teeth, the inner teeth about twice length of outer ones. Dorsal fin spines third to last subequal; anal fin spines second and third subequal, caudal fin rounded. Head, body, and fins pale, are covered with close-set (sometimes coalesced), dark brown or reddish-brown spots, the interspaces forming an irregular pale reticulum and some midlateral spots often joined to form horizontal bands. The pectoral fins are covered with distinct small black spots, largely confined to the rays which are the best diagnostic colour character of this species. Tips of interspinous dorsal-fin membranes are white or pale yellow.", "about": "The Honeycomb Rockcod is one of the most common and abundant small groupers in the Indo-Pacific: South Africa to French Polynesia. Its depth range is 1-50 m, but usually occurs shallower than 20 m. This species inhabits coastal and offshore coral reefs. It is especially common in shallow lagoons and semi-protected seaward reefs and can also be found in marginal coral reef habitats. Juveniles are common in thickets of staghorn Acropora corals. Its entire life is generally restricted to a relatively small range within its home reef. It feeds on crustaceans and fishes with an increase of piscivory with age. It is important to artisanal fisheries because of its abundance in shallow water and caught with handlines, fish traps, and spear.", "biology": "It is a protogynous hermaphrodite. Males were larger than females in Reunion and sex change occurred at 3-5 years of age. Studies suggest this species forms spawning aggregations outside their home reefs. Its maximum total length is 32 cm.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase, The IUCN Red List of Threatened Species"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.01219, "exponent": 3.079, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Cromileptes altivelis',
  'Humpback Rockcod',
  'Boggel Klipkabeljou',
  'Rockcod Humpback',
  ARRAY['Humpback Grouper','Baramundi Cod'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Decreasing, Data Deficient',
  NULL,
  '{"other_names": "Humpback Grouper, Baramundi Cod", "meristics": "Dorsal spines: 10; Dorsal soft rays: 17-19; Anal spines: 3; Anal soft rays: 9-10.", "appearance": "This species is easily distinguished by its extreme smallness of size of the anterior part of the head as compared to the elevated postorbital part, absence of canine teeth, except for a very small pair at the front of the upper jaw. Pale greenish brown, with widely spaced, round, black spots on head, body, and fins; some spots on body and base of median fins overlain by a large dusky blotch. Black spots on juveniles fewer than on adults and may be as large as or larger than eye.", "about": "Western Pacific from southern Japan to Palau, Guam, New Caledonia, and southern Queensland (Australia); in the eastern Indian Ocean from the Nicobars to Broome, Western Australia. Reports of Humpback Rockcod from the western Indian Ocean are unsubstantiated, although one from Kenya seems valid. Records from the Hawaiian Islands are probably based on released aquarium fishes. Humpback Rockcod occurs on well-developed coral reefs as well as in dead or silty reef areas. It is found in tide pools and is also caught at depths of 40 m. It feeds on small fishes and crustaceans. Growth of Humpback Rockcod in captivity is very slow. Juveniles are prized as aquarium fishes, and adults are one of the most expensive fishes in fish markets wherever it occurs. Humpback Rockcod may have potential as a species for aquaculture. Caught with hook-and-line, spear, and in traps.", "biology": "Max length: 70.0 cm TL.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0128, "exponent": 3.034, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Cephalopholis leopardus',
  'Leopard Rockcod',
  'Luiperd Klipkabeljou',
  'Rockcod Leopard',
  ARRAY['Leopard Hind'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Least Concern, Unknown',
  NULL,
  '{"other_names": "Leopard Hind", "meristics": "Dorsal spines: 9; Dorsal soft rays: 13-15; Anal spines: 3; Anal soft rays: 9-10.", "appearance": "Head and body mottled reddish brown, paler ventrally with numerous red-orange or pinkish red spots: dark brown saddle on caudal peduncle and a similar, but much smaller spot just behind; dark brown or reddish submarginal streak on upper part of caudal fin and a fainter reddish streak on lower part of fin; dark brown spot at rear end of operculum; pectoral fins yellowish on some fish.", "about": "Western Indian Ocean to the central Pacific, including east African coast from Kenya to about 15\u00baS, most islands of the Indian Ocean, Viet Nam, Indonesia, Philippines, Taiwan, Okinawa, Papua New Guinea, northern Australia, and most islands of the west-central Pacific. Leopard Rockcod are absent from the Red Sea and Persian Gulf. It occurs on well-developed coral reefs in depths of 3-38 m. It is one of the smallest species of groupers known. It is omnivorous with diet composed of fishes and crustaceans. Like most small groupers, this is a secretive species, which is usually hiding in caves and crevices in the reef. It is too small to be of interest to fisheries.", "biology": "Max length: 20 cm TL, (larger sizes reported in the literature are unsubstantiated).", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Variola louti',
  'Lyretail Rockcod',
  'Maanstert Klipkabeljou',
  'Rockcod Lyretail',
  ARRAY['Yellow-edge Lyretail','Swallowtail Rockcod','Geelrand Maanstert'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Yellow-edge Lyretail, Swallowtail Rockcod, Geelrand Maanstert", "meristics": "Dorsal spines: 9; Dorsal soft rays: 13-14; Anal spines: 3; Anal soft rays: 8.", "appearance": "The Lyretail Rockcod has an oblong-shaped body on which the length of the head is longer than the depth of the body. The preopercle is rounded with fine serrations along its edge with the lower edge being fleshy. There are three flat spines on the gill cover, which has a straight upper edge. The caudal fin is crescent shaped with its upper and lower lobes extended and are around two times the length of the fin''s middle rays. The overall colour of this grouper is reddish and it is covered in blue spots. The dorsal and anal fins have pointed posterior tips. They have a reddish colour on the upper body which frequently shades to orangish to white on the lower part of the body. They are marked with many small bluish or pinkish spots on the head, body and fins. The rear margins of the fins are yellow.", "about": "This species is distributed in the tropical Indo-West Pacific from Durban, South Africa north to the Red Sea, the southwest Indian coast, southern Japan through the Coral Sea and east to the Pitcairn Islands, including southern Japan to Australia and most of the tropical islands of the Indian and west central Pacific oceans. Its depth range is three to 240 m. This species typically occurs in clear-water, coral reef habitats deeper than 15 m. It prefers islands and offshore reefs, rather than continental shorelines. It feeds primarily on fishes, crabs, shrimps and stomatopods.", "biology": "Females reach maturity at 33 cm SL. Spawning aggregations have been observed. Max length: 100.0 cm TL; Common length: 75.0 cm TL; Max published weight: 12.0 kg", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "coefficient": 0.01219, "exponent": 3.079, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus malabaricus',
  'Malabar Rockcod',
  'Malabar Klipkabeljou',
  'Rockcod Malabar',
  ARRAY['Malabar Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Decreasing, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 11; Dorsal soft rays: 14-16; Anal spines: 3; Anal soft rays: 8.", "appearance": "Body is elongated. Caudal fin rounded. Colour: grey or greenish light brown or light grey to yellowish brown colour; five vertical darker bands often bifurcate ventrally, many dark spots on head and trunk, often small whitish spots in adults.", "about": "The Malabar Rockcod is widespread in Indo-Pacific, from Western Indian Ocean across to Tonga in the east, Japan in the north and Australia in the south. Their southern limit in SA is reported as East London. It is often confused with the Blue-and-yellow Rockcod (E. coioides) and the Greasy rockcod (E. tauvina). The adult movement patterns are unknown. Movement of juveniles reported from inshore and estuarine nursery habitats to depths of 150 m as adults. Adults are found on a variety of reef and non-reef habitats to depths of 150 m. Juveniles are found on inshore reefs and in estuaries. Adults eat equal mixture of fishes and crustaceans, sometimes octopus. The juvenile\u2019 diet is unknown. Based on catch composition research, is not a common component of linefish catches in SA; also, likely to be under-reported owing to misidentification. Elsewhere, it is caught by trawls. It is also an important aquaculture species in the Indo-Pacific region.", "biology": "Reproductive style: Protogynous hermaphrodite (born female and changes sex to male). Breeding/spawning season: Aug-Oct. Breeding/spawning locality: Unknow. Age at maturity: Males: >5 years. Length at maturity: 45-50 cm TL; West Pacific.  Maximum recorded age: Unknown. Maximum recorded weight: 38 kg; New Caledonia (IGFA). Maximum recorded length: 150 cm TL.", "citation": "ST Fennessy, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180 and Marine Species Identification Portal, FishBase"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0128, "exponent": 3.034, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Plectropomus punctatus',
  'Marbled Leopard Rockcod',
  'Marmer Luiperd Klipkabeljou',
  'Rockcod Marbled Leopard',
  ARRAY['Marbled Coral Grouper','Marbled Leopard Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Marbled Coral Grouper, Marbled Leopard Grouper", "meristics": "Dorsal spines: 7-8; Dorsal soft rays: 10-12; Anal spines: 3; Anal soft rays: 8.", "appearance": "Adults brown to reddish or purplish brown, often irregularly mottled with pale olive-green, orange, or white; no small blue spots, broad dark bars, or wedge-shaped blotches on body; pectoral fins dark brown; pelvic and anal fins sometimes with a bluish band or row of blue spots distally. Body of juveniles (7-12 cm standard length) brownish dorsally, with numerous round or oval pale spots (about half eye diameter in size); interspinous membranes between first 5 dorsal-fin spines black; subadults (14-20 cm standard length) with numerous horizontal pale streaks and elongate pale spots (about an eye diameter in length) on head and body.", "about": "Marbled Rockcod is known only from the western Indian Ocean, but not the Red Sea, Persian Gulf or continental coast from Arabia to India. We have examined specimens or verified records from Kenya, Zanzibar, Mozambique, South Africa, Comoros, Madagascar, Aldabra, Seychelles, Mauritius, St. Brandon\u2019s Shoals, Nazareth Bank, and Chagos. Marbled Rockcod is usually found solitary or in small groups in shallow water (less than 10 m), and often drifts well above the bottom however, 3 specimens from the Nazareth Bank (northeast of St. Brandon\u2019s Shoals) were caught on a vertical longline at depths of 45-62 m. This species feeds exclusively on fish, including parrotfishes, wrasses, holocentrids, acanthurids, a triggerfish, and a juvenile Sphyraena. Marbled Rockcod was common in the markets of Zanzibar in the 1950\u2019s. It probably is (or was) also of commercial importance in the Seychelles and Mauritius. It is caught with hook-and-line, trolling, spear, and on benthic longlines.", "biology": "Max length: 96.0 cm TL; Max published weight: 17.3 kg.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.01122, "exponent": 3.05, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus chabaudi',
  'Moustache Rockcod',
  'Snorbaard Klipkabeljou',
  'Rockcod Moustache',
  ARRAY['Moustache Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 11; Dorsal soft rays: 13-14; Anal spines: 3; Anal soft rays: 8-9.", "appearance": "Fresh specimen, 33 cm SL: head and body greyish brown, shading to pale purplish grey ventrally, with 3 broad dark bars between dorsal fin and lateral line, continuing faintly below lateral line; broad dark blotch dorsally on caudal peduncle, reaching about halfway to lateral line; nape and dorsal part of head faintly yellowish; prominent dark brown streak along edge of maxillary groove; fins greyish brown, the tips of spinous dorsal-fin membranes dark reddish brown; rays of median and pelvic fins paler than membranes; pectoral-fin rays brown, the membranes pale. Colour of fresh specimens, 88-115 cm SL from deep water off Kenya: rosy slate or chocolate brown; throat and hidden membranes of head pinkish grey; eye nondescript silvery; no bars, spots, blotches, marginal coloured bands, or conspicuous moustache streak.", "about": "Moustache Rockcod is known from the east coast of Africa (from Kenya to Knysna, South Africa and the Kerala coast of India; however, there are no records between Kenya and Durban, and none between Kenya and India. Along the coast of South Africa, Moustache Rockcod occurs on rocky bottom at depths of 9 to at least 55 m, but it is less common than Yellowbelly Rockcod; and juveniles have not been found in tidepools. Off the coast of Kenya, Moustache Rockcod occurs below the major thermocline at depths of 125-200 m. It is also known only from deep water (250-300 m) off the Kerala coast of India. The presence of this species off the coasts of Kenya and India appears to be an example of \u201ctropical submergence\u201d by which temperate species are able to live at tropical latitudes only in the cooler deep-water zone. Moustache Rockcod are \u201cquite common in the trawl catches off the Keraia coast at a depth of about 250-300 m.\u201d They are probably of some interest to sport and commercial hook-and-line fisheries in SouthAfrica.", "biology": "Max length: 137 cm TL; Common length: 70.0 cm TL; Max published weight: 55.0 kg.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus coioides',
  'Orangespotted Rockcod',
  'Oranjegevlekte Klipkabeljou',
  'Rockcod Orangespotted',
  ARRAY['Orange-spotted Rockcod','Orange Spot Rockcod','Orange-spotted Grouper','Estuary Cod'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Decreasing, Least Concern',
  NULL,
  '{"other_names": "Orange-spotted Rockcod, Orange Spot Rockcod, Orange-spotted Grouper, Estuary Cod", "meristics": "Dorsal spines: 11; Dorsal soft rays: 13-16; Anal spines: 3; Anal soft rays: 8.", "appearance": "This species is distinguished by the following characters: elongated body, lower jaw with 2-3 rows of subequal teeth; Colour: head and body tan dorsally, shading to whitish ventrally; numerous small brownish orange or reddish brown spots on head, body, and median fins; body with 5 faint, irregular, oblique, dark bars which bifurcate ventrally (irregular H-shaped bars); back with 3-4 blackish saddles; orange spots become poorly defined and darker with growth.", "about": "The Orangespotted Rockcod has often been confused with Greasy Rockcod (Epinephelus tauvina) or Malabar Rockcod (Epinephelus malabaricus). This species is distributed in the Indo-Pacific Ocean from Durban, South Africa; north along East Africa, including Madagascar, Reunion and Mauritius, to the Red Sea and Persian Gulf; east to Palau and Fiji; north to the Ryukyu Islands, Japan; and south to the Arafura Sea and northern Australia. It has also migrated through the Suez Canal to the eastern Mediterranean. Its depth range is 0-100 m. This predatory species inhabits coral reefs typically along continental coastlines and large islands. Juveniles often occur in estuaries over sand, mud and gravel substrate and amongst mangroves. It primarily consumes fish, shrimps, crabs and other benthic crustaceans. Overfishing is a major threat to populations in the Persian Gulf and China, but this is not considered a major threat on a global level at this time. Degradation of estuaries (juvenile habitat) and coral reefs (adult habitat) may also impact this species.", "biology": "It is a diandric protogynous hermaphrodite (males can develop directly from the juvenile phase as well as by sex change from functional females). Juvenile sexual differentiation in this species can be significantly influenced by social factors. Female age at first maturity is four years at 34 cm TL. Males reach maturity at 67.5 cm and 7.5 years and transition occurs at 50-75 cm at 4 to 8 years of age. Length at maturity: 48.3 cm range 25-30 cm. Max length: 120 cm TL; Max published weight: 15.0 kg; Max reported age: 22 years.", "citation": "Amorim, P., Choat, J.H., Fennessy, S., Law, C., Ma, K., Myers, R., Nair, R., Rhodes, K., Sadovy, Y., Samoilys, M., Suharti, S. & To, A. 2018, FishBase, the IUCN Red List of Threatened Species."}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Cephalopholis argus',
  'Peacock Rockcod',
  'Pou Klipkabeljou',
  'Rockcod Peacock',
  ARRAY['Peacock Hind','Bluespotted Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Peacock Grouper, Peacock Hind", "meristics": "Dorsal spines: 9; Dorsal soft rays: 15-17; Anal spines: 3; Anal soft rays: 9.", "appearance": "Caudal fin rounded, pelvic fins short.  Dark brown, covered with small, black-edged blue ocelli; often with 5 or 6 pale bars on rear part of body and a large pale area over the chest; posterior margin of median fins usually with a narrow white edge; distal part of pectoral fins sometimes maroonybrown; triangular membranes at tips of dorsal-fin spines orange-gold.", "about": "Peacock Rockcod is the most widely distributed of the groupers, occurring from the Red Sea to South Africa and east to French Polynesia and the Pitcairn group, including northern Australia, Lord Howe Island and Japan. They have recently been introduced to the Hawaiian Islands. It is a common tropical species found in a variety of coral reef habitats from tide pools to depths of at least 40 m. In the Gulf of Aqaba, Peacock Rockcod is found in social units comprising up to 12 adults, including one dominant male. Each group occupies a specific area (up to 2 000 m\u00b2) that is defended by the territorial male and subdivided into secondary territories, each inhabited by a single female. The preferred habitat of Peacock Rockcod is the 1-10 m reef zone. Primarily (75 to 95%) piscivorous, Peacock Rockcod has been blamed for numerous cases of ciguatera in the Pacific region. In the Red Sea researchers found that this species feeds mainly in the early morning and late afternoon; but at Madagascar, it appears to feed more at night. This common and widespread species is important to artisanal fisheries throughout the Indo-West Pacific region. Caught with hook-and-line, spear, and with traps.", "biology": "Max length: 60.0 cm TL; Common length: 40.0 cm TL.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus fasciatus',
  'Redbarred Rockcod',
  'Rooibalk Klipkabeljou',
  'Rockcod Redbarred',
  ARRAY['Blacktip Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Redbarred Grouper, Blacktp Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 15-17; Anal spines: 3; Anal soft rays: 8.", "appearance": "Ground colour varying from pale greenish grey, to pale reddish yellow to scarlet; body often with 5 or 6 faint dark bars, the last on peduncle; body scales (except ventrally) with pale centre and dark rear margin, producing a faint checked pattern; dorsal part of head and nape, including upper jaw, dark red or reddish brown or with bands and blotches of similar colour; often a dark band from below eye to interopercle; rim of orbit black and often bordered by a pale bluish line. Fins reddish orange, pale yellowish green, or greenish brown, the outer triangular part of interspinous membranes of dorsal fin black (dark red in fish from Western Australia and in some specimens from deep water), with pale yellow or white spot behind tip of each spine: soft dorsal, anal, and caudal fins often with pale yellow, white, or pale blue margins; pectoral fins may be yellowish distally. Pacific specimens usually with irregular pale or white blotches and spots on body (often a midlateral series diminishing in size posteriorly from behind pectoral-fin base to caudal peduncle) and a blackish brown line along crease of dorsal-fin base.", "about": "Redbarred Rockcod is one of the most common groupers in the Indo-Pacific region, and it is one of the two most widely distributed species of grouper in the world. Redbarred Rockcod is known from the Red Sea and western Indian Ocean (south to Port Alfred) to Fremantle, Western Australia, and in the Pacific it ranges from Japan and Korea to southern Queensland and Lord Howe Island and eastward to the Pitcairn Islands. It occurs at most (virtually all) of the tropical and subtropical islands of the Indian Ocean and the west-central Pacific, but it is not known from Hawaii. Its absence from the Persian Gulf is puzzling. Redbarred Rockcod is found on coral reefs and rocky bottom from the shore to depths of 160 m. At Madagascar it is one of the most abundant serranids at depths of 20-45 m, and it feeds during the day and night on brachyuran crabs, fishes, shrimps, and galatheid crabs in that order of importance.  Redbarred Rockcod from Kenyan waters ate crabs, stomatopods, fishes, ophiuroids, and octopus. Red Sea specimens consumed mostly fish and also some crustaceans (mainly crabs). Because of its abundance in shallow water, Redbarred Rockcod is widely used for food. It is readily caught with hook-and-line, spear, traps, and gill nets.", "biology": "Max length: 40.0 cm TL; Common length: 22.0 cm TL; Max published weight: 2.0 kg.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Gracila albomarginata',
  'Rededged Rockcod',
  'Rooirand Klipkabeljou',
  'Rockcod Rededged',
  ARRAY['Masked Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Masked Grouper", "meristics": "Dorsal spines: 9; Dorsal soft rays: 14-16; Anal spines: 3; Anal soft rays: 9-10.", "appearance": "It is readily identified by the white square on their sides, blue in natural light. The oblong body is slightly compressed. The head is smaller compared to other groupers. The caudal fin is truncate. Colouration: greenish or reddish brown to brownish grey body colour, with several narrow curved dark bars on the midlateral part of the body; dark brown spot midlaterally on the caudal peduncle; 3-5 blue lines across head. The juveniles are colourful being violet with bright red margins on their dorsal, anal and caudal fins.", "about": "This Indo-Pacific species is distributed from northern Mozambique, east to French Polynesia, north to Okinawa (Japan) and south to the northern Great Barrier Reef (Australia) in a depth range 6-120 m. It is uncommon and an active swimmer usually found on outer reef slopes and channels adjacent to deep water usually solitary but also in small schools of 3-4 individuals. It hovers above the bottom and probably feeds primarily on fish.", "biology": "Max length: 40.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Aethaloperca rogaa',
  'Redmouth Rockcod',
  'Rooibek Kabeljou',
  'Rockcod Redmouth',
  ARRAY['Redmouth Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Redmouth Grouper", "meristics": "Dorsal spines: 9; Dorsal soft rays: 17-18; Anal spines: 3; Anal soft rays: 8-9.", "appearance": "Dark brown to black, occasionally with an orange cast, usually with a pale vertical bar on side of abdomen; distal part of spinous dorsal fin dark orange to brownish red: inside of mouth, gill cavity and upper jaw membranes reddish orange. Juveniles have a broad white posterior margin on caudal fin and a narrow white margin on soft dorsal fin. A 30 cm TL juvenile from the Maldives had a yellow-green spot on each body scale and the median fins with a pale bluish grey margin posteriorly.", "about": "Distributed in the Red Sea to South Africa and east to the Gilbert Islands (Kiribati) in the central Pacific; Japan, Philippines, Indonesia, Australia, Papua New Guinea, Solomon Islands, Caroline Islands, Palau, Thailand, India, Pakistan, Persian Gulf, Gulf of Oman, and probably all of the tropical islands of the Indian Ocean (although not yet found at Reunion or Mauritius). Redmouth Rockcod prefers well-developed coral reefs in depths of 3 to at least 60 m. It is usually seen in or near caves and holes in the reef.  Small cave dwelling fish are the primary food of this species, and it also eats stomatopods. It is an uncommon species, occasionally seen in markets, caught with hook-and-line, spear, and probably in traps.", "biology": "Preliminary data indicate that it spawns at any time of the year and matures (females?) at a length of about 35 cm SL. Max length: 60.0 cm TL.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Cephalopholis sexmaculata',
  'Six-blotch Rockcod',
  'Seskol Klipkabeljou',
  'Rockcod Six-blotch',
  ARRAY['Sixblotch Hind'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Sixblotch Hind", "meristics": "Dorsal spines: 9; Dorsal soft rays: 14-16; Anal spines: 3; Anal soft rays: 9.", "appearance": "Orange-red, with small blue spots sparsely scattered on body but more densely on head and median fins; head also with elongated blue spots and lines; body with 4 dark bars (sometimes very faint) merging dorsally with blackish blotches at base of dorsal fin and extending onto the fin; 2 similar but smaller dark bar/blotches on caudal peduncle; the spaces between the dark body bars sometimes very pale; pectoral fins orange-red.", "about": "It is distributed in the Red Sea and Indo-Pacific from South Africa (southern KZN) to French Polynesia. Confirmed records from the Indian Ocean include Kenya, Zanzibar, Comoro Islands, Mauritius, Reunion, Maldives, Chagos Islands, and Christmas Island. It is absent from the Persian Gulf. In the Pacific Ocean, Six-blotched Rockcod is known from Indonesia, South China Sea, Philippines, southern Japan, Palau, Queensland, New Guinea, Solomon Islands, Caroline Islands, Mariana Islands, Marshall Islands, Line Islands, Fiji Islands, Society Islands, and the Marquesas. It is found on coral reefs in depths of 10-150 m; a secretive species, generally seen hiding in caves and crevices on the outer reef slope. Six-blotched Rockcod is active nocturnally in shallow water and diurnally in deeper water. This species feeds mainly on fish. Six-blotched Rockcod is of commercial interest to certain local fisheries and is caught with hook-and-line, traps, and spear.", "biology": "Sexual maturity occurs at 25 cm SL. Max length: 50.0 cm TL; Max published weight: 7.0 kg.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Anyperodon leucogrammicus',
  'Slender Rockcod',
  'Slank Klipkabeljou',
  'Rockcod Slender',
  ARRAY['Slender Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Slender Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 14-16; Anal spines: 3; Anal soft rays: 8-9.", "appearance": "The body and head are elongate and markedly compressed, its depth distinctly less than head length. Adults greenish to brownish grey with numerous orange-red spots on head (becoming smaller anteriorly), body, dorsal fin, and basally on caudal fin; 4 longitudinal whitish bands or series of streaks often visible on postorbital head and body; membranes of soft dorsal, anal, and caudal fins clear. Juveniles (less than 8 cm total length) with longitudinal, dark-edged, pale bluish grey stripes on a gold back kground; a blue-edged black spot (or double spot) at base of caudal fin and in dorsal fin; an elongate, blackish spot often present on each side of snout in front of nostrils.", "about": "Found in the Red Sea to Mozambique and east to the Marshall Islands, Samoa, and the Phoenix islands; Japan, Philippines, Indonesia, Australia, Papua New Guinea, and probably all of the islands of the tropical Indian Ocean. Slender Rockcod is a coralreef species usually found on protected reefs in depths of 5-80 m. Adults are primarily piscivorous. The distinctive blue and gold striped juveniles are mimics of other rockcods and other species with a similar colour pattern. These wrasses feed on micro-invertebrates and this mimetic association presumably allows Slender Rockcod (in the guise of a harmless wrasse) to approach the small fishes and crustaceans on which it feeds. It is often seen in markets but not plentiful enough to be of commercial importance and is caught with hook-and-line, spear, and probably in traps.", "biology": "Max length: 65.0 cm TL.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Dermatolepis striolata',
  'Smooth Rockcod',
  'Gladde Klipkabeljou',
  'Rockcod Smooth',
  ARRAY['Smooth Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Smooth Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 18-19; Anal spines: 3; Anal soft rays: 9-10.", "appearance": "The dorsal head profile is slightly concave or straight; caudal fin rounded or truncate with rounded corners. Yellowish to reddish brown, paler ventrally; adults with small, round, dark spots all over head, body and fins; juveniles with numerous small dark brown spots on head, body (where they are often horizontally elongate, even forming short lines) and fins (where they are less distinct); median fins also with pale spots; head and body also with more or less distinct, irregular, pale blotches of various sizes.", "about": "Western Indian Ocean: Gulf of Oman and south coast of Arabian Peninsula, Aldabra, Comoros, Madagascar, and coast of Africa from Kenya to South Africa (Durban). It is a rare species seen on shallow coral reefs to depths of 15 m. There are reports of shoaling behaviour in small groups (8 or fewer individuals). The stomach contents of 5 specimens were examined; 3 were empty and 2 contained fish remains. It is too rare to be of commercial importance.", "biology": "Max length: 85.0 cm TL; Max published weight: 10.5 kg.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus magniscuttis',
  'Speckled Rockcod',
  'Gespikkelde Klipkabeljou',
  'Rockcod Speckled',
  NULL,
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Speckled Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 14-15; Anal spines: 3; Anal soft rays: 8.", "appearance": "It has a flat to convex interorbital area, dorsal head profile almost straight; 2 rows of teeth on midlateral part of lower jaw, inner teeth larger than outer teeth. Generally pale brown, with small dark brown (or greyish green?) spots unevenly scattered on dorsolateral parts of head (posterior to eyes) and body, dorsal fin and caudal fin; one row of close-set spots running posteriorly from the eye in a distinct line; no spots on body below level of pectoral fins or on anal and paired fins. Juveniles have 6 or 7 dark lines running horizontally along the body and these disappear with age.", "about": "Indo-West Pacific from KZN, South Africa, Mozambique, R\u00e9union, Mauritius, New Caledonia, Philippines, New Guinea, New Ireland, and Fiji. Not known from the Red Sea or Persian Gulf. Speckled Rockcod is known only from deep water (128-300 m) in the vicinity of coral reefs. It is an excellent food fish; apparently of some commercial importance at Reunion, where it is fairly common.", "biology": "Max length: 150 cm TL; Max published weight: 50.0 kg.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus hexagonatus',
  'Starspotted Rockcod',
  'Stergevlekte Klipkabeljou',
  'Rockcod Starspotted',
  ARRAY['Whitespeckled Rockcod','White-speckled Rockcod','Starspotted grouper','Witspikkel Klipkabeljou'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Whitespeckled Rockcod, White-speckled Rockcod, Starspotted grouper, Witspikkel Klipkabeljou", "meristics": "Dorsal spines: 11; Dorsal soft rays: 15-17; Anal spines: 3; Anal soft rays: 8.", "appearance": "Head and body covered with polygonal (mostly hexagonal) brown spots that tend to merge, leaving only conspicuous triangular white dots at corners of the polygons; spots on belly and ventral part of head more rounded and separated and often reddish brown; 4 or 5 brownish black saddle blotches (formed by groups of darker spots) on dorsal part of body and caudal peduncle, the first 4 extending onto base of dorsal fin; irregular dark bar, formed by darker polygonal spots, on lower part of body below each saddle blotch; dark spots on head progressively smaller anteriorly; large dark brown or olive spot just behind eye, sometimes joined to a horizontally elongate spot of the same colour on opercle; all fins with close-set dark brown or reddish brown spots and white dots, except outer half of pectoral fins with faint dark spots and no white dots; leading edge of pelvic fins and distal margin of anal fin with pale edge and dark brown submarginal band; margin of interspinous dorsal-fin membranes with a blackish brown triangle and short white or pale yellow filament behind tip of each spine. The conspicuous white dots on the body and fins often persist on preserved specimens.", "about": "Starspotted Rockcod is one of the most widely distributed groupers, occurring from the western Indian Ocean to Henderson Island in the Pitcairn Group. It is an insular species, and its absence in the Red Sea and Persian Gulf may be due to its avoidance of continental shelf environments. It is known from most of the tropical Indo-Pacific islands. Starspotted Rockcod occurs at Latham and Zanzibar islands off the coast of Tanzania, but none have been taken on the African coast, except from the Kenyan coast north of Kilifi Creek. Examined specimens from the Comoros, Mauritius, Reunion, Japan (Ryukyu and Izu Islands), and most of the islands of the western and central Pacific region (both on and off the Pacific Plate). It is known from islands of the Great Barrier Reef, but no specimens have been collected from the mainland coast of Australia. There are no verifiable records from Indonesia, the Philippines, Taiwan, or the Hawaiian Islands. Starspotted Rockcod is a coral-reef species, which is usually found in shallow outer-reef areas exposed to surge. It feeds mainly on fishes and crustaceans (especially stomatopods and brachyuran crabs). This fish is too small to be of commercial importance but probably of some importance as a food fish in artisanal fisheries because of its abundance at most islands. It is caught with hook-and-line, traps, spear, and in gillnets.", "biology": "Max length: 27.5 cm TL.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Cephalopholis spiloparaea',
  'Strawberry Rockcod',
  'Aarbei Klipkabeljou',
  'Rockcod Strawberry',
  ARRAY['Strawberry Hind'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Strawberry Grouper, Strawberry Hind", "meristics": "Dorsal spines: 9; Dorsal soft rays: 14-16; Anal spines: 3; Anal soft rays: 9-10.", "appearance": "It has eyes large. Pale reddish orange, mottled and blotched with dark red or brownish red; faint pale spots usually present on head, body, and median fins; caudal fin usually coloured like the body (but distinctly yellowish in some fish from the Comoro Islands) and with a bluish white submarginal band at corners of fin, becoming narrow and marginal (or even disappearing) at centre of fin; ventral margin of soft portion of anal fin (and to lesser extent the dorsal fin) bluish, usually persisting as dusky in preserved specimens; some fish with about 8 faint dark saddle blotches at base of dorsal fin and another on front of caudal peduncle.", "about": "Indo-Pacific from Pinda, Mozambique to French Polynesia and the Pitcairn Group; in the western Pacific, Strawberry Rockcod ranges from the Ryukyu Islands of Japan to Heron Island at the southern end of the Great Barrier Reef. Except for the specimens collected at Pinda, this species is known only from insular localities. Perhaps the most common species of grouper on Indo-Pacific coral reefs below 40 m; specimens have been collected at depths of 15-108 m. It appears that nothing is known of the biology of this species. Because of its small size and relatively deep habitat, this grouper is of little commercial importance.", "biology": "Max length: 30.0 cm TL.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus longispinis',
  'Streakyspot Rockcod',
  'Strepiekol Klipkabeljou',
  'Rockcod Streakyspot',
  ARRAY['Streakyspot Grouper','Longspine Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Streakyspot Grouper, Longspine Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 16-17; Anal spines: 3; Anal soft rays: 8.", "appearance": "It has a slightly rounded caudal fin, a flat to slightly convex interorbital area, convex dorsal head profile, a flat to slightly convex interorbital area and a convex dorsal head profile. Head and body pale; grayish brown, covered with small, dark reddish-brown spots that are round and widely spaced on head and front half of body, but obliquely elongated, closer together and darker posteriorly; fins with similar dark brown spots; a row of dark spots along distal margin of soft dorsal and caudal fins. Juveniles with fewer dark spots on head and body, and 2 to 4 dark blotches on body at base of dorsal fin and extending onto fin; ventral part of head and body with irregular white spots; white blotch on upper part of operculum.", "about": "Continental and insular localities in the Indian Ocean from Kenya to South Africa and east to the Watubela Group of the eastern Banda Sea, including Madagascar, Comoros, Aldabra, Seychelles, Chagos Islands, Mauritius, R\u00e9union, Maldives, Lakshadweep Islands, India, Sri Lanka, Nicobars, and the Nazareth Bank. Not known from the Red Sea or Persian Gulf. Streakyspot Grouper is usually found on coral reefs or rocky areas and occasionally on sandy bottom; depths of capture range from 1-70 m. It feeds mainly on crustaceans, especially crabs and stomatopods, and rarely on fish and squid. It is not uncommon and is caught with hook-and-line, spear, traps, and in trawls.", "biology": "Length at maturity: 23 cm. Max length: 55.0 cm TL; Max published weight: 2.7 kg.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  '[{"measure": "TL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -11.11575031, "exponent": 3.017632008}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus posteli',
  'Tiger Rockcod',
  'Tier Klipkabeljou',
  'Rockcod Tiger',
  ARRAY['Stripedfin Rockcod','Striped-fin Rockcod','Tiger Grouper','Streepvin Klipkabeljou'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  NULL,
  '{"other_names": "Stripedfin Rockcod, Striped-fin Rockcod, Tiger Grouper, Streepvin Klipkabeljou", "meristics": "Dorsal spines: 11; Dorsal soft rays: 15-16; Anal spines: 3; Anal soft rays: 9.", "appearance": "It has a concave interorbital area; front of upper jaw with a pair of large, curved, fixed canines and a pair of similar but slightly smaller canines at front of lower jaw; lateral part of lower jaws with an outer row of 8-9 slightly smaller fixed canines and an inner row of smaller, more slender depressible teeth. Head and body covered with small, irregular, close-set dark red or reddish brown spots, the pale interspaces forming an irregular pale network pattern; dark spots run in rows paralleling the fin rays of soft dorsal and caudal fins; interspinous membranes of dorsal fin with several horizontal dark streaks; live fish with 4 narrow, irregular, oblique, pale bars running down and forward on body: dark spots of some rows on distal part of caudal and soft dorsal fins of some fish merge to form dark streaks running along or between the fin rays: soft dorsal, caudal, anal, and paired fins generally darker than body. This distinctive species is not closely related to the other reticulated species of Epinephelus (Starspotted Rockcod et al.). The teeth in the jaws are larger than any other species of the genus, except the Largetooth Rockcod, Epinephelus bruneus, which is unknown in SA.", "about": "Tiger Rockcod is known only from Madagascar, South Africa (KZNl), and southern Mozambique. It is found on coral reefs in depths of 20-50 m. Stripedfin Rockcod appears to be rare, but it is of some importance to South African sport fishermen along the northern coast of a KZN. It is caught with hook-and-line and spear.", "biology": "Max length: 100.0 cm TL; Max published weight: 10.0 kg.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Cephalopholis sonnerati',
  'Tomato Rockcod',
  'Tamatie Klipkabeljou',
  'Rockcod Tomato',
  ARRAY['Tomato Hind','Tamatie-klipkabeljou'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Tomato Hind, Tamatie-klipkabeljou", "meristics": "Dorsal spines: 9; Dorsal soft rays: 14-16; Anal spines: 3; Anal soft rays: 9.", "appearance": "The dorsal head profile of adults is straight to concave, the nape distinctly convex; pectoral fins subequal to pelvic fins, in specimens larger than 40 cm, the pelvic fins are longer than the pectoral fins; pelvic fins reaching to or beyond anus. Adults from the Indian Ocean with orangey-red to reddish brown body, often with scattered small whitish or purple spots; purple network on head, maxilla, and lips; pectoral fins orange distally; membranes of soft dorsal, caudal, anal, and pelvic fins dusky: dorsal-fin rays orange distally; pelvic-fin tips blackish. Adults from the Pacific Ocean generally pale reddish to yellowish brown, covered with small brownish red or dark brown spots. Juveniles are dark reddish brown to nearly black, the rear margin of the caudal fin and sometimes the pectoral fins whitish. A juvenile of about 10 cm total length photographed in 30 m at Mauritius shows yellow spots on the head, scattered blackish spots (which could be quickly changed to pale greenish) on the body. Larger juveniles are brownish orange with scattered pale greenish spots on the body and a black spot between the upper and middle opercular spines.", "about": "Tomato Rockcod are found in the Indo-Pacific from the east coast of Africa (from Socotra to Durban) to the Line Islands in the central Pacific; in the western Pacific, Tomato Rockcod ranges from southern Japan to southern Queensland. It is known from both continental and insular localities, including most of the islands in the tropical Indian and Pacific Oceans, but it has not been reported from the Red Sea or Persian Gulf. It was not found at the Chagos Archipelago, despite an intensive survey there. Tomato Rockcod is a coral reef species usually caught in depths of 30-100 m, but at Madagascar it occurs in depths of 10-20 m. It feeds on fish and crustaceans. This common and widespread species is of commercial importance throughout most of its range. It is caught with hook-and-line and in traps.", "biology": "Females mature at about 28 cm SL and males at about 34 cm SL. Max length: 57.0 cm TL; Common length: 30.0 cm TL.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0145, "exponent": 3.13, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus multinotatus',
  'Whiteblotched Rockcod',
  'Witvlek Klipkabeljou',
  'Rockcod Whiteblotched',
  ARRAY['White-blotched Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Least Concern, Decreasing',
  NULL,
  '{"other_names": "White-blotched Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 15-17; Anal spines: 3; Anal soft rays: 8.", "appearance": "It has a slightly emarginate to truncate caudal fin; short pelvic fins; distinctly convex interorbital, slightly convex dorsal head profile; posterior nostrils of adults is twice the size of anterior nostrils; 2 rows of teeth on midlateral part of lower jaw. Head and body of live or fresh fish dark purplish grey, with scattered irregular whitish spots and blotches; pelvic fins greyish black; rear margins of median fins with narrow white edge. Specimens from the western Indian Ocean (except the Persian Gulf and Gulf of Oman) usually have numerous small dark reddish-brown spots over the ventral parts of the head and body. Juveniles (4-7 cm SL) from the Persian Gulf are dark greyish blue with caudal fin, peduncle, soft dorsal, and rear part of anal fin yellow: pelvic fins and ventral margin of anal fin blackish.", "about": "Whiteblotched Rockcod is known only from the Indian Ocean (but not the Red Sea); from Zanzibar, southern Mozambique, Madagascar, Mauritius, R\u00e9union, St. Brandon\u2019s Shoals, Seychelles, Persian Gulf, Gulf of Oman, and Western Australia (from Monte Bello Islands to Shark Bay). Literature records include Tanzania (Mafia and Pemba islands), Kenya, Comoros, Chagos, and the Maldive Islands. Juveniles are found on inshore coral reefs, and adults are more common in deeper water (to depths of 90 m). Stomach contents of 3 fish consisted of small fishes and crabs. The colour pattern of juveniles in the Persian Gulf (see above) is remarkably similar to that of the damselfish Neopacentrus sindensis, which occurs from the Persian Gulf to Pakistan. It has been suggested that the juveniles of Whiteblotched Rockcod may be a mimic of the Arabian Demoiselle. When it gets larger than the damselfish, it develops whitish spots on the body and begins to lose its yellow colouration. The basis for this mimicry would seem to be that the grouper is enabled to get closer to its unsuspecting prey. It probably is of some commercial importance in local fisheries. It is caught with hook-and- line and traps.", "biology": "Females mature at 50 cm SL (3.6 kg) and possibly at smaller size.  Max length: 100.0 cm; Common length: 75.0 cm TL; Max published weight: 9.0 kg", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0167, "exponent": 2.964, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus albomarginatus',
  'Whiteedged Rockcod',
  'Witrand Klipkabeljou',
  'Rockcod Whiteedged',
  ARRAY['White-edged Grouper','Captain Fine'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  40,
  NULL,
  5,
  NULL,
  NULL,
  'Decreasing, Vulnerable',
  'Collapsed, <25% of pristine',
  '{"other_names": "Captain Fine", "meristics": "Dorsal spines: 11; Dorsal soft rays: 14; Anal spines: 3; Anal soft rays: 8.", "appearance": "Caudal fin truncate, corners rounded in adults, posterior edge convex in juveniles. Head, body, dorsal and caudal fins pale brown, with numerous small dark brown spots; ventral parts of head and body without spots: on juveniles, many of the spots merge to form double spots; prominent dark brown streak along upper edge of maxillary groove. Margin of interspinous dorsal-fin membranes yellow or gold; soft dorsal and anal fins dusky distally, with prominent white edge; upper and lower edges of rear part of caudal fin also white-edged; pectoral fins yellowish or reddish orange.", "about": "Whiteedged Rockcod are endemic and found on the south-east coast of Africa from Quissico (MOZ) to East London (EC). They appear to be resident based on limited tag-recapture data (ORI Tagging Project). Likely to be a northward movement of pre-adults from southern to northern KZN, as no spawning occurs south of Durban, although considerable numbers of small, reproductively inactive fish are caught in southern KZN. Adults inhabit deeper (>50 m) rocky reefs, to at least 120 m. The juveniles\u2019 habitat is unknown, probably reefs >30 m as seldom seen by divers. Adults feed on crustaceans, fish and squid while the juvenile\u2019s diet is unknown. Whiteedged Rockcod are reasonably common and are of interest to anglers but makes a fairly small contribution to ski-boat catches in KZN i.e. 0.9% and 2% by weight to recreationals and commercials respectively; slightly higher contribution (3-5% by weight) to semi-industrial lineboat catches in southern MOZ.", "biology": "Reproductive style: Probably protogynous (born female and changes sex to male). Breeding/spawning season: Summer, Oct-Feb. Breeding/spawning locality: Deep reefs off northern KZN and southern MOZ. Age at maturity: Females: 3.1 years; Males: derived from females; southern MOZ. Length at maturity: Females: 341 mm TL; Males: derived from females with a minimum observed length of 591 mm TL; southern MOZ. Maximum recorded age: 15 years; male; northern KZN. Maximum recorded weight: 13.0 kg. Maximum recorded length: 100.0 cm TL; male, northern KZN.", "citation": "ST Fennessy, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-18, FishBase"}',
  '[{"measure": "TL", "reference": "Visboekie", "coefficient": 8.76e-06, "exponent": 3.085, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus coeruleopunctatus',
  'Whitespotted Rockcod',
  'Witkol Klipkabeljou',
  'Rockcod Whitespotted',
  ARRAY['White-spotted Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "White-spotted Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 15-17; Anal spines: 3; Anal soft rays: 8.", "appearance": "Head pointed, dorsal profile almost straight; short pelvic fins, not reaching anus. Adults are brownish grey, the body covered with small pale spots overlain with large pale blotches; oblique black saddle on rear half of peduncle; 4 or 5 indistinct black blotches at base of dorsal fin; prominent black streak on maxillary groove. Large adults (over 40 cm SL) brownish, covered with indistinct, contiguous, small pale spots. Juveniles (less than 20 cm SL) dark grey to black, covered with prominent pupil-size white spots and smaller white dots.", "about": "Whitespotted Rockcod is a widely distributed species that ranges from the east coast of Africa (south to East London, South Africa) to Fiji in the central Pacific. It is not known from the Red Sea but does occur in the Persian Gulf. Specimens have been examined from South Africa, Mozambique, Tanzania, Zanzibar, Kenya, Madagascar, Seychelles, Chagos Islands, India, Sri Lanka, Nicobars, Thailand, Indonesia, Philippines, Taiwan, Papua New Guinea, Australia (Queensland and New South Wales), Palau, Solomon Islands, New Caledonia, Vanuatu, Caroline Islands, Marshall Islands, Gilbert Islands (Kiribati), and Fiji. Reliable records dicate that the species is also known from Japan, Ogasawara Islands, Lakshadweep Islands, and the Maldives. The record from northwestern Australia is doubtful; no specimens of Whitespotted Rockcod in the Western Australian Museum. It inhabits coral reefs, usually in or near caves in a depth range of 2-65 m. Juveniles are found in tidepools. It feeds on fish and crustaceans. It is caught with hook-and-line, spear, and in traps and is probably of some importance to fisheries in areas where it is common, but there is no fishery information on this species.", "biology": "Max length: 76.0 cm TL.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus marginatus',
  'Yellowbelly Rockcod',
  'Geelpens Klipkabeljou',
  'Rockcod Yellowbelly',
  ARRAY['Dusky Grouper'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  60,
  NULL,
  1,
  NULL,
  NULL,
  'Decreasing, Vulnerable',
  'Over exploited, 25-40% of pristine',
  '{"other_names": "Dusky Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 14-16; Anal spines: 3; Anal soft rays: 8 - 9.", "appearance": "Body robust; caudal fin rounded; scales small, embedded in thick skin. Head and body dark reddish brown or greyish dorsally, usually yellowish gold ventrally; irregular white, pale greenish yellow or silvery grey blotches usually visible on body and head and mostly arranged in vertical series: black maxillary streak more or less distinct: median fins dark brown: distal edge of anal fin, caudal fin, and often the pectoral fins with a distinct white margin; pelvic fins blackish distally; pectoral fins dark reddish brown or grey; margin of spinous dorsal fin and basal part of paired fins often golden yellow. Colours more evident in young", "about": "Yellowbelly Rockcod are distributed from western Africa (Angola) to Europe (Mediterranean Sea), SA (WC) to MOZ, east coast of South America; also reports from Oman and India. In SA they occur from Cape Point to Inhambane. They are highly resident based on tagging data and underwater observations. Ontogenetic habitat shift is evident with larger fish being found on deeper reefs. Likely to be a northward movement of pre-adults from the WC and EC to KZN, as no spawning occurs south of KZN; generally smaller reproductively inactive fish are caught in the former two provinces. Adults inhabit rocky reefs from surf zone to 200 m. Juveniles are found in tidal pools and shallow reefs in inshore waters. Adults feed on crustaceans, fish and squid. Juveniles eat a higher proportion of crustaceans but also small fish. Yellowbelly Rockcod make a fairly low contribution to ski-boat catches in KZN - 1.3% by weight to commercials, 1.7% to recreationals; around 1% to spearfishing catches and 0.3% to recreational shorefishing and very low contribution (0.1% by weight) to semi-industrial lineboat catches in southern MOZ.", "biology": "Reproductive style: Protogynous hermaphrodite (born female and changes sex to male). Breeding/spawning season: Oct-Feb in KZN. Breeding/spawning locality: Reefs off KZN and southern MOZ. Age at maturity: Females: 6.5 years; Males: 9.5 years (minimum age, males derived from mature females). Length at maturity: Females: 62.2 cm TL; Males: 80.0 cm TL (minimum length as males derived from mature females). Estimated length at maturity of females is 47.0 cm TL in Brazilian waters. Maximum recorded age: 16 years; female; northern KZN. Reported maximum age of Yellowbelly Rockcod is 60 years in the Mediterranean. Maximum recorded weight: 27.5 kg; KZN (SADSAA).  Others have recorded the maximum weight as 60 kg. Maximum recorded length: 112.5 cm TL. Others have recorded the maximum length of Yellowbelly Rockcod as 150 cm TL in the Mediterranean.", "citation": "ST Fennessy, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180 and Marine Species Identification Portal, FishBase"}',
  '[{"measure": "TL", "reference": "Visboekie", "coefficient": 1.25e-05, "exponent": 3.055, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Epinephelus flavocaeruleus',
  'Yellowtail Rockcod',
  'Geelstert Klipkapeljou',
  'Rockcod Yellowtail',
  ARRAY['Purple Rockcod','Yellowtail Grouper','Blue-and-yellow Grouper','Yellow Razorrock','Pole Rockcod'],
  'Serranidae',
  'Sea basses: Groupers and Rockcods',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Purple Rockcod, Yellowtail Grouper, Blue-and-yellow Grouper", "meristics": "Dorsal spines: 11; Dorsal soft rays: 16-17; Anal spines: 3; Anal soft rays: 8.", "appearance": "Head and body dark bluish violet to dark greyish blue, sometimes flecked with pale blue; fins and jaws bright yellow; some fish with corners of caudal fin, margin of soft dorsal and anal fins and tips of pelvic fins blackish. Yellow diminishes with growth, and large adults are dark greyish, dark blue, violet, chestnut brown, or almost black.", "about": "It is found in the Indian Ocean from Djibouti south to Algoa Bay, South Africa and eastward to the Andaman Sea and Sabang Island at the northwest tip of Sumatera (Sumatra), including Mozambique, Zanzibar, Kenya, Reunion, Mauritius, St. Brandon\u2019s Shoals, Seychelles, Lakshadweep Islands, Chagos, Rodriguez, Sri Lanka, and Andaman Islands. Yellowtail Rockcod is not known from the Red Sea or the Persian Gulf. Juveniles of Yellowtail Rockcod (to about 40 cm TL) are found on shallow coral reefs, but adults are more common on deeper reefs, to depths of 150 m. It feeds on a variety of fishes, crabs, shrimps, spiny lobsters, squids, and small octopuses. It is often found in the markets of Mombasa and Zanzibar and is caught with hook-and-line and spear.", "biology": "Females are mature at about 50 cm SL.  Max length: 90.0 cm TL; Common length: 45.0 cm TL; Max published weight: 15.0 kg.", "citation": "FAO Fisheries Synopsis No. 125, Volume 16. GROUPERS OF THE WORLD by Phillip C. Heemstra and John E. Randall; 1993, FishBase"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0167, "exponent": 2.964, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Pereulixia kosiensis',
  'Kosi Rockskipper',
  'Kosi Klipspringer',
  'Rockskipper Kosi',
  NULL,
  'Blenniidae',
  'Combtooth blennies',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 11-12; Dorsal soft rays: 11-12; Anal spines: 2; Anal soft rays: 12-14.", "appearance": "Body dusky with widely scattered, pale bordered, dark or white spots.", "about": "The Kosi Rockskipper is found in the Western Indian Ocean: Pakistan south to Durban, South Africa. It is a marine, demersal species that is commonly found in exposed shallow, rocky shores and tidepools to depths of 2 m.", "biology": "It is oviparous and exhibits distinct pairing. Eggs are demersal and adhesive, and are attached to the substrate via a filamentous, adhesive pad or pedestal. Larvae are planktonic, often found in shallow, coastal waters. Max length: 20.0 cm SL.", "citation": "FishBase"}',
