INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Chrysoblephus laticeps',
  'Roman',
  'Roman',
  'Roman',
  ARRAY['Red Roman','Rooi Roman'],
  'Sparidae',
  'Seabreams, Porgies',
  30,
  NULL,
  2,
  NULL,
  NULL,
  'Stable, Near Threatened',
  'Optimally exploited, 40-50% of pristine; Not overfished',
  '{"other_names": "Red Roman, Rooi Roman", "meristics": "Dorsal spines: 11 - 12; Dorsal soft rays: 10-11; Anal spines: 3; Anal soft rays: 7-9.", "appearance": "The Roman is of robust build, orange to red in colour, showing a striking white saddle and white bar over the gill cover, with a horizontal blue line linking the eyes. Its canines are prominent and it has several rows of molars in both upper and lower jaws.", "about": "Roman are endemic, from NAM to Port St Johns but very rare on the West Coast. They are also reported from southern Madagascar and Mauritius, although these records are doubtful. The distribution of stock is primarily between Cape Point and Port St Johns. They are resident with small home ranges (<100 m linear extent). Adults inhabit deeper high- and low-profile inshore and offshore reefs to 100 m while juveniles are found on shallow subtidal reefs to 30 m. Adults feed on mainly echinoderms (starfish, sea urchin, or sea cucumbers), (sea lilies and feather stars and brittlestars) and cephalopods (octopus and squid) with some crustaceans (decapods - shrimp, crab, or lobster) and polychaetes (bristle worms). Juveniles eat mainly (bristle worms) with some small-bodied decapods (shrimp, crab, or lobster) and mysids (shrimp-like crustaceans). Roman are important to commercial and recreational ski-boat fisheries in WC and EC and are frequently speared by divers and occasionally taken by shore anglers fishing in deep water.  Despite a decline in total commercial catch between 1985 and 2007, Roman have sustained fishing pressure better than many other species of endemic sparid reef fish. It has been suggested that this is largely due to the small size at maturity (~180 mm FL) compared to the minimum legal size (272 mm FL; 300 mm TL). Furthermore, because of their small home range (1,000-3,000 m\u00b2) Roman can be effectively protected within small to medium sized MPAs.", "biology": "The male attracts a harem of females, with which he will mate. A large male is very aggressive and will defend his harem and territory against all intruders, including competitive males. His frantic protective behaviour attracts the attention of predators, which increases his risk of being eaten. Should this happen, the dominant female will immediately take over his role. Sex change takes a little longer. Reproductive style: Protogynous hermaphrodite (changes sex from female to male). The species forms pairs before an elaborate courtship ritual and spawning, the eggs being released well above the seafloor. Breeding/spawning season: Oct-Jan (EC). Spawning behaviour observed during diving surveys between Nov-Feb in the Goukamma area. Breeding/spawning locality: EC and Goukamma area. Age at maturity: Females: 2.5 years; EC; 3.5 years; Goukamma area. Length at maturity: Females: 172 mm FL; Port Elizabeth; 180 mm FL; Tsitsikamma MPA; 184 m FL; Goukamma area. Maximum recorded age: 17 years; male; Tsitsikamma MPA; 19 years; male; Goukamma area. Maximum recorded weight: 4.57 kg (SAUFF). Maximum recorded length: 512 mm FL; Tsitsikamma MPA.", "citation": "A G\u00f6tz, SE Kerwath In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180 and Two Oceans Aquarium, Wikipedia For more information: http://speciesstatus.sanbi.org/assessment/last-assessment/1009/"}',
  '[{"measure": "FL", "reference": "Visboekie", "coefficient": 1.2e-05, "exponent": 3.154, "formula_type": "power-mm", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Etrumeus wongratanai',
  'East Coast Roundherring',
  'Ooskus Rondeharing',
  'Roundherring East Coast',
  ARRAY['Bigeye Herring'],
  'Dussumieriidae',
  'Round herrings',
  NULL,
  NULL,
  NULL,
  NULL,
  NULL,
  'Unknown, Least Concern',
  'Status: Unknown. Fishing pressure light',
  '{"other_names": "Bigeye Herring", "meristics": "Dorsal soft rays: 21-22; Anal soft rays: 10; Vertebrae: 50-53.", "appearance": "When fresh, colour of body iridescent blueygreen on dorsal half of body, the scale edges of upper two or three rows are black, abruptly silvery white ventrally; a semicircular blackish spot posterior to upper third of opercle.", "about": "This marine species has a confirmed distribution off the coast of KwaZulu-Natal, southeast Africa. It may also be distributed off Somalia, in the Gulf of Aden, off the Horn of Africa and possibly off Madagascar, though this has yet to be confirmed. Its recent taxonomic separation from Etrumeus teres contributes to the uncertainty of its distribution. E. wongratanai can be easily separated from E. sadina based on vertebral counts 52-53 vs. 49, respectively. This marine, pelagic and schooling species feeds on larger prey such as copepods, ostracods, amphipods, and decapods. It is likely that adults prefer slightly deeper waters along the shelf break. This species is a component of the purse seine fishery that targets anchovies and sardines off South Africa.", "biology": "This marine, pelagic and schooling species has a maximum standard length of 18.5 cm.", "citation": "FishBase,Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Gilchristella aestuaria',
  'Estuarine Roundherring',
  'Rivier Rondeharing',
  'Roundherring Estuarine',
  ARRAY['Gilchrist''s Round Herring','Whitebait','Witaas'],
  'Ehiravidae',
  'River sprats',
  NULL,
  NULL,
  NULL,
  NULL,
  NULL,
  'Unknown, Least Concern',
  'Depleted',
  '{"other_names": "Gilchrist''s Roundherring, Whitebait, Witaas,", "meristics": "Dorsal spines: 0; Dorsal soft rays: 14-15; Anal spines: 0; Anal soft rays: 20.", "appearance": "Diagnosis: Body rather compressed, belly rounded, with 6 to 9 thin unkeeled pre-pelvic scutes (a thickened horny or bony plate) followed by an equally thin pelvic scute, all with slender ascending arms; no post-pelvic scutes; snout pointed, lower jaw a little prominent, with a single row of fine teeth in each jaw.", "about": "The Estuarine Roundherring can be found in a marine environment and in freshwater and is present in open and closed estuaries, lower reaches of some large rivers just above estuary heads and some permanent coastal, freshwater lakes. It is a member of the herring family Clupeidae that occurs off the coasts of Southern Africa. This species is primarily found in estuaries, lagoons, lakes, and rivers. It has been recorded in the following areas: Namibia; South Africa, from the Orange River, Northern Cape Province, Saldanha Bay, Eastern Cape Province, KwaZulu-Natal to Lake Piti, Mozambique. It is the only species in its genus. It is considered an extremely important fish in estuaries of South Africa. This small sardine-like fish lives in large shoals and provides an important link in the food chain as a food source to larger fish and water birds. High salinities in estuarine systems have resulted in mass mortalities. Salinity increases are often due to over-extraction of water from upper catchments.", "biology": "Length at maturity: 3.0 cm range 2 - 3 cm. Max length: 9.0 cm SL; Max. reported age: 6 years.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
