  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Etrumeus whiteheadi',
  'Redeye Roundherring',
  'Rooioog  Rondeharing',
  'Roundherring Redeye',
  ARRAY['Redeye Round Herring','West Coast Round Herring','South Atlantic Herring','Suidafrikaanse Rondeharing'],
  'Dussumieriidae',
  'Round herrings',
  NULL,
  NULL,
  NULL,
  NULL,
  NULL,
  'Unknown, Least Concern',
  'Stock Status: Optimal; Fishing Pressure: Optimal',
  '{"other_names": "Roundherring, West Coast Roundherring, South Atlantic Herring, Suidafrikaanse Rondeharing", "meristics": "Dorsal spines: 0; Dorsal soft rays: 18-20; Anal spines: 0; Anal soft rays: 12 - 13; Vertebrae: 50.", "appearance": "The pelvic fin base is below or just before base of last dorsal finray.", "about": "This marine, inshore species is widely distributed off the southeastern Atlantic to western Indian Ocean coasts from southern Angola and over the continental shelf from Walvis Bay in central Namibia to Durban, South Africa. The depth range of this species is 0-200 m; however, similar to other closely related species, it probably occupies depths between 0 - 50 m. Juveniles shoal in near-shore upwelling zones, while adults occur in the deeper waters at the continental shelf edge. Redeye Roundherring forms shoals together with Pilchard and is often confused with Pilchard. It feeds on larger types of zooplankton. It is commercially harvested by both South Africa and Namibian fisheries and fluctuations have been documented in the landings of this species over the last 20 years; however, there is currently no evidence of decline (FAO 2019), assuming fishing effort has been relatively constant.", "biology": "Max length: 22.0 cm SL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Status of the South African Marine Fishery Resources, DFFE 2020."}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.00468, "exponent": 3.11, "formula_type": "power", "result_unit": "g"}]'
);

