INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Plectorhinchus gibbosus',
  'Black Rubberlip',
  'Swart Rubberlip',
  'Rubberlip Black',
  ARRAY['Harry Hotlips','Black Sweetlips','Brown Sweetlip'],
  'Haemulidae',
  'Grunts',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Harry Hotlips, Black Sweetlips, Brown Sweetlip", "meristics": "Dorsal spines: 14; Dorsal soft rays: 15-16; Anal spines: 3; Anal soft rays: 7-8.", "appearance": "The Black Rubberlip has fleshy lips which become grossly swollen with age; it has 6 pores on its chin but has no median pit. The overall colour is silvery-grey to brownish with dark edges to the scales, a dusky face, with a thin dark bar on the anterior of the operculum which has a black rear margin. The soft rayed part of the dorsal fin, anal fin and caudal fin are dusky. The juveniles are brownish in colour with a pink or transparent caudal fin and mimic fallen leaves by drifting with the body oriented with their sides parallel to the surface.", "about": "This common species is widely distributed, known from South Africa across south-east Asia and China to northern Australia and east as far as Polynesia. This species is found in shallow coastal waters, around reefs and in estuaries; it will enter mangroves and occasionally freshwater. An adult are found mainly in protected inshore reefs deep offshore and sometimes swims in small groups. They are known to eat crustaceans and other invertebrates and fish. It is excellent food fish.", "biology": "They are oviparous with distinct pairing during breeding. Max length: 75.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.0302, "exponent": 2.86, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Plectorhinchus gaterinus',
  'Blackspotted Rubberlip',
  'Swartspikkel Rubberlip',
  'Rubberlip Blackspotted',
  NULL,
  'Haemulidae',
  'Grunts',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 13; Dorsal soft rays: 19-20; Anal spines: 3; Anal soft rays: 7.", "appearance": "The Blackspotted Rubberlip has fleshy lips, but these do not have a swollen appearance. The chin has 6 pores but lacks a median pit. Its pelvic fins are longer than its pectoral fins. The predominant colour is yellowish grey with a greenish back and yellowish belly. The body and fins are marked with rows of dark grey or black-brown spots. The juveniles are light silver-grey and have yellow fins and 5-7 brown horizontal bands which split up into lines of spots once the fish grows to a SL of 10 to 12 cm.", "about": "This species is distributed in the Western Indian Ocean from East Africa in KwaZulu-Natal, South Africa; Kenya; Zanzibar, Tanzania; the Mozambique Channel, Mauritius, Reunion, Comoros, the Seychelles and Madagascar to the Red Sea, Sea of Oman and the Persian Gulf and Pakistan. Its depth range is 5-55 m. It often forms schools under ledges or along coral slopes by day. It also occurs around sand banks and near estuaries. Juveniles can also occur in seagrass beds. It primarily consumes small fish and crustaceans. The flesh is not regarded highly as it has an iodoform taste.", "biology": "It is oviparous with distinct pairing during breeding. Max length: 50.0 cm TL; Common length: 35.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -10.77190018, "exponent": 2.946597099}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Plectorhinchus chubbi',
  'Dusky Rubberlip',
  'Donker Rubberlip',
  'Rubberlip Dusky',
  NULL,
  'Haemulidae',
  'Grunts',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 1-12; Dorsal soft rays: 16-17; Anal spines: 3; Anal soft rays: 7-8.", "appearance": "Dusky Rubberlips are oblong fish with a uniformly silvery pale grey-brown bronzy colour with with small white spots on body; underside may be paler; fins dark brown; chin pink; juveniles, dusky olive to greyish. It has a sloping, slightly rounded head profile and thick, fleshy lips that become more so with age.", "about": "The Dusky Rubberlip is distributed in east Africa, from Somalia down to SA and India, and is also reported in Indonesia and NW Australia. Adults inhabit rocky and coral reefs down to 80 m. Juveniles occupy Intertidal weedy areas and are sometimes associated with patches of drifting and decaying seaweed. Adults are omnivorous eating small crabs, shrimps, polychaete worms and small fish. Juveniles are known to feed on small shrimps associated with rotting seaweed. Their flesh makes good eating and is therefore popular amongst anglers and spearfishers. It is, however, often reluctant to take baits so is mostly taken by spearfishers.", "biology": "Their reproductive style is unknown. Their breeding/spawning season is in summer recorded off Park Rynie on the KZN south coast.  The age and length at maturity is unknown. The maximum recorded age is unknown. The maximum recorded weight is 9.7 kg and the maximum recorded length is 75 cm TL.", "citation": "JQ Maggs, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, ORI Fish Fact Sheet"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.01905, "exponent": 2.96, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Plectorhinchus schotaf',
  'Minstrel Rubberlip',
  'Minstreel Rubberlip',
  'Rubberlip Minstrel',
  ARRAY['Minstrel Sweetlips'],
  'Haemulidae',
  'Grunts',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Minstrel Sweetlips", "meristics": "Dorsal fin spines: 12 (rarely 13); Dorsal soft rays 18-21.", "appearance": "Minstrel Rubberlip is distinguished by brown stripes on upper part of back. It is an olive-brown, tan, or greyish brown with the rear margin of the gill membrane dusky, and the opercular membrane is red to orangish; silver to pale grey below. The mouth, tongue and pectoral fin bases are red. The caudal fin is bright yellow in juveniles, to dull yellowish or greenish brown in adults.", "about": "This species appears to be restricted to southeast Africa from Port St. Johns, South Africa to the Arabian Sea, including the Red Sea and Persian Gulf and east to the western Pacific. This species is most commonly found in surf zones around rocky and coral reefs as well as in seagrass beds, and occasionally in rivers and estuaries in a depth range 0-80 m. Juveniles are found in tide pools. This species is nocturnal and feeds on zooplankton and small fish.", "biology": "They are oviparous with distinct pairing during breeding. Max length: 80.0 cm TL; Common length: 60.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Fishes of Australia, Aaustralian Museum"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.01318, "exponent": 3.01, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Plectorhinchus sordidus',
  'Redlip Rubberlip',
  'Rooilip Rubberlip',
  'Rubberlip Redlip',
  ARRAY['Sordid Rubberlip'],
  'Haemulidae',
  'Grunts',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Sordid Rubberlip", "meristics": "Dorsal spines: 12; Dorsal soft rays: 17-18; Anal spines: 3; Anal soft rays: 7-8.", "appearance": "Adults are dark grey with orange-red lips and opercle edge, fins dark, membrane at front of dorsal fin narrowly black. Juveniles are colourful.", "about": "This species is distributed in the western Indian Ocean from South Africa (Transkei) north to the Red Sea, Sea of Oman, the Persian Gulf, and Pakistan including Madagascar, R\u00e9union, Mauritius and the Seychelles. Its depth range is 2-25 m. This species inhabits inshore and offshore rocky and coral reefs, as well as seagrass beds. They can be found solitary or in small groups close to shelter. It is a migratory fish, moving inshore during the summer and back out into deep water later in the year. The fish are gregarious, feeding on zooplankton and fish larvae including crustaceans.", "biology": "Spawning takes place near the surface in the open sea. The eggs have filaments attached and are pelagic. Mean age and size at sexual maturity for males was 1.8 years and 24.8 cm and 2.1 years and 26 cm for females. Max length: 60.0 cm TL; Max reported age: 14 years.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -10.77190018, "exponent": 2.946597099}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Plectorhinchus centurio',
  'Sailfin Rubberlip',
  'Seilvin Rubberlip',
  'Rubberlip Sailfin',
  NULL,
  'Haemulidae',
  'Grunts',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 9-10; Dorsal soft rays: 21-25; Anal spines: 3; Anal soft rays: 7.", "appearance": "The Sailfin Rubberlip has fleshy lips which increase in size as the fish mature. There are 6 pores on the chin, but the median pit is absent. The adults are predominantly blueygrey in colour marked with bright yellow to golden orange spots, these frequently form a pattern of lines on the head. The inside of the mouth is vivid orange to red. The dorsal fins have black margins; the anal and pelvic fins have black tips. The juveniles have clear alternating black and white longitudinal stripes. Their underparts are silvery yellow, and they have yellow dorsal and caudal fins yellow marked with black blotches and broken stripes. The stripes grow as the fish grows and begin to split into small circular spots then they fade on body although they occur only sometimes on the caudal fin.", "about": "It has a wide Indo-Pacific distribution. Its range extends from the eastern coast of Africa from the Red Sea to KwaZulu-Natal eastwards as far as Fiji and north to Japan. It is found solitarily or in groups, frequently in turbid water. Its habitat can encompass open muddy, sandy or silty substrates in protected bays or estuaries, around rock outcrops, shipwrecks and rubble. It can also occur in shallow coastal waters and on coral reefs. Juveniles prefer weedy habitats. They feed on benthic invertebrates and smaller fishes. It is found as deep as 80 m and is commonest over silty substrates", "biology": "The males and females form distinct pairs when spawning. Length at Maturity: 31.8 cm. Max length: 100.0 cm FL; Common length: 55.0 cm TL; Max published weight: 6.3 kg.", "citation": "FishBase, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.01413, "exponent": 2.98, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Plectorhinchus playfairi',
  'Whitebarred Rubberlip',
  'Witbalk Rubberlip',
  'Rubberlip Whitebarred',
  NULL,
  'Haemulidae',
  'Grunts',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Not Evaluated',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 12; Dorsal soft rays: 19-20; Anal spines: 3; Anal soft rays: 7.", "appearance": "Black with white crossbars, fading to grey with 4 white bars; lips, skin around mouth and pectorals pink in colour. Juveniles may be banded or spotted and are usually a completely different colour from the adults.", "about": "Whitebarred Rubberlip are distributed in the western Indian Ocean: Red Sea and southern Oman down to SA and Madagascar. In southern Africa they are found along the KZN and EC coast as far south as Port St Johns. They are most likely residents but there is little tagging data to support this. Adults inhabit rocky and coral reefs from intertidal zone down to 80 m while juveniles occupy shallow (< 20 m) coral and rocky reefs. The adults feed on small invertebrate animals such as shrimps, marine worms, small crabs and occasionally juvenile fish. The juvenile\u2019s diet is unknown. They are taken mostly along the Maputaland coast in northern KZN but seldom take a baited hook. In shallow water (<20 m) most are too small to be of interest to spearfishers. Although nowhere abundant in SA, this species is most prevalent in northern KZN, where it occurs in the St Lucia and Maputaland MPAs. Within these two reserves, this species may not be caught from a boat but may be targeted by shore anglers in controlled zones.", "biology": "Almost nothing is known about this fish\u2019s reproductive style, age and length at maturity or breeding season or locality. The maximum recorded weight is 6.8 kg and the maximum recorded length is 90 cm TL.", "citation": "JQ Maggs, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.01349, "exponent": 3.01, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Istiophorus platypterus',
  'Sailfish',
  'Seilvis',
  'Sailfish',
  NULL,
  'Istiophoridae',
  'Billfishes',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Least Concern',
  '(IOTC: Not overfished, subject to overfishing) (ICCAT: Overfished and overfishing uncertain)',
  '{"other_names": "", "meristics": "Dorsal spines: 0; Dorsal soft rays: 47-53; Anal spines: 2; Anal soft rays: 12-15.", "appearance": "Overall slender elongate and fairly compressed with a high, sail-like first dorsal fin; upper jaw prolonged into a very long beak, slender and round in cross-section, usually a little more than twice the length of the elongated lower jaw; jaws and palatines with villiform teeth; no gill rakers; two dorsal fins, the first very large (42-49 rays) the second small (6-7 rays); pectoral fins 18-20 rays; pelvic fins I, 2 soft rays fused together, very long and narrow, depressible into a groove; caudal peduncle with double keels on each side; body covered with small, embedded scales with 1 or 2 blunt points. Colour of back dark with about 20 bluish vertical bars; belly pale silver; sides often have pale, bluish gray vertical bars or rows of spots; membrane of first dorsal fin blue- black with numerous dark spots; bases of first and second anal fins often tinged with silvery white; remaining fins blackish brown or dark blue.", "about": "Sailfish are distributed throughout the tropical, subtropical and temperate waters of the Indian and Pacific Oceans, reaching 45\u00b0S in the WIO. It has been suggested that the related Atlantic Sailfish (Istiophorus albicans) should be treated as a conspecific (belong to the same species). Adults are epipelagic, found primarily near the surface although has been recorded to depths of 160 m but normally remain above the thermocline. Densest concentrations of Sailfish are found near large land masses or offshore islands. Juveniles are also epipelagic, often close to the surface at night and appear to be closely associated with warm currents. Sailfish usually travel alone or in small groups. They appear to feed mostly in midwater along the edges of reefs or current eddies. They are only targeted as gamefish in sport fisheries but valued as bycatch in artisanal and commercial fisheries throughout their distribution. They are caught using a number of methods including trolling lures strip baits, feathers or spoons, as well as live bait fishing and kite fishing, longlining, gillnets, harpooning etc. The most action is found where Sailfish are located on or near the surface where they feed. Off SA, Sailfish are primarily targeted by recreational ski-boat anglers in northern KZN. Their fighting ability and spectacular aerial acrobatics endear the Sailfish to the saltwater angler, but it tires quickly and is considered a light tackle species. Recent acoustic tagging and tracking experiments suggest that this species is quite hardy and that survival of released specimens is good. Catch-and-release angling by sport fisheries and use of circle hooks is encouraged to reduce post-release mortality.", "biology": "Sailfish are heterosexual; females usually grow larger than males. Spawning occurs with males and females forming pairs, or two or three males with one female. Spawning may occur throughout the year with a peak in summer in tropical epipelagic shelf waters. Little reproductive activity is observed in KZN waters. Sailfish mature at ~3 years however the sample size of data collected during opportunistic sampling was modest. The maximum recorded age is 12 years; Taiwan and 13 years; Gulf of Mexico. The maximum recorded weight is 100.2 kg; Ecuador; (IGFA). SA angling record is 64.9 kg; (SADSAA). The maximum recorded length is 348 cm LJFL (Lower Jaw Fork Length); (IGFA).", "citation": "RP van der Elst, BQ Mann In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, FishBase, IGFA"}',
  '[{"measure": "LBFL", "reference": "FishBase", "variant": "Atlantic", "coefficient": 0.0162, "exponent": 2.74, "formula_type": "power", "result_unit": "g"}, {"measure": "LBFL", "reference": "FishBase", "variant": "Pacific", "coefficient": 0.0162, "exponent": 2.74, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Cheimerius nufar',
  'Santer',
  'Santer',
  'Santer',
  ARRAY['Basterman','Soldier','Soldaat','Santer Seabream'],
  'Sparidae',
  'Seabreams, Porgies',
  30,
  NULL,
  5,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  'Overfished',
  '{"other_names": "Basterman, Soldier, Soldaat, Santer Seabream", "meristics": "Dorsal spines: 11 - 12; Dorsal soft rays: 10-11; Anal spines: 3; Anal soft rays: 8.", "appearance": "The Santer is an oval-shaped fish, with a silver-pink body and poorly defined vertical pink bands on its sides that are particularly indistinct on adults. Anal and pelvic fins have a bluish tinge in live specimens. Its pectoral fins are pale blue. Juveniles and sub-adults have 5 red vertical crossbars on their sides. The 3rd to the 7th dorsal spines are more elongate.", "about": "Santer are distributed Western Indian Ocean, from the Red Sea south to SA and east to India and Sri Lanka. In SA they are found from MOZ to Cape Point. They are nomadic. Adults do not appear to undertake extensive migrations with a mean distance moved of 29 km in one study. Another study found that Santer were nomadic between reef complexes with an average distance moved of 5.2 km but with a relatively large proportion (22.8%) that moved over 10 km. Santer are well known to undertake local movements into shallow water during stormy weather or following cold water upwelling events. Adults are found in loose shoals primarily over deeper, low relief reefs down to 130 m. Juveniles are found on shallow reefs and over sand adjacent to reefs from 7-60 m. This fish is a predator, hunting smaller fish such as pilchards, crab, octopus and squid. Juveniles feed on mysids (crustaceans with a carapace), penaeids (shrimp), octopus and small reef fish such as gobies. Santer are important to the commercial and recreational ski-boat fishery in MOZ, KZN, EC and WC but are seldom taken by spearfishermen, however they are occasionally caught from the shore at specific sites in the south EC. They are also recorded infrequently from inshore bottom trawls along the southern Cape coast and off MOZ. It is the second most important commercial linefish caught in KZN and southern MOZ and has become of increasing importance in the EC. On-going monitoring of catch and effort in both recreational and commercial sectors of the linefshery should be continued and improved where possible.", "biology": "Reproductive style: Rudimentary hermaphrodite (possessing both male and female potential in the juvenile phase) and functional gonochorist (separate sexes) when adult, but spawning behaviour and early gonadal development suggest the possibility of protogynous sex change (male to female). Breeding/spawning season: Occurs from Jun-Oct peaking between Jul-Aug in MOZ; May-Nov peaking between Aug-Oct in KZNand Nov-Feb peaking between Dec-Jan in the EC. Breeding/spawning locality: Throughout its distribution including MOZ, KZN and the EC. Age at maturity: Combined sexes: 3-4 years; EC. Length at maturity: Females: 250 mm FL; KZN. Combined sexes: 340 mm TL; EC. Combined sexes: 258 mm FL; MOZ. Maximum recorded age: 22 years; EC. Maximum recorded age of 25 years in the Gulf of Aden. Maximum recorded weight: 8 kg; KZN (SADSAA). Maximum recorded length: 75 cm TL.", "citation": "BQ Mann, ST Fennessy In: Mann BQ. (Ed). Southern African Marine Linefish Species Profiles. Special Publication, Oceanographic Research Institute, Durban 9: 179-180, ORI Fish Fact Sheet, Two Oceans Aquarium. For more information: http://speciesstatus.sanbi.org/assessment/last-assessment/1044/"}',
  '[{"measure": "FL", "reference": "Visboekie", "coefficient": 0.00024, "exponent": 2.571, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Scomberesox saurus',
  'Saury',
  'Saury',
  'Saury',
  ARRAY['Atlantic Saury','Saury Pike','Atlantic Needlefish'],
  'Scomberesocidae',
  'Sauries',
  NULL,
  NULL,
  NULL,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Atlantic Saury, Saury Pike, Atlantic Needlefish", "meristics": "They have a row of finlets behind their dorsal and anal fins.", "appearance": "The Saury is an elongated slender fish with very long, beak-like jaws with minute teeth. They are similar in appearance to garfish but differ in having much smaller teeth. The juvenile\u2019s jaws are relatively short at first but elongate as they grow.", "about": "This species occurs in the east and west Atlantic in a depth range 0-30 m. This is a pelagic, oceanodromous species. It is a highly migratory species, forming schools and is gregarious. Occasionally schools can be found close to shore in large numbers. The species feeds on zooplankton and fish larvae, and it is preyed upon by fish, including tunas, marlin bluefish and cod. Individuals often leap out of the water when chased by predators.", "biology": "Sauries are serial spawners meaning that one female will probably reproduce in different localities as the area suitable for spawning expands. Atlantic Saury is a very fast-growing, short-lived species. Length at maturity: 26.5 cm. Max length: 50.0 cm TL; Common length: 32.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Pristis pristis',
  'Largetooth Sawfish',
  'Groottand Saagvis',
  'Sawfish Largetooth',
  ARRAY['Common Sawfish','Greattooth Sawfish'],
  'Pristidae',
  'Sawfishes',
  Prohibited,
  NULL,
  0,
  NULL,
  NULL,
  'Decreasing, Critically Endangered',
  'Critically Endangered, Locally Extinct',
  '{"other_names": "Greattooth Sawfish, Common Sawfish", "appearance": "Largetooth Sawfish are olive gray to brown on top and have a white underside. Although sawfish have shark-like bodies, they are actually a type of ray, as their gills and mouths are found on the underside of their bodies. Their skeletons have no bones and are instead made of cartilage, a firm tissue more flexible than bone. They are named after their \"saws\" (rostra); long, flat snouts edged with teeth. Largetooth Sawfish have a rostral blade with 20 or fewer pairs of rostral spines or teeth on each side of their snout. Largetooth Sawfish look very similar to Smalltooth Sawfish, and it can be hard to tell the two species apart.", "biology": "The Largetooth Sawfish has the largest historical range of all five sawfish species, but its population has dramatically declined worldwide. Largetooth Sawfish and Smalltooth Sawfish are the two species of sawfish that have historically inhabited U.S. waters, though Largetooth Sawfish have not been found in the United States in 50 years. Weight and lifespan are unknown. They are highly protected. Max length: 750 cm TL; Common length: 250 cm TL; Max reported age: 30 years.", "citation": "FishBase, IGFA"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Pristis pectinata',
  'Smalltooth Sawfish',
  'Kleintand Saagvis',
  'Sawfish Smalltooth',
  NULL,
  'Pristidae',
  'Sawfishes',
  Prohibited,
  NULL,
  0,
  NULL,
  NULL,
  'Decreasing, Critically Endangered',
  'Critically Endangered',
  '{"other_names": "", "appearance": "The Smalltooth Sawfish has the following characters: its first dorsal fin originates about over the origin of its pelvic fins; its caudal fin is short, but broader relative to the length of the fin, with lower lobe only faintly indicated; its rostral teeth are numerous, 23 or more on each side; its saw is relatively somewhat short; its second dorsal fin has the posterior margin much less deeply concave than other species; and its pectoral fins are smaller.", "about": "The Smalltooth Sawfish, despite their shark-like bodies, are actually rays, as their gills and mouths are found on the underside of their bodies and belong to a group of fish called elasmobranchs that includes rays, skates, and sharks. Smalltooth Sawfish live in tropical seas and estuaries (semi-enclosed areas where rivers meet the sea) of the Atlantic Ocean. They rely heavily on very shallow, warm, saline coastal waters fringed with dense mangrove roots, and sometimes enter the lower reaches of freshwater river systems. Smalltooth Sawfish populations have declined dramatically due to habitat loss associated with coastal development and accidental capture in fisheries. Smalltooth Sawfish also are listed as a migratory species threatened with extinction (Appendix I) under the United Nations Environment Programme Convention on the Conservation of Migratory Species of Wild Animals. Participating countries strive to strictly protect these animals, conserving and restoring the places where they live, and mitigating obstacles to migration. Smalltooth Sawfish eat a variety of fish and invertebrates (e.g., shrimp and crabs).  They use their rostra to slash through schools of fish, swinging it from side to side to impale and stun prey. Their rostra also contain electro-sensitive organs, which can sense the weak amount of electricity produced by other animals. These organs likely help sawfish find shrimp and crabs on the seafloor. Ongoing public, political, and financial support is also integral to preventing further sawfish extinction.", "biology": "Smalltooth Sawfish are \"yolk-sac viviparous\". Their young are attached to yolk sacs that nourish the embryo inside the mother''s body and emerge as fully developed pups. The pups are born with their saw fully developed, but it is very flexible and sheathed in a thick gelatinous material to avoid injuring the mother at birth (the sheath dissolves quickly thereafter). A mother Smalltooth Sawfish can have 7 to 14 pups per litter. Newborn sawfish are approximately 610 mm long at birth and double in size over their first year.  Sawfish reach sexual maturity at around 7 years and when they\u2019ve grown to about 335,3 cm long. The length of the female Smalltooth Sawfish gestation period, or pregnancy, is believed to be 12 months and females can give birth every other year. Max length: 760 cm TL; Common length: 550 cm TL; Max published weight: 350.0 kg. They are likely to live for several decades.", "citation": "FishBase, IGFA, NOAA, The IUCN Red List of Threatened Species"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
