INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Tetrapturus pfluegeri',
  'Longbill Spearfish',
  'Langbek Speervis',
  'Spearfish Longbill',
  NULL,
  'Istiophoridae',
  'Billfishes',
  NULL,
  NULL,
  5,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "", "meristics": "Dorsal spines: 0; Dorsal soft rays: 50-57; Anal spines: 0; Anal soft rays: 18-21.", "appearance": "The Longbill Spearfish is a blueish black colour from above, and silvery-white and splattered with brown on the sides and silvery white below. The pectorals are blackish-brown, and the dorsal fins are dark blue. 1st anal fin is dark blue with silvery white at base; 2nd anal fin is blackish brown.", "about": "It is found above the thermocline in open waters between 40\u00b0N and 35\u00b0S. It feeds on pelagic fish such as needlefish, tuna, and jack, as well as squids. There are no fisheries that target this species. It is incidentally caught by anglers. Pelagic longline vessels targeting tuna and swordfish have incidental catches of Longbill Spearfish. Little information is available on the movements and habitat utilization of Longbill Spearfish however indications are that the fish spend the vast majority of time in waters 22-26\u00b0C. They feed mainly on pelagic fish and squids.", "biology": "The Longbill Spearfish is a batch spawner that spawns in tropical waters of both hemispheres. Females probably spawn once a year from late November to early May. The maximum age observed was 7 years for females, 6 years for males. The maximum length of the Longbill Spearfish is 207 cm LJFL. The weight range of fish taken by sports fishing is from 9 to 36.5 kg, with rare specimens over 45 kg. The most common size caught by pelagic longlines is about 165 cm TL throughout the Atlantic fishing grounds. Max length: 254 cm FL; Common length: 165 cm OT; Max. published weight: 58.0 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
