INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Coris aygula',
  'Clown Coris Wrasse',
  'Nar-coris',
  'Wrasse Coris Clown',
  ARRAY['Twinspot Wrasse'],
  'Labridae',
  'Wrasses',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Least Concern, Unknown',
  NULL,
  '{"other_names": "Twinspot Wrasse", "meristics": "Dorsal spines: 9; Dorsal soft rays: 12-13; Anal spines: 3; Anal soft rays: 12.", "appearance": "Caudal fin slightly rounded in females, truncate and with filamentous rays in large males, pelvic fins of males very long. A marked difference in appearance is noted between juveniles and adults; juveniles are white and orange with false eyes, shaded by orange on the dorsal fin. As they age, they lose the twin spots and the colouration deepens to uniformly dark-green, blue or black with light banding and vivid markings on the face and fins. Large males develop a prominent forehead and an elongate first dorsal spine.", "about": "The Clown Cloris is native to the Indian Ocean and the western Pacific Ocean. This is a common species although not always abundant. It is an inhabitant of exposed outer reef flats, lagoon reefs, and seaward reefs where they prefer areas of sand or rubble at depths from 2-30 m often in semi-exposed surge zones. They are generally solitary as adults, while juveniles can often be found in tide pools. It feeds mainly on hard-shelled invertebrates including crustaceans, molluscs and sea urchins.", "biology": "This species is probably protogynous (separate sexes). Max length: 120 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Thalassoma hebraicum',
  'Goldbar Wrasse',
  'Goudstaaf Lipvis',
  'Wrasse Goldbar',
  ARRAY['Goudstaaf-lipvis'],
  'Labridae',
  'Wrasses',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Goudstaaf-lipvis", "meristics": "Dorsal spines: 8; Dorsal soft rays: 13; Anal spines: 3; Anal soft rays: 11.", "appearance": "The Goldbar Wrasse has a golden bar that divides its body from its head. Its olive-gold head is veined in brilliant blue lines. It has an appealing purple-hazed, bluish body.", "about": "This species is widespread in the western Indian Ocean and is found from Somalia to South Africa in the west, via the main Indian Ocean Islands, and eastwards to Madagascar and the Maldive Islands. It is also recorded in Sri Lanka. This species is coral reef-associated, found on lagoon and seaward reefs to depths of 30 m. There appears to be no distinctive colour dimorphism in this species. This species is collected for the aquarium trade and is caught as a food fish in subsistence fisheries.", "biology": "It is oviparous with distinct pairing during breeding. It changes sex from female to male when a male is required. Max length: 23.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Thalassoma trilobatum',
  'Ladder Wrasse',
  'Leer Lipvis',
  'Wrasse Ladder',
  ARRAY['Leer-lipvis'],
  'Labridae',
  'Wrasses',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Leer-lipvis", "meristics": "Dorsal spines: 8; Dorsal soft rays: 13; Anal spines: 3; Anal soft rays: 11.", "appearance": "This species is distinguished by the following characters: Pectoral fin rays usually 16; lateral line scales 25; caudal fin of initial phase slightly rounded to truncate, of terminal males truncate to slightly double emaginate; Body of initial (female) phase greenish grey to pale green with 5-6 dark saddles on back, pair of dark (sometimes diffused) stripes on side, a dark vertical line on most scales of body, and with a diagonal (or C-shaped) pink to dark red below front of eye; while body of terminal male salmon-pink to orange anteriorly, with 2 longitudinal series of vertical green rectangles, every fourth pair of upper series extending as a single green bar across back, the head orange-brown without bands and caudal fin brownish to greenish, shading distally to pink, the posterior third of rays blue. This fish is like the Surge Wrasse (Thalassoma purpureum) but is distinguished from it by the spotted head of the females, the lack ''V-shaped'' mark on the snout and in the males by the head colour. The head of the male is plain brown to orange or shaded with blue. The female has a more spotted and shorter head which lacks the distinct ''V'' on the snout.", "about": "This species is very widespread and occurs throughout much of the Indo-West central Pacific region. It is found from East Africa in the western Indian Ocean to the Pitcairn Islands in the east, the Ryukyu Islands (southern Japan) in the north, and Rapa Island in the South Pacific Ocean. This species is generally found inshore), over shallow exposed reef flats, usually with rock base and mixed coral and algae. It also occurs on surge-swept reef flats, reef margins, and clear rocky shorelines, but may venture into deeper waters up to 10 m depth. It is benthopelagic. Habitats include seagrass beds and coral reefs. It feeds on crustaceans (especially crabs), molluscs, and ophiuroids (brittle stars). Diet at recruit/juvenile stage is mainly zoobenthos and smaller nekton. This species is commercially important in the marine aquarium fish trade. It is only of minor importance in artisanal and subsistence fisheries in parts of its range.", "biology": "They are oviparous and the male and female pair up to spawn, once the eggs hatch the larval stage lasts from 60-99 days. Max length: 30.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "FL", "reference": "FishBase", "formula_type": "log-linear", "coefficient": -11.165960309999999, "exponent": 3.007407904}]'
);

