  'Anchichoerops natalensis',
  'Natal Wrasse',
  'Natalse Lipvis',
  'Wrasse Natal',
  NULL,
  'Labridae',
  'Wrasses',
  Prohibited,
  NULL,
  0,
  NULL,
  NULL,
  'Unknown, Least Concern',
  'Protected',
  '{"other_names": "", "meristics": "Dorsal spines: 13; Dorsal soft rays: 9; Anal spines: 3; Anal soft rays: 10-11.", "appearance": "Colour variable: reddish with yellow to orange spots or orangey-yellow to brown with darker spots; sometimes with dark bars on body.", "about": "The Natal Wrasse is endemic to the western Indian Ocean from Transkei, South Africa to southern Mozambique. It is considered rare throughout its range. It inhabits rocky reefs to depths of at least 60 m. It feeds on macro zoobenthos (crustaceans, molluscs, echinoderms and ascidians). This is a no-take species in South Africa.", "biology": "They are oviparous and the male and female pair up to spawn. Max length: 75.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.01, "exponent": 3.04, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
