INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Thalassoma purpureum',
  'Surge Wrasse',
  'Branders Lipvis',
  'Wrasse Surge',
  ARRAY['Branders-lipvis'],
  'Labridae',
  'Wrasses',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Unknown, Least Concern',
  NULL,
  '{"other_names": "Branders-lipvis", "meristics": "Dorsal spines: 8; Dorsal soft rays: 12-14; Anal spines: 3; Anal soft rays: 10-12.", "appearance": "It has a rather deep, laterally compressed body and a pair of caniniform teeth in the front of its bottom jaw. This is a colourful species of wrasse in which the females are greenish with their snout marked with a dark red \u2018V\u2019. Males are greenish blue in colour with 2-3 irregular bright reddish pink to turquoise stripes along their flanks and they have a large head with a blunt snout which is greenish-blue with pinkish-purple markings. Juveniles and females are green and reddish with 2 longitudinal bands of elliptical or rectangular green spots. In the initial phase it has a vertical dark red line below front of eye usually with a branch to front of snout. It is very similar to the Ladder Wrasse (Thalassoma trilobatum), especially the females, but the Surge Wrasse has a larger head and no spots on the head.", "about": "This species is widely distributed in the Indo-Pacific, as it occurs from East Africa and the Red Sea to central America. It is found around coral reefs between 0-10 m depth, in clean coastal waters to outer reef flats, often in the surge zone but also down to 15 m. It lives groups of females which are spread out over large areas of reef, and which are dominated by a few males which grow much larger than females. It is a carnivorous species which preys on small invertebrates such as crabs, sea urchins, brittlestars and molluscs, as well as small fishes and polychaete worms.", "biology": "It is a protogynous hermaphrodite, (they first function as females and later transform into males when required), and forms spawning groups. They are pelagic spawners. Max length: 46.0 cm TL; Max published weight: 1.2 kg.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "coefficient": 0.02593, "exponent": 2.8, "formula_type": "power", "result_unit": "g"}]'
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Cheilinus trilobatus',
  'Tripletail Wrasse',
  'Driestert Lipvis',
  'Wrasse Tripletail',
  ARRAY['Driestert-lipvis'],
  'Labridae',
  'Wrasses',
  NULL,
  NULL,
  10,
  NULL,
  NULL,
  'Stable, Least Concern',
  NULL,
  '{"other_names": "Driestert-lipvis", "meristics": "Dorsal spines: 9; Dorsal soft rays: 10; Anal spines: 3; Anal soft rays: 8.", "appearance": "It has a moderately deep body with a tri-lobed tail. Pelvic fins long, reaching anus in small fish, well beyond in adults; centre of caudal fin rounded in adults, with the upper and lower rays forming elongate lobes giving the fin a trilobed appearance; lateral line interrupted below posterior portion of dorsal-fin base, with a total of 22-23 pored scales; scales reaching well onto bases of dorsal and anal fins; scales in front of dorsal fin extending forward to above centre of eye; cheek and opercle scaly; lower jaw without scales. 2 strong canines are situated anteriorly in each jaw. Its body is variably pigmented from green to brown with mottled purple and red markings; 4 vertical dark bars on body that are often indistinct on large individuals; head with numerous small red spots; red lines radiating from anterior and posterior of eye; no enlarged tooth present of rear of upper jaw; scales on sides each with a vertical, slightly curved red line; dorsal, anal, and pectoral fins yellow or green with distal red streaks; caudal fin green with a red posterior margin; juveniles with 3-4 dark spots midlaterally on sides and more prominent dark bars. This species is capable of rapid colour changes.", "about": "The Tripletail Wrasse is broadly distributed in the Indo-Pacific, from East Africa to Tuamoto and from Japan to New Caledonia. Juveniles are found on algae reefs and usually around stinging hydrozoans, while adults inhabit coral lagoon, coastal reef-flats, seaward reefs and along shallow reef margins with high coral cover. It is also occasionally observed in grassy areas at depths of 1-30 m. It feeds on hard-shelled benthic invertebrates, including sea urchins, molluscs, crustaceans and occasionally small fishes.", "biology": "It is oviparous with distinct pairing during breeding. Max length: 45.0 cm TL.", "citation": "FishBase, The IUCN Red List of Threatened Species"}',
  NULL
);

INSERT INTO species (
  scientific_name, common_name, afrikaans_name, catalogue_name,
  other_names, family_scientific, family_common,
  minimum_size_cm, minimum_size_note, bag_limit, bag_limit_note,
  closed_season, iucn_status, sa_status, text_data, formulas
) VALUES (
  'Polyprion americanus',
  'Atlantic Wreckfish',
  'Atlantise Wrakvis',
  'Wreckfish Atlantic',
  NULL,
  'Polyprionidae',
  'Wreckfishes',
  Prohibited,
  NULL,
  0,
  NULL,
  NULL,
  'Unknown, Data Deficient',
  'Protected',
  '{"other_names": "", "meristics": "Dorsal spines: 10 - 12; Dorsal soft rays: 11-13; Anal spines: 3; Anal soft rays: 8-10.", "appearance": "The Wreckfish is a large fish with a deep, robust body and a large head with a protruding lower jaw. The anal fin has a short base and has three robust spines. The caudal fin is broad and square. The body is covered with small, firmly attached scales which run up the base of the dorsal and anal fin. It has a large mouth and eyes. The preoperculum has a spiny margin while the operculum has a thick bony strut running horizontally at eye level which terminates in a spine. The back and flanks are dark brown in colour with darker spots and blotches fading to yellowish on the belly. Juveniles have black blotches on their head and body. They have also been described as being bluish grey on the back with a paler silvery sheen on the underside. The fins are blackish brown.", "about": "The Wreckfish has a widespread but discontinuous anti-tropical distribution, primarily in the Atlantic Ocean but also ranging into the Indian and Pacific Oceans in a depth range 40-600 m, usually 100-200 m. Eastern Atlantic: Norway to South Africa, including the Mediterranean, Canary Islands, Madeira, Cape Verde, and Tristan da Cunha. Western Atlantic: Newfoundland, Canada and Gulf of Maine to North Carolina, USA. Western Indian Ocean: St. Paul and Amsterdam islands. Southwest Pacific: New Zealand. It has been recorded from Uruguay to Argentina. The species lives deep and offshore. It is normally a solitary species, but the adults gather to breed during the summer. The juveniles form shoals for protection from predators which hunt small fish in mid-water. Once they attain a length of 50-75 cm they become solitary and look for a territory on the bottom. Wreckfish inhabit demersal habitats where temperature ranges from 6.0 to 16.3\u00b0C. Pelagic juveniles feed on teleost fishes, particularly mackerel species. Adults feed on squids, cuttlefish, crustaceans, octopuses and bottom dwelling fish. Wreckfish is a commercially valuable species and is valued as a food fish in some parts of its range. It is a prohibited no sale species in South Africa.", "biology": "Max length: 210 cm TL; Common length: 80.0 cm TL; Max published weight: 100.0 kg. Wreckfish may live for as much as 90 years.", "citation": "FishBase, The IUCN Red List of Threatened Species, Wikipedia"}',
  '[{"measure": "TL", "reference": "FishBase", "variant": "Atlantic", "coefficient": 0.0195, "exponent": 2.96, "formula_type": "power", "result_unit": "g"}]'
);

